INSERT IGNORE INTO `script_texts` VALUES ('24664', '-1585023', 'Don\'t look so smug! I know what you\'re thinking, but Tempest Keep was merely a set back. Did you honestly believe I would trust the future to some blind, half-night elf mongrel? Oh no, he was merely an instrument, a stepping stone to a much larger plan! It has all led to this, and this time, you will not interfere!', null, null, null, null, null, null, null, 'У вас такой самодовольный вид! Я знаю, о чем вы думаете, но Крепость Бурь была лишь небольшим испытанием. Неужели вы полагаете, что я доверил бы будущее, какому то слепому ночному эльфу-полукровке? Ха-ха-ха! О нет, он был лишь орудием, средством достижения иной, более высокой цели! И теперь, теперь вы уже не сможете помешать мне!', '12413', '1', '0', '0', 'kaelthas MT SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('24560', '-1585022', 'Not what I had... planned...', null, null, null, null, null, null, null, 'На это... я... не рассчитывала...', '12397', '1', '0', '0', 'delrissa SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('24560', '-1585021', 'It\'s been a kick, really.', null, null, null, null, null, null, null, 'Не плохо повеселились, правда?', '12411', '1', '0', '0', 'delrissa PlayerDeath5');
INSERT IGNORE INTO `script_texts` VALUES ('24560', '-1585020', 'One is such a lonely number.', null, null, null, null, null, null, null, 'Один в поле - не воин. Да?', '12410', '1', '0', '0', 'delrissa PlayerDeath4');
INSERT IGNORE INTO `script_texts` VALUES ('24560', '-1585019', 'Not really much of a group, anymore, is it?', null, null, null, null, null, null, null, 'Ну что? На группу вы уже не похожи.', '12409', '1', '0', '0', 'delrissa PlayerDeath3');
INSERT IGNORE INTO `script_texts` VALUES ('24560', '-1585017', 'I call that a good start.', null, null, null, null, null, null, null, 'Это я называю удачным началом.', '12405', '1', '0', '0', 'delrissa PlayerDeath1');
INSERT IGNORE INTO `script_texts` VALUES ('24560', '-1585018', 'I could have sworn there were more of you.', null, null, null, null, null, null, null, 'Могу поклясться, вас было больше.', '12407', '1', '0', '0', 'delrissa PlayerDeath2');
INSERT IGNORE INTO `script_texts` VALUES ('24560', '-1585015', 'Now I\'m getting annoyed.', null, null, null, null, null, null, null, 'Меня это начинает раздражать.', '12401', '1', '0', '0', 'delrissa LackeyDeath3');
INSERT IGNORE INTO `script_texts` VALUES ('24560', '-1585016', 'Lackies be damned! I\'ll finish you myself!', null, null, null, null, null, null, null, 'Да будут прокляты эти прислужники! Я сама покончу с вами!', '12403', '1', '0', '0', 'delrissa LackeyDeath4');
INSERT IGNORE INTO `script_texts` VALUES ('24560', '-1585014', 'Well, aren\'t you lucky?', null, null, null, null, null, null, null, 'Повезло, да?', '12400', '1', '0', '0', 'delrissa LackeyDeath2');
INSERT IGNORE INTO `script_texts` VALUES ('24560', '-1585013', 'Oh, the horror.', null, null, null, null, null, null, null, 'Ужасно, просто ужасно!', '12398', '1', '0', '0', 'delrissa LackeyDeath1');
INSERT IGNORE INTO `script_texts` VALUES ('24560', '-1585012', 'Annihilate them!', null, null, null, null, null, null, null, 'Уничтожьте их!', '12395', '1', '0', '0', 'delrissa SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('24744', '-1585011', 'discharges pure energy!', null, null, null, null, null, null, null, 'освобождает чистую энергию!', '0', '3', '0', '0', 'vexallus EMOTE_DISCHARGE_ENERGY');
INSERT IGNORE INTO `script_texts` VALUES ('24744', '-1585010', 'Con...sume.', null, null, null, null, null, null, null, 'Пог... ло... тить.', '12393', '1', '0', '0', 'vexallus SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('24744', '-1585009', 'Un...leash...', null, null, null, null, null, null, null, 'За... пускать...', '12390', '1', '0', '0', 'vexallus SAY_OVERLOAD');
INSERT IGNORE INTO `script_texts` VALUES ('24744', '-1585008', 'Un...con...tainable.', null, null, null, null, null, null, null, 'Не... удер.. жать.', '12392', '1', '0', '0', 'vexallus SAY_ENERGY');
INSERT IGNORE INTO `script_texts` VALUES ('24744', '-1585007', 'Drain...life!', null, null, null, null, null, null, null, 'Пить... жизнь!', '12389', '1', '0', '0', 'vexallus SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('24723', '-1585006', '%s begins to channel from the nearby Fel Crystal...', null, null, null, null, null, null, null, 'создает канал с соседним Кристаллом Скверны...', '0', '3', '0', '0', 'selin EMOTE_CRYSTAL');
INSERT IGNORE INTO `script_texts` VALUES ('24723', '-1585005', 'No! More... I must have more!', null, null, null, null, null, null, null, 'Нет! Еще... Мне нужно еще!', '12383', '1', '0', '0', 'selin SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('24723', '-1585004', 'I am invincible!', null, null, null, null, null, null, null, 'Я непобедим!', '12385', '1', '0', '0', 'selin SAY_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('24723', '-1585003', 'Enough distractions!', null, null, null, null, null, null, null, 'Хватит размениваться на мелочи!', '12388', '1', '0', '0', 'selin SAY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('24723', '-1585002', 'Yes! I am a god!', null, null, null, null, null, null, null, 'Да! Я бог!', '12382', '1', '0', '0', 'selin SAY_EMPOWERED');
INSERT IGNORE INTO `script_texts` VALUES ('24723', '-1585001', 'My hunger knows no bounds!', null, null, null, null, null, null, null, 'Мой голод неутолим!', '12381', '1', '0', '0', 'selin SAY_ENERGY');
INSERT IGNORE INTO `script_texts` VALUES ('24723', '-1585000', 'You only waste my time!', null, null, null, null, null, null, null, 'Я попусту теряю с вами время!', '12378', '1', '0', '0', 'selin SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1580108', 'Can it be?', null, null, null, null, null, null, null, 'Возможно ли это?', '12525', '0', '0', '0', 'lady liadrinn - SAY2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1580109', 'Blessed ancestors! I feel it... so much love... so much grace... there are... no words... impossible to describe...', null, null, null, null, null, null, null, 'О славные предки! Я чувствую столько любви... милосердия.... нет слов... чтобы описать это...', '12525', '0', '0', '0', 'lady liadrinn - SAY3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1580106', 'Farewell...', null, null, null, null, null, null, null, 'Прощай...', '12523', '0', '0', '0', 'prophet velen - SAY8');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1580107', 'Our arrogance was unpardonable. We damned one of the most noble beings of all. We may never atone for this sin.', null, null, null, null, null, null, null, 'Мы были непростительно высокомерны! Мы прокляли благороднейшие из всех живущих существ. Не знаю сможем ли мы изкупить этот грех.', '12524', '0', '0', '0', 'lady liadrinn - SAY1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1580105', 'Salvation, young one. It waits for us all.', null, null, null, null, null, null, null, 'Спасение, детя моё. Спасение сужденно всем нам.', '12522', '0', '0', '0', 'prophet velen - SAY7');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1580104', 'In time, the light and hope held within - will rebirth more than this mere fount of power... Mayhap, they will rebirth the soul of a nation.', null, null, null, null, null, null, null, 'Придёт день когда свет и надежда живущие в этом сердце, помогут возрадить не только источник могущества, но быть может и душу целого народа.', '12521', '0', '0', '0', 'prophet velen - SAY6');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1580103', 'Gaze now, mortals - upon the HEART OF M\'URU! Unblemished. Bathed by the light of Creation - just as it was at the Dawn.', null, null, null, null, null, null, null, 'Возритесь же смертные - над сердцем М\'УРУ! Не порочные. Омытые Светом Творение - такое каким оно было на заре Вселенной.', '12520', '0', '0', '0', 'prophet velen - SAY5');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1580102', 'Then fortunate it is, that I have reclaimed the noble naaru\'s spark from where it fell! Where faith dwells, hope is never lost, young blood elf.', null, null, null, null, null, null, null, 'К счастью, мне удалось сберечь искру души благородного на ару! Запомни юный эльф крови, там где властвует судьба жива и надежда.', '12519', '0', '0', '0', 'prophet velen - SAY4');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1580101', 'The creature Entropius, whom you were forced to destroy, was once the noble naaru, M\'uru. In life, M\'uru channeled vast energies of LIGHT and HOPE. For a time, a misguided few sought to steal those energies...', null, null, null, null, null, null, null, 'Энтропиус которого вам пришлось уничтожить, некогда был благородным на ару под именем М\'уру. Он был проводником энергии света и надежды. И нашлись заблудшии души что пожелали завладеть этой энергией...', '12518', '0', '0', '0', 'prophet velen - SAY3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1580100', 'As the Legion\'s final defeat draws ever-nearer, stand proud in the knowledge that you have saved worlds without number from the flame. Just as this day marks an ending, so too does it herald a new beginning...', null, null, null, null, null, null, null, 'Близиться время когла Легион будет повержен, и пусть сердца ваши преисполнядца гордости. Ибо благодаря вам бесчисленные миры были спасены, от губительного пламени...', '12516', '0', '0', '0', 'prophet velen - SAY2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1580099', 'Mortal heroes, your victory here today was foretold long ago. My brother\'s anguished cry of defeat will echo across the universe, bringing renewed hope to all those who still stand against the Burning Crusade.', null, null, null, null, null, null, null, 'Смертные герои, ваша сегодняшняя победа была предричена ещё в давние времена, крик моего брата исполненный боли и бессильного гнева эхом разнесётся по вселенной, его поражения принесёт надежду всем кто ещё не склонился перед Пылающим Легионом.', '12515', '0', '0', '0', 'prophet velen - SAY1');
INSERT IGNORE INTO `script_texts` VALUES ('25319', '-1580098', 'I have channeled all I can! The power is in your hands!', null, null, null, null, null, null, null, 'Я отдал всё что мог! Моя энергия в ваших руках!', '12443', '1', '0', '0', 'KJ - SAY_KALEC_ORB_READY4');
INSERT IGNORE INTO `script_texts` VALUES ('25319', '-1580097', 'Another orb is ready! Make haste!', null, null, null, null, null, null, null, 'Готова ещё одна сфера! Торопитесь!', '12442', '1', '0', '0', 'KJ - SAY_KALEC_ORB_READY3');
INSERT IGNORE INTO `script_texts` VALUES ('25319', '-1580096', 'I have empowered another orb! Use it quickly!', null, null, null, null, null, null, null, 'Я наполнил энергией ещё одну сферу! Быстрее используйте её!', '12441', '1', '0', '0', 'KJ - SAY_KALEC_ORB_READY2');
INSERT IGNORE INTO `script_texts` VALUES ('25319', '-1580095', 'I will channel my power into the orbs, be ready!', null, null, null, null, null, null, null, 'Я наполню сферы своей энергией, готовтесь!', '12440', '1', '0', '0', 'KJ - SAY_KALEC_ORB_READY1');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580094', '%s begins to channel dark energy', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'KJ - EMOTE_KJ_DARKNESS');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580093', 'Nooooooooooooo!', null, null, null, null, null, null, null, null, '12527', '1', '0', '0', 'KJ - SAY_KJ_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('25319', '-1580092', 'You are not alone. The Blue Dragonflight shall help you vanquish the Deceiver.', null, null, null, null, null, null, null, 'Вы будете сражаться не одни. Синие Драконы помогут вам победить Искусителя.', '12438', '1', '0', '0', 'KJ - SAY_KALECGOS_JOIN');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580091', 'Strike now, heroes, while he is weakened! Vanquish the Deceiver!', null, null, null, null, null, null, null, 'Разите же герои, розите пока он слаб! Да будет повержен Искуситель!', '12449', '1', '0', '0', 'KJ - SAY_KALECGOS_ENCOURAGE');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580090', 'Goodbye, Anveena, my love. Few will remember your name, yet this day you change the course of destiny. What was once corrupt is now pure. Heroes, do not let her sacrifice be in vain.', null, null, null, null, null, null, null, 'Прощай, Анвина, любовь моя. Не многие будут помнить твоё имя но сегодня ты сумела изменить течение судьбы. Что было оскверненно отныне стало чистым. Герой пусть жертва Анвины будет не напрасной.', '12450', '1', '0', '0', 'KJ - SAY_KALECGOS_GOODBYE');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580089', 'The nightmare is over, the spell is broken! Goodbye, Kalec, my love!', null, null, null, null, null, null, null, 'Кашмар окончился, чары разрушины ! Прощай, Кейлек любовь моя!', '12514', '1', '0', '0', 'KJ - SAY_ANVEENA_GOODBYE');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580088', 'Yes, Anveena! Let fate embrace you now!', null, null, null, null, null, null, null, 'Да, Анвина! Шагни на встречу своей судьбе!', '12448', '1', '0', '0', 'KJ - SAY_KALECGOS_FATE');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580087', 'Kalec... Kalec?', null, null, null, null, null, null, null, 'Кейлек... Кейлек?', '12513', '1', '0', '0', 'KJ - SAY_ANVEENA_KALEC');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580085', 'But I\'m... lost... I cannot find my way back!', null, null, null, null, null, null, null, 'Ну я.... я потерялась.... я не могу найти путь назад!', '12512', '1', '0', '0', 'KJ - SAY_ANVEENA_LOST');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580086', 'Anveena, I love you! Focus on my voice, come back for me now! Only you can cleanse the Sunwell!', null, null, null, null, null, null, null, 'Анвина, Я люблю тебя! Слушай мой голос вернись ко мне! Только ты сможеш отчистить Солнечный Колодец!', '12447', '1', '0', '0', 'KJ - SAY_KALECGOS_FOCUS');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580084', 'You must let go! You must become what you were always meant to be! The time is now, Anveena!', null, null, null, null, null, null, null, 'Ты должна освободиться! Ставь тем чем ты должна быть чем ты была всегда! Анвина время настало!', '12446', '1', '0', '0', 'KJ - SAY_KALECGOS_LETGO');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580082', 'Anveena, you must awaken, this world needs you!', null, null, null, null, null, null, null, 'Пробудись Анвина, ты нужна этому миру!', '12445', '1', '0', '0', 'KJ - SAY_KALECGOS_AWAKEN');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580083', 'I serve only the Master now.', null, null, null, null, null, null, null, 'Теперь я повинуюсь только моему Госпадину.', '12511', '1', '0', '0', 'KJ - SAY_ANVEENA_IMPRISONED');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580080', 'Do not harbor false hope. You cannot win!', null, null, null, null, null, null, null, 'Не стоит тешить себя ложной надеждой! Вам не победить!', '12509', '1', '0', '0', 'KJ - SAY_KJ_PHASE4');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580081', 'Aggghh! The powers of the Sunwell... turned... against me! What have you done? WHAT HAVE YOU DONE?', null, null, null, null, null, null, null, 'Аггххх! Сила Солнечного Колодца обратилась против меня! Что вы наделали? ЧТО ВЫ НАДЕЛАЛИ?', '12510', '1', '0', '0', 'KJ - SAY_KJ_PHASE5');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580076', 'Chaos!', null, null, null, null, null, null, null, 'Хаос!', '12505', '1', '0', '0', 'KJ - SAY_KJ_DARKNESS1');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580077', 'Destruction!', null, null, null, null, null, null, null, 'Разрушение!', '12506', '1', '0', '0', 'KJ - SAY_KJ_DARKNESS2');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580078', 'Oblivion!', null, null, null, null, null, null, null, 'Забвение!', '12507', '1', '0', '0', 'KJ - SAY_KJ_DARKNESS3');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580079', 'I will not be denied! This world shall fall!', null, null, null, null, null, null, null, 'Никто не остановит меня! Этот мир падёт!', '12508', '1', '0', '0', 'KJ - SAY_KJ_PHASE3');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580075', 'The enemy is among you.', null, null, null, null, null, null, null, 'Враг среди вас.', '12504', '1', '0', '0', 'KJ - SAY_KJ_REFLECTION2');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580074', 'Who can you trust?', null, null, null, null, null, null, null, 'Кому можно доверять?', '12503', '1', '0', '0', 'KJ - SAY_KJ_REFLECTION1');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580073', 'Anak-ky\'ri!', null, null, null, null, null, null, null, 'Анак-ки-ри!', '12502', '1', '0', '0', 'KJ - SAY_KJ_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580072', 'Another step towards destruction!', null, null, null, null, null, null, null, 'Ещё один шаг на пути к гибели!', '12501', '1', '0', '0', 'KJ - SAY_KJ_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('25315', '-1580071', 'The expendible have perished... So be it! Now I shall succeed where Sargeras could not! I will bleed this wretched world and secure my place as the true master of the Burning Legion. The end has come! Let the unraveling of this world commence!', null, null, null, null, null, null, null, 'Те кем можно было пожертвовать мертвы... Так тому и быть! Я добъюсь успеха там где Саргерас потерпел поражение! Я заставлю этот жалкий мирок истекать кровью и навеки закреплю за собой место повелителя Пылающего Легиона. Пробил последний час этого мира!', '12500', '1', '0', '0', 'KJ - SAY_KJ_EMERGE');
INSERT IGNORE INTO `script_texts` VALUES ('25608', '-1580070', 'Drain the girl! Drain her power until there is nothing but a vacant shell!', null, null, null, null, null, null, null, 'Вытягивайте силу из девчёнки! Пусть от неё останется только безжизненная оболочка!', '12499', '1', '0', '0', 'KJ - SAY_KJ_OFFCOMBAT5');
INSERT IGNORE INTO `script_texts` VALUES ('25608', '-1580068', 'I have waited long enough!', null, null, null, null, null, null, null, 'Я ждал слишком долго!', '12497', '1', '0', '0', 'KJ - SAY_KJ_OFFCOMBAT3');
INSERT IGNORE INTO `script_texts` VALUES ('25608', '-1580069', 'Fail me and suffer for eternity!', null, null, null, null, null, null, null, 'Если подведёш меня будеш страдать вечно!', '12498', '1', '0', '0', 'KJ - SAY_KJ_OFFCOMBAT4');
INSERT IGNORE INTO `script_texts` VALUES ('25608', '-1580067', 'Stay on task! Do not waste time!', null, null, null, null, null, null, null, null, '12496', '1', '0', '0', 'KJ - SAY_KJ_OFFCOMBAT2');
INSERT IGNORE INTO `script_texts` VALUES ('7607', '-1209000', 'Oh no! Here they come!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'Weegli Blastfuse SAY_WEEGLI_OHNO');
INSERT IGNORE INTO `script_texts` VALUES ('25608', '-1580066', 'All my plans have led to this!', null, null, null, null, null, null, null, 'Как долго я предвкушал этот момент!', '12495', '1', '0', '0', 'KJ - SAY_KJ_OFFCOMBAT1');
INSERT IGNORE INTO `script_texts` VALUES ('25165', '-1580064', '%s directs Shadow Nova at $N', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'eredar - emote shadow nova');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580065', '%s directs Conflagration at $N', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'eredar - emote conflagration');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580063', 'These are the pillars...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'eredar - YELL_INTRO_ALY_8');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580062', 'These are the hallmarks...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'eredar - YELL_INTRO_SAC_7');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580061', 'Chaos...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'eredar - YELL_INTRO_ALY_6');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580059', 'Hatred...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'eredar - YELL_INTRO_ALY_4');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580060', 'Mistrust...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'eredar - YELL_INTRO_SAC_5');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580058', 'Confusion...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'eredar - YELL_INTRO_SAC_3');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580057', 'Depravity...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'eredar - YELL_INTRO_ALY_2');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580056', 'Misery...', null, null, null, null, null, null, null, 'Страдания... Пороки... Ненависть... Вот столпы нашей силы.', '12484', '1', '0', '0', 'eredar - YELL_INTRO_SAC_1');
INSERT IGNORE INTO `script_texts` VALUES ('25165', '-1580055', 'Time is a luxury you no longer possess!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'eredar - YELL_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('25165', '-1580054', 'I... fade.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'eredar - YELL_SAC_DEAD');
INSERT IGNORE INTO `script_texts` VALUES ('25165', '-1580053', 'Ee-nok Kryul!', null, null, null, null, null, null, null, 'И-нок Краюл!', '12487', '1', '0', '0', 'eredar - YELL_SAC_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('25165', '-1580052', 'Shadow engulf.', null, null, null, null, null, null, null, 'Тень поглотит тебя.', '12486', '1', '0', '0', 'eredar - YELL_SAC_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580049', 'Your luck has run its curse!', null, null, null, null, null, null, null, 'Удача отвернулась от вас!', '12493', '1', '0', '0', 'eredar - YELL_BERSERK');
INSERT IGNORE INTO `script_texts` VALUES ('25165', '-1580050', 'Shadow to the aid of fire!', null, null, null, null, null, null, null, 'Тень в помощь пламени!', '12485', '1', '0', '0', 'eredar - YELL_SHADOW_NOVA');
INSERT IGNORE INTO `script_texts` VALUES ('25165', '-1580051', 'Alythess! Your fire burns within me!', null, null, null, null, null, null, null, 'Алитесс! Твоё пламя бушует во мне!', '12488', '1', '0', '0', 'eredar - YELL_SISTER_ALYTHESS_DEAD');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580048', 'De-ek Anur!', null, null, null, null, null, null, null, 'Де-рек Ману!', '12494', '1', '0', '0', 'eredar - YELL_ALY_DEAD');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580047', 'Ed-ir Halach!', null, null, null, null, null, null, null, 'Ед-ир Хараш!', '12491', '1', '0', '0', 'eredar - YELL_ALY_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580046', 'Fire consume.', null, null, null, null, null, null, null, 'Да поглатит тебя пламя.', '12490', '1', '0', '0', 'eredar - YELL_ALY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580045', 'Sacrolash!', null, null, null, null, null, null, null, 'Сакролаш!', '12492', '1', '0', '0', 'eredar - YELL_SISTER_SACROLASH_DEAD');
INSERT IGNORE INTO `script_texts` VALUES ('25166', '-1580044', 'Fire to the aid of shadow!', null, null, null, null, null, null, null, 'Пламя в помощь тени!', '12489', '1', '0', '0', 'eredar - YELL_CANFLAGRATION');
INSERT IGNORE INTO `script_texts` VALUES ('25038', '-1580043', 'Madrigosa deserved a far better fate. You did what had to be done, but this battle is far from over.', null, null, null, null, null, null, null, 'Мадригоса не заслужила такой учести. Вы выполнили свой долг, но эта битва ещё не окончена.', '12439', '1', '0', '0', 'felmyst - YELL_KALECGOS');
INSERT IGNORE INTO `script_texts` VALUES ('25038', '-1580042', 'Kil\'jaeden... will... prevail...', null, null, null, null, null, null, null, 'Килджедан... победит...', '12483', '1', '0', '0', 'felmyst - YELL_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('25038', '-1580041', 'No more hesitation! Your fates are written!', null, null, null, null, null, null, null, 'Прочь сомнения! Ваши судьбы предрешены!', '12482', '1', '0', '0', 'felmyst - YELL_BERSERK');
INSERT IGNORE INTO `script_texts` VALUES ('25038', '-1580040', 'I am stronger than ever before!', null, null, null, null, null, null, null, 'Я сильнее чем когда либо прежде!', '12479', '1', '0', '0', 'felmyst- YELL_TAKEOFF');
INSERT IGNORE INTO `script_texts` VALUES ('25038', '-1580039', 'Choke on your final breath!', null, null, null, null, null, null, null, 'Это был ваш последний вздох!', '12478', '1', '0', '0', 'felmyst - YELL_BREATH');
INSERT IGNORE INTO `script_texts` VALUES ('25038', '-1580038', 'The end has come!', null, null, null, null, null, null, null, 'Настал твой последний час!', '12481', '1', '0', '0', 'felmyst - YELL_KILL2');
INSERT IGNORE INTO `script_texts` VALUES ('25038', '-1580037', 'I kill for the master!', null, null, null, null, null, null, null, 'Я несу смерть во имя господина!', '12480', '1', '0', '0', 'felmyst - YELL_KILL1');
INSERT IGNORE INTO `script_texts` VALUES ('25038', '-1580036', 'Glory to Kil\'jaeden! Death to all who oppose!', null, null, null, null, null, null, null, 'Слава Килджедану! Смерть его врагам!', '12477', '1', '0', '0', 'felmyst - YELL_BIRTH');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580035', 'Malygos, my lord! I did my best!', null, null, null, null, null, null, null, 'Малигос, господин мой! Я сделала все, что могла!', '12476', '1', '0', '0', 'madrigosa YELL_MADR_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580034', 'Speak, I grow weary of asking!', null, null, null, null, null, null, null, 'Говори, я устала ждать ответа!', '12475', '1', '0', '0', 'madrigosa YELL_MADR_TRAP');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580033', 'You will tell me where they are!', null, null, null, null, null, null, null, 'Ты расскажешь мне, что с ними случилось!', '12474', '1', '0', '0', 'madrigosa YELL_MADR_ICE_BLOCK');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580032', 'Where is Anveena, demon? What has become of Kalec?', null, null, null, null, null, null, null, 'Где Анвина? Отвечай демон! Что с Кейликом?', '12473', '1', '0', '0', 'madrigosa YELL_MADR_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580031', 'Hold, friends! There is information to be had before this devil meets his fate!', null, null, null, null, null, null, null, 'Постойте, друзья! Нам нужно кое-что узнать, прежде чем этот демон встретит свою смерть.', '12472', '1', '0', '0', 'madrigosa YELL_MADR_ICE_BARRIER');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580030', 'Gah! Well done... Now... this gets... interesting...', null, null, null, null, null, null, null, 'Ааах! Неплохо... это становиться интересным...', '12471', '1', '0', '0', 'brutallus YELL_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580029', 'So much for a real challenge... Die!', null, null, null, null, null, null, null, 'Да, я ожидал от вас большего... Умрите!', '12470', '1', '0', '0', 'brutallus YELL_BERSERK');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580028', 'I live for this!', null, null, null, null, null, null, null, 'Я живу ради этого!', '12469', '1', '0', '0', 'brutallus YELL_LOVE3');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580027', 'Another day, another glorious battle!', null, null, null, null, null, null, null, 'Новый день, новая славная битва!', '12468', '1', '0', '0', 'brutallus YELL_LOVE2');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580026', 'Bring the fight to me!', null, null, null, null, null, null, null, 'Я жажду битв!', '12467', '1', '0', '0', 'brutallus YELL_LOVE1');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580025', 'Too easy!', null, null, null, null, null, null, null, 'Легче легкого!', '12466', '1', '0', '0', 'brutallus YELL_KILL3');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580024', 'You are meat!', null, null, null, null, null, null, null, 'Ты просто кусок мяса!', '12465', '1', '0', '0', 'brutallus YELL_KILL2');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580023', 'Perish, insect!', null, null, null, null, null, null, null, 'Умри насекомое!', '12464', '1', '0', '0', 'brutallus YELL_KILL1');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580022', 'Ahh! More lambs to the slaughter!', null, null, null, null, null, null, null, 'Ааа! Новые овечки на заклание!', '12463', '1', '0', '0', 'brutallus YELL_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580021', 'Come, try your luck!', null, null, null, null, null, null, null, 'Давай, попытай счастья!', '12462', '1', '0', '0', 'brutallus YELL_INTRO_TAUNT');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580020', 'That was fun.', null, null, null, null, null, null, null, 'Хорошая разминка, а теперь я жду достойного противника.', '12461', '1', '0', '0', 'brutallus YELL_INTRO_KILL_MADRIGOSA');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580018', 'Grah! Your magic is weak!', null, null, null, null, null, null, null, 'Ха! Твоя магия просто смехотворна!', '12459', '1', '0', '0', 'brutallus YELL_INTRO_BREAK_ICE');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580019', 'I will crush you!', null, null, null, null, null, null, null, 'Я сокрушу тебя!', '12460', '1', '0', '0', 'brutallus YELL_INTRO_CHARGE');
INSERT IGNORE INTO `script_texts` VALUES ('24882', '-1580017', 'Puny lizard! Death is the only answer you\'ll find here!', null, null, null, null, null, null, null, 'Жалкая ящерица! Ты найдешь здесь свою смерть!', '12458', '1', '0', '0', 'brutallus YELL_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('24892', '-1580016', 'I have toyed with you long enough!', null, null, null, null, null, null, null, 'Надоело с вами возиться!', '12457', '1', '0', '0', 'sathrovarr SAY_SATH_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('24892', '-1580015', 'Haven\'t you heard? I always win!', null, null, null, null, null, null, null, 'Ты что не слышал? Я всегда побеждаю!', '12456', '1', '0', '0', 'sathrovarr SAY_SATH_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('24892', '-1580014', 'Pitious mortal!', null, null, null, null, null, null, null, 'Жалкий смертный!', '12455', '1', '0', '0', 'sathrovarr SAY_SATH_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('24892', '-1580013', 'I will watch you bleed!', null, null, null, null, null, null, null, 'Я буду смотреть как вы истекаете кровью!', '12454', '1', '0', '0', 'sathrovarr SAY_SATH_SPELL2');
INSERT IGNORE INTO `script_texts` VALUES ('24892', '-1580012', 'Your misery is my delight!', null, null, null, null, null, null, null, 'Приятно видеть ваши муки!', '12453', '1', '0', '0', 'sathrovarr SAY_SATH_SPELL1');
INSERT IGNORE INTO `script_texts` VALUES ('24892', '-1580011', 'I\'m... never on... the losing... side...', null, null, null, null, null, null, null, 'Я... никогда.. не... проигрываю..', '12452', '1', '0', '0', 'sathrovarr SAY_SATH_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('24892', '-1580010', 'There will be no reprieve. My work here is nearly finished.', null, null, null, null, null, null, null, 'Хахаха отсрочки не будет. Мои труды почти завершены.', '12451', '1', '0', '0', 'sathrovarr SAY_SATH_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('24891', '-1580009', 'I am forever in your debt. Once we have triumphed over Kil\'jaeden, this entire world will be in your debt as well.', null, null, null, null, null, null, null, 'Я навеки у вас в долгу. И когда мы победим Кил\'джедена, весь мир будет обязан вам спасением.', '12431', '1', '0', '0', 'kalecgos humanoid SAY_GOOD_PLRWIN');
INSERT IGNORE INTO `script_texts` VALUES ('24891', '-1580008', 'Hurry! There is not much of me left!', null, null, null, null, null, null, null, 'Скорее! Пока я, это все еще я!', '12430', '1', '0', '0', 'kalecgos humanoid SAY_GOOD_NEAR_DEATH2');
INSERT IGNORE INTO `script_texts` VALUES ('24891', '-1580007', 'Aaahhh! Help me, before I lose my mind!', null, null, null, null, null, null, null, 'Ааахггг! Помогите, пока я не утратил разум!', '12429', '1', '0', '0', 'kalecgos humanoid SAY_GOOD_NEAR_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('24891', '-1580006', 'I need... your help... Cannot... resist him... much longer...', null, null, null, null, null, null, null, 'Мне нужна... помощь...Я не смогу долго... сопротивляться...', '12428', '1', '0', '0', 'kalecgos humanoid SAY_GOOD_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('24850', '-1580005', 'My awakening is complete! You shall all perish!', null, null, null, null, null, null, null, 'Теперь я пробудился! И все вы погибните!', '12427', '1', '0', '0', 'kalecgos SAY_EVIL_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('24850', '-1580003', 'In the name of Kil\'jaeden!', null, null, null, null, null, null, null, 'Во имя Кил\'джедена!', '12425', '1', '0', '0', 'kalecgos SAY_EVIL_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('24850', '-1580004', 'You were warned!', null, null, null, null, null, null, null, 'Я предупреждал тебя!', '12426', '1', '0', '0', 'kalecgos SAY_EVIL_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('24850', '-1580002', 'Your pain has only begun!', null, null, null, null, null, null, null, 'Вы еще не знаете, что такое боль!', '12424', '1', '0', '0', 'kalecgos SAY_EVIL_SPELL2');
INSERT IGNORE INTO `script_texts` VALUES ('24850', '-1580001', 'I will purge you!', null, null, null, null, null, null, null, 'Я вас уничтожу!', '12423', '1', '0', '0', 'kalecgos SAY_EVIL_SPELL1');
INSERT IGNORE INTO `script_texts` VALUES ('24850', '-1580000', 'Aggh! No longer will I be a slave to Malygos! Challenge me and you will be destroyed!', null, null, null, null, null, null, null, 'Аг! Да отныне я не раб Малигоса! Да сгинет тот, кто бросит мне вызов!', '12422', '1', '0', '0', 'kalecgos SAY_EVIL_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('26723', '-1576044', 'Stay. Enjoy your final moments.', null, null, null, null, null, null, null, 'Останьтесь. Насладитесь последними моментами своей жизни.', '13451', '1', '0', '0', 'keristrasza SAY_CRYSTAL_NOVA');
INSERT IGNORE INTO `script_texts` VALUES ('26723', '-1576043', 'Dragonqueen... Life-Binder... preserve... me.', null, null, null, null, null, null, null, 'Королева Драконов... Хранительница жизни... защити... меня.', '13454', '1', '0', '0', 'keristrasza SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('26723', '-1576042', 'Finish it! FINISH IT! Kill me, or I swear by the Dragonqueen you\'ll never see daylight again!', null, null, null, null, null, null, null, 'Довольно! Хватит! Прикончите меня или клянусь Королевой Драконов, вы больше никогда не увидите дневного света!', '13452', '1', '0', '0', 'keristrasza SAY_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('26723', '-1576041', 'Now we\'ve come to the truth!', null, null, null, null, null, null, null, 'Ну вот мы и выяснили правду!', '13453', '1', '0', '0', 'keristrasza SAY_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('26723', '-1576040', 'Preserve? Why? There\'s no truth in it. No no no... only in the taking! I see that now!', null, null, null, null, null, null, null, 'Защищать? Зачем? Это не правильно. Нет нет нет... нужно только брать! Теперь я это понимаю!', '13450', '1', '0', '0', 'keristrasza SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('26794', '-1576023', 'Bleed!', null, null, null, null, null, null, null, 'Крооовь!', '13332', '1', '0', '0', 'ormorok SAY_CRYSTAL_SPIKES');
INSERT IGNORE INTO `script_texts` VALUES ('26794', '-1576024', 'Aaggh! Kill!', null, null, null, null, null, null, null, 'Ааа! Убить!', '13329', '1', '0', '0', 'ormorok SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('26794', '-1576022', 'Back!', null, null, null, null, null, null, null, 'Проооч!', '13331', '1', '0', '0', 'ormorok SAY_REFLECT');
INSERT IGNORE INTO `script_texts` VALUES ('26794', '-1576021', 'Aaggh!', null, null, null, null, null, null, null, 'Ааггх!', '13330', '1', '0', '0', 'ormorok SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('26794', '-1576020', 'Noo!', null, null, null, null, null, null, null, 'Уничтожение неизбежно!', '13328', '1', '0', '0', 'ormorok SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('26763', '-1576013', 'Indestructible.', null, null, null, null, null, null, null, 'Уничтожение неизбежно.', '13189', '1', '0', '0', 'anomalus SAY_SHIELD');
INSERT IGNORE INTO `script_texts` VALUES ('26763', '-1576012', 'Reality... unwoven.', null, null, null, null, null, null, null, 'Реальность... расплетена.', '13188', '1', '0', '0', 'anomalus SAY_RIFT');
INSERT IGNORE INTO `script_texts` VALUES ('26763', '-1576011', 'Of course.', null, null, null, null, null, null, null, 'Хаос зовёт.', '13187', '1', '0', '0', 'anomalus SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('26763', '-1576010', 'Chaos beckons.', null, null, null, null, null, null, null, 'Хаос зовет.', '13186', '1', '0', '0', 'anomalus SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('26731', '-1576005', 'I\'ll give you more than you can handle.', null, null, null, null, null, null, null, 'Вы получите больше, чем заслуживаете.', '13322', '1', '0', '0', 'grand magus telestra SAY_SPLIT_2');
INSERT IGNORE INTO `script_texts` VALUES ('26731', '-1576004', 'There\'s plenty of me to go around.', null, null, null, null, null, null, null, 'Меня на всех хватит.', '13321', '1', '0', '0', 'grand magus telestra SAY_SPLIT_1');
INSERT IGNORE INTO `script_texts` VALUES ('26731', '-1576003', 'Now to finish the job!', null, null, null, null, null, null, null, 'Ну а теперь, покончим с этим!', '13323', '1', '0', '0', 'grand magus telestra SAY_MERGE');
INSERT IGNORE INTO `script_texts` VALUES ('26731', '-1576002', 'Damn the... luck.', null, null, null, null, null, null, null, 'Фортуна, будь ты... проклята.', '13320', '1', '0', '0', 'grand magus telestra SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('26731', '-1576001', 'Death becomes you!', null, null, null, null, null, null, null, 'Смерть тебе к лицу!', '13324', '1', '0', '0', 'grand magus telestra SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('26731', '-1576000', 'You know what they say about curiosity.', null, null, null, null, null, null, null, 'Говорят любопытство наказуемо.', '13319', '1', '0', '0', 'grand magus telestra SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('24201', '-1574020', 'Skarvald, you incompetent slug! Return and make yourself useful!', null, null, null, null, null, null, null, 'Скарвалд, некомпетентное ты ничтожество! Вернись, и сделай хоть что-нибудь полезное!', '13203', '1', '0', '0', 'dalronn YELL_DALRONN_SKA_DIEDFIRST');
INSERT IGNORE INTO `script_texts` VALUES ('24201', '-1574019', 'You may serve me yet.', null, null, null, null, null, null, null, 'Ты мне еще пригодишься.', '13202', '1', '0', '0', 'dalronn YELL_DALRONN_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('24201', '-1574018', 'There\'s no... greater... glory.', null, null, null, null, null, null, null, 'Нет... смерти... почетнее.', '13201', '1', '0', '0', 'dalronn YELL_DALRONN_DAL_DIEDFIRST');
INSERT IGNORE INTO `script_texts` VALUES ('24201', '-1574017', 'See... you... soon.', null, null, null, null, null, null, null, 'До... скорой... встречи.', '13200', '1', '0', '0', 'dalronn YELL_DALRONN_SKA_DIED');
INSERT IGNORE INTO `script_texts` VALUES ('24201', '-1574016', 'By all means, don\'t assess the situation, you halfwit! Just jump into the fray!', null, null, null, null, null, null, null, 'Конечно, зачем напрягаться, пытаясь оценить обстановку, недоумок. Лучше сразу полезть на рожон!', '13199', '1', '0', '0', 'dalronn YELL_DALRONN_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('24200', '-1574015', 'Pagh! What sort of necromancer lets death stop him? I knew you were worthless!', null, null, null, null, null, null, null, 'Ха! Хороший некромант не дал бы смерти остановить себя! Я знал, что ты бесполезен!', '13233', '1', '0', '0', 'skarvald YELL_SKARVALD_DAL_DIEDFIRST');
INSERT IGNORE INTO `script_texts` VALUES ('24200', '-1574014', '???', null, null, null, null, null, null, null, '???', '13232', '1', '0', '0', 'skarvald YELL_SKARVALD_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('24200', '-1574013', 'A warrior\'s death.', null, null, null, null, null, null, null, 'Достойная смерть.', '13231', '1', '0', '0', 'skarvald YELL_SKARVALD_SKA_DIEDFIRST');
INSERT IGNORE INTO `script_texts` VALUES ('24200', '-1574012', 'Not... over... yet.', null, null, null, null, null, null, null, 'Это... не... конец.', '13230', '1', '0', '0', 'skarvald YELL_SKARVALD_DAL_DIED');
INSERT IGNORE INTO `script_texts` VALUES ('24200', '-1574011', 'Dalronn! See if you can muster the nerve to join my attack!', null, null, null, null, null, null, null, 'Далронн! Хватит ли у тебя смелости атаковать вместе со мной!', '13229', '1', '0', '0', 'skarvald YELL_SKARVALD_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('23954', '-1574010', 'I am a warrior born!', null, null, null, null, null, null, null, 'Я прирожденный воин!', '13214', '1', '0', '0', 'ingvar SAY_KILL_SECOND');
INSERT IGNORE INTO `script_texts` VALUES ('23954', '-1574009', 'Mjul orm agn gjor!', null, null, null, null, null, null, null, 'Мью орм агн джор!', '13212', '1', '0', '0', 'ingvar SAY_KILL_FIRST');
INSERT IGNORE INTO `script_texts` VALUES ('23954', '-1574008', 'No! I can do... better! I can...', null, null, null, null, null, null, null, 'Нет! Я смогу... это сделать! Я смогу...', '13211', '1', '0', '0', 'ingvar SAY_DEATH_SECOND');
INSERT IGNORE INTO `script_texts` VALUES ('23954', '-1574007', 'My life for the... death god!', null, null, null, null, null, null, null, 'Моя жизнь... за бога смерти!', '13213', '1', '0', '0', 'ingvar SAY_DEATH_FIRST');
INSERT IGNORE INTO `script_texts` VALUES ('23954', '-1574006', 'I return! A second chance to carve out your skull!', null, null, null, null, null, null, null, 'Я вернулся! Еще один шанс раздвоить вам головы!', '13209', '1', '0', '0', 'ingvar SAY_AGGRO_SECOND');
INSERT IGNORE INTO `script_texts` VALUES ('23954', '-1574005', 'I\'ll paint my face with your blood!', null, null, null, null, null, null, null, 'Я раскрашу свое лицо вашей кровью.', '13207', '1', '0', '0', 'ingvar SAY_AGGRO_FIRST');
INSERT IGNORE INTO `script_texts` VALUES ('23953', '-1574003', 'Darkness waits', null, null, null, null, null, null, null, 'Тьма ждет!', '13223', '1', '0', '0', 'keleseth SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('23953', '-1574004', 'I join... the night.', null, null, null, null, null, null, null, 'Ночь, прими меня!', '13225', '1', '0', '0', 'keleseth SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('23953', '-1574001', 'Not so fast.', null, null, null, null, null, null, null, 'Не так быстро.', '13222', '1', '0', '0', 'keleseth SAY_FROST_TOMB');
INSERT IGNORE INTO `script_texts` VALUES ('23953', '-1574002', 'Aranal, lidel! Their fate shall be yours!', null, null, null, null, null, null, null, 'Аранал, Лидел! Их судьба должна быть подобна вашей!', '13224', '1', '0', '0', 'keleseth SAY_SKELETONS');
INSERT IGNORE INTO `script_texts` VALUES ('23953', '-1574000', 'Your blood is mine!', null, null, null, null, null, null, null, 'Ваша кровь принадлежит мне!', '13221', '1', '0', '0', 'keleseth SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('30007', '-1571034', 'Hailling from the distant mountains of Alterac, one of the fiercest competitors this arena has ever seen: KORRAK THE BLOODRAGER!!!', null, null, null, null, null, null, null, null, '13363', '1', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('30020', '-1571032', 'Whisker! Where are you? Assist me!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('30007', '-1571033', 'The champion of the Winterax trolls has challenged you, Treeofdoom! I hope you\'re ready!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('30007', '-1571031', 'This battle must be seen to be believed! Once a mild-mannered tuskarr fisherman, our next fighter turned to the life of a soulless mercenary when his entire family was wiped out by a vicious pack of lion seals and III-tempered penguins! Now he\'s just In It for the gold! Ladies and gentlemen, ORINOKO TUSKBREAKER!!', null, null, null, null, null, null, null, null, '13363', '1', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25478', '-1571030', 'Noooo! This tortue is inhumane! You have what you want... why don\'t you just kill me?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25478', '-1571029', 'I\'ve given you the information, $C ! You\'re wasting your time....', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25478', '-1571027', 'Stop! I beg you, please stop. Please...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25478', '-1571028', 'Alright! I am beaten. Your previous archmage is held in a prison, elevated and sealed. Even if you manage to reach her, Salrand herself holds the key. Your mission is folly!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25478', '-1571026', 'Aahhhh! Release me! I am of no use to you. I swear it!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25478', '-1571025', 'Aargh! Do your worst, $C ! I\'ll tell you NOTHING!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25478', '-1571024', 'Pathetic fool! A servant of malygos would sooner die than aid an emeny...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('26170', '-1571023', 'Do not worry, Leryssa. I will come back to you when I\'m done. Nothing in the world will stop me from coming home to the only family that I have left.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('26170', '-1571022', 'I know that look in your eye... I\'m not going to be able to talk you out of this. If you die on me again...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('26170', '-1571021', 'You might be right, sister. My obligations to my land and King have been fulfilled. But there is still something that I owe to myself.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25251', '-1571020', 'Don\'t leave me again! You want to fight for your country, but they don\'t even want you! They sent you here to die!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('26170', '-1571019', 'I cannot return home with you just yet, Leryssa. I am not quite done with the Scourge.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25251', '-1571018', 'I thought... I thought you were... dead.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('26170', '-1571017', 'Leryssa... you... you\'re all right!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25251', '-1571016', 'Thassarian, you\'re alive!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25250', '-1571015', 'Ugh! My head won\'t stop spinning...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25301', '-1571013', 'Yes, my lord!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25250', '-1571014', 'What... what happened to me?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('26203', '-1571012', 'Do not fail me, San\'layn. Return to Icecrown with this fool\'s head or do not bother to return.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25301', '-1571011', 'Allow me to take care of the intruders, lord. I will feed their entrails to the maggots.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('26170', '-1571010', 'I would sooner slit my own throat. You will pay for what you did to your own men, Arthas... for what you did to me! I swear it.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('26203', '-1571009', 'Now this is a surprise, Thassarian. I hadn\'t heard from Mograine or the other death knights for months. You\'ve come to rejoin the Scourge, I take it?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('26170', '-1571008', 'What have you done to my sister, you motherless elf scum!?', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('26170', '-1571007', 'Leryssa!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('26203', '-1571006', 'The power you\'ve bestowed upon me has allowed me great mental influence over human minds. I bear these offerings as proof of my progress.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('26203', '-1571005', 'Your progress in this region has been impressive, Blood Prince. I am pleased...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('25301', '-1571004', 'My liege, the infiltration and control of the Alliance power structure by our cultists is well underway.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('30154', '-1571003', 'I\'m not afraid of anything -- bring it on!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'aggro_Agnetta');
INSERT IGNORE INTO `script_texts` VALUES ('28217', '-1571002', 'Home time!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'npc_injured_rainspeaker_oracle SAY_START_IRO');
INSERT IGNORE INTO `script_texts` VALUES ('28217', '-1571001', 'Let me know when you ready to go, okay?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'npc_injured_rainspeaker_oracle SAY_QUEST_ACCEPT_IRO ');
INSERT IGNORE INTO `script_texts` VALUES ('28217', '-1571000', 'You save me! We thank you. We going to go back to village now. You come too... you can stay with us! Puppy-men kind of mean anyway. ', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'npc_injured_rainspeaker_oracle SAY_END_IRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568023', 'I... be waitin\' on da udda side....', null, null, null, null, null, null, null, 'Я.. буду ждать вас в другом мире...', '12077', '1', '0', '0', 'nalorakk SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568022', 'Da Amani gonna rule again!', null, null, null, null, null, null, null, 'Амани будут править вновь!', '12076', '1', '0', '0', 'nalorakk SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568021', 'Mua-ha-ha! Now whatchoo got to say?', null, null, null, null, null, null, null, 'Мухахаха! Че ты там хотел сказать, а?', '12075', '1', '0', '0', 'nalorakk SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568020', 'You had your chance, now it be too late!', null, null, null, null, null, null, null, 'У вас был шанс и вы его упустили!', '12074', '1', '0', '0', 'nalorakk SAY_BERSERK');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568019', 'Make way for Nalorakk!', null, null, null, null, null, null, null, 'С дороги!', '12073', '1', '0', '0', 'nalorakk SAY_TOTROLL');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568018', 'You call on da beast, you gonna get more dan you bargain for!', null, null, null, null, null, null, null, 'Хотели разбудить во мне зверя, вам это удалось!', '12072', '1', '0', '0', 'nalorakk SAY_TOBEAR');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568017', 'I bring da pain!', null, null, null, null, null, null, null, 'Я несу боль!', '12071', '1', '0', '0', 'nalorakk SAY_SURGE');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568016', 'You be dead soon enough!', null, null, null, null, null, null, null, 'Очень скоро вы умрете!', '12070', '1', '0', '0', 'nalorakk SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568015', 'Don\'t be delayin\' your fate. Come to me now. I make your sacrifice quick.', null, null, null, null, null, null, null, 'Я чую вас чужаки, не оттягивайте неизбежное. Идите сюда. И ваша смерть будет быстрой.', '12079', '1', '0', '0', 'nalorakk SAY_EVENT2_SACRIFICE');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568014', 'What could be better than servin\' da bear spirit for eternity? Come closer now. Bring your souls to me!', null, null, null, null, null, null, null, 'Что может быть лучше, вечной службы духу медведя? Отдайте мне свои души!', '12078', '1', '0', '0', 'nalorakk SAY_EVENT1_SACRIFICE');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568013', 'I be losin\' me patience! Go on: make dem wish dey was never born!', null, null, null, null, null, null, null, 'Мое терпение на исходе! Вперед, пускай они пожалеют о том, что появились на свет!', '12069', '1', '0', '0', 'nalorakk SAY_WAVE4_PLATFORM');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568012', 'Ride now! Ride out dere and bring me back some heads!', null, null, null, null, null, null, null, 'Стражники! Отправляйтесь туда и принесите мне их головы!', '12068', '1', '0', '0', 'nalorakk SAY_WAVE3_STAIR2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568011', 'Guards, go already! Who you more afraid of, dem... or me?', null, null, null, null, null, null, null, 'Стражники, не мешкайте! Кого вы боитесь больше их... или меня?', '12067', '1', '0', '0', 'nalorakk SAY_WAVE2_STAIR1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1568010', 'Get da move on, guards! It be killin\' time!', null, null, null, null, null, null, null, 'Вперед, стражники! Начнем резню!', '12066', '1', '0', '0', 'nalorakk SAY_WAVE1_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('23578', '-1568009', 'Come, friends. Your bodies gonna feed ma hatchlings, and your souls are going to feed me with power!', null, null, null, null, null, null, null, 'Сюда, чужаки. Дух дракондора жаждет достойных душ.', '12040', '1', '0', '0', 'janalai SAY_EVENT_FRIENDS');
INSERT IGNORE INTO `script_texts` VALUES ('23578', '-1568008', 'Come, strangers. The spirit of the dragonhawk hot be hungry for worthy souls.', null, null, null, null, null, null, null, 'Идите сюда, друзья. Ваши тела станут кормом для моих птенцов, а ваши души сделают меня сильнее.', '12039', '1', '0', '0', 'janalai SAY_EVENT_STRANGERS');
INSERT IGNORE INTO `script_texts` VALUES ('23578', '-1568007', 'Zul\'jin... got a surprise for you...', null, null, null, null, null, null, null, 'У Зул\'джина есть сюрприз для вас...', '12038', '1', '0', '0', 'janalai SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('23578', '-1568006', 'Tazaga-choo!', null, null, null, null, null, null, null, 'Тазага-шоо!', '12037', '1', '0', '0', 'janalai SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('23578', '-1568005', 'It all be over now, mon!', null, null, null, null, null, null, null, 'Ну вот тебе и конец!', '12036', '1', '0', '0', 'janalai SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('23578', '-1568004', 'You done run outta time!', null, null, null, null, null, null, null, 'Ваше время вышло!', '12035', '1', '0', '0', 'janalai SAY_BERSERK');
INSERT IGNORE INTO `script_texts` VALUES ('23578', '-1568003', 'I show you strength... in numbers.', null, null, null, null, null, null, null, 'Я покажу вам... что такое численное превосходство.', '12034', '1', '0', '0', 'janalai SAY_ALL_EGGS');
INSERT IGNORE INTO `script_texts` VALUES ('23578', '-1568002', 'Where ma hatcha? Get to work on dem eggs!', null, null, null, null, null, null, null, 'Эй хранители, займитесь яйцами!', '12033', '1', '0', '0', 'janalai SAY_SUMMON_HATCHER');
INSERT IGNORE INTO `script_texts` VALUES ('23578', '-1568001', 'I burn ya now!', null, null, null, null, null, null, null, 'Щас я вас сожгу!', '12032', '1', '0', '0', 'janalai SAY_FIRE_BOMBS');
INSERT IGNORE INTO `script_texts` VALUES ('23578', '-1568000', 'Spirits of da wind be your doom!', null, null, null, null, null, null, null, 'Духи ветра станут вашей погибелью!', '12031', '1', '0', '0', 'janalai SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('19044', '-1565019', '%s grows in size!', null, null, null, null, null, null, null, 'растет в размере!', '0', '2', '0', '0', 'gruul EMOTE_GROW');
INSERT IGNORE INTO `script_texts` VALUES ('19044', '-1565018', 'Aaargh...', null, null, null, null, null, null, null, 'Уааааггхх...', '11363', '1', '0', '0', 'gruul SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('19044', '-1565017', 'Die', null, null, null, null, null, null, null, 'Умри', '11362', '1', '0', '0', 'gruul SAY_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('19044', '-1565016', 'Unworthy', null, null, null, null, null, null, null, 'Ничтожество', '11361', '1', '0', '0', 'gruul SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('19044', '-1565015', 'No more', null, null, null, null, null, null, null, 'Вот и все', '11360', '1', '0', '0', 'gruul SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('19044', '-1565014', 'Beg... for life', null, null, null, null, null, null, null, 'Проси... пощады', '11359', '1', '0', '0', 'gruul SAY_SHATTER2');
INSERT IGNORE INTO `script_texts` VALUES ('19044', '-1565013', 'Stay', null, null, null, null, null, null, null, 'Застынь', '11358', '1', '0', '0', 'gruul SAY_SHATTER1');
INSERT IGNORE INTO `script_texts` VALUES ('19044', '-1565012', 'No escape', null, null, null, null, null, null, null, 'Вам не уйти', '11357', '1', '0', '0', 'gruul SAY_SLAM2');
INSERT IGNORE INTO `script_texts` VALUES ('19044', '-1565011', 'Scurry', null, null, null, null, null, null, null, 'Бегите', '11356', '1', '0', '0', 'gruul SAY_SLAM1');
INSERT IGNORE INTO `script_texts` VALUES ('18831', '-1565008', 'Mulgar is king!', null, null, null, null, null, null, null, 'Слава королю Молгару!', '11375', '1', '0', '0', 'maulgar SAY_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('18831', '-1565009', 'Gruul... will crush you...', null, null, null, null, null, null, null, 'Грулл... сокрушит вас...', '11376', '1', '0', '0', 'maulgar SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('19044', '-1565010', 'Come... and die.', null, null, null, null, null, null, null, 'Придите... и умрите.', '11355', '1', '0', '0', 'gruul SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('18831', '-1565007', 'Aha-ha ha ha!', null, null, null, null, null, null, null, 'Аха-ха ха ха!', '11374', '1', '0', '0', 'maulgar SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('18831', '-1565006', 'You not so tough afterall!', null, null, null, null, null, null, null, 'Ты слабее чем я думал!', '11373', '1', '0', '0', 'maulgar SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('18831', '-1565005', 'Good, now you fight me!', null, null, null, null, null, null, null, 'Хорошо, теперь сразитесь со мной!', '11372', '1', '0', '0', 'maulgar SAY_OGRE_DEATH4');
INSERT IGNORE INTO `script_texts` VALUES ('18831', '-1565004', 'I\'m not afraid of you.', null, null, null, null, null, null, null, 'Я вас не боюсь!', '11371', '1', '0', '0', 'maulgar SAY_OGRE_DEATH3');
INSERT IGNORE INTO `script_texts` VALUES ('18831', '-1565003', 'Pah! Does not prove anything!', null, null, null, null, null, null, null, 'Ха! Это ничего не доказывает!', '11370', '1', '0', '0', 'maulgar SAY_OGRE_DEATH2');
INSERT IGNORE INTO `script_texts` VALUES ('18831', '-1565002', 'You won\'t kill next one so easy!', null, null, null, null, null, null, null, 'Следующего вам так легко не убить!', '11369', '1', '0', '0', 'maulgar SAY_OGRE_DEATH1');
INSERT IGNORE INTO `script_texts` VALUES ('18831', '-1565001', 'You will not defeat the hand of Gruul!', null, null, null, null, null, null, null, 'Вы не сможете противостоять воинам Груула!', '11368', '1', '0', '0', 'maulgar SAY_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('18831', '-1565000', 'Gronn are the real power in outland.', null, null, null, null, null, null, null, 'Единственная настоящая сила в Запределье, это гром.', '11367', '1', '0', '0', 'maulgar SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('22950', '-1564096', 'Diel ma\'ahn... oreindel\'o', null, null, null, null, null, null, null, 'Диэль ман... ориндаль\'о', '11443', '1', '0', '0', 'council zere DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('22951', '-1564095', 'Destiny... awaits.', null, null, null, null, null, null, null, 'Это... ваша судьба.', '11485', '1', '0', '0', 'council mala DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('22952', '-1564094', 'You got lucky!', null, null, null, null, null, null, null, 'Вам просто повезло!', '11527', '1', '0', '0', 'council vera DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1564092', 'Belesa menoor!', null, null, null, null, null, null, null, 'Белеса менор!', '11442', '1', '0', '0', 'council zere SLAY_COMT');
INSERT IGNORE INTO `script_texts` VALUES ('22949', '-1564093', 'Lord Illidan... I...', null, null, null, null, null, null, null, 'Владыка Иллидан... Я...', '11425', '1', '0', '0', 'council gath DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('22950', '-1564091', 'As it should be!', null, null, null, null, null, null, null, 'Как и должно быть!', '11484', '1', '0', '0', 'council mala SLAY_COMT');
INSERT IGNORE INTO `script_texts` VALUES ('22951', '-1564090', 'A glorious kill!', null, null, null, null, null, null, null, 'Отличная робота!', '11526', '1', '0', '0', 'council vera SLAY_COMT');
INSERT IGNORE INTO `script_texts` VALUES ('22949', '-1564089', 'Well done!', null, null, null, null, null, null, null, 'Отлично!', '11424', '1', '0', '0', 'council gath SLAY_COMT');
INSERT IGNORE INTO `script_texts` VALUES ('22950', '-1564088', 'Shorel\'aran.', null, null, null, null, null, null, null, 'Шорел\'оран.', '11441', '1', '0', '0', 'council zere SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('22951', '-1564087', 'My work is done.', null, null, null, null, null, null, null, 'Мое дело сделано.', '11483', '1', '0', '0', 'council mala SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('22952', '-1564086', 'Valiant effort!', null, null, null, null, null, null, null, 'Это была достойная попытка!', '11525', '1', '0', '0', 'council vera SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('22949', '-1564085', 'Selama am\'oronor!', null, null, null, null, null, null, null, 'Селама аманур.', '11423', '1', '0', '0', 'council gath SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('22950', '-1564084', 'Sha\'amoor ara mashal?', null, null, null, null, null, null, null, 'Шаму ара машол.', '11445', '1', '0', '0', 'council zere SPECIAL2');
INSERT IGNORE INTO `script_texts` VALUES ('22951', '-1564083', 'I\'m full of surprises!', null, null, null, null, null, null, null, 'У меня еще много сюрпризов!', '11487', '1', '0', '0', 'council mala SPECIAL2');
INSERT IGNORE INTO `script_texts` VALUES ('22952', '-1564082', 'Anar\'alah belore!', null, null, null, null, null, null, null, 'Анар\'алах белоре!', '11529', '1', '0', '0', 'council vera SPECIAL2');
INSERT IGNORE INTO `script_texts` VALUES ('22949', '-1564081', 'You are mine!', null, null, null, null, null, null, null, 'Теперь ты принадлежишь мне!', '11427', '1', '0', '0', 'council gath SPECIAL2');
INSERT IGNORE INTO `script_texts` VALUES ('22950', '-1564080', 'Diel fin\'al', null, null, null, null, null, null, null, 'Диель Финол', '11444', '1', '0', '0', 'council zere SPECIAL1');
INSERT IGNORE INTO `script_texts` VALUES ('22951', '-1564079', 'No second chances!', null, null, null, null, null, null, null, 'Второго шанса не будет!', '11486', '1', '0', '0', 'council mala SPECIAL1');
INSERT IGNORE INTO `script_texts` VALUES ('22952', '-1564078', 'You\'re not caught up for this!', null, null, null, null, null, null, null, 'Я тебе не по зубам!', '11528', '1', '0', '0', 'council vera SPECIAL1');
INSERT IGNORE INTO `script_texts` VALUES ('22949', '-1564077', 'Enjoy your final moments!', null, null, null, null, null, null, null, 'Наслаждайтесь последними мгновениями вашей жизни!', '11426', '1', '0', '0', 'council gath SPECIAL1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1564076', 'Sha\'amoor sine menoor!', null, null, null, null, null, null, null, 'Шел мо сине менол!', '11446', '1', '0', '0', 'council zere ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1564075', 'For Quel\'Thalas! For the Sunwell!', null, null, null, null, null, null, null, 'За Кель\'Талас! За Солнечный Колодец!', '11488', '1', '0', '0', 'council mala ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1564074', 'You wish to kill me? Hahaha, you first!', null, null, null, null, null, null, null, 'Хотите убить меня? Ха ха, нет уж лучше я вас!', '11530', '1', '0', '0', 'council vera ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1564073', 'Enough games!', null, null, null, null, null, null, null, 'Поиграли и хватит!', '11428', '1', '0', '0', 'council gath ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1564072', 'Common... such a crude language. Bandal!', null, null, null, null, null, null, null, 'Всеобщий язык такой грубый. БАНДАЛ.', '11440', '1', '0', '0', 'council zere AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1564071', 'Flee or die!', null, null, null, null, null, null, null, 'Бегите или умрите!', '11482', '1', '0', '0', 'council mala AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1564069', 'You wish to test me?', null, null, null, null, null, null, null, 'Хотите испытать меня?', '11524', '1', '0', '0', 'council vera AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1564070', 'I have better things to do...', null, null, null, null, null, null, null, 'У меня есть более важные дела...', '11422', '1', '0', '0', 'council gath AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('23420', '-1564068', 'I won\'t... be... ignored.', null, null, null, null, null, null, null, 'Вы еще... вспомните... обо мне.', '11404', '1', '0', '0', 'essence ANGER_SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('23420', '-1564067', 'Beware, coward.', null, null, null, null, null, null, null, 'Берегись, трус.', '11405', '1', '0', '0', 'essence ANGER_SAY_BEFORE');
INSERT IGNORE INTO `script_texts` VALUES ('23420', '-1564066', 'On your knees!', null, null, null, null, null, null, null, 'На колени!', '11403', '1', '0', '0', 'essence ANGER_SAY_SPEC');
INSERT IGNORE INTO `script_texts` VALUES ('23420', '-1564065', 'Enough. No more.', null, null, null, null, null, null, null, 'Хватит. Довольно.', '11402', '1', '0', '0', 'essence ANGER_SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('23420', '-1564064', '<maniacal cackle>', null, null, null, null, null, null, null, 'хахахахахах..ха.ха', '11401', '1', '0', '0', 'essence ANGER_SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('23420', '-1564063', 'So... foolish.', null, null, null, null, null, null, null, 'Так... глупо.', '11400', '1', '0', '0', 'essence ANGER_SAY_FREED2');
INSERT IGNORE INTO `script_texts` VALUES ('23419', '-1564060', 'I\'ll be waiting...', null, null, null, null, null, null, null, 'Я буду ждать...', '11413', '1', '0', '0', 'essence DESI_SAY_RECAP');
INSERT IGNORE INTO `script_texts` VALUES ('23419', '-1564061', 'I won\'t be far...', null, null, null, null, null, null, null, 'Я буду неподалеку...', '11414', '1', '0', '0', 'essence DESI_SAY_AFTER');
INSERT IGNORE INTO `script_texts` VALUES ('23420', '-1564062', 'Beware: I live!', null, null, null, null, null, null, null, 'Берегитесь: Я жив!', '11399', '1', '0', '0', 'essence ANGER_SAY_FREED');
INSERT IGNORE INTO `script_texts` VALUES ('23419', '-1564058', 'Your reach exceeds your grasp.', null, null, null, null, null, null, null, 'Твоим возможностям далеко до твоих желаний.', '11412', '1', '0', '0', 'essence DESI_SAY_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('23419', '-1564059', 'Be careful what you wish for...', null, null, null, null, null, null, null, 'Будь осторожней в своих желаниях...', '11411', '1', '0', '0', 'essence DESI_SAY_SPEC');
INSERT IGNORE INTO `script_texts` VALUES ('23419', '-1564057', 'Yes... you\'ll stay with us now...', null, null, null, null, null, null, null, 'Да... теперь ты останешься с нами...', '11410', '1', '0', '0', 'essence DESI_SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('23419', '-1564056', 'Fulfilment is at hand!', null, null, null, null, null, null, null, 'Скоро твои мечты осуществляться!', '11409', '1', '0', '0', 'essence DESI_SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('23419', '-1564055', 'You can have anything you desire... for a price.', null, null, null, null, null, null, null, 'Ваши мечты осуществляться... но за это придется заплатить.', '11408', '1', '0', '0', 'essence DESI_SAY_FREED');
INSERT IGNORE INTO `script_texts` VALUES ('23418', '-1564054', '%s becomes enraged!', null, null, null, null, null, null, null, 'приходит в ярость!', '0', '3', '0', '0', 'essence SUFF_EMOTE_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('23418', '-1564053', 'Now what do I do?', null, null, null, null, null, null, null, 'Ну и что мне теперь делать?', '11421', '1', '0', '0', 'essence SUFF_SAY_AFTER');
INSERT IGNORE INTO `script_texts` VALUES ('23418', '-1564052', 'I don\'t want to go back!', null, null, null, null, null, null, null, 'Я не хочу обратно!', '11420', '1', '0', '0', 'essence SUFF_SAY_RECAP');
INSERT IGNORE INTO `script_texts` VALUES ('23418', '-1564051', 'The pain is only beginning!', null, null, null, null, null, null, null, 'Это только начало боли!', '11419', '1', '0', '0', 'essence SUFF_SAY_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('23418', '-1564049', 'Look at what you make me do!', null, null, null, null, null, null, null, 'Вы заставили меня это сделать!', '11417', '1', '0', '0', 'essence SUFF_SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('23418', '-1564050', 'I didn\'t ask for this!', null, null, null, null, null, null, null, 'Я не просил об этом!', '11418', '1', '0', '0', 'essence SUFF_SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('23418', '-1564048', 'Don\'t leave me alone!', null, null, null, null, null, null, null, 'Не покидай меня!', '11416', '1', '0', '0', 'essence SUFF_SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('22871', '-1564046', 'The wheel...spins...again....', null, null, null, null, null, null, null, 'Колесо жизни... завершило... очередной оборот...', '11521', '1', '0', '0', 'teron SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('23418', '-1564047', 'Pain and suffering are all that await you!', null, null, null, null, null, null, null, 'Вас ожидают лишь боль и страдания!', '11415', '1', '0', '0', 'essence SUFF_SAY_FREED');
INSERT IGNORE INTO `script_texts` VALUES ('22871', '-1564045', 'YOU WILL SHOW THE PROPER RESPECT!', null, null, null, null, null, null, null, 'Я ТРЕБУЮ ДОЛЖНОГО УВАЖЕНИЯ!', '11520', '1', '0', '0', 'teron SAY_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('22871', '-1564043', 'Give in!', null, null, null, null, null, null, null, 'Сдавайтесь!', '11518', '1', '0', '0', 'teron SAY_SPECIAL1');
INSERT IGNORE INTO `script_texts` VALUES ('22871', '-1564044', 'I have something for you...', null, null, null, null, null, null, null, 'У меня для вас кое-что есть...', '11519', '1', '0', '0', 'teron SAY_SPECIAL2');
INSERT IGNORE INTO `script_texts` VALUES ('22871', '-1564042', 'Death... really isn\'t so bad.', null, null, null, null, null, null, null, 'Смерть... не так уж страшна.', '11516', '1', '0', '0', 'teron SAY_SPELL2');
INSERT IGNORE INTO `script_texts` VALUES ('22871', '-1564041', 'What are you afraid of?', null, null, null, null, null, null, null, 'Чего вы боитесь?', '11517', '1', '0', '0', 'teron SAY_SPELL1');
INSERT IGNORE INTO `script_texts` VALUES ('22871', '-1564040', 'It gets worse...', null, null, null, null, null, null, null, 'Сейчас тебе будет плохо...', '11515', '1', '0', '0', 'teron SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('22871', '-1564039', 'I have use for you!', null, null, null, null, null, null, null, 'Ты мне пригодишься!', '11514', '1', '0', '0', 'teron SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('22871', '-1564038', 'Vengeance is mine!', null, null, null, null, null, null, null, 'Я отомщу!', '11513', '1', '0', '0', 'teron SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('22871', '-1564037', 'I was the first, you know. For me, the wheel of death has spun many times. <laughs> So much time has passed. I have a lot of catching up to do...', null, null, null, null, null, null, null, 'Я был первым, колесо моей жизни сделало уже не один оборот <laughs> столько времени прошло. Мне нужно столько наверстать.', '11512', '1', '0', '0', 'teron SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('22948', '-1564036', 'Aaaahrg...', null, null, null, null, null, null, null, 'Аааауаа...', '11439', '1', '0', '0', 'bloodboil SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('22948', '-1564033', 'I hunger!', null, null, null, null, null, null, null, 'Я голоден!', '11436', '1', '0', '0', 'bloodboil SAY_SPECIAL2');
INSERT IGNORE INTO `script_texts` VALUES ('22948', '-1564034', '<babbling>', null, null, null, null, null, null, null, '<babbling>', '11437', '1', '0', '0', 'bloodboil SAY_ENRAGE1');
INSERT IGNORE INTO `script_texts` VALUES ('22948', '-1564035', 'I\'ll rip the meat from your bones!', null, null, null, null, null, null, null, 'Я выпущу вам кишки!', '11438', '1', '0', '0', 'bloodboil SAY_ENRAGE2');
INSERT IGNORE INTO `script_texts` VALUES ('22948', '-1564032', 'Drink your blood! Eat your flesh!', null, null, null, null, null, null, null, 'Пей свою кровь! Жри свою плоть!', '11435', '1', '0', '0', 'bloodboil SAY_SPECIAL1');
INSERT IGNORE INTO `script_texts` VALUES ('22948', '-1564031', 'More! I want more!', null, null, null, null, null, null, null, 'Больше! Больше крови!', '11434', '1', '0', '0', 'bloodboil SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('22948', '-1564030', 'Time to feast!', null, null, null, null, null, null, null, 'Пришло время кровавого пира!', '11433', '1', '0', '0', 'bloodboil SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('22948', '-1564029', 'Horde will... crush you.', null, null, null, null, null, null, null, 'Орда... уничтожит вас.', '11432', '1', '0', '0', 'bloodboil SOUND_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('22947', '-1564028', 'I wasn\'t... finished.', null, null, null, null, null, null, null, 'Я... еще не закончила.', '11511', '1', '0', '0', 'shahraz SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('22947', '-1564027', 'Stop toying with my emotions!', null, null, null, null, null, null, null, 'Хватит играть с моими чувствами!', '11510', '1', '0', '0', 'shahraz SAY_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('22947', '-1564026', 'So much for a happy ending.', null, null, null, null, null, null, null, 'Какая счастливая кончина.', '11509', '1', '0', '0', 'shahraz SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('22947', '-1564025', 'Easy come, easy go.', null, null, null, null, null, null, null, 'Как пришло, так и ушло.', '11508', '1', '0', '0', 'shahraz SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('22947', '-1564024', 'I\'m all... yours.', null, null, null, null, null, null, null, 'Я вся... твоя.', '11507', '1', '0', '0', 'shahraz SAY_SPELL3');
INSERT IGNORE INTO `script_texts` VALUES ('22947', '-1564023', 'Don\'t be shy.', null, null, null, null, null, null, null, 'Не нужно стеснятся.', '11506', '1', '0', '0', 'shahraz SAY_SPELL2');
INSERT IGNORE INTO `script_texts` VALUES ('22947', '-1564022', 'You seem a little tense.', null, null, null, null, null, null, null, 'Не напрягайся так.', '11505', '1', '0', '0', 'shahraz SAY_SPELL1');
INSERT IGNORE INTO `script_texts` VALUES ('22947', '-1564021', 'So... business or pleasure?', null, null, null, null, null, null, null, 'Так... Вы тут по делу или ради моего удовольствия?', '11504', '1', '0', '0', 'shahraz SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('22947', '-1564020', 'Enjoying yourselves?', null, null, null, null, null, null, null, 'Что, развлекаемся!', '11503', '1', '0', '0', 'shahraz SAY_TAUNT3');
INSERT IGNORE INTO `script_texts` VALUES ('22990', '-1564017', 'Hail Akama!', null, null, null, null, null, null, null, 'Град Акама!', '0', '1', '0', '0', 'akama shade broken SAY_BROKEN_FREE_02');
INSERT IGNORE INTO `script_texts` VALUES ('22947', '-1564018', 'You play, you pay.', null, null, null, null, null, null, null, 'Будешь со мной играть, поплатишься.', '11501', '1', '0', '0', 'shahraz SAY_TAUNT1');
INSERT IGNORE INTO `script_texts` VALUES ('22947', '-1564019', 'I\'m not impressed.', null, null, null, null, null, null, null, 'Не впечатляет.', '11502', '1', '0', '0', 'shahraz SAY_TAUNT2');
INSERT IGNORE INTO `script_texts` VALUES ('22990', '-1564016', 'Hail our leader! Hail Akama!', null, null, null, null, null, null, null, 'Приветствуйте нашего лидера! Град Акама!', '0', '1', '0', '0', 'akama shade broken SAY_BROKEN_FREE_01');
INSERT IGNORE INTO `script_texts` VALUES ('22990', '-1564015', 'Come out from the shadows! I\'ve returned to lead you against our true enemy! Shed your chains and raise your weapons against your Illidari masters!', null, null, null, null, null, null, null, 'Выходите из теней! Я возвратился, чтобы вести вас против нашего истинного врага! Сбросьте свои цепи и поднимите ваше оружие против ваших хозяинов Иллидари!', '0', '1', '0', '0', 'akama shade SAY_FREE');
INSERT IGNORE INTO `script_texts` VALUES ('22990', '-1564014', 'I will not last much longer...', null, null, null, null, null, null, null, 'Нет слишком рано...', '11386', '1', '0', '0', 'akama shade SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('22990', '-1564013', 'No! Not yet...', null, null, null, null, null, null, null, 'Я долго не продержусь...', '11385', '1', '0', '0', 'akama shade SAY_LOW_HEALTH');
INSERT IGNORE INTO `script_texts` VALUES ('22898', '-1564012', 'The ground begins to crack open!', null, null, null, null, null, null, null, 'Земля начинает трескаться!', '0', '3', '0', '0', 'supremus EMOTE_GROUND_CRACK');
INSERT IGNORE INTO `script_texts` VALUES ('22898', '-1564011', '%s punches the ground in anger!', null, null, null, null, null, null, null, '%s в гневе ударяет кулаком по земле!', '0', '3', '0', '0', 'supremus EMOTE_PUNCH_GROUND');
INSERT IGNORE INTO `script_texts` VALUES ('22898', '-1564010', '%s acquires a new target!', null, null, null, null, null, null, null, '%s приобретает новую цель!', '0', '3', '0', '0', 'supremus EMOTE_NEW_TARGET');
INSERT IGNORE INTO `script_texts` VALUES ('22887', '-1564009', 'Lord Illidan will... crush you.', null, null, null, null, null, null, null, 'Владыка Иллидан... сокрушит вас.', '11459', '1', '0', '0', 'SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('22887', '-1564008', 'My patience has ran out! Die, DIE!', null, null, null, null, null, null, null, 'Мое терпение иссякло! СМЕРТЬ ВАМ!', '11458', '1', '0', '0', 'SAY_ENRAGE2');
INSERT IGNORE INTO `script_texts` VALUES ('22887', '-1564007', 'Bal\'amer ch\'itah!', null, null, null, null, null, null, null, 'Бел\'амер ж\'ита!', '11457', '1', '0', '0', 'SAY_ENRAGE1');
INSERT IGNORE INTO `script_texts` VALUES ('22887', '-1564006', 'Blood will flow!', null, null, null, null, null, null, null, 'Да прольется кровь!', '11454', '1', '0', '0', 'SAY_SPECIAL2');
INSERT IGNORE INTO `script_texts` VALUES ('22887', '-1564005', 'Bel\'anen dal\'lorei!', null, null, null, null, null, null, null, 'Бел\'анен дал\'лорей!', '11453', '1', '0', '0', 'SAY_SPECIAL1');
INSERT IGNORE INTO `script_texts` VALUES ('22887', '-1564004', 'Time for you to go!', null, null, null, null, null, null, null, 'Твое время истекло!', '11456', '1', '0', '0', 'SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('22887', '-1564003', 'Your success was short lived!', null, null, null, null, null, null, null, 'Твой успех был краток!', '11455', '1', '0', '0', 'SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('22887', '-1564002', 'I\'ll deal with you later!', null, null, null, null, null, null, null, 'Я займусь вами позже!', '11452', '1', '0', '0', 'SAY_NEEDLE2');
INSERT IGNORE INTO `script_texts` VALUES ('22887', '-1564001', 'Stick around!', null, null, null, null, null, null, null, 'Стоять на месте!', '11451', '1', '0', '0', 'SAY_NEEDLE1');
INSERT IGNORE INTO `script_texts` VALUES ('22887', '-1564000', 'You will die in the name of Lady Vashj!', null, null, null, null, null, null, null, 'Вы умрете во славу Леди Вайши!', '11450', '1', '0', '0', 'SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('18887', '-1560049', 'Thrall, you escaped!', null, null, null, null, null, null, null, 'Раб, ты сбежал!', '0', '0', '0', '0', 'taretha SAY_TA_ESCAPED');
INSERT IGNORE INTO `script_texts` VALUES ('18887', '-1560048', 'I\'m free! Thank you all!', null, null, null, null, null, null, null, 'Я свободен! Спасибо вам всем!', '0', '0', '0', '0', 'taretha SAY_TA_FREE');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560046', 'Thank you, strangers. You have given me hope.', null, null, null, null, null, null, null, 'Спасибо, незнакомцы. Вы подарили мне надежду.', '10456', '1', '0', '0', 'thrall hillsbrad SAY_TH_LEAVE_COMBAT2');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560047', 'I will not waste this chance. I will seek out my destiny.', null, null, null, null, null, null, null, 'Я не упущю свой шанс. Я пойду на встречу своей судьбе.', '10457', '1', '0', '0', 'thrall hillsbrad SAY_TH_LEAVE_COMBAT3');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560045', 'I am truly in your debt, strangers.', null, null, null, null, null, null, null, 'Я ваш должник, незнакомцы.', '10455', '1', '0', '0', 'thrall hillsbrad SAY_TH_LEAVE_COMBAT1');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560044', 'I did not ask for this!', null, null, null, null, null, null, null, 'Я не хотел этого!', '10454', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_KILL3');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560043', 'It should not have come to this!', null, null, null, null, null, null, null, 'До этого не должно было дойти!', '10453', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_KILL2');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560042', 'You have forced my hand!', null, null, null, null, null, null, null, 'Я был вынужден это сделать!', '10452', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_KILL1');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560041', 'Blackmoore has much to answer for!', null, null, null, null, null, null, null, 'Блекмор ответит за все!', '10451', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_AGGRO4');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560040', 'I am a slave no longer!', null, null, null, null, null, null, null, 'Я больше не раб!', '10450', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560039', 'This day is long overdue. Out of my way!', null, null, null, null, null, null, null, 'Я давно ждал этого дня. С дороги!', '10449', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560038', 'I have earned my freedom!', null, null, null, null, null, null, null, 'Я заслужил свободу!', '10448', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560037', 'A good day...to die...', null, null, null, null, null, null, null, 'Отличный день… для смерти…', '10461', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_DIE2');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560036', 'Taretha...', null, null, null, null, null, null, null, 'Тарета...', '10460', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_DIE1');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560035', 'I will fight to the last!', null, null, null, null, null, null, null, 'Я буду драться до последнего!', '10459', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_LOW_HP2');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560034', 'Things are looking grim...', null, null, null, null, null, null, null, 'Тучи сгущаются...', '10458', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_LOW_HP1');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560033', 'Goodbye, Taretha. I will never forget your kindness.', null, null, null, null, null, null, null, 'Прощай, Тарета. Я никогда не забуду твоей доброты.', '10472', '1', '0', '0', 'thrall hillsbrad SAY_TH_EVENT_COMPLETE');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560032', 'No!', null, null, null, null, null, null, null, 'Нет!', '10471', '1', '0', '5', 'thrall hillsbrad SAY_TH_EPOCH_KILL_TARETHA');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560031', 'Who or what was that?', null, null, null, null, null, null, null, 'Что это было?', '10470', '1', '0', '1', 'thrall hillsbrad SAY_TH_EPOCH_WONDER');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560030', 'Taretha! What foul magic is this?', null, null, null, null, null, null, null, 'Тарета! Что это за грязное волшебство?', '0', '0', '0', '0', 'thrall hillsbrad SAY_TH_MEET_TARETHA');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560029', 'Taretha must be in the inn. Let\'s go.', null, null, null, null, null, null, null, 'Тарета должна быть в гостинице. Давайте идти.', '0', '0', '0', '0', 'thrall hillsbrad SAY_TH_CHURCH_END');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560028', 'Let\'s ride!', null, null, null, null, null, null, null, 'Поехали!', '10469', '0', '0', '1', 'thrall hillsbrad SAY_TH_MOUNTS_UP');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560027', 'Very well. Tarren Mill lies just west of here. Since time is of the essence...', null, null, null, null, null, null, null, 'Отлично. Мельница Таренн к западу от сюда. У нас мало времени...', '10468', '1', '0', '0', 'thrall hillsbrad SAY_TH_START_EVENT_PART2');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560026', 'I\'ll never be chained again!', null, null, null, null, null, null, null, 'Никто больше не сможет заковать меня в цепи!', '10467', '1', '0', '0', 'thrall hillsbrad SAY_TH_SKARLOC_TAUNT');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560025', 'A rider approaches!', null, null, null, null, null, null, null, 'Смотрите, всадник!', '10466', '0', '0', '0', 'thrall hillsbrad SAY_TH_SKARLOC_MEET');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560024', 'As long as we\'re going with a new plan, I may aswell pick up a weapon and some armor.', null, null, null, null, null, null, null, 'Пока мы следуем новому плану, я могу также поднять оружие и некоторую броню.', '0', '0', '0', '0', 'thrall hillsbrad SAY_TH_ARMORY');
INSERT IGNORE INTO `script_texts` VALUES ('17876', '-1560023', 'Very well then. Let\'s go!', null, null, null, null, null, null, null, 'Отлично. Вперед!', '10465', '1', '0', '0', 'thrall hillsbrad SAY_TH_START_EVENT_PART1');
INSERT IGNORE INTO `script_texts` VALUES ('18096', '-1560022', 'No!...The master... will not... be pleased.', null, null, null, null, null, null, null, 'Нет! Хозяин... будет... не доволен.', '10427', '1', '0', '0', 'epoch_hunter SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('18096', '-1560021', 'Struggle as much as you like!', null, null, null, null, null, null, null, 'Ваши усилия тщетны!', '10424', '1', '0', '0', 'epoch_hunter SAY_BREATH2');
INSERT IGNORE INTO `script_texts` VALUES ('18096', '-1560020', 'Not so fast!', null, null, null, null, null, null, null, 'Не так быстро!', '10423', '1', '0', '0', 'epoch_hunter SAY_BREATH1');
INSERT IGNORE INTO `script_texts` VALUES ('18096', '-1560019', 'Thrall will remain a slave. Taretha will die. You have failed.', null, null, null, null, null, null, null, 'Тралл останеться рабом. А Тарета умрет.', '10426', '1', '0', '0', 'epoch_hunter SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('18096', '-1560018', 'You are...irrelevant.', null, null, null, null, null, null, null, 'Тебе здесь не место.', '10425', '1', '0', '0', 'epoch_hunter SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('18096', '-1560017', 'You cannot fight fate!', null, null, null, null, null, null, null, 'С судьбой бороться бесполезно!', '10422', '1', '0', '0', 'epoch_hunter SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('18096', '-1560016', 'Enough! I will erase your very existence!', null, null, null, null, null, null, null, 'Довольно! От вас не останется и следа!', '10421', '1', '0', '0', 'epoch_hunter SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('18096', '-1560015', 'Ah, there you are. I had hoped to accomplish this with a bit of subtlety, but I suppose direct confrontation was inevitable. Your future, Thrall, must not come to pass and so...you and your troublesome friends must die!', null, null, null, null, null, null, null, 'А, вот и ты. Я надеялся решить эту проблему более изящно, но похоже открытого боя не избежать, у тебя нет будущего, Тралл. Так что тебе и твоим назойливым друзьям придется умереть.', '10420', '1', '0', '0', 'epoch_hunter SAY_ENTER3');
INSERT IGNORE INTO `script_texts` VALUES ('18096', '-1560014', 'Taretha\'s life hangs in the balance. Surely you care for her. Surely you wish to save her...', null, null, null, null, null, null, null, 'Жизнь Тареты висит на волоске. Разумеется она тебе не безразлична. Разумеется, ты хочешь ее спасти.', '10419', '1', '0', '0', 'epoch_hunter SAY_ENTER2');
INSERT IGNORE INTO `script_texts` VALUES ('18096', '-1560013', 'Thrall! Come outside and face your fate!', null, null, null, null, null, null, null, 'Тралл! Выйди и встреть свою судьбу!', '10418', '1', '0', '0', 'epoch_hunter SAY_ENTER1');
INSERT IGNORE INTO `script_texts` VALUES ('17848', '-1560012', 'Thrall... must not... go free.', null, null, null, null, null, null, null, 'Тралл… не должен... освободиться.', '10434', '1', '0', '0', 'lieutenant_drake SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17848', '-1560011', 'Run, you blasted cowards!', null, null, null, null, null, null, null, 'Бегите, презренные трусы!', '10431', '1', '0', '0', 'lieutenant_drake SAY_SHOUT');
INSERT IGNORE INTO `script_texts` VALUES ('17848', '-1560010', 'Time to bleed!', null, null, null, null, null, null, null, 'Сейчас прольется кровь!', '10430', '1', '0', '0', 'lieutenant_drake SAY_MORTAL');
INSERT IGNORE INTO `script_texts` VALUES ('17848', '-1560009', 'You will not interfere!', null, null, null, null, null, null, null, 'Ничего у тебя не вышло!', '10433', '1', '0', '0', 'lieutenant_drake SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('17848', '-1560008', 'No more middling for you.', null, null, null, null, null, null, null, 'Больше ты мне не помешаешь.', '10432', '1', '0', '0', 'lieutenant_drake SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('17848', '-1560007', 'I know what you\'re up to, and I mean to put an end to it, permanently!', null, null, null, null, null, null, null, 'Я знаю, что вы, задумали и я покончу с этим... раз и навсегда!', '10429', '1', '0', '0', 'lieutenant_drake SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17862', '-1560005', 'Guards! Urgh..Guards..!', null, null, null, null, null, null, null, 'Стража! Стража...!', '10411', '1', '0', '0', 'skarloc SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17848', '-1560006', 'You there, fetch water quickly! Get these flames out before they spread to the rest of the keep! Hurry, damn you!', null, null, null, null, null, null, null, 'Эй воды сюда, живо! Тушите огонь пока вся крепость не сгорела, ну быстрей же будьте вы прокляты!', '10428', '1', '0', '0', 'lieutenant_drake SAY_ENTER');
INSERT IGNORE INTO `script_texts` VALUES ('17862', '-1560004', 'Did you really think you would leave here alive?', null, null, null, null, null, null, null, 'Никто из вас не уйдет живым!', '10410', '1', '0', '0', 'skarloc SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('17862', '-1560003', 'Thrall will never be free!', null, null, null, null, null, null, null, 'Тралл никогда не будет свободен!', '10409', '1', '0', '0', 'skarloc SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('17862', '-1560001', 'You\'re a slave. That\'s all you\'ll ever be.', null, null, null, null, null, null, null, 'Ты раб. И всегда им будешь.', '10407', '1', '0', '0', 'skarloc SAY_TAUNT1');
INSERT IGNORE INTO `script_texts` VALUES ('17862', '-1560002', 'I don\'t know what Blackmoore sees in you. For my money, you\'re just another ignorant savage!', null, null, null, null, null, null, null, 'Не знаю, что Блекмор в тебе нашел. По мне так ты просто тупой варвар.', '10408', '1', '0', '0', 'skarloc SAY_TAUNT2');
INSERT IGNORE INTO `script_texts` VALUES ('18373', '-1558009', 'This is... where.. I belong...', null, null, null, null, null, null, null, 'Такова... моя доля...', '10518', '1', '0', '0', 'maladaar SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17862', '-1560000', 'Thrall! You didn\'t really think you would escape did you? You and your allies shall answer to Blackmoore - after I\'ve had my fun!', null, null, null, null, null, null, null, 'Тралл! Ты ведь не думал, что в самом деле сможешь сбежать. Ты ответишь перед Блекмором как и твои союзники, но сперва я позабавлюсь.', '10406', '1', '0', '0', 'skarloc SAY_ENTER');
INSERT IGNORE INTO `script_texts` VALUES ('18373', '-1558008', '<laugh> Now, you\'ll stay for eternity!', null, null, null, null, null, null, null, 'Ты останешься здесь навсегда... Ха ха ха ха', '10517', '1', '0', '0', 'maladaar SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('18373', '-1558007', 'These walls will be your doom.', null, null, null, null, null, null, null, 'Эти стены, станут твоей гробницей.', '10516', '1', '0', '0', 'maladaar SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('18373', '-1558006', 'Stare into the darkness of your soul.', null, null, null, null, null, null, null, 'Вглядитесь во тьму ваших душ.', '10511', '1', '0', '0', 'maladaar SAY_SOUL_CLEAVE');
INSERT IGNORE INTO `script_texts` VALUES ('18373', '-1558005', 'Let your mind be clouded.', null, null, null, null, null, null, null, 'Да затмится ваш разум.', '10510', '1', '0', '0', 'maladaar SAY_ROAR');
INSERT IGNORE INTO `script_texts` VALUES ('18373', '-1558004', 'Serve your penitence!', null, null, null, null, null, null, null, 'Примите свое наказание!', '10515', '1', '0', '0', 'maladaar SAY_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('18373', '-1558003', 'There\'s no turning back now!', null, null, null, null, null, null, null, 'Пути назад больше нет!', '10514', '1', '0', '0', 'maladaar SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('18373', '-1558002', 'You will pay with your life!', null, null, null, null, null, null, null, 'Вы обречены!', '10513', '1', '0', '0', 'maladaar SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('18373', '-1558001', 'Rise my fallen brothers. Take form and fight!', null, null, null, null, null, null, null, 'Встаньте, мои павшие братья, встаньте и сражайтесь!', '10512', '1', '0', '0', 'maladaar SAY_SUMMON');
INSERT IGNORE INTO `script_texts` VALUES ('18341', '-1557013', 'To the void... once... more..', null, null, null, null, null, null, null, 'Снова... в пустоту...', '10566', '1', '0', '0', 'pandemonius SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('18341', '-1557014', 'shifts into the void...', null, null, null, null, null, null, null, 'превращается в пустого...', '0', '3', '0', '0', 'pandemonius EMOTE_DARK_SHELL');
INSERT IGNORE INTO `script_texts` VALUES ('18373', '-1558000', 'You have defiled the resting place of our ancestors. For this offense, there can be but one punishment. It is fitting that you have come to a place of the dead... for you will soon be joining them.', null, null, null, null, null, null, null, 'Вы осквернили покой наших предков. Есть лишь один способ искупить эту вину. Надеюсь вам понравилось в обители мертвых, потому, что скоро вы пополните их ряды.', '10509', '1', '0', '0', 'maladaar SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('18341', '-1557011', 'Yes! I am... empowered!', null, null, null, null, null, null, null, 'Да! Я чувствую себя... сильнее!', '10564', '1', '0', '0', 'pandemonius SAY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('18341', '-1557012', 'More... I must have more!', null, null, null, null, null, null, null, 'Больше... больше душ!', '10565', '1', '0', '0', 'pandemonius SAY_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('18341', '-1557010', 'Do not... resist.', null, null, null, null, null, null, null, 'Не... сопротивляйтесь.', '10563', '1', '0', '0', 'pandemonius SAY_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('18341', '-1557009', 'So... full of life!', null, null, null, null, null, null, null, 'Так много... жизненной силы!', '10562', '1', '0', '0', 'pandemonius SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('18341', '-1557008', 'I will feed on your soul.', null, null, null, null, null, null, null, 'Я пожру твою душу.', '10561', '1', '0', '0', 'pandemonius SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('18344', '-1557007', 'I must bid you... farewell.', null, null, null, null, null, null, null, 'Я должен проститься... с вами.', '10546', '1', '0', '0', 'shaffar SAY_DEAD');
INSERT IGNORE INTO `script_texts` VALUES ('18344', '-1557006', 'I have such fascinating things to show you.', null, null, null, null, null, null, null, 'Поверьте, у меня есть чем вас удивить.', '10540', '1', '0', '0', 'shaffar SAY_SUMMON');
INSERT IGNORE INTO `script_texts` VALUES ('18344', '-1557005', 'And now we part company.', null, null, null, null, null, null, null, 'Это было увлекательно.', '10545', '1', '0', '0', 'shaffar SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('18344', '-1557004', 'It has been... entertaining.', null, null, null, null, null, null, null, 'И вот мы... расстаемся.', '10544', '1', '0', '0', 'shaffar SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('18344', '-1557002', 'An epic battle. How exciting!', null, null, null, null, null, null, null, 'Достойный противник. Великолепно!', '10542', '1', '0', '0', 'shaffar SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('18344', '-1557003', 'I have longed for a good adventure.', null, null, null, null, null, null, null, 'Я как раз соскучился по приключениям.', '10543', '1', '0', '0', 'shaffar SAY_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('18344', '-1557001', 'We have not yet been properly introduced.', null, null, null, null, null, null, null, 'Нас не представили друг другу должным образом.', '10541', '1', '0', '0', 'shaffar SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('18344', '-1557000', 'What is this? You must forgive me, but I was not expecting company. As you can see, we are somewhat preoccupied right now. But no matter. As I am a gracious host, I will tend to you... personally.', null, null, null, null, null, null, null, 'Что такое? Прошу меня извинить, я не ждал гостей, как видите мы тут немного заняты, но не волнуйтесь, как гостеприимный хозяин я уделю вам внимание... лично.', '10539', '1', '0', '0', 'shaffar SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('18473', '-1556015', 'begins to channel arcane energy...', null, null, null, null, null, null, null, 'начинается канал загадочной энергии…', '0', '3', '0', '0', 'ikiss EMOTE_ARCANE_EXP');
INSERT IGNORE INTO `script_texts` VALUES ('18473', '-1556013', 'Ikiss will not..<squawk>..die', null, null, null, null, null, null, null, 'Икисс…..<squawk>….умрет.', '10560', '1', '0', '0', 'ikiss SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('18473', '-1556012', '<squawk>', null, null, null, null, null, null, null, '<squawk>', '10559', '1', '0', '0', 'ikiss SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('18473', '-1556011', 'You die..<squawk>..stay away from Trinkets', null, null, null, null, null, null, null, 'Умри... <squawk>... не пущу к Блестяшкам...', '10558', '1', '0', '0', 'ikiss SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('18473', '-1556010', 'No escape for..<squawk>..for you', null, null, null, null, null, null, null, 'Вам не скрыться...<squawk>', '10556', '1', '0', '0', 'ikiss SAY_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('18473', '-1556009', 'Ikiss cut you pretty..<squawk>..slice you. Yes!', null, null, null, null, null, null, null, 'Икисс расправиться с вами красавчики..<squawk>..Да. Порежет на кусочки!', '10555', '1', '0', '0', 'ikiss SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('18473', '-1556008', 'You make war on Ikiss?..<squawk>', null, null, null, null, null, null, null, 'Хотите сразиться с Икиссом?..<squawk>', '10554', '1', '0', '0', 'ikiss SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('18473', '-1556007', '<squawk>..Trinkets yes pretty Trinkets..<squawk>..power, great power. <squawk>..power in Trinkets..<squawk>', null, null, null, null, null, null, null, '<squawk>..Блестяшки, да красивые Блестяшки..<squawk>..сила, огромная сила.<squawk>..сила в Блестяшках..<squawk>', '10557', '1', '0', '0', 'ikiss SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('18472', '-1556006', 'No more life..hrm. No more pain. <squawks weakly>', null, null, null, null, null, null, null, 'Нет жизни... Нет боли.', '10508', '1', '0', '0', 'syth SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('18472', '-1556005', 'Uhn.. Be free..<squawk>', null, null, null, null, null, null, null, 'Хм... Отправляйся в лучший мир ...<squawk>', '10507', '1', '0', '0', 'syth SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('18472', '-1556003', 'Nice pets have.. weapons. Not so..<squawk>..nice.', null, null, null, null, null, null, null, 'У милых зверюшек есть оружие. Это не очень хорошо.', '10505', '1', '0', '0', 'syth SAY_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('18472', '-1556004', 'Death.. meeting life is.. <squawking>', null, null, null, null, null, null, null, 'Да... жизнь так скоротечна... <squawking>', '10506', '1', '0', '0', 'syth SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('18472', '-1556002', 'Nice pets..hrm.. Yes! <squawking>', null, null, null, null, null, null, null, 'Какие милые зверушки... Ахх! <squawking>', '10504', '1', '0', '0', 'syth SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('18708', '-1555036', 'draws energy from the air.', null, null, null, null, null, null, null, 'использует энергию из воздуха.', '0', '2', '0', '0', 'murmur EMOTE_SONIC_BOOM');
INSERT IGNORE INTO `script_texts` VALUES ('18472', '-1556000', 'I have pets..<squawk>..of my own!', null, null, null, null, null, null, null, 'У меня свои... зверушки есть!', '10502', '1', '0', '0', 'syth SAY_SUMMON');
INSERT IGNORE INTO `script_texts` VALUES ('18472', '-1556001', 'Hrrmm.. Time to.. hrrm.. make my move.', null, null, null, null, null, null, null, 'Хррмм…. А теперь... мой ход.', '10503', '1', '0', '0', 'syth SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('18732', '-1555035', 'I give my life... Gladly.', null, null, null, null, null, null, null, 'Я расстаюсь с жизнью... С радостью.', '10529', '1', '0', '0', 'vorpil SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('18732', '-1555033', 'I serve with pride.', null, null, null, null, null, null, null, 'Я с честью исполняю свой долг.', '10527', '1', '0', '0', 'vorpil SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('18732', '-1555034', 'Your death is for the greater cause!', null, null, null, null, null, null, null, 'Твоя смерть послужит великой цели!', '10528', '1', '0', '0', 'vorpil SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('18732', '-1555032', 'Come to my aid, heed your master now!', null, null, null, null, null, null, null, 'Ко мне, услышьте своего господина!', '10523', '1', '0', '0', 'vorpil SAY_HELP');
INSERT IGNORE INTO `script_texts` VALUES ('18732', '-1555031', 'Good, a worthy sacrifice.', null, null, null, null, null, null, null, 'Отлично, достойные жертвы.', '10526', '1', '0', '0', 'vorpil SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('18732', '-1555030', 'You\'ll be a fine example, for the others.', null, null, null, null, null, null, null, 'Вы будете прекрасным примером для других.', '10525', '1', '0', '0', 'vorpil SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('18732', '-1555029', 'I\'ll make an offering of your blood!', null, null, null, null, null, null, null, 'Я принесу в жертву вашу кровь!', '10524', '1', '0', '0', 'vorpil SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('18732', '-1555028', 'Keep your minds focused for the days of reckoning are close at hand. Soon, the destroyer of worlds will return to make good on his promise. Soon the destruction of all that is will begin!', null, null, null, null, null, null, null, 'Не расслабляйтесь ни на секунду, ибо грядет час скорой расплаты! Разрушитель миров вернется, дабы каждому воздать по делам его! Скоро, скоро настанут времена хаоса и разрушения! Они уже на пороге!', '10522', '1', '0', '0', 'vorpil SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555027', 'Arrgh, aah...ahhh', null, null, null, null, null, null, null, 'Агрр, аах….ахх', '10501', '1', '0', '0', 'blackhearth SAY2_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555026', 'Help us, hurry!', null, null, null, null, null, null, null, 'На помощь, быстрея!', '10495', '1', '0', '0', 'blackhearth SAY2_HELP');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555025', 'You failed, haha haha', null, null, null, null, null, null, null, 'Неудачник, хаха хаха', '10500', '1', '0', '0', 'blackhearth SAY2_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555024', 'Now you gone for good.', null, null, null, null, null, null, null, 'Теперь ты совсем мертвый.', '10499', '1', '0', '0', 'blackhearth SAY2_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555023', 'You be dead people!', null, null, null, null, null, null, null, 'Вы будете мертвые.', '10498', '1', '0', '0', 'blackhearth SAY2_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555022', 'Time to kill!', null, null, null, null, null, null, null, 'Пора убивать!', '10497', '1', '0', '0', 'blackhearth SAY2_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555021', 'You\'ll be sorry!', null, null, null, null, null, null, null, 'Вы пожалеете!', '10496', '1', '0', '0', 'blackhearth SAY2_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555020', 'Dark one promise!', null, null, null, null, null, null, null, 'Так обещал владыка!', '10494', '1', '0', '0', 'blackhearth SAY2_INTRO3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555019', 'So we have place in new universe.', null, null, null, null, null, null, null, 'Для нас найдется место в новой вселенной..', '10493', '1', '0', '0', 'blackhearth SAY2_INTRO2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555018', 'Be ready for Dark One\'s return.', null, null, null, null, null, null, null, 'Будьте готовы к возвращению темного владыки.', '10492', '1', '0', '0', 'blackhearth SAY2_INTRO1');
INSERT IGNORE INTO `script_texts` VALUES ('18667', '-1555017', 'This... no... good...', null, null, null, null, null, null, null, 'Это… плохо...', '10491', '1', '0', '0', 'blackhearth SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555016', 'Help us, hurry!', null, null, null, null, null, null, null, 'На помощь, быстрея!', '10485', '1', '0', '0', 'blackhearth SAY_HELP');
INSERT IGNORE INTO `script_texts` VALUES ('18667', '-1555015', 'Nice try!', null, null, null, null, null, null, null, 'Славная попытка!', '10490', '1', '0', '0', 'blackhearth SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('18667', '-1555014', 'No comin\' back for you!', null, null, null, null, null, null, null, 'От туда ты не вернешься!', '10489', '1', '0', '0', 'blackhearth SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('18667', '-1555013', 'I see dead people!', null, null, null, null, null, null, null, 'Я вижу мёртвецов!', '10488', '1', '0', '0', 'blackhearth SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('18667', '-1555012', 'Time for fun!', null, null, null, null, null, null, null, 'Пора веселиться!', '10487', '1', '0', '0', 'blackhearth SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('18667', '-1555011', 'You\'ll be sorry!', null, null, null, null, null, null, null, 'Вы пожалеете!', '10486', '1', '0', '0', 'blackhearth SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555010', 'Power will be yours!', null, null, null, null, null, null, null, 'Власть будет принадлежать вам!', '10484', '1', '0', '0', 'blackhearth SAY_INTRO3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555009', 'All creation must be unmade!', null, null, null, null, null, null, null, 'Устои мироздания рухнут!', '10483', '1', '0', '0', 'blackhearth SAY_INTRO2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1555008', 'All flesh must burn.', null, null, null, null, null, null, null, 'Все живое должно сгореть в огне.', '10482', '1', '0', '0', 'blackhearth SAY_INTRO1');
INSERT IGNORE INTO `script_texts` VALUES ('18731', '-1555007', 'Do not...grow...overconfident, mortal.', null, null, null, null, null, null, null, 'Не слишком гордитесь, смертные.', '10480', '1', '0', '0', 'hellmaw SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('18731', '-1555006', 'This is the part I enjoy most.', null, null, null, null, null, null, null, 'Обожаю такие моменты.', '10479', '1', '0', '0', 'hellmaw SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('18731', '-1555005', 'Do you fear death?', null, null, null, null, null, null, null, 'Ты боишься смерти? Ха ха ха ха.', '10478', '1', '0', '0', 'hellmaw SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('18731', '-1555004', 'Aid me, you fools, before it\'s too late!', null, null, null, null, null, null, null, 'Глупцы, помогите мне, пока не поздно!', '10474', '1', '0', '0', 'hellmaw SAY_HELP');
INSERT IGNORE INTO `script_texts` VALUES ('18731', '-1555001', 'Pathetic mortals! You will pay dearly!', null, null, null, null, null, null, null, 'Жалкие смертные, вы дорого заплатите!', '10475', '1', '0', '0', 'hellmaw SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('18731', '-1555002', 'I will break you!', null, null, null, null, null, null, null, 'Я сокрушу вас!', '10476', '1', '0', '0', 'hellmaw SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('18731', '-1555003', 'Finally! Something to relieve the tedium!', null, null, null, null, null, null, null, 'Наконец-то! Хоть кто-то развеет мою скуку!', '10477', '1', '0', '0', 'hellmaw SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('18731', '-1555000', 'Infidels have invaded the sanctuary! Sniveling pests...You have yet to learn the true meaning of agony!', null, null, null, null, null, null, null, 'Нечистивые вторглись в святилище! Гнусные твари... вы еще узнаете что такое истинные муки!', '10473', '1', '0', '0', 'hellmaw SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('19220', '-1554027', 'The project will... continue.', null, null, null, null, null, null, null, 'Проект продолжиться.', '11200', '1', '0', '0', 'pathaleon SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('19220', '-1554026', 'Looks like you lose.', null, null, null, null, null, null, null, 'Похоже я оказался сильнее.', '11195', '1', '0', '0', 'pathaleon SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('19220', '-1554025', 'A minor inconvenience.', null, null, null, null, null, null, null, 'Досадное недоразумение.', '11194', '1', '0', '0', 'pathaleon SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('19220', '-1554024', 'I prefeer to be hands-on...', null, null, null, null, null, null, null, 'Предпочитаю практичность и удобства.', '11199', '1', '0', '0', 'pathaleon SAY_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('19220', '-1554023', 'Time to supplement my work force.', null, null, null, null, null, null, null, 'Пора расширить штат сотрудников.', '11196', '1', '0', '0', 'pathaleon SAY_SUMMON');
INSERT IGNORE INTO `script_texts` VALUES ('19220', '-1554022', 'You work for me now!', null, null, null, null, null, null, null, 'Теперь ты служишь мне!', '11198', '1', '0', '0', 'pathaleon SAY_DOMINATION_2');
INSERT IGNORE INTO `script_texts` VALUES ('19220', '-1554021', 'I\'m looking for a team player...', null, null, null, null, null, null, null, 'Требуется комуникабельный работник...', '11197', '1', '0', '0', 'pathaleon SAY_DOMINATION_1');
INSERT IGNORE INTO `script_texts` VALUES ('19220', '-1554020', 'We are on a strict timetable. You will not interfere!', null, null, null, null, null, null, null, 'У нас очень строгий график, не вмешивайтесь.', '11193', '1', '0', '0', 'pathaleon SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('19221', '-1554019', 'Anu... bala belore...alon.', null, null, null, null, null, null, null, 'Ану... бала белоре... алон.', '11192', '1', '0', '0', 'sepethrea SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('19221', '-1554018', 'En\'dala finel el\'dal', null, null, null, null, null, null, null, 'Эн\'дала финель эн\'даль', '11188', '1', '0', '0', 'sepethrea SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('19221', '-1554017', 'And don\'t come back!', null, null, null, null, null, null, null, 'И не возвращайся!', '11187', '1', '0', '0', 'sepethrea SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('19221', '-1554016', 'Anar\'endal dracon!', null, null, null, null, null, null, null, 'Анар\'эндаль дракон!', '11190', '1', '0', '0', 'sepethrea SAY_DRAGONS_BREATH_2');
INSERT IGNORE INTO `script_texts` VALUES ('19221', '-1554015', 'Think you can take the heat?', null, null, null, null, null, null, null, 'Лезете в самое пекло?', '11189', '1', '0', '0', 'sepethrea SAY_DRAGONS_BREATH_1');
INSERT IGNORE INTO `script_texts` VALUES ('19221', '-1554014', 'I am not alone.', null, null, null, null, null, null, null, 'Я не одна.', '11191', '1', '0', '0', 'sepethrea SAY_SUMMON');
INSERT IGNORE INTO `script_texts` VALUES ('19221', '-1554013', 'Don\'t value your life very much, do you?', null, null, null, null, null, null, null, 'Хм. Кажется вы не слишком-то цените свою жизнь.', '11186', '1', '0', '0', 'sepethrea SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('19710', '-1554012', 'raises his hammer menacingly...', null, null, null, null, null, null, null, 'поднимает свой молоток угрожающе...', '0', '3', '0', '0', 'ironhand EMOTE_HAMMER');
INSERT IGNORE INTO `script_texts` VALUES ('19710', '-1554011', 'My calculations did not...', null, null, null, null, null, null, null, 'Мои расчеты не... оправ...дались...', '11114', '1', '0', '0', 'ironhand SAY_DEATH_1');
INSERT IGNORE INTO `script_texts` VALUES ('19710', '-1554010', 'The processing will continue a schedule!', null, null, null, null, null, null, null, 'Обработка проходит по плану!', '11111', '1', '0', '0', 'ironhand SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('19710', '-1554009', 'A foregone conclusion.', null, null, null, null, null, null, null, 'Закономерный результат!', '11110', '1', '0', '0', 'ironhand SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('19710', '-1554008', 'Low tech yet quiet effective!', null, null, null, null, null, null, null, 'Примитивно, но эффективно!', '11113', '1', '0', '0', 'ironhand SAY_HAMMER_2');
INSERT IGNORE INTO `script_texts` VALUES ('19710', '-1554007', 'With the precise angle and velocity...', null, null, null, null, null, null, null, 'Точно рассчитав угол и скорость...', '11112', '1', '0', '0', 'ironhand SAY_HAMMER_1');
INSERT IGNORE INTO `script_texts` VALUES ('19710', '-1554006', 'You have approximately five seconds to live.', null, null, null, null, null, null, null, 'Жить вам осталось около пяти секунд!', '11109', '1', '0', '0', 'ironhand SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1554005', 'An unforseen... contingency', null, null, null, null, null, null, null, 'Это непредвиденная... случайность', '11106', '1', '0', '0', 'gyro SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1554004', 'Yes, the only logical outcome.', null, null, null, null, null, null, null, 'Да, таков единственный логический исход.', '11103', '1', '0', '0', 'gyro SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1554003', 'Your strategy was flawed!', null, null, null, null, null, null, null, 'Твоя стратегия была небезупречна!', '11102', '1', '0', '0', 'gyro SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1554002', 'If my division is correct, you should be quite dead.', null, null, null, null, null, null, null, 'Если мои расчеты не врут, вы должны быть уже мертвы.', '11105', '1', '0', '0', 'gyro SAY_SAW_ATTACK2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1554001', 'Measure twice; cut once!', null, null, null, null, null, null, null, '\"Семь раз отмерь; один отрежь!\"', '11104', '1', '0', '0', 'gyro SAY_SAW_ATTACK1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1554000', 'I predict a painful death.', null, null, null, null, null, null, null, 'Я предвижу чью-то мучительную смерть.', '11101', '1', '0', '0', 'gyro SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17977', '-1553012', 'So... confused. Do not... belong here!', null, null, null, null, null, null, null, 'Все это... так странно. Мне здесь... не место!', '11235', '1', '0', '0', 'warp SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17977', '-1553011', 'Maybe this is not-- No, we fight! Come to my aid.', null, null, null, null, null, null, null, 'Может это и--Нет, мы будем биться! Ко мне, на помощь.', '11234', '1', '0', '0', 'warp SAY_SUMMON_2');
INSERT IGNORE INTO `script_texts` VALUES ('17977', '-1553010', 'Children, come to me!', null, null, null, null, null, null, null, 'Ко мне, дети мои!', '11233', '1', '0', '0', 'warp SAY_SUMMON_1');
INSERT IGNORE INTO `script_texts` VALUES ('17977', '-1553009', 'What am I doing? Why do I...', null, null, null, null, null, null, null, 'Что я делаю? Почему я...', '11232', '1', '0', '0', 'warp SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('17977', '-1553008', 'You must die! But wait: this does not-- No, no... you must die!', null, null, null, null, null, null, null, 'Ты должен умереть! Погоди: это еще не значит...Нет, нет... ты должен умереть!', '11231', '1', '0', '0', 'warp SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('17977', '-1553007', 'Who disturbs this sanctuary?', null, null, null, null, null, null, null, 'Кто посмел вторгнуться в святилище?', '11230', '1', '0', '0', 'warp SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17980', '-1553006', 'emits a strange noise.', null, null, null, null, null, null, null, 'испускает странный шум.', '0', '2', '0', '0', 'laj EMOTE_SUMMON');
INSERT IGNORE INTO `script_texts` VALUES ('17975', '-1553005', 'The specimens...must be preserved.', null, null, null, null, null, null, null, 'Образцы... нужно сохранить.', '11149', '1', '0', '0', 'freywinn SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17975', '-1553004', 'Nature bends to my will!', null, null, null, null, null, null, null, 'Природа покорна моей воле!', '11148', '1', '0', '0', 'freywinn SAY_TREE_2');
INSERT IGNORE INTO `script_texts` VALUES ('17975', '-1553003', 'Endorel aluminor!', null, null, null, null, null, null, null, 'Ендерель ануминор!', '11147', '1', '0', '0', 'freywinn SAY_TREE_1');
INSERT IGNORE INTO `script_texts` VALUES ('17975', '-1553002', 'You will feed the worms.', null, null, null, null, null, null, null, 'Отправляйся на корм червям.', '11146', '1', '0', '0', 'freywinn SAY_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('17975', '-1553001', 'Your life cycle is now concluded!', null, null, null, null, null, null, null, 'Твой жизненный цикл закончен!', '11145', '1', '0', '0', 'freywinn SAY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('17975', '-1553000', 'What are you doing? These specimens are very delicate!', null, null, null, null, null, null, null, 'Что вы делаете? Эти образцы очень хрупкие!', '11144', '1', '0', '0', 'freywinn SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('20904', '-1552030', 'Welcome, O\'great one. I am your humble servant.', null, null, null, null, null, null, null, 'Приветствую вас о великий, я ваш скромный слуга!', '11229', '1', '0', '0', 'mellichar YELL_WELCOME');
INSERT IGNORE INTO `script_texts` VALUES ('20904', '-1552029', 'One final cell remains. Yes, O\'great one, right away!', null, null, null, null, null, null, null, 'Осталась последняя камера, да о великий, сию минуту.', '11228', '1', '0', '0', 'mellichar YELL_RELEASE4');
INSERT IGNORE INTO `script_texts` VALUES ('20904', '-1552028', 'Anarchy! Bedlam! Oh, you are so wise! Yes, I see it now, of course!', null, null, null, null, null, null, null, 'Анархия, бедлам, а вы так мудры, да конечно теперь я вижу.', '11227', '1', '0', '0', 'mellichar YELL_RELEASE3');
INSERT IGNORE INTO `script_texts` VALUES ('20904', '-1552027', 'What is this? A lowly gnome? I will do better, O\'great one.', null, null, null, null, null, null, null, 'А это еще кто, что за жалкий гном? Прости меня о великий, я немедленно устраню это недоразумение.', '11226', '1', '0', '0', 'mellichar YELL_RELEASE2B');
INSERT IGNORE INTO `script_texts` VALUES ('20904', '-1552026', 'Behold another terrifying creature of incomprehensible power!', null, null, null, null, null, null, null, 'Смотрите, вот еще одно ужасное создание НЕБЫВАЛОЙ МОЩИ!', '11225', '1', '0', '0', 'mellichar YELL_RELEASE2A');
INSERT IGNORE INTO `script_texts` VALUES ('20904', '-1552025', 'Yes, yes... another! Your will is mine!', null, null, null, null, null, null, null, 'Да, да... следующий! Ваша воля для меня закон!', '11224', '1', '0', '0', 'mellichar YELL_RELEASE1');
INSERT IGNORE INTO `script_texts` VALUES ('20904', '-1552024', 'The naaru kept some of the most dangerous beings in existence here in these cells. Let me introduce you to another...', null, null, null, null, null, null, null, 'Здесь наару держали самых опасных обитателей вселенной, позвольте мне познакомить вас с одним из них.', '11223', '1', '0', '0', 'mellichar YELL_INTRO2');
INSERT IGNORE INTO `script_texts` VALUES ('20977', '-1552022', 'Who\'s bad? Who\'s bad? That\'s right: we bad!', null, null, null, null, null, null, null, 'Как мы их всех сделали?!', '11183', '1', '0', '0', 'millhouse SAY_COMPLETE');
INSERT IGNORE INTO `script_texts` VALUES ('20904', '-1552023', 'I knew the prince would be angry but, I... I have not been myself. I had to let them out! The great one speaks to me, you see. Wait--outsiders. Kael\'thas did not send you! Good... I\'ll just tell the prince you released the prisoners!', null, null, null, null, null, null, null, 'Я знаю, принцу это не понравиться, но я был не в себе, мне пришлось их выпустить. Великий говорит со мной, понимаете! Постойте... чужаки, Кель\'тас вас не посылал, ладно, скажу принцу, что это вы выпустили пленников. Ха ха ха ха ха', '11222', '1', '0', '0', 'mellichar YELL_INTRO1');
INSERT IGNORE INTO `script_texts` VALUES ('20977', '-1552021', 'You\'ll be hearing from my lawyer...', null, null, null, null, null, null, null, 'Мой адвокат этого так не оставит...', '11182', '1', '0', '0', 'millhouse SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('20977', '-1552020', 'Heal me! Oh, for the love of all that is holy, HEAL me! I\'m dying!', null, null, null, null, null, null, null, 'Подлечите меня! Во имя всего святого, ПОДЛЕЧИТЕ! Я умираю!', '11181', '1', '0', '0', 'millhouse SAY_LOWHP');
INSERT IGNORE INTO `script_texts` VALUES ('20977', '-1552019', 'Ice, ice, baby!', null, null, null, null, null, null, null, 'Лед, лед, детка!', '11180', '1', '0', '0', 'millhouse SAY_ICEBLOCK');
INSERT IGNORE INTO `script_texts` VALUES ('20977', '-1552018', 'I\'m gonna light you up, sweet cheeks!', null, null, null, null, null, null, null, 'Сейчас ты у меня поджаришься, зайка!', '11179', '1', '0', '0', 'millhouse SAY_PYRO');
INSERT IGNORE INTO `script_texts` VALUES ('20977', '-1552017', 'You guys, feel free to jump in anytime.', null, null, null, null, null, null, null, 'Присоединяйтесь, как сможете.', '11178', '1', '0', '0', 'millhouse SAY_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('20977', '-1552016', 'I didn\'t even break a sweat on that one.', null, null, null, null, null, null, null, 'Я даже не вспотел.', '11177', '1', '0', '0', 'millhouse SAY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('20977', '-1552015', 'Aaalllriiiight!! Who ordered up an extra large can of whoop-ass?', null, null, null, null, null, null, null, 'Ах хахаха!! Хорошо, ну кто тут заказывал большую драку!', '11176', '1', '0', '0', 'millhouse SAY_READY');
INSERT IGNORE INTO `script_texts` VALUES ('20977', '-1552014', 'And of course i\'ll need some mana. You guys are gonna love this, just wait.', null, null, null, null, null, null, null, 'И конечно мне нужна мана. Вам это понравиться, вот увидите.', '11175', '1', '0', '0', 'millhouse SAY_DRINK');
INSERT IGNORE INTO `script_texts` VALUES ('20977', '-1552013', 'Fantastic! Next, some protective spells. Yes! Now we\'re cookin\'', null, null, null, null, null, null, null, 'Великолепно! Добавим пару защитных заклинаний. Вот! А теперь приступим.', '11174', '1', '0', '0', 'millhouse SAY_BUFFS');
INSERT IGNORE INTO `script_texts` VALUES ('20977', '-1552012', 'I just need to get some things ready first. You guys go ahead and get started. I need to summon up some water...', null, null, null, null, null, null, null, 'Сперва мне нужно кое-что подготовить. А вы пока начинайте. Сейчас я создам немного воды.', '11173', '1', '0', '0', 'millhouse SAY_WATER');
INSERT IGNORE INTO `script_texts` VALUES ('20977', '-1552011', '\"Lowly\"? I don\'t care who you are friend, no one refers to the mighty Millhouse Manastorm as \"Lowly\"! I have no idea what goes on here, but I will gladly join your fight against this impudent imbecile! Prepare to defend yourself, cretin!', null, null, null, null, null, null, null, 'Это я то жалкий? Слушай, дружок я не знаю кто ты, но никто не смеет называть меня Милхарусом Манашторм Жалким, я понятия не имею, что тут происходит, но с радостью присоединюсь к вашей битве с этим бесстыжим недоумком. Защищайся, болван!', '11172', '1', '0', '0', 'millhouse SAY_INTRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('20977', '-1552010', 'Where in Bonzo\'s brass buttons am I? And who are-- yaaghh, that\'s one mother of a headache!', null, null, null, null, null, null, null, 'Как я здесь очутился, мегатрон меня раздери? И кто это свалился-- ай уу а на мою больную голову?', '11171', '1', '0', '0', 'millhouse SAY_INTRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('20912', '-1552009', 'I am merely one of... infinite multitudes.', null, null, null, null, null, null, null, 'Я всего лишь один... из бесконечного множества.', '11126', '1', '0', '0', 'skyriss SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('20912', '-1552008', 'We span the universe, as countless as the stars!', null, null, null, null, null, null, null, 'Мы бесчисленны как звезды, мы заполоним вселенную.', '11131', '1', '0', '0', 'skyriss SAY_IMAGE');
INSERT IGNORE INTO `script_texts` VALUES ('20912', '-1552007', 'I will show you horrors undreamed of!', null, null, null, null, null, null, null, 'Я покажу вам такие ужасы, каких не увидишь даже во сне!', '11130', '1', '0', '0', 'skyriss SAY_FEAR_2');
INSERT IGNORE INTO `script_texts` VALUES ('20912', '-1552006', 'Flee in terror!', null, null, null, null, null, null, null, 'Бегите, трусливые твари!', '11129', '1', '0', '0', 'skyriss SAY_FEAR_1');
INSERT IGNORE INTO `script_texts` VALUES ('20912', '-1552005', 'Your will is no longer your own.', null, null, null, null, null, null, null, 'Ты больше не принадлежишь себе.', '11128', '1', '0', '0', 'skyriss SAY_MIND_2');
INSERT IGNORE INTO `script_texts` VALUES ('20912', '-1552004', 'You will do my bidding, weakling.', null, null, null, null, null, null, null, 'Подчиняйся, ничтожество', '11127', '1', '0', '0', 'skyriss SAY_MIND_1');
INSERT IGNORE INTO `script_texts` VALUES ('20912', '-1552002', 'Your fate is written!', null, null, null, null, null, null, null, 'Твой путь окончен!', '11124', '1', '0', '0', 'skyriss SAY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('20912', '-1552003', 'The chaos I have sown here is but a taste...', null, null, null, null, null, null, null, 'Тот хаос, что я посеял здесь, это только начало...', '11125', '1', '0', '0', 'skyriss SAY_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('20912', '-1552001', 'Bear witness to the agent of your demise!', null, null, null, null, null, null, null, 'Станьте свидетелями собственной гибели!', '11123', '1', '0', '0', 'skyriss SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('20912', '-1552000', 'It is a small matter to control the mind of the weak... for I bear allegiance to powers untouched by time, unmoved by fate. No force on this world or beyond harbors the strength to bend our knee... not even the mighty Legion!', null, null, null, null, null, null, null, 'Мне не сложно контролировать разум слабого создания... Ибо я верен силам не подвластным времени и судьбе. Ничто в этом мире и вне его не сможет поставить нас на колени, даже могучий Легион!', '11122', '1', '0', '0', 'skyriss SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('20063', '-1550043', 'More perils... await', null, null, null, null, null, null, null, 'Впереди... еще много опасностей', '11158', '1', '0', '0', 'telonicus SAY_TELONICUS_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('20063', '-1550042', 'Anar\'alah belore!', null, null, null, null, null, null, null, 'Анар\'алах белоре!', '11157', '1', '0', '0', 'telonicus SAY_TELONICUS_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('20062', '-1550041', 'This is not over!', null, null, null, null, null, null, null, 'Это еще не конец!', '11118', '1', '0', '0', 'capernian SAY_CAPERNIAN_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('20062', '-1550040', 'The sin\'dore reign supreme!', null, null, null, null, null, null, null, 'Эльфы крови - высшая раса.', '11117', '1', '0', '0', 'capernian SAY_CAPERNIAN_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('20060', '-1550039', 'NO! I ...will... not...', null, null, null, null, null, null, null, 'НЕТ! Я ...не...', '11153', '1', '0', '0', 'sanguinar SAY_SANGUINAR_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('20060', '-1550038', 'Blood for blood!', null, null, null, null, null, null, null, 'Кровь за кровь!', '11152', '1', '0', '0', 'sanguinar SAY_SANGUINAR_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('20064', '-1550037', 'sets his gaze on $N!', null, null, null, null, null, null, null, 'устанавливает свой пристальный взгляд на $N!', '0', '2', '0', '0', 'thaladred EMOTE_THALADRED_GAZE');
INSERT IGNORE INTO `script_texts` VALUES ('20064', '-1550036', 'Forgive me, my prince! I have... failed.', null, null, null, null, null, null, null, 'Простите меня, мой принц! Я... не сумел.', '11204', '1', '0', '0', 'thaladred SAY_THALADRED_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('20064', '-1550035', 'Prepare yourselves!', null, null, null, null, null, null, null, 'Готовьтесь!', '11203', '1', '0', '0', 'thaladred SAY_THALADRED_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550034', 'For...Quel...thalas!', null, null, null, null, null, null, null, 'За... Кель... Талас!', '11274', '1', '0', '0', 'kaelthas SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550033', 'By the power of the sun!', null, null, null, null, null, null, null, 'Призываю силу солнца!', '11266', '1', '0', '0', 'kaelthas SAY_SUMMON_PHOENIX2');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550032', 'Anara\'nel belore!', null, null, null, null, null, null, null, 'Анара\'нель белоре.', '11267', '1', '0', '0', 'kaelthas SAY_SUMMON_PHOENIX1');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550031', 'Having trouble staying grounded?', null, null, null, null, null, null, null, 'Что, земля из под ног уходит?', '11265', '1', '0', '0', 'kaelthas SAY_GRAVITYLAPSE2');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550030', 'Let us see how you fare when your world is turned upside down.', null, null, null, null, null, null, null, 'Посмотрим, что будет, если перевернуть ваш мир вверх дном!', '11264', '1', '0', '0', 'kaelthas SAY_GRAVITYLAPSE1');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550029', 'Bow to my will.', null, null, null, null, null, null, null, 'Склонитесь перед моей волей.', '11269', '1', '0', '0', 'kaelthas SAY_MINDCONTROL2');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550028', 'Obey me.', null, null, null, null, null, null, null, 'Покоритесь мне.', '11268', '1', '0', '0', 'kaelthas SAY_MINDCONTROL1');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550027', 'This was Child\'s play.', null, null, null, null, null, null, null, 'От тебя толку... как от ребенка.', '11272', '1', '0', '0', 'kaelthas SAY_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550026', 'You gambled...and lost.', null, null, null, null, null, null, null, 'Твоя карта... бита.', '11271', '1', '0', '0', 'kaelthas SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550025', 'You will not prevail.', null, null, null, null, null, null, null, 'Вам не победить.', '11270', '1', '0', '0', 'kaelthas SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550024', 'I have not come this far to be stopped! The future I have planned will not be jeopardized! Now you will taste true power!!', null, null, null, null, null, null, null, 'Я не за тем ступил на этот путь, чтобы остановиться на пол дороги! Мои планы должны сбыться, и они сбудутся! Узрите же истинную мощь!', '11273', '1', '0', '0', 'kaelthas SAY_PHASE5_NUTS');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550023', 'Alas, sometimes one must take matters into one\'s own hands. Balamore shanal!', null, null, null, null, null, null, null, 'Увы, иногда приходится брать все в свои руки. Баланоре шаналь!', '11263', '1', '0', '0', 'kaelthas SAY_PHASE4_INTRO2');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550022', 'Perhaps I underestimated you. It would be unfair to make you fight all four advisors at once, but... fair treatment was never shown to my people. I\'m just returning the favor.', null, null, null, null, null, null, null, 'Возможно я недооценил вас. Было бы несправедливо заставлять вас драться с четырьмя советниками сразу... но... Мои люди тоже никогда не знали справедливости, я лишь возвращаю долг.', '11262', '1', '0', '0', 'kaelthas SAY_PHASE3_ADVANCE');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550021', 'As you see, I have many weapons in my arsenal...', null, null, null, null, null, null, null, 'Как видете, оружия у меня предостаточно...', '11261', '1', '0', '0', 'kaelthas SAY_PHASE2_WEAPON');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550020', 'You have persevered against some of my best advisors... but none can withstand the might of the Blood Hammer. Behold, Lord Sanguinar!', null, null, null, null, null, null, null, 'Вы справились с моими лучшими советниками... Но перед мощью Кровавого Молота не устоит никто. Узрите Лорда Сангвинара!', '11260', '1', '0', '0', 'kaelthas SAY_INTRO_SANGUINAR');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550019', 'Let us see how your nerves hold up against the Darkener, Thaladred.', null, null, null, null, null, null, null, 'Впечатляет, посмотрим, хватит ли у вас смелости выступить против Святокрада Талетрада.', '11259', '1', '0', '0', 'kaelthas SAY_INTRO_THALADRED');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550018', 'Well done, you have proven worthy to test your skills against my master engineer, Telonicus.', null, null, null, null, null, null, null, 'Неплохо, теперь вы можете потягаться с моим главным инженером, Телоникусом.', '11258', '1', '0', '0', 'kaelthas SAY_INTRO_TELONICUS');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550017', 'Capernian will see to it that your stay here is a short one.', null, null, null, null, null, null, null, 'Коперниан проследит, чтобы вы не задержались здесь надолго.', '11257', '1', '0', '0', 'kaelthas SAY_INTRO_CAPERNIAN');
INSERT IGNORE INTO `script_texts` VALUES ('19622', '-1550016', 'Energy. Power. My people are addicted to it... a dependence made manifest after the Sunwell was destroyed. Welcome... to the future. A pity you are too late to stop it. No one can stop me now! Selama ashal\'anore!', null, null, null, null, null, null, null, 'Энергия. Сила. Мои люди без них не могут.. эта зависимость возникла после уничтожения солнечного колодца. Добро пожаловать в будущее, мне очень жаль, но вы не сможете ничего изменить. Теперь меня никто не остановит. Силима Ашаля Норе.', '11256', '1', '0', '0', 'kaelthas SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('18805', '-1550015', 'I become ONE... with the VOID!', null, null, null, null, null, null, null, 'Я становлюсь ОДНИМ... из ПУСТЫХ!', '0', '1', '0', '0', 'solarian SAY_VOIDB');
INSERT IGNORE INTO `script_texts` VALUES ('18805', '-1550014', 'Enough of this! Now I call upon the fury of the cosmos itself.', null, null, null, null, null, null, null, 'Этого достаточно! Теперь я призываю ярость космоса непосредственно.', '0', '1', '0', '0', 'solarian SAY_VOIDA');
INSERT IGNORE INTO `script_texts` VALUES ('18805', '-1550013', 'The warmth of the sun... awaits.', null, null, null, null, null, null, null, 'Тепло солнца... ждет меня.', '11135', '1', '0', '0', 'solarian SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('18805', '-1550012', 'For the Sunwell!', null, null, null, null, null, null, null, 'За Солнечный Колодец!', '11138', '1', '0', '0', 'solarian SAY_KILL3');
INSERT IGNORE INTO `script_texts` VALUES ('18805', '-1550011', 'By the blood of the Highborne!', null, null, null, null, null, null, null, 'За кровь Высокорожденных!', '11137', '1', '0', '0', 'solarian SAY_KILL2');
INSERT IGNORE INTO `script_texts` VALUES ('18805', '-1550009', 'I will crush your delusions of grandeur!', null, null, null, null, null, null, null, 'Я навсегда избавлю вас от мании величия!', '11140', '1', '0', '0', 'solarian SAY_SUMMON2');
INSERT IGNORE INTO `script_texts` VALUES ('18805', '-1550010', 'Your soul belongs to the Abyss!', null, null, null, null, null, null, null, 'Твоя душа принадлежит Бездне!', '11136', '1', '0', '0', 'solarian SAY_KILL1');
INSERT IGNORE INTO `script_texts` VALUES ('18805', '-1550008', 'Ha ha ha! You are hopelessly outmatched!', null, null, null, null, null, null, null, 'Ха ха ха! Вы безнадёжно слабы!', '11139', '1', '0', '0', 'solarian SAY_SUMMON1');
INSERT IGNORE INTO `script_texts` VALUES ('18805', '-1550007', 'Tal anu\'men no Sin\'dorei!', null, null, null, null, null, null, null, 'Тал ану\'мен но Син\'дорей!', '11134', '1', '0', '0', 'solarian SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('19516', '-1550006', 'Calculating force parameters...', null, null, null, null, null, null, null, 'Расчёт силовых параметров...', '11219', '1', '0', '0', 'voidreaver SAY_POUNDING2');
INSERT IGNORE INTO `script_texts` VALUES ('19516', '-1550005', 'Alternative measure commencing...', null, null, null, null, null, null, null, 'Принятие альтернативных мер...', '11218', '1', '0', '0', 'voidreaver SAY_POUNDING1');
INSERT IGNORE INTO `script_texts` VALUES ('19516', '-1550004', 'Systems... shutting... down...', null, null, null, null, null, null, null, 'Отключение... системы...', '11214', '1', '0', '0', 'voidreaver SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('19516', '-1550003', 'Threat neutralized.', null, null, null, null, null, null, null, 'Угроза нейтрализована.', '11217', '1', '0', '0', 'voidreaver SAY_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('19516', '-1550001', 'Extermination, successful.', null, null, null, null, null, null, null, 'Уничтожение успешно завершено.', '11215', '1', '0', '0', 'voidreaver SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('19516', '-1550002', 'Imbecile life form, no longer functional.', null, null, null, null, null, null, null, 'Чужеродная форма жизни деактивирована.', '11216', '1', '0', '0', 'voidreaver SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('19516', '-1550000', 'Alert, you are marked for extermination!', null, null, null, null, null, null, null, 'Внимание, вы подлежите уничтожению!', '11213', '1', '0', '0', 'voidreaver SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548055', 'Lord Illidan, I... I am... sorry.', null, null, null, null, null, null, null, 'Владыка Иллидан, я... простите.', '11544', '1', '0', '0', 'vashj SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548054', 'Be\'lamere an\'delay', null, null, null, null, null, null, null, 'Бе\'лэнер Ан\'делай', '11543', '1', '0', '0', 'vashj SAY_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548053', 'You have failed!', null, null, null, null, null, null, null, 'Тебе не повезло!', '11542', '1', '0', '0', 'vashj SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548052', 'Your time ends now!', null, null, null, null, null, null, null, 'Твое время вышло!', '11541', '1', '0', '0', 'vashj SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548051', 'Seek your mark!', null, null, null, null, null, null, null, 'Прямо в цель!', '11537', '1', '0', '0', 'vashj SAY_BOWSHOT2');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548050', 'Straight to the heart!', null, null, null, null, null, null, null, 'Прямо в сердце!', '11536', '1', '0', '0', 'vashj SAY_BOWSHOT1');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548049', 'You may want to take cover.', null, null, null, null, null, null, null, 'Вам не пора прятаться?', '11540', '1', '0', '0', 'vashj SAY_PHASE3');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548048', 'The time is now! Leave none standing!', null, null, null, null, null, null, null, 'Время пришло! Не щадите никого!', '11539', '1', '0', '0', 'vashj SAY_PHASE2');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548047', 'I did not wish to lower myself by engaging your kind, but you leave me little choice!', null, null, null, null, null, null, null, 'Я не желаю унижаться, связываясь с вашим родом, но вы не оставляете мне выбора!', '11538', '1', '0', '0', 'vashj SAY_PHASE1');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548046', 'Death to the outsiders!', null, null, null, null, null, null, null, 'Смерть чужакам!', '11535', '1', '0', '0', 'vashj SAY_AGGRO4');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548045', 'I spit on you, surface filth!', null, null, null, null, null, null, null, 'Я уничтожу вас, земная грязь!', '11534', '1', '0', '0', 'vashj SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548044', 'Victory to Lord Illidan!', null, null, null, null, null, null, null, 'Слава Владыке Иллидану!', '11533', '1', '0', '0', 'vashj SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548043', 'I\'ll split you from stem to stern!', null, null, null, null, null, null, null, 'Я уничтожу вас!', '11532', '1', '0', '0', 'vashj SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('21212', '-1548042', 'Water is life. It has become a rare commodity here in Outland. A commodity that we alone shall control. We are the Highborne, and the time has come at last for us to retake our rightful place in the world!', null, null, null, null, null, null, null, 'Вода, это жизнь. Здесь, в Запределье она стала редким товаром, товаром который контролируем мы! Мы высокорожденные и наконец для нас настало время занять в мире место положенное нам по праву!', '11531', '1', '0', '0', 'vashj SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('21213', '-1548041', 'summons Watery Globules!', null, null, null, null, null, null, null, 'вызывает Водяные Капли!', '0', '2', '0', '0', 'morogrim EMOTE_WATERY_GLOBULES');
INSERT IGNORE INTO `script_texts` VALUES ('21213', '-1548040', 'The violent earthquake has alerted nearby murlocs!', null, null, null, null, null, null, null, 'Сильное землетрясение привело в готовность мурлоков поблизости!', '0', '3', '0', '0', 'morogrim EMOTE_EARTHQUAKE');
INSERT IGNORE INTO `script_texts` VALUES ('21213', '-1548039', 'sends his enemies to their watery graves!', null, null, null, null, null, null, null, 'посылает своих врагов к их водяным могилам!', '0', '2', '0', '0', 'morogrim EMOTE_WATERY_GRAVE');
INSERT IGNORE INTO `script_texts` VALUES ('21213', '-1548038', 'Great... currents of... Ageon.', null, null, null, null, null, null, null, 'Великие течения... Агеона.', '11329', '1', '0', '0', 'morogrim SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('21213', '-1548037', 'Only the strong survive.', null, null, null, null, null, null, null, 'Выживают сильнейшие.', '11328', '1', '0', '0', 'morogrim SAY_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('21213', '-1548036', 'Strugging only makes it worse.', null, null, null, null, null, null, null, 'Будешь сопротивляться, будет хуже!', '11327', '1', '0', '0', 'morogrim SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('21213', '-1548035', 'It is done!', null, null, null, null, null, null, null, 'Готово!', '11326', '1', '0', '0', 'morogrim SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('21213', '-1548033', 'There is nowhere to hide!', null, null, null, null, null, null, null, 'Вам не спрятаться!', '11324', '1', '0', '0', 'morogrim SAY_SUMMON_BUBL1');
INSERT IGNORE INTO `script_texts` VALUES ('21213', '-1548034', 'Soon it will be finished!', null, null, null, null, null, null, null, 'Скоро всё закончится!', '11325', '1', '0', '0', 'morogrim SAY_SUMMON_BUBL2');
INSERT IGNORE INTO `script_texts` VALUES ('21213', '-1548031', 'By the Tides, kill them at once!', null, null, null, null, null, null, null, 'Именем великого прилива!', '11322', '1', '0', '0', 'morogrim SAY_SUMMON1');
INSERT IGNORE INTO `script_texts` VALUES ('21213', '-1548032', 'Destroy them my subjects!', null, null, null, null, null, null, null, 'Уничтожьте их, мои слуги!', '11323', '1', '0', '0', 'morogrim SAY_SUMMON2');
INSERT IGNORE INTO `script_texts` VALUES ('21213', '-1548030', 'Flood of the deep, take you!', null, null, null, null, null, null, null, 'Да поглотит вас пучина вод!', '11321', '1', '0', '0', 'morogrim SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('21214', '-1548029', 'Her ... excellency ... awaits!', null, null, null, null, null, null, null, 'Ее превосходительство... ждет!', '11285', '1', '0', '0', 'karathress SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('21214', '-1548028', 'I am rid of you.', null, null, null, null, null, null, null, 'С тобой покончено.', '11284', '1', '0', '0', 'karathress SAY_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('21214', '-1548027', 'Alana be\'lendor!', null, null, null, null, null, null, null, 'Алана Бел\'ендор!', '11283', '1', '0', '0', 'karathress SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('21214', '-1548025', 'More knowledge, more power!', null, null, null, null, null, null, null, 'Больше знаний, больше силы!', '11281', '1', '0', '0', 'karathress SAY_GAIN_ABILITY3');
INSERT IGNORE INTO `script_texts` VALUES ('21214', '-1548026', 'Land-dwelling scum!', null, null, null, null, null, null, null, 'Сухопутная мразь!', '11282', '1', '0', '0', 'karathress SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('21214', '-1548024', 'I am more powerful than ever!', null, null, null, null, null, null, null, 'Я сильнее чем раньше!', '11280', '1', '0', '0', 'karathress SAY_GAIN_ABILITY2');
INSERT IGNORE INTO `script_texts` VALUES ('21214', '-1548023', 'Go on, kill them! I\'ll be the better for it!', null, null, null, null, null, null, null, 'Давайте, убейте их! Мне от этого будет только лучше!', '11279', '1', '0', '0', 'karathress SAY_GAIN_ABILITY1');
INSERT IGNORE INTO `script_texts` VALUES ('21214', '-1548022', 'Your overconfidence will be your undoing! Guards, lend me your strength!', null, null, null, null, null, null, null, 'Ваша самоуверенность вас погубит! Стражи, дайте мне сил!', '11278', '1', '0', '0', 'karathress SAY_GAIN_BLESSING');
INSERT IGNORE INTO `script_texts` VALUES ('21214', '-1548021', 'Guards, attention! We have visitors...', null, null, null, null, null, null, null, 'Стража, к бою! У нас гости...', '11277', '1', '0', '0', 'karathress SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('21215', '-1548020', 'You cannot kill me! Fools, I\'ll be back! I\'ll... aarghh...', null, null, null, null, null, null, null, 'Вы не можете убить меня! Глупцы... я вернусь! Я... ааааааа...', '11317', '1', '0', '0', 'leotheras SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('21215', '-1548019', 'At last I am liberated. It has been too long since I have tasted true freedom!', null, null, null, null, null, null, null, 'Наконец-то я свободен. Как же давно я ищу свой вкус настоящей свободы!', '11309', '1', '0', '0', 'leotheras SAY_FREE');
INSERT IGNORE INTO `script_texts` VALUES ('21215', '-1548018', 'No... no! What have you done? I am the master! Do you hear me? I am... aaggh! Can\'t... contain him...', null, null, null, null, null, null, null, 'Нет! НЕТ! Что вы наделали?! Я главный, слышишь меня? Я... аааа... мне его не удержать...', '11313', '1', '0', '0', 'leotheras SAY_FINAL_FORM');
INSERT IGNORE INTO `script_texts` VALUES ('21215', '-1548017', 'Who\'s the master now?', null, null, null, null, null, null, null, 'Ну, кто теперь главный?', '11316', '1', '0', '0', 'leotheras SAY_NIGHTELF_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('21215', '-1548016', 'That\'s right! Yes!', null, null, null, null, null, null, null, 'Вот так! Да!', '11315', '1', '0', '0', 'leotheras SAY_NIGHTELF_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('21215', '-1548015', 'Kill! KILL!', null, null, null, null, null, null, null, 'Смерть! СМЕРТЬ!', '11314', '1', '0', '0', 'leotheras SAY_NIGHTELF_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('21215', '-1548012', 'I have no equal.', null, null, null, null, null, null, null, 'Мен нет равных.', '11306', '1', '0', '0', 'leotheras SAY_DEMON_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('21215', '-1548013', 'Perish, mortal.', null, null, null, null, null, null, null, 'Умри, смертное ничтожество!', '11307', '1', '0', '0', 'leotheras SAY_DEMON_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('21215', '-1548014', 'Yes, YES! Ahahah!', null, null, null, null, null, null, null, 'Да, ДА! Урааааа!', '11308', '1', '0', '0', 'leotheras SAY_DEMON_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('21215', '-1548011', 'We all have our demons...', null, null, null, null, null, null, null, 'В каждом из нас есть свой демон...', '11305', '1', '0', '0', 'leotheras SAY_INNER_DEMONS');
INSERT IGNORE INTO `script_texts` VALUES ('21215', '-1548010', 'Be gone, trifling elf.  I am in control now!', null, null, null, null, null, null, null, 'Прочь, жалкий эльф. Настало моё время.', '11304', '1', '0', '0', 'leotheras SAY_SWITCH_TO_DEMON');
INSERT IGNORE INTO `script_texts` VALUES ('21215', '-1548009', 'Finally, my banishment ends!', null, null, null, null, null, null, null, 'Наконец-то мое заточение окончено!', '11312', '1', '0', '0', 'leotheras SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('21216', '-1548008', 'You are the disease, not I', null, null, null, null, null, null, null, 'Увы болезнь, не я.', '11300', '1', '0', '0', 'hydross SAY_CORRUPT_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('21216', '-1548007', 'You are no better than they!', null, null, null, null, null, null, null, 'Ты не чем не лучше их!', '11299', '1', '0', '0', 'hydross SAY_CORRUPT_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('21216', '-1548006', 'I will purge you from this place.', null, null, null, null, null, null, null, 'Я очищу это место от тебя.', '11298', '1', '0', '0', 'hydross SAY_CORRUPT_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('21216', '-1548004', 'I am... released...', null, null, null, null, null, null, null, 'Я… свободна...', '11293', '1', '0', '0', 'hydross SAY_CLEAN_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('21216', '-1548005', 'Aaghh, the poison...', null, null, null, null, null, null, null, 'Ахх, яд…', '11297', '1', '0', '0', 'hydross SAY_SWITCH_TO_CORRUPT');
INSERT IGNORE INTO `script_texts` VALUES ('21216', '-1548003', 'I have no choice.', null, null, null, null, null, null, null, 'У меня не было выбора.', '11292', '1', '0', '0', 'hydross SAY_CLEAN_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('21216', '-1548001', 'Better, much better.', null, null, null, null, null, null, null, 'Так лучше, намного лучше.', '11290', '1', '0', '0', 'hydross SAY_SWITCH_TO_CLEAN');
INSERT IGNORE INTO `script_texts` VALUES ('21216', '-1548002', 'They have forced me to this...', null, null, null, null, null, null, null, 'Меня заставили...', '11291', '1', '0', '0', 'hydross SAY_CLEAN_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('21216', '-1548000', 'I cannot allow you to interfere!', null, null, null, null, null, null, null, 'Я не позволю вам вмешиваться!', '11289', '1', '0', '0', 'hydross SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17798', '-1545023', 'For her Excellency... for... Vashj!', null, null, null, null, null, null, null, 'За нашу повелительницу… за... Вайш!', '10397', '1', '0', '0', 'kalithresh SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17798', '-1545022', 'Ah ha ha ha ha ha ha!', null, null, null, null, null, null, null, 'Ах ха ха ха ха ха ха!', '10396', '1', '0', '0', 'kalithresh SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('17798', '-1545021', 'Scram, surface filth!', null, null, null, null, null, null, null, 'Сгинь, сухопутная тварь!', '10395', '1', '0', '0', 'kalithresh SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('17798', '-1545020', 'Ba\'ahntha sol\'dorei!', null, null, null, null, null, null, null, 'Ба\'антаха сол\'дерай!', '10394', '1', '0', '0', 'kalithresh SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('17798', '-1545019', 'I despise all of your kind!', null, null, null, null, null, null, null, 'Я презираю подобных вам!', '10393', '1', '0', '0', 'kalithresh SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('17798', '-1545018', 'Your head will roll!', null, null, null, null, null, null, null, 'Головы с плечь!', '10392', '1', '0', '0', 'kalithresh SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('17798', '-1545017', 'This is not nearly over...', null, null, null, null, null, null, null, 'Всё только начинается...', '10391', '1', '0', '0', 'kalithresh SAY_REGEN');
INSERT IGNORE INTO `script_texts` VALUES ('17796', '-1545015', 'Mommy!', null, null, null, null, null, null, null, 'Мамочка!', '10375', '1', '0', '0', 'mekgineer SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17798', '-1545016', 'You deem yourselves worthy simply because you bested my guards? Our work here will not be compromised!', null, null, null, null, null, null, null, 'Гордитесь, что сумели справились с моей охраной? Ха ха ха! Нет уж, нашему делу, никто не помешает!', '10390', '1', '0', '0', 'kalithresh SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('17796', '-1545014', 'Have fun dyin\', cupcake!', null, null, null, null, null, null, null, 'Веселой смерти!', '10374', '1', '0', '0', 'mekgineer SOUND_SLAY_3');
INSERT IGNORE INTO `script_texts` VALUES ('17796', '-1545013', 'I own you!', null, null, null, null, null, null, null, 'Получай!', '10373', '1', '0', '0', 'mekgineer SOUND_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('17796', '-1545012', 'You just got served, punk!', null, null, null, null, null, null, null, 'Твой запас прочности, иссяк!', '10372', '1', '0', '0', 'mekgineer SOUND_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('17796', '-1545011', 'I\'m bringin\' the pain!', null, null, null, null, null, null, null, 'Эй ребята тут надо кое что настроить!', '10371', '1', '0', '0', 'mekgineer SAY_AGGRO_4');
INSERT IGNORE INTO `script_texts` VALUES ('17796', '-1545009', 'Eat hot metal, scumbag!', null, null, null, null, null, null, null, 'Сейчас вы отведаете раскаленного метала!', '10369', '1', '0', '0', 'mekgineer SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('17796', '-1545010', 'I\'ll come over there!', null, null, null, null, null, null, null, 'Ну держитесь!', '10370', '1', '0', '0', 'mekgineer SAY_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('17796', '-1545008', 'You\'re in for a world of hurt!', null, null, null, null, null, null, null, 'Вы узнаете, что такое боль!', '10368', '1', '0', '0', 'mekgineer SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('17796', '-1545007', 'I\'m bringin\' the pain!', null, null, null, null, null, null, null, 'Эй ребята тут надо кое что настроить!', '10367', '1', '0', '0', 'mekgineer SAY_MECHANICS');
INSERT IGNORE INTO `script_texts` VALUES ('17797', '-1545006', 'Our matron will be.. the end of.. you..', null, null, null, null, null, null, null, 'Наша Госпожа... прикончит... вас...', '10366', '1', '0', '0', 'thespia SAY_DEAD');
INSERT IGNORE INTO `script_texts` VALUES ('17797', '-1545005', 'For my lady and master!', null, null, null, null, null, null, null, 'За мою Госпожу и моего Повелителя!', '10365', '1', '0', '0', 'thespia SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('17797', '-1545003', 'You will drown in blood!', null, null, null, null, null, null, null, 'Вы захлебнётесь в своей же крови!', '10363', '1', '0', '0', 'thespia SAY_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('17797', '-1545004', 'To the depths of oblivion with you!', null, null, null, null, null, null, null, 'Извергнись в пучины забвения!', '10364', '1', '0', '0', 'thespia SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('17797', '-1545002', 'Meet your doom, surface dwellers!', null, null, null, null, null, null, null, 'Встречайте свою погибель, сухопутные крысы!', '10362', '1', '0', '0', 'thespia SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('17797', '-1545001', 'The depths will consume you!', null, null, null, null, null, null, null, 'Глубины поглотят вас!', '10361', '1', '0', '0', 'thespia SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('17797', '-1545000', 'Surge forth my pets!', null, null, null, null, null, null, null, 'Вперёд, мои слуги!', '10360', '1', '0', '0', 'thespia SAY_SUMMON');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1544015', 'breaks free!', null, null, null, null, null, null, null, 'вырывается на волю!', '0', '2', '0', '0', 'magtheridon EMOTE_FREED');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544014', 'bonds begin to weaken!', null, null, null, null, null, null, null, 'оковы начинают слабеть!', '0', '2', '0', '0', 'magtheridon EMOTE_BEGIN');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544011', 'The Legion...will consume you...all...', null, null, null, null, null, null, null, 'Легион поглотит... Вас... всех...', '10258', '1', '0', '0', 'magtheridon SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544012', '%s becomes enraged!', null, null, null, null, null, null, null, 'приходит в ярость!', '0', '2', '0', '0', 'magtheridon EMOTE_BERSERK');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544013', 'begins to cast Blast Nova!', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"начинает читать заклинание \"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Вспышка Огненной звезды\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"!\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '3', '0', '0', 'magtheridon EMOTE_BLASTNOVA');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544010', 'Did you think me weak? Soft? Who is the weak one now?!', null, null, null, null, null, null, null, 'Тебе казалось, что я слаб? Мягок? Так кто из нас слабее?!', '10255', '1', '0', '0', 'magtheridon SAY_PLAYER_KILLED');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544009', 'I will not be taken so easily. Let the walls of this prison tremble...and FALL!!!', null, null, null, null, null, null, null, 'Пусть стены темницы содрогнуться и... ПАДУТ!', '10257', '1', '0', '0', 'magtheridon SAY_CHAMBER_DESTROY');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544008', 'Not again...NOT AGAIN!', null, null, null, null, null, null, null, 'Нет... Только НЕ СНОВА!', '10256', '1', '0', '0', 'magtheridon SAY_BANISH');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544007', 'Thank you for releasing me. Now...die!', null, null, null, null, null, null, null, 'Спасибо за то, что освободили меня. А теперь... умрите!', '10254', '1', '0', '0', 'magtheridon SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544006', 'I...am...UNLEASHED!!!', null, null, null, null, null, null, null, 'Я... СВОБОДЕН!!!', '10253', '1', '0', '0', 'magtheridon SAY_FREED');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544005', 'My blood will be the end of you!', null, null, null, null, null, null, null, 'Моя кровь станет вашей погибелью!', '10252', '1', '0', '0', 'magtheridon SAY_TAUNT6');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544004', 'How long do you believe your pathetic sorcery can hold me?', null, null, null, null, null, null, null, 'Вы думаете, что ваша жалкая магия будет удерживать меня вечно?', '10251', '1', '0', '0', 'magtheridon SAY_TAUNT5');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544003', 'Away, you mindless parasites. My blood is my own!', null, null, null, null, null, null, null, 'Прочь, паразиты. Моя кровь принадлежит мне!', '10250', '1', '0', '0', 'magtheridon SAY_TAUNT4');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544002', 'Illidan is an arrogant fool. I will crush him and reclaim Outland as my own.', null, null, null, null, null, null, null, 'Иллидан самодовольный глупец. Я сокрушу его, и Запределье будет моим!', '10249', '1', '0', '0', 'magtheridon SAY_TAUNT3');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544001', 'Vermin! Leeches! Take my blood and choke on it!', null, null, null, null, null, null, null, 'Паразиты! Кровососы! Пейте мою кровь, и захлебнитесь ею.', '10248', '1', '0', '0', 'magtheridon SAY_TAUNT2');
INSERT IGNORE INTO `script_texts` VALUES ('17257', '-1544000', 'Wretched, meddling insects. Release me and perhaps I will grant you a merciful death!', null, null, null, null, null, null, null, 'Гнусные, назойливые насекомые. Освободите меня, и я, быть может, дарую вам лёгкую смерть!', '10247', '1', '0', '0', 'magtheridon SAY_TAUNT1');
INSERT IGNORE INTO `script_texts` VALUES ('17537', '-1543025', 'descends from the sky', null, null, null, null, null, null, null, '%s опускается вниз.', '0', '3', '0', '0', 'vazruden EMOTE');
INSERT IGNORE INTO `script_texts` VALUES ('17537', '-1543024', 'My lord will be the end you all...', null, null, null, null, null, null, null, 'Мой господин уничтожит всех вас...', '10299', '1', '0', '0', 'vazruden SAY_DIE');
INSERT IGNORE INTO `script_texts` VALUES ('17537', '-1543023', 'Your days are done!', null, null, null, null, null, null, null, 'Твои дни сочтены!', '10298', '1', '0', '0', 'vazruden SAY_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('17537', '-1543022', 'It is over. Finished!', null, null, null, null, null, null, null, 'Вот и все!', '10297', '1', '0', '0', 'vazruden SAY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('17537', '-1543019', 'Your time is running out!', null, null, null, null, null, null, null, 'Ваши дни сочтены!', '10294', '1', '0', '0', 'vazruden SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('17537', '-1543020', 'You are nothing, I answer a higher call!', null, null, null, null, null, null, null, 'Ничтожества, я служу высшим силам!', '10295', '1', '0', '0', 'vazruden SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('17537', '-1543021', 'The Dark Lord laughs at you!', null, null, null, null, null, null, null, 'Темный Властелин семется над вами!', '10296', '1', '0', '0', 'vazruden SAY_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('17537', '-1543018', 'Is there no one left to test me?', null, null, null, null, null, null, null, 'Что, больше некому сразиться со мной?', '10293', '1', '0', '0', 'vazruden SAY_WIPE');
INSERT IGNORE INTO `script_texts` VALUES ('17308', '-1543016', 'I am victorious!', null, null, null, null, null, null, null, 'Победа за мной!', '10283', '1', '0', '0', 'omor SAY_WIPE');
INSERT IGNORE INTO `script_texts` VALUES ('17307', '-1543017', 'You have faced many challenges, pity they were all in vain. Soon your people will kneel to my lord!', null, null, null, null, null, null, null, 'Вы пережили множество испытаний, жаль, что все это было напрасно. Скоро ваш народ склониться перед моим господином!', '10292', '1', '0', '0', 'vazruden SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('17308', '-1543014', 'Die, weakling!', null, null, null, null, null, null, null, 'Умри, ничтожество!', '10282', '1', '0', '0', 'omor SAY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('17308', '-1543015', 'It is... not over.', null, null, null, null, null, null, null, 'Это... ещё не конец.', '10284', '1', '0', '0', 'omor SAY_DIE');
INSERT IGNORE INTO `script_texts` VALUES ('17308', '-1543013', 'A-Kreesh!', null, null, null, null, null, null, null, 'А-креш!', '10278', '1', '0', '0', 'omor SAY_CURSE');
INSERT IGNORE INTO `script_texts` VALUES ('17308', '-1543012', 'Achor-she-ki! Feast my pet! Eat your fill!', null, null, null, null, null, null, null, 'Акор-ши-кай! Приди мой зверь! Ешь досыта!', '10277', '1', '0', '0', 'omor SAY_SUMMON');
INSERT IGNORE INTO `script_texts` VALUES ('17308', '-1543011', 'Your insolence will be your death.', null, null, null, null, null, null, null, 'Ваша дерзость вас погубит.', '10281', '1', '0', '0', 'omor SAY_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('17308', '-1543010', 'I will not be defeated!', null, null, null, null, null, null, null, 'Вам меня не одолеть!', '10279', '1', '0', '0', 'omor SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('17308', '-1543009', 'You dare stand against me?!', null, null, null, null, null, null, null, 'Вы смеете противостоять мне?!', '10280', '1', '0', '0', 'omor SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('17306', '-1543007', 'Much too easy...', null, null, null, null, null, null, null, 'Слишком просто...', '10335', '1', '0', '0', 'gargolmar SAY_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('17306', '-1543008', 'Hahah.. <cough> ..argh!', null, null, null, null, null, null, null, 'Хе хе х..<cough>..агггг!', '10336', '1', '0', '0', 'gargolmar SAY_DIE');
INSERT IGNORE INTO `script_texts` VALUES ('17306', '-1543005', 'I\'m gonna enjoy this.', null, null, null, null, null, null, null, 'Думаю мне понравиться этот бой.', '10333', '1', '0', '0', 'gargolmar SAY_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('17306', '-1543006', 'Say farewell!', null, null, null, null, null, null, null, 'Прощайся с жизнью!', '10334', '1', '0', '0', 'gargolmar SAY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('17306', '-1543004', 'Heh... this may hurt a little.', null, null, null, null, null, null, null, 'Кхе... Сейчас будет немного больно.', '10332', '1', '0', '0', 'gargolmar SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('17306', '-1543003', 'What have we here...?', null, null, null, null, null, null, null, 'Так, что у нас тут...?', '10331', '1', '0', '0', 'gargolmar SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('17306', '-1543001', 'Heal me! QUICKLY!', null, null, null, null, null, null, null, 'Лечите меня! БЫСТРО!', '10329', '1', '0', '0', 'gargolmar SAY_HEAL');
INSERT IGNORE INTO `script_texts` VALUES ('17306', '-1543002', 'Back off, pup!', null, null, null, null, null, null, null, 'Назад, ничтожества!', '10330', '1', '0', '0', 'gargolmar SAY_SURGE');
INSERT IGNORE INTO `script_texts` VALUES ('17306', '-1543000', 'Do you smell that? Fresh meat has somehow breached our citadel. Be wary of any intruders.', null, null, null, null, null, null, null, 'Вы чувствуете этот запах? Свежее мясо так или иначе пришло в нашу цитадель. Опасайтесь любых злоумышленников.', '0', '1', '0', '0', 'gargolmar SAY_TAUNT');
INSERT IGNORE INTO `script_texts` VALUES ('17381', '-1542013', 'It is pointless to resist.', null, null, null, null, null, null, null, 'Сопротивление бесполезно.', '10290', '1', '0', '0', 'the_maker SAY_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('17381', '-1542014', 'Stay away from... me.', null, null, null, null, null, null, null, 'Прочь... прочь от меняяя...', '10291', '1', '0', '0', 'the_maker SAY_DIE');
INSERT IGNORE INTO `script_texts` VALUES ('17381', '-1542012', 'Let\'s see what I can make of you.', null, null, null, null, null, null, null, 'Посмотрим на что вы сгодитесь.', '10289', '1', '0', '0', 'the_maker SAY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('17381', '-1542011', 'Anger... Hate... These are tools I can use.', null, null, null, null, null, null, null, 'Злоба… Ненависть… Это и есть мои инструменты.', '10288', '1', '0', '0', 'the_maker SAY_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('17380', '-1542008', 'Come intruders....', null, null, null, null, null, null, null, 'Заходи злоумышленник…', '0', '1', '0', '0', 'broggok SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17381', '-1542009', 'My work must not be interrupted.', null, null, null, null, null, null, null, 'Не мешайте мне работать!', '10286', '1', '0', '0', 'the_maker SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('17381', '-1542010', 'Perhaps I can find a use for you.', null, null, null, null, null, null, null, 'Может быть я найду вам применение.', '10287', '1', '0', '0', 'the_maker SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('17377', '-1542007', 'Good luck... you\'ll need it..', null, null, null, null, null, null, null, 'Удачи… она вам понадобится!', '10171', '1', '0', '0', 'kelidan SAY_DIE');
INSERT IGNORE INTO `script_texts` VALUES ('17377', '-1542006', 'Closer... Come closer.. and burn!', null, null, null, null, null, null, null, 'Ближе... Подойдите ближе... и горите!', '10165', '1', '0', '0', 'kelidan SAY_NOVA');
INSERT IGNORE INTO `script_texts` VALUES ('17377', '-1542005', 'Your friends will soon be joining you.', null, null, null, null, null, null, null, 'Твои друзья скоро к тебе присоединяться.', '10170', '1', '0', '0', 'kelidan SAY_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('17377', '-1542004', 'Just as you deserve!', null, null, null, null, null, null, null, 'Только этого ты и заслуживаешь!', '10169', '1', '0', '0', 'kelidan SAY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('17377', '-1542003', 'You fools! He\'ll kill us all!', null, null, null, null, null, null, null, 'Глупцы! Он же всех нас убьёт!', '10168', '1', '0', '0', 'kelidan SAY_ADD_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('17377', '-1542002', 'Ignorant whelps!', null, null, null, null, null, null, null, 'Безмозглые щенки!', '10167', '1', '0', '0', 'kelidan SAY_ADD_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('17377', '-1542001', 'You mustn\'t let him loose!', null, null, null, null, null, null, null, 'Никто не должен освободить его!', '10166', '1', '0', '0', 'kelidan SAY_ADD_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('17377', '-1542000', 'Who dares interrupt... What is this? What have you done? You ruin everything!', null, null, null, null, null, null, null, 'Кто посмел прервать... Вы? Вы понимаете, что вы наделали? Вы всё испортите!', '10164', '1', '0', '0', 'kelidan SAY_WAKE');
INSERT IGNORE INTO `script_texts` VALUES ('16808', '-1540046', 'I am the only Warchief!', null, null, null, null, null, null, null, 'Я истинный Вождь!', '10327', '1', '0', '0', 'kargath SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('16808', '-1540047', 'The true Horde... will.. prevail...', null, null, null, null, null, null, null, 'Истинная Орда... восторжествует...', '10328', '1', '0', '0', 'kargath SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('16808', '-1540045', 'For the real Horde!', null, null, null, null, null, null, null, 'За истинную орду!', '10326', '1', '0', '0', 'kargath SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('16808', '-1540044', 'I am called Bladefist for a reason, as you will see!', null, null, null, null, null, null, null, 'Меня не зря называют Остроруком!', '10325', '1', '0', '0', 'kargath SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('16808', '-1540043', 'I\'ll carve the meat from your bones!', null, null, null, null, null, null, null, 'Я сорву мясо с ваших костей!', '10324', '1', '0', '0', 'kargath SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('16808', '-1540042', 'Ours is the true Horde! The only Horde!', null, null, null, null, null, null, null, 'Истинная Орда это мы! И только мы!', '10323', '1', '0', '0', 'kargath SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540041', '%s enrages', null, null, null, null, null, null, null, 'прогневит', '0', '2', '0', '0', 'omrogg EMOTE_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540040', 'I...hate...you...', null, null, null, null, null, null, null, 'Я... тебя... ненавижу...', '10322', '1', '0', '0', 'omrogg YELL_DIE_R');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540039', 'This all...your fault!', null, null, null, null, null, null, null, 'Это... ты виноват!', '10311', '1', '0', '0', 'omrogg YELL_DIE_L');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540038', 'That\'s because I do all the hard work!', null, null, null, null, null, null, null, 'Вечно мне достается вся грязная работа!', '10321', '1', '0', '0', 'omrogg KillingDelay_1');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540037', 'I\'m tired. You kill next one!', null, null, null, null, null, null, null, 'Я устал. Давай дальше сам!', '10320', '1', '0', '0', 'omrogg Killing_2');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540035', 'We kill his friend!', null, null, null, null, null, null, null, 'Мы убивать его друга!', '10301', '1', '0', '0', 'omrogg ThreatDelay2_4');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540036', 'This one die easy!', null, null, null, null, null, null, null, 'Легкая добыча!', '10310', '1', '0', '0', 'omrogg Killing_1');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540034', 'H\'ey...', null, null, null, null, null, null, null, 'Эй!', '10307', '1', '0', '0', 'omrogg ThreatDelay2_3');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540033', 'Whhy! He almost dead!', null, null, null, null, null, null, null, 'Тебе скучно? Да там пару ударов оставалось сделать!', '10316', '1', '0', '0', 'omrogg ThreatDelay2_2');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540032', 'Ha ha ha.', null, null, null, null, null, null, null, 'Ха ха ха.', '10304', '1', '0', '0', 'omrogg ThreatDelay2_1');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540031', 'Hey you numbskull!', null, null, null, null, null, null, null, 'Эй тупица!', '10312', '1', '0', '0', 'omrogg ThreatDelay1_4');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540030', 'I\'m not done yet, idiot!', null, null, null, null, null, null, null, 'Я ещё не закончил, болван!', '10313', '1', '0', '0', 'omrogg ThreatDelay1_3');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540029', 'Me get bored...', null, null, null, null, null, null, null, 'Моя стать скучно...', '10305', '1', '0', '0', 'omrogg ThreatDelay1_2');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540028', 'That\'s not funny!', null, null, null, null, null, null, null, 'Не смешно!', '10314', '1', '0', '0', 'omrogg ThreatDelay1_1');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540027', 'Me not like this one...', null, null, null, null, null, null, null, 'Моя не нравиться вот этот!', '10300', '1', '0', '0', 'omrogg Threat_4');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540026', 'Me kill someone else...', null, null, null, null, null, null, null, 'Мы убивать еще кого-нибудь…', '10302', '1', '0', '0', 'omrogg Threat_3');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540025', 'What are you doing!', null, null, null, null, null, null, null, 'Ты что творишь!', '10315', '1', '0', '0', 'omrogg Threat_2');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540024', 'You stay here. Me go kill someone else!', null, null, null, null, null, null, null, 'Ты стоять здесь. Моя пойти убивать другого!', '10303', '1', '0', '0', 'omrogg Threat_1');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540023', 'You always hungry. That why we so fat!', null, null, null, null, null, null, null, 'Ты вечно хочешь есть! Потому-то у нас такое брюхо!', '10319', '1', '0', '0', 'omrogg GoCombatDelay_3');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540022', 'No, we will NOT let you live!', null, null, null, null, null, null, null, 'Нет, в живых мы никого не оставим!', '10318', '1', '0', '0', 'omrogg GoCombatDelay_2');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540021', 'Why don\'t you let me do the talking?', null, null, null, null, null, null, null, 'Молчи командовать здесь буду я!', '10317', '1', '0', '0', 'omrogg GoCombatDelay_1');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540020', 'Me hungry!', null, null, null, null, null, null, null, 'Моя хочет есть!', '10309', '1', '0', '0', 'omrogg GoCombat_3');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540019', 'If you nice me let you live.', null, null, null, null, null, null, null, 'Если вы хорошо вести, моя оставлять жить.', '10308', '1', '0', '0', 'omrogg GoCombat_2');
INSERT IGNORE INTO `script_texts` VALUES ('16809', '-1540018', 'Smash!', null, null, null, null, null, null, null, 'Бей!', '10306', '1', '0', '0', 'omrogg GoCombat_1');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540017', 'What a ... a shame.', null, null, null, null, null, null, null, 'Какой позор!', '10276', '1', '0', '0', 'nethekurse SAY_DIE');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540016', 'Ohh! Just die.', null, null, null, null, null, null, null, 'Ооо! Умри.', '10275', '1', '0', '0', 'nethekurse SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540015', 'You Loose.', null, null, null, null, null, null, null, 'Я победил.', '10274', '1', '0', '0', 'nethekurse SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540014', 'I had more fun torturing the peons.', null, null, null, null, null, null, null, 'Рабов пытать и то было интереснее.', '10273', '1', '0', '0', 'nethekurse SAY_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540013', 'Come on! ... Show me a real fight.', null, null, null, null, null, null, null, 'Давайте! ... Покажите мне на что вы способны!', '10272', '1', '0', '0', 'nethekurse SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540012', 'I\'m already bored.', null, null, null, null, null, null, null, 'Мне скучно.', '10271', '1', '0', '0', 'nethekurse SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540011', 'Your pain amuses me.', null, null, null, null, null, null, null, 'Ты так забавно страдаешь.', '10261', '1', '0', '0', 'nethekurse SAY_TAUNT_3');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540010', 'Run covad, ruun!', null, null, null, null, null, null, null, 'Беги, трусливая тварь! Спасайся!', '10260', '1', '0', '0', 'nethekurse SAY_TAUNT_2');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540009', 'Beg for your pittyfull life!', null, null, null, null, null, null, null, 'Моли о пощаде!', '10259', '1', '0', '0', 'nethekurse SAY_TAUNT_1');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540008', 'Thank you for saving me the trouble! Now it\'s my turn to have some fun...', null, null, null, null, null, null, null, 'Спасибо за помощь! А теперь мой черёд поразвлечься!', '10270', '1', '0', '0', 'nethekurse PEON_DIE_4');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540007', 'I was going to kill him anyway!', null, null, null, null, null, null, null, 'Я бы всё равно его убил!', '10269', '1', '0', '0', 'nethekurse PEON_DIE_3');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540006', 'Ahh, what a waste... Next!', null, null, null, null, null, null, null, 'Ооо, какая утрата... Следующий!', '10268', '1', '0', '0', 'nethekurse PEON_DIE_2');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540005', 'One pitiful wretch down. Go on, take another one.', null, null, null, null, null, null, null, 'Одним ничтожеством меньше. Кто следующий?', '10267', '1', '0', '0', 'nethekurse PEON_DIE_1');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540004', 'You want him? Very well, take him!', null, null, null, null, null, null, null, 'Он вам правда нужен? Ну тогда забирайте!', '10266', '1', '0', '0', 'nethekurse PEON_ATTACK_4');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540003', 'Don\'t waste your time on that one. He\'s weak!', null, null, null, null, null, null, null, 'Вам не жаль тратить время. На такого слабака!', '10265', '1', '0', '0', 'nethekurse PEON_ATTACK_3');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540002', 'Yes, beat him mercilessly. His skull is a thick as an ogres.', null, null, null, null, null, null, null, 'Можете бить его сколько угодно. У него череп как у огра.', '10264', '1', '0', '0', 'nethekurse PEON_ATTACK_2');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540001', 'You can have that one. I no longer need him.', null, null, null, null, null, null, null, 'Можете с ним развлечься. Мне он больше не нужен.', '10263', '1', '0', '0', 'nethekurse PEON_ATTACK_1');
INSERT IGNORE INTO `script_texts` VALUES ('16807', '-1540000', 'You wish to fight us all at once? This should be amusing!', null, null, null, null, null, null, null, 'Хотите сразиться со всеми сразу? Это будет изумительно!', '10262', '1', '0', '0', 'nethekurse SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('17968', '-1534030', 'Bow to my will.', null, null, null, null, null, null, null, 'Склонитесь перед моей волей.', '11042', '1', '0', '0', 'archimonde SAY_SOUL_CHARGE2');
INSERT IGNORE INTO `script_texts` VALUES ('17968', '-1534029', 'You are mine now.', null, null, null, null, null, null, null, 'Теперь Вы мои.', '10988', '1', '0', '0', 'archimonde SAY_SOUL_CHARGE1');
INSERT IGNORE INTO `script_texts` VALUES ('17968', '-1534028', 'No, it cannot be! Nooo!', null, null, null, null, null, null, null, 'Нет, не может быть! Нееет!', '10992', '1', '0', '0', 'archimonde SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17968', '-1534027', 'At last it is here. Mourn and lament the passing of all you have ever known and all that would have been! Akmin-kurai!', null, null, null, null, null, null, null, 'Наконец-то скорбь и плачь. Вот всё что вы и всё прочее будете ведать впредь! Какми-Карай!', '10993', '1', '0', '0', 'archimonde SAY_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('17968', '-1534026', 'I am the coming of the end!', null, null, null, null, null, null, null, 'Я - вестник гибели!', '11045', '1', '0', '0', 'archimonde SAY_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('17968', '-1534025', 'Your soul will languish for eternity.', null, null, null, null, null, null, null, 'Твоя душа будет чахнуть вечно.', '10991', '1', '0', '0', 'archimonde SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('17968', '-1534024', 'All creation will be devoured!', null, null, null, null, null, null, null, 'Всё сотворённое будет поглощено!', '11044', '1', '0', '0', 'archimonde SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('17968', '-1534023', 'Away vermin!', null, null, null, null, null, null, null, 'Прочь червь!', '11043', '1', '0', '0', 'archimonde SAY_AIR_BURST2');
INSERT IGNORE INTO `script_texts` VALUES ('17968', '-1534022', 'A-kreesh!', null, null, null, null, null, null, null, 'А-креш!', '10989', '1', '0', '0', 'archimonde SAY_AIR_BURST1');
INSERT IGNORE INTO `script_texts` VALUES ('17968', '-1534020', 'This world will burn!', null, null, null, null, null, null, null, 'Этот мир сгорит в огне!', '10990', '1', '0', '0', 'archimonde SAY_DOOMFIRE1');
INSERT IGNORE INTO `script_texts` VALUES ('17968', '-1534021', 'Manach sheek-thrish!', null, null, null, null, null, null, null, 'Манак шик-треш!', '11041', '1', '0', '0', 'archimonde SAY_DOOMFIRE2');
INSERT IGNORE INTO `script_texts` VALUES ('17968', '-1534019', 'Your resistance is insignificant.', null, null, null, null, null, null, null, 'Ваше сопротивление смехотворно.', '10987', '1', '0', '0', 'archimonde SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17852', '-1534017', 'Uraaa...', null, null, null, null, null, null, null, 'Уааагмм…', '11034', '1', '0', '0', 'thrall hyjal DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1534018', 'All of your efforts have been in vain, for the draining of the World Tree has already begun. Soon the heart of your world will beat no more.', null, null, null, null, null, null, null, 'Все ваши усилия были напрасны, ибо Древо Жизни уже иссыхает! Скоро сердце вашего мира перестанет биться!', '10986', '1', '0', '0', 'archimonde SAY_PRE_EVENTS_COMPLETE');
INSERT IGNORE INTO `script_texts` VALUES ('17852', '-1534016', 'We have played our part and done well. It is up to the others now.', null, null, null, null, null, null, null, 'Мы сыграли свою роль и сыграли ее хорошо. Теперь дело за остальными!', '11035', '1', '0', '0', 'thrall hyjal SUCCESS');
INSERT IGNORE INTO `script_texts` VALUES ('17852', '-1534015', 'It is over. Withdraw! We have failed.', null, null, null, null, null, null, null, 'Всё кончено. Отступаем! Мы проиграли.', '11033', '1', '0', '0', 'thrall hyjal FAILURE');
INSERT IGNORE INTO `script_texts` VALUES ('17852', '-1534014', 'Do not give an inch of ground!', null, null, null, null, null, null, null, 'Не отступать ни на шаг!', '11060', '1', '0', '0', 'thrall hyjal RALLY 2');
INSERT IGNORE INTO `script_texts` VALUES ('17852', '-1534013', 'Victory or death!', null, null, null, null, null, null, null, 'Победа или смерть!', '11059', '1', '0', '0', 'thrall hyjal RALLY 1');
INSERT IGNORE INTO `script_texts` VALUES ('17852', '-1534012', 'Hold them back! Do not falter!', null, null, null, null, null, null, null, 'Держать строй, ни шагу назад!', '11030', '1', '0', '0', 'thrall hyjal BEGIN');
INSERT IGNORE INTO `script_texts` VALUES ('17852', '-1534011', 'Make ready for another wave! LOK-TAR OGAR!', null, null, null, null, null, null, null, 'Готовьтесь к новой атаке! ЛОК-ТАР ОГАР!', '11032', '1', '0', '0', 'thrall hyjal INCOMING');
INSERT IGNORE INTO `script_texts` VALUES ('17852', '-1534010', 'Bring the fight to me and pay with your lives!', null, null, null, null, null, null, null, 'Нападайте, но вы заплатите за это своими жизнями!', '11061', '1', '0', '0', 'thrall hyjal ATTACKED 2');
INSERT IGNORE INTO `script_texts` VALUES ('17852', '-1534009', 'I will lie down for no one!', null, null, null, null, null, null, null, 'Я не склонюсь не перед кем!', '11031', '1', '0', '0', 'thrall hyjal ATTACKED 1');
INSERT IGNORE INTO `script_texts` VALUES ('17772', '-1534008', 'I did... my best.', null, null, null, null, null, null, null, 'Я сделала... что могла.', '11010', '1', '0', '0', 'jaina hyjal DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17772', '-1534007', 'We have won valuable time. Now we must pull back!', null, null, null, null, null, null, null, 'Мы выиграли время. Теперь отходим!', '11011', '1', '0', '0', 'jaina hyjal SUCCESS');
INSERT IGNORE INTO `script_texts` VALUES ('17772', '-1534006', 'We are lost. Fall back!', null, null, null, null, null, null, null, 'Всё пропало. Отступаем!', '11009', '1', '0', '0', 'jaina hyjal FAILURE');
INSERT IGNORE INTO `script_texts` VALUES ('17772', '-1534005', 'We must hold strong!', null, null, null, null, null, null, null, 'Держать позиции!', '11051', '1', '0', '0', 'jaina hyjal RALLY 2');
INSERT IGNORE INTO `script_texts` VALUES ('17772', '-1534004', 'Hold them back as long as possible.', null, null, null, null, null, null, null, 'Сдерживайте их натиск как можно дольше.', '11050', '1', '0', '0', 'jaina hyjal RALLY 1');
INSERT IGNORE INTO `script_texts` VALUES ('17772', '-1534003', 'Don\'t give up! We must prevail!', null, null, null, null, null, null, null, 'Держитесь! Мы должны победить!', '11006', '1', '0', '0', 'jaina hyjal BEGIN');
INSERT IGNORE INTO `script_texts` VALUES ('17772', '-1534002', 'Stay alert! Another wave approaches.', null, null, null, null, null, null, null, 'Не расслабляться! Они снова идут!', '11008', '1', '0', '0', 'jaina hyjal INCOMING');
INSERT IGNORE INTO `script_texts` VALUES ('17772', '-1534001', 'They\'ve broken through!', null, null, null, null, null, null, null, 'Они прорвались!', '11049', '1', '0', '0', 'jaina hyjal ATTACKED 2');
INSERT IGNORE INTO `script_texts` VALUES ('17772', '-1534000', 'I\'m in jeopardy, help me if you can!', null, null, null, null, null, null, null, 'Я в опасности, помогите!', '11007', '1', '0', '0', 'jaina hyjal ATTACKED 1');
INSERT IGNORE INTO `script_texts` VALUES ('15936', '-1533118', 'Noo... o...', null, null, null, null, null, null, null, 'Нее... т...', '8828', '1', '0', '0', 'heigan SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('15936', '-1533117', 'Hungry worms will feast on your rotten flesh!', null, null, null, null, null, null, null, 'Голодные черви будут пировать в ваших гниющих трупах!', '8834', '1', '0', '0', 'heigan SAY_TAUNT5');
INSERT IGNORE INTO `script_texts` VALUES ('15936', '-1533116', 'The end is upon you.', null, null, null, null, null, null, null, 'Вам конец.', '8833', '1', '0', '0', 'heigan SAY_TAUNT4');
INSERT IGNORE INTO `script_texts` VALUES ('15936', '-1533115', 'Soon... the world will tremble!', null, null, null, null, null, null, null, 'Скоро мир содрогнется!', '8832', '1', '0', '0', 'heigan SAY_TAUNT3');
INSERT IGNORE INTO `script_texts` VALUES ('15936', '-1533114', 'I see endless suffering, I see torment, I see rage. I see... everything!', null, null, null, null, null, null, null, 'Я вижу бесконечное страдание, я вижу муки, я вижу ярость, я вижу... все...', '8831', '1', '0', '0', 'heigan SAY_TAUNT2');
INSERT IGNORE INTO `script_texts` VALUES ('15936', '-1533113', 'The races of the world will perish. It is only a matter of time.', null, null, null, null, null, null, null, 'Все народы этого мира исчезнут без следа, это лишь вопрос времени!', '8830', '1', '0', '0', 'heigan SAY_TAUNT1');
INSERT IGNORE INTO `script_texts` VALUES ('15936', '-1533110', 'I see you!', null, null, null, null, null, null, null, 'Я вижу вас!', '8826', '1', '0', '0', 'heigan SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('15936', '-1533111', 'You...are next!', null, null, null, null, null, null, null, 'Пришло ваше время!', '8827', '1', '0', '0', 'heigan SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('15936', '-1533112', 'Close your eyes... sleep!', null, null, null, null, null, null, null, 'Закрой глаза... усни!', '8829', '1', '0', '0', 'heigan SAY_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('15936', '-1533109', 'You are mine now!', null, null, null, null, null, null, null, 'Теперь вы принадлежите мне!', '8825', '1', '0', '0', 'heigan SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533108', 'Fools, you have spread your powers too thin. Be free, my minions!', null, null, null, null, null, null, null, 'Глупцы, вы только зря растратили силы. Слуги мои, освободитесь!', '14482', '1', '0', '0', 'kelthuzad SAY_SPECIAL2_DISPELL');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533107', 'Enough! I grow tired of these distractions! ', null, null, null, null, null, null, null, 'Довольно! Вся эта суета меня раздражает!', '14483', '1', '0', '0', 'kelthuzad SAY_SPECIAL3_MANA_DET');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533106', 'Your petty magics are no challenge to the might of the Scourge! ', null, null, null, null, null, null, null, 'Ваша жалкая магия, ничто перед мощью плети!', '14481', '1', '0', '0', 'kelthuzad SAY_SPECIAL1_MANA_DET');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533105', 'Minions, servants, soldiers of the cold dark, obey the call of Kel\'Thuzad!', null, null, null, null, null, null, null, 'Соратники, слуги, солдаты холодной тьмы, повинуйтесь зову Кел\'Тузада!', '14471', '1', '0', '0', 'kelthuzad SAY_SUMMON_MINIONS');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533104', 'Very well... warriors of the frozen wastes, rise up! I command you to fight, kill, and die for your master. Let none survive...', null, null, null, null, null, null, null, 'Очень хорошо... воины замороженных отходов, поднимитесь! Я приказываю, чтобы вы боролись, убивали и умерли за вашего хозяина. Не позвольте ни одному выжить...', '0', '1', '0', '0', 'kelthuzad SAY_ANSWER_REQUEST');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533103', 'Master! I require aid! ', null, null, null, null, null, null, null, 'Господин! Мне нужна помощь!', '14470', '1', '0', '0', 'kelthuzad SAY_REQUEST_AID');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533102', 'I will freeze the blood in your veins!', null, null, null, null, null, null, null, 'Я заморожу кровь в ваших жилах!', '14474', '1', '0', '0', 'kelthuzad SAY_FROST_BLAST');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533101', 'There will be no escape!', null, null, null, null, null, null, null, 'Тебе не уйти!', '14473', '1', '0', '0', 'kelthuzad SAY_CHAIN2');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533100', 'Your soul, is bound to me now!', null, null, null, null, null, null, null, 'Теперь твоя душа связана с моей!', '14472', '1', '0', '0', 'kelthuzad SAY_CHAIN1');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533099', 'AAAAGHHH!... Do not rejoice... your victory is a hollow one... for I shall return with powers beyond your imagining!', null, null, null, null, null, null, null, 'АААААГГГГ!... Не стоит радоваться... Ваша победа бессмысленна, ибо я вернусь в невообразимой мощи!', '14480', '1', '0', '0', 'kelthuzad SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533098', '<Kel\'Thuzad cackles maniacally!>', null, null, null, null, null, null, null, '<Kel\'Thuzad cackles maniacally!>', '14479', '1', '0', '0', 'kelthuzad SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533097', 'The dark void awaits you!', null, null, null, null, null, null, null, 'Тебя ждет тьма и пустота!', '14478', '1', '0', '0', 'kelthuzad SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533096', 'The end is upon you!', null, null, null, null, null, null, null, 'Трепещите настал час вашей гибели!', '14477', '1', '0', '0', 'kelthuzad SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533095', 'Scream your dying breath!', null, null, null, null, null, null, null, 'Кричите! Кричите изо всех сил!', '14476', '1', '0', '0', 'kelthuzad SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('15990', '-1533094', 'Pray for mercy!', null, null, null, null, null, null, null, 'Молите о пощаде!', '14475', '1', '0', '0', 'kelthuzad SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533093', 'You have no idea what horrors lie ahead. You have seen nothing! The frozen heart of Naxxramas awaits you!', null, null, null, null, null, null, null, 'Вы и представить себе не можете какие кошмары ждут вас впереди. Вы ещё ничего не видели, вас ожидает застывшее серцде Наксрамаса!', '14466', '1', '0', '0', 'kelthuzad SAY_TAUNT4');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533092', 'I grow tired of these games. Proceed, and I will banish your souls to oblivion!', null, null, null, null, null, null, null, 'Я устал от этих игр. Ещё немного и я отправлю Ваши души во мрак забвения!', '14465', '1', '0', '0', 'kelthuzad SAY_TAUNT3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533091', 'Fools, you think yourselves triumphant? You have only taken one step closer to the abyss! ', null, null, null, null, null, null, null, 'Глупцы, наслаждаетесь своим триумфом? Вы просто сделали ещё один шаг в сторону бездны!', '14464', '1', '0', '0', 'kelthuzad SAY_TAUNT2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533090', 'Who dares violate the sanctity of my domain? Be warned, all who trespass here are doomed.', null, null, null, null, null, null, null, 'Кто посмел вторгнуться в мои владения? Всякий, явившийся без приглашения - обречён!', '14463', '1', '0', '0', 'kelthuzad SAY_TAUNT1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533089', 'No!!! A curse upon you, interlopers! The armies of the Lich King will hunt you down. You will not escape your fate...', null, null, null, null, null, null, null, 'Нет, будьте вы прокляты незваные гости. Армии Короля Лича поймают вас, вы не сбежите от своей погибели.', '14484', '1', '0', '0', 'kelthuzad SAY_CAT_DIED');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533088', 'Yes, master!', null, null, null, null, null, null, null, 'Как прикажете, повелитель!', '14469', '1', '0', '0', 'kelthuzad SAY_SAPP_DIALOG5');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533087', 'Your security measures have failed! See to this interruption immediately!', null, null, null, null, null, null, null, 'Твоя защита оказалась ненадежной, немедленно займись нашими гостями.', '8882', '1', '0', '0', 'kelthuzad SAY_SAPP_DIALOG4_LICH');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533086', 'I see no complications... Wait... What is this?', null, null, null, null, null, null, null, 'Да, господин, близится час их полного уничтожения... Что такое?', '14468', '1', '0', '0', 'kelthuzad SAY_SAPP_DIALOG3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533085', 'It is good that you serve me so faithfully. Soon, all will serve the Lich King and in the end, you shall be rewarded...so long as you do not falter.', null, null, null, null, null, null, null, 'Ты верно служил мне много лет, скоро весь мир окажется под властью Короля Лича, и в конце концов ты получишь награду, если не подведешь и впредь.', '8881', '1', '0', '0', 'kelthuzad SAY_SAPP_DIALOG2_LICH');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533084', 'Our preparations continue as planned, master.', null, null, null, null, null, null, null, 'Ваши силы готовы нанести удар по врагу, мой повелитель.', '14467', '1', '0', '0', 'kelthuzad SAY_SAPP_DIALOG1');
INSERT IGNORE INTO `script_texts` VALUES ('15989', '-1533082', 'takes in a deep breath...', null, null, null, null, null, null, null, 'глубоко вздыхает...', '0', '2', '0', '0', 'sapphiron EMOTE_BREATH');
INSERT IGNORE INTO `script_texts` VALUES ('15989', '-1533083', '%s enrages!', null, null, null, null, null, null, null, 'бесите!', '0', '2', '0', '0', 'sapphiron EMOTE_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('15954', '-1533080', 'Breathe no more!', null, null, null, null, null, null, null, 'Этот вздох был для тебя последним!', '8850', '1', '0', '0', 'noth SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('15954', '-1533081', 'I will serve the master... in... death!', null, null, null, null, null, null, null, 'Я буду служить Господину... даже.. после смерти!', '8848', '1', '0', '0', 'noth SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('15954', '-1533079', 'My task is done!', null, null, null, null, null, null, null, 'Дело сделано!', '8849', '1', '0', '0', 'noth SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('15954', '-1533078', 'Rise, my soldiers! Rise and fight once more!', null, null, null, null, null, null, null, 'Встаньте, мои воины! Встаньте и сражайтесь вновь!', '8851', '1', '0', '0', 'noth SAY_SUMMON');
INSERT IGNORE INTO `script_texts` VALUES ('15954', '-1533076', 'Your life is forfeit!', null, null, null, null, null, null, null, 'Прощайся с жизнью!', '8846', '1', '0', '0', 'noth SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('15954', '-1533077', 'Die, trespasser!', null, null, null, null, null, null, null, 'Смерть, чужакам!', '8847', '1', '0', '0', 'noth SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('15954', '-1533075', 'Glory to the master!', null, null, null, null, null, null, null, 'Слава Господину!', '8845', '1', '0', '0', 'noth SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533074', 'Death... will not stop me...', null, null, null, null, null, null, null, 'Смерть... меня не остановит...', '14580', '1', '0', '0', 'rivendare_naxx SAY_RIVE_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533073', 'Life is meaningless. It is in death that we are truly tested.', null, null, null, null, null, null, null, 'Жизнь бессмысленна, лишь смерть покажет чего мы стоим.', '14579', '1', '0', '0', 'rivendare_naxx SAY_RIVE_TAUNT3');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533072', 'Conserve your anger! Harness your rage! You will all have outlets for your frustration soon enough.', null, null, null, null, null, null, null, 'Не растачайте свой гнев попусту, взращивайте ярость, скоро вы сможете дать им выход!', '14578', '1', '0', '0', 'rivendare_naxx SAY_RIVE_TAUNT2');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533071', 'Enough prattling. Let them come! We shall grind their bones to dust.', null, null, null, null, null, null, null, 'Хватит болтать, готовьтесь к бою. Мы сотрем их в порошок!', '14577', '1', '0', '0', 'rivendare_naxx SAY_RIVE_TAUNT1');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533070', 'Bow to the might of the scourge!', null, null, null, null, null, null, null, 'Трепещите перед мощью плети!', '14576', '1', '0', '0', 'rivendare_naxx SAY_RIVE_SPECIAL');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533069', 'The master\'s will is done.', null, null, null, null, null, null, null, 'Воля господина исполнена.', '14575', '1', '0', '0', 'rivendare_naxx SAY_RIVE_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533068', 'You will find no peace in death.', null, null, null, null, null, null, null, 'И даже смерть не даст тебе покоя.', '14574', '1', '0', '0', 'rivendare_naxx SAY_RIVE_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533067', 'Be still!', null, null, null, null, null, null, null, 'Стоять!', '14573', '1', '0', '0', 'rivendare_naxx SAY_RIVE_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533066', 'None shall pass!', null, null, null, null, null, null, null, 'Никто не пройдет.', '14572', '1', '0', '0', 'rivendare_naxx SAY_RIVE_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533065', 'You seek death?', null, null, null, null, null, null, null, 'Ищите смерти?', '14571', '1', '0', '0', 'rivendare_naxx SAY_RIVE_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533064', 'It is... as it should be.', null, null, null, null, null, null, null, 'Все так... как должно быть.', '8914', '1', '0', '0', 'zeliek SAY_ZELI_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533062', 'I- I have no choice but to obey!', null, null, null, null, null, null, null, 'У меня нет выбора, я должен повиноваться!', '8916', '1', '0', '0', 'zeliek SAY_ZELI_SPECIAL');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533063', 'Forgive me!', null, null, null, null, null, null, null, 'Прости меня!', '8915', '1', '0', '0', 'zeliek SAY_ZELI_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533061', 'Do not continue! Turn back while there\'s still time!', null, null, null, null, null, null, null, 'Остановитесь, уходите прочь, пока ещё есть время!', '8919', '1', '0', '0', 'zeliek SAY_ZELI_TAUNT3');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533058', 'Flee, before it\'s too late!', null, null, null, null, null, null, null, 'Бегите, пока не поздно!', '8913', '1', '0', '0', 'zeliek SAY_ZELI_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533059', 'Invaders, cease this foolish venture at once! Turn away while you still can!', null, null, null, null, null, null, null, 'Чужаки, откажитесь от этой безрассудной авантюры! Бегите, пока не поздно!', '8917', '1', '0', '0', 'zeliek SAY_ZELI_TAUNT1');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533060', 'Perhaps they will come to their senses, and run away as fast as they can!', null, null, null, null, null, null, null, 'Может быть они облагоразумятся, и успеют скрыться!', '8918', '1', '0', '0', 'zeliek SAY_ZELI_TAUNT2');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533056', 'Next time, bring more friends!', null, null, null, null, null, null, null, 'В следующий раз приводи больше друзей!', '8901', '1', '0', '0', 'korthazz SAY_KORT_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533057', 'What a bloody waste this is!', null, null, null, null, null, null, null, 'Всё это было зря!', '8900', '1', '0', '0', 'korthazz SAY_KORT_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533055', 'I like my meat extra crispy!', null, null, null, null, null, null, null, 'Люблю мясо с хрустящей корочкой!', '8902', '1', '0', '0', 'korthazz SAY_KORT_SPECIAl');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533054', 'I\'m gonna enjoy killin\' these slack-jawed daffodils!', null, null, null, null, null, null, null, 'С удовольствием прикончу этих вертлявых выродков!', '8905', '1', '0', '0', 'korthazz SAY_KORT_TAUNT3');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533053', 'I heard about enough of yer sniveling. Shut yer fly trap \'afore I shut it for ye!', null, null, null, null, null, null, null, 'Хорош скулить, заткнитесь. Пока я не разозлился!', '8904', '1', '0', '0', 'korthazz SAY_KORT_TAUNT2');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533052', 'To arms, ye roustabouts! We\'ve got company!', null, null, null, null, null, null, null, 'К оружию, олухи. У нас гости!', '8903', '1', '0', '0', 'korthazz SAY_KORT_TAUNT1');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533051', 'Come out and fight, ye wee ninny!', null, null, null, null, null, null, null, 'Выходите и деритесь, ничтожества!', '8899', '1', '0', '0', 'korthazz SAY_KORT_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533050', 'Tou... che!', null, null, null, null, null, null, null, 'Кто... же!', '8893', '1', '0', '0', 'blaumeux SAY_BLAU_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533048', 'Your life is mine!', null, null, null, null, null, null, null, 'Твоя жизнь принадлежит мне!', '8895', '1', '0', '0', 'blaumeux SAY_BLAU_SPECIAL');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533049', 'Who\'s next?', null, null, null, null, null, null, null, 'Кто следующий?', '8894', '1', '0', '0', 'blaumeux SAY_BLAU_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533047', 'The first kill goes to me! Anyone care to wager?', null, null, null, null, null, null, null, 'Первая жертва будет на моём счету! Кто-нибудь хочет пари?', '8898', '1', '0', '0', 'blaumeux SAY_BLAU_TAUNT3');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533046', 'I do hope they stay alive long enough for me to... introduce myself.', null, null, null, null, null, null, null, 'Надеюсь они проживут достаточно долго, чтобы я успела представиться.', '8897', '1', '0', '0', 'blaumeux SAY_BLAU_TAUNT2');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533044', 'Defend youself!', null, null, null, null, null, null, null, 'Защищайтесь!', '8892', '1', '0', '0', 'blaumeux SAY_BLAU_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('16063', '-1533045', 'Come, Zeliek, do not drive them out. Not before we\'ve had our fun.', null, null, null, null, null, null, null, 'Ну что ж Зелик, не прогоняй их. Сперва мы с ними позабавимся.', '8896', '1', '0', '0', 'blaumeux SAY_BLAU_TAUNT1');
INSERT IGNORE INTO `script_texts` VALUES ('16060', '-1533043', 'I have waited long enough! Now, you face the harvester of souls!', null, null, null, null, null, null, null, 'Я ждал слишком долго! Сейчас Вы предстанете перед ликом Жнеца Душ!', '8808', '1', '0', '0', 'gothik SAY_TELEPORT');
INSERT IGNORE INTO `script_texts` VALUES ('16060', '-1533041', 'Death is the only escape.', null, null, null, null, null, null, null, 'Смерть, единственный выход.', '8806', '1', '0', '0', 'gothik SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('16060', '-1533042', 'I... am... undone!', null, null, null, null, null, null, null, 'Меня... больше... нет!', '8805', '1', '0', '0', 'gothik SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('15928', '-1533035', 'Thank... you...', null, null, null, null, null, null, null, 'Спаси... бо...', '8870', '1', '0', '0', 'thaddius SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533036', 'Pleeease!', null, null, null, null, null, null, null, 'Освободите меня!', '8873', '1', '0', '0', 'thaddius SAY_SCREAM1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533037', 'Stop, make it stop!', null, null, null, null, null, null, null, 'Стойте, умоляю, стойте!', '8874', '1', '0', '0', 'thaddius SAY_SCREAM2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533038', 'Help me! Save me!', null, null, null, null, null, null, null, 'Помогите! Спасите!', '8875', '1', '0', '0', 'thaddius SAY_SCREAM3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533039', 'Please, nooo!', null, null, null, null, null, null, null, 'Умоляю, неееет!', '8876', '1', '0', '0', 'thaddius SAY_SCREAM4');
INSERT IGNORE INTO `script_texts` VALUES ('16060', '-1533040', 'Foolishly you have sought your own demise. Brazenly you have disregarded powers beyond your understanding. You have fought hard to invade the realm of the harvester. Now there is only one way out - to walk the lonely path of the damned.', null, null, null, null, null, null, null, 'Глупцы, вы сами нашли свою погибель! Вам никогда не понять, сколь велика сила, потревоженная вами! Вы сражались отчаянно, чтобы проникнуть в царство Жнеца! Теперь перед вами лежит лишь один путь, путь проклятых...', '8807', '1', '0', '0', 'gothik SAY_SPEECH');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533034', 'Now YOU feel pain!', null, null, null, null, null, null, null, 'Познайте же боль!', '8871', '1', '0', '0', 'thaddius SAY_ELECT');
INSERT IGNORE INTO `script_texts` VALUES ('15928', '-1533033', 'You die now!', null, null, null, null, null, null, null, 'Теперь умри!', '8877', '1', '0', '0', 'thaddius SAY_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('15928', '-1533031', 'EAT YOUR BONES!', null, null, null, null, null, null, null, 'Я СОЖРУ ВАШИ КОСТИ!', '8868', '1', '0', '0', 'thaddius SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('15928', '-1533032', 'BREAK YOU!', null, null, null, null, null, null, null, 'РАСТЕРЗАЮ!', '8869', '1', '0', '0', 'thaddius SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('15928', '-1533030', 'KILL!', null, null, null, null, null, null, null, 'УБЬЮ!', '8867', '1', '0', '0', 'thaddius SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533029', 'You are too late... I... must... OBEY!', null, null, null, null, null, null, null, 'Слишком поздно... я... должен... ПОВИНОВАТЬСЯ!', '8872', '1', '0', '0', 'thaddius SAY_GREET');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533028', 'No... more... Feugen...', null, null, null, null, null, null, null, 'Фойгена... больше... нет...', '8803', '1', '0', '0', 'feugen SAY_FEUG_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533027', 'Feugen make master happy!', null, null, null, null, null, null, null, 'Фойген радует господина!', '8804', '1', '0', '0', 'feugen SAY_FEUG_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533024', 'Stalagg kill!', null, null, null, null, null, null, null, 'Столог убивать!', '8866', '1', '0', '0', 'stalagg SAY_STAL_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533025', 'Master save me...', null, null, null, null, null, null, null, 'Господин, спаси меня...', '8865', '1', '0', '0', 'stalagg SAY_STAL_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533026', 'Feed you to master!', null, null, null, null, null, null, null, 'Я скормлю вас господину!', '8802', '1', '0', '0', 'feugen SAY_FEUG_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1533023', 'Stalagg crush you!', null, null, null, null, null, null, null, 'Столог сокрушит Вас!', '8864', '1', '0', '0', 'stalagg SAY_STAL_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('16028', '-1533022', '%s becomes enraged!', null, null, null, null, null, null, null, 'приходит в ярость!', '0', '2', '0', '0', 'patchwerk EMOTE_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('16028', '-1533020', 'What happened to... Patch...', null, null, null, null, null, null, null, 'Что случилось с... Лоску... у...', '8911', '1', '0', '0', 'patchwerk SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('16028', '-1533021', 'goes into a berserker rage!', null, null, null, null, null, null, null, 'входит в гнев берсеркера!', '0', '2', '0', '0', 'patchwerk EMOTE_BERSERK');
INSERT IGNORE INTO `script_texts` VALUES ('16028', '-1533019', 'No more play?', null, null, null, null, null, null, null, 'Больше не хочешь играть?', '8912', '1', '0', '0', 'patchwerk SAY_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('16028', '-1533017', 'Patchwerk want to play!', null, null, null, null, null, null, null, 'Лоскутик хочет поиграть!', '8909', '1', '0', '0', 'patchwerk SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('16028', '-1533018', 'Kel\'Thuzad make Patchwerk his Avatar of War!', null, null, null, null, null, null, null, 'Кел\'Тузад объявил Лоскутика воплощением войны!', '8910', '1', '0', '0', 'patchwerk SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('15953', '-1533014', 'You have failed!', null, null, null, null, null, null, null, 'Тебе не повезло!', '8800', '1', '0', '0', 'faerlina SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('15953', '-1533015', 'Pathetic wretch!', null, null, null, null, null, null, null, 'Жалкое ничтожество!', '8801', '1', '0', '0', 'faerlina SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('15953', '-1533016', 'The master... will avenge me!', null, null, null, null, null, null, null, 'Господин... отомстит за меня!', '8798', '1', '0', '0', 'faerlina SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('15953', '-1533013', 'Run while you still can!', null, null, null, null, null, null, null, 'Бегите, пока ещё можете!', '8797', '1', '0', '0', 'faerlina SAY_AGGRO4');
INSERT IGNORE INTO `script_texts` VALUES ('15953', '-1533012', 'Kneel before me, worm!', null, null, null, null, null, null, null, 'Склонитесь пере до мной, черви!', '8796', '1', '0', '0', 'faerlina SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('15953', '-1533010', 'Slay them in the master\'s name!', null, null, null, null, null, null, null, 'Убейте их во имя господина!', '8794', '1', '0', '0', 'faerlina SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('15953', '-1533011', 'You cannot hide from me!', null, null, null, null, null, null, null, 'Вам не скрыться от меня!', '8795', '1', '0', '0', 'faerlina SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('15953', '-1533009', 'Your old lives, your mortal desires, mean nothing. You are acolytes of the master now, and you will serve the cause without question! The greatest glory is to die in the master\'s service!', null, null, null, null, null, null, null, 'Ваши дряхлые жизни, ваши бренные желания, ничего не значат. Теперь вы адепты Господина, вы будете служить нашему делу не спрашивая ни о чём! Умереть за господина, величайшая доблесть!', '8799', '1', '0', '0', 'faerlina SAY_GREET');
INSERT IGNORE INTO `script_texts` VALUES ('15956', '-1533008', 'Shh... it will all be over soon.', null, null, null, null, null, null, null, 'Шшш... скоро всё будет кончено.', '8789', '1', '0', '0', 'anubrekhan SAY_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('15956', '-1533007', 'Closer now... tasty morsels. I\'ve been too long without food. Without blood to drink.', null, null, null, null, null, null, null, 'Подойдите ближе... лакомые кусочки. Я слишком долго мечтал о еде и крови.', '8793', '1', '0', '0', 'anubrekhan SAY_TAUNT4');
INSERT IGNORE INTO `script_texts` VALUES ('15956', '-1533006', 'Which one shall I eat first? So difficult to choose... the all smell so delicious.', null, null, null, null, null, null, null, 'Кого мне съесть первым? Сложный выбор... все они пахнут так вкусно...', '8792', '1', '0', '0', 'anubrekhan SAY_TAUNT3');
INSERT IGNORE INTO `script_texts` VALUES ('15956', '-1533005', 'Where to go? What to do? So many choices that all end in pain, end in death.', null, null, null, null, null, null, null, 'Куда идти? Что делать? Выбирайте, но все пути ведут к боли и смерти!', '8791', '1', '0', '0', 'anubrekhan SAY_TAUNT2');
INSERT IGNORE INTO `script_texts` VALUES ('15956', '-1533004', 'I hear little hearts beating. Yesss... beating faster now. Soon the beating will stop.', null, null, null, null, null, null, null, 'Я слышу биение маленьких сердец. Дааа... Теперь они бьются быстрее, но скоро они остановятся.', '8790', '1', '0', '0', 'anubrekhan SAY_TAUNT1');
INSERT IGNORE INTO `script_texts` VALUES ('15956', '-1533003', 'Yes, Run! It makes the blood pump faster!', null, null, null, null, null, null, null, 'Бегите, бегите, я люблю горячую кровь!', '8787', '1', '0', '0', 'anubrekhan SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('15956', '-1533000', 'Ahh... welcome to my parlor.', null, null, null, null, null, null, null, 'Ооо... добро пожаловать в мою гостиную.', '8788', '1', '0', '0', 'anubrekhan SAY_GREET');
INSERT IGNORE INTO `script_texts` VALUES ('15956', '-1533001', 'Just a little taste...', null, null, null, null, null, null, null, 'Посмотрим, какие вы на вкус...', '8785', '1', '0', '0', 'anubrekhan SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('15956', '-1533002', 'There is no way out.', null, null, null, null, null, null, null, 'Вам от сюда не выбраться.', '8786', '1', '0', '0', 'anubrekhan SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('16812', '-1532114', 'But don\'t take it from me, see for yourself what tragedy lies ahead when the paths of star-crossed lovers meet. And now...on with the show!', null, null, null, null, null, null, null, 'Но что я вам рассказываю, вы сами увидите какая трагедия ждет возлюбленных, чьи пути пересеклись по воле звезд. Итак.. смотрите!', '9343', '1', '0', '0', 'barnes RAJ4');
INSERT IGNORE INTO `script_texts` VALUES ('16812', '-1532112', 'Tonight, we explore a tale of forbidden love!', null, null, null, null, null, null, null, 'Сегодня мы увидим историю любви!', '9341', '1', '0', '0', 'barnes RAJ2');
INSERT IGNORE INTO `script_texts` VALUES ('16812', '-1532113', 'But beware, for not all love stories end happily, as you may find out. Sometimes, love pricks like a thorn.', null, null, null, null, null, null, null, 'Но помните, не у всех любовных историй счастливый конец. Порой любовь разит подобно молнии.', '9342', '1', '0', '0', 'barnes RAJ3');
INSERT IGNORE INTO `script_texts` VALUES ('16812', '-1532111', 'Welcome, Ladies and Gentlemen, to this evening\'s presentation!', null, null, null, null, null, null, null, 'Дамы и господа, добро пожаловать на наше вечернее представление!', '9176', '1', '0', '0', 'barnes RAJ1');
INSERT IGNORE INTO `script_texts` VALUES ('16812', '-1532110', 'But don\'t let me pull the wool over your eyes. See for yourself what lies beneath those covers! And now... on with the show!', null, null, null, null, null, null, null, 'Но нет не позволяйте мне затуманить ваш взор. Взгляните же что скрывается за пеляной! Итак... представление начинается!', '9337', '1', '0', '0', 'barnes HOOD4');
INSERT IGNORE INTO `script_texts` VALUES ('16812', '-1532109', 'Take for instance, this quiet, elderly woman, waiting for a visit from her granddaughter. Surely there is nothing to fear from this sweet, grey-haired, old lady.', null, null, null, null, null, null, null, 'Представим на миг, что эта тихая, старушка, ожидает в гости свою внучку. Разве вас испугает вид этой милой вседовласой дамы?', '9336', '1', '0', '0', 'barnes HOOD3');
INSERT IGNORE INTO `script_texts` VALUES ('16812', '-1532108', 'Tonight, things are not what they seem. For tonight, your eyes may not be trusted', null, null, null, null, null, null, null, 'Не верьте глазам своим, ибо все в этом зале не то, чем кажется на первый взгляд.', '9335', '1', '0', '0', 'barnes HOOD2');
INSERT IGNORE INTO `script_texts` VALUES ('16812', '-1532107', 'Good evening, Ladies and Gentlemen! Welcome to this evening\'s presentation!', null, null, null, null, null, null, null, 'Добрый вечер, Дамы и Господа! Добро пожаловать на вечернее представление!', '9175', '1', '0', '0', 'barnes HOOD1');
INSERT IGNORE INTO `script_texts` VALUES ('16812', '-1532106', 'Will she survive? Will she prevail? Only time will tell. And now ... on with the show!', null, null, null, null, null, null, null, 'Сумеет ли она выжить? Вернеться ли она домой. Время покажет. И так... начинаем представление!', '9340', '1', '0', '0', 'barnes OZ4');
INSERT IGNORE INTO `script_texts` VALUES ('16812', '-1532105', 'But she is pursued... by a wicked malevolent crone!', null, null, null, null, null, null, null, 'Но ее преследует... злобная омерзительная старая карга!', '9339', '1', '0', '0', 'barnes OZ3');
INSERT IGNORE INTO `script_texts` VALUES ('16812', '-1532104', 'Tonight we plumb the depths of the human soul as we join a lost, lonely girl trying desperately -- with the help of her loyal companions -- to find her way home!', null, null, null, null, null, null, null, 'Сегодня мы изменим глубины человеческой души вместе с этой потерявшейся маленькой девочкой, которой верные спутники помогают искать путь домой!', '9338', '1', '0', '0', 'barnes OZ2');
INSERT IGNORE INTO `script_texts` VALUES ('16812', '-1532103', 'Welcome Ladies and Gentlemen, to this evening\'s presentation!', null, null, null, null, null, null, null, 'Дамы и господа, добро пожаловать на наше вечернее представление!', '9174', '1', '0', '0', 'barnes OZ1');
INSERT IGNORE INTO `script_texts` VALUES ('15690', '-1532102', 'I refuse to concede defeat. I am a prince of the Eredar! I am...', null, null, null, null, null, null, null, 'Я не признаю поражения. Я эрендарский принц! И я...', '9221', '1', '0', '0', 'malchezaar SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('15690', '-1532101', 'You face not Malchezaar alone, but the legions I command!', null, null, null, null, null, null, null, 'Вы противостоите не только Мальчезару, но и всем подвластным мне легионам!', '9322', '1', '0', '0', 'malchezaar SAY_SUMMON2');
INSERT IGNORE INTO `script_texts` VALUES ('15690', '-1532100', 'All realities, all dimensions are open to me!', null, null, null, null, null, null, null, 'Мне открыты все реальности, все измерения!', '9224', '1', '0', '0', 'malchezaar SAY_SUMMON1');
INSERT IGNORE INTO `script_texts` VALUES ('15690', '-1532099', 'You are, but a plaything, unfit even to amuse.', null, null, null, null, null, null, null, 'Ты просто игрушка, не интересная игрушка.', '9319', '1', '0', '0', 'malchezaar SAY_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('15690', '-1532098', 'Your greed, your foolishness has brought you to this end.', null, null, null, null, null, null, null, 'Жадность и глупость погубили тебя.', '9318', '1', '0', '0', 'malchezaar SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('15690', '-1532097', 'Surely you did not think you could win.', null, null, null, null, null, null, null, 'Смирись и прими свою смерть.', '9222', '1', '0', '0', 'malchezaar SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('15690', '-1532096', 'How can you hope to withstand against such overwhelming power?', null, null, null, null, null, null, null, 'Как вы осмелились бросить вызов столь колосальной мощи?', '9321', '1', '0', '0', 'malchezaar SAY_SPECIAL3');
INSERT IGNORE INTO `script_texts` VALUES ('15690', '-1532095', 'The cerestial forces are mine to manipulate.', null, null, null, null, null, null, null, 'Мне подвластны небесные силы.', '9320', '1', '0', '0', 'malchezaar SAY_SPECIAL2');
INSERT IGNORE INTO `script_texts` VALUES ('15690', '-1532094', 'Who knows what secrets hide in the dark.', null, null, null, null, null, null, null, 'Кто знает какие тайны скрыты во мраке. Ах да, я знаю.', '9223', '1', '0', '0', 'malchezaar SAY_SPECIAL1');
INSERT IGNORE INTO `script_texts` VALUES ('15690', '-1532093', 'I see the subtlety of conception is beyond primitives such as you.', null, null, null, null, null, null, null, 'Я вижу тонкости замысла недоступные таким глупцам как вы.', '9317', '1', '0', '0', 'malchezaar SAY_AXE_TOSS2');
INSERT IGNORE INTO `script_texts` VALUES ('15690', '-1532091', 'Madness has brought you here to me. I shall be your undoing!', null, null, null, null, null, null, null, 'Безумие привело вас сюда. Я стану вашей погибелью!', '9218', '1', '0', '0', 'malchezaar SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('15690', '-1532092', 'Simple fools! Time is the fire in which you\'ll burn!', null, null, null, null, null, null, null, 'Глупцы! Время это огонь сжигающий вас!', '9220', '1', '0', '0', 'malchezaar SAY_AXE_TOSS1');
INSERT IGNORE INTO `script_texts` VALUES ('15689', '-1532089', 'cries out in withdrawal, opening gates to the warp.', null, null, null, null, null, null, null, 'удаленно выкрикивает, открывая ворота в прыгуаны.', '0', '3', '0', '0', 'netherspite EMOTE_PHASE_PORTAL');
INSERT IGNORE INTO `script_texts` VALUES ('15689', '-1532090', 'goes into a nether-fed rage!', null, null, null, null, null, null, null, 'становится менее злым!', '0', '3', '0', '0', 'netherspite EMOTE_PHASE_BANISH');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532088', 'Where did you get that?! Did HE send you?!', null, null, null, null, null, null, null, 'От куда у вас это? Это ОН Вас послал?!', '9249', '1', '0', '0', 'aran SAY_ATIESH');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532087', 'At last... The nightmare is.. over...', null, null, null, null, null, null, null, 'Наконеч-то... кошмар... закончился...', '9244', '1', '0', '0', 'aran SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532086', 'You\'ve wasted enough of my time. Let these games be finished!', null, null, null, null, null, null, null, 'Вы отняли у меня слишком много времени. Покончим с этим.', '9247', '1', '0', '0', 'aran SAY_TIMEOVER');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532084', 'I want this nightmare to be over!', null, null, null, null, null, null, null, 'Этот кошмар, когда-нибудь закончится?', '9250', '1', '0', '0', 'aran SAY_KILL1');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532085', 'Torment me no more!', null, null, null, null, null, null, null, 'Хватит меня мучить!', '9328', '1', '0', '0', 'aran SAY_KILL2');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532082', 'Surely you would not deny an old man a replenishing drink? No, no I thought not.', null, null, null, null, null, null, null, 'Вы ведь не откажите старику в стаканчике бодрящего напитка? Нет, я думаю не откажите!', '9248', '1', '0', '0', 'aran SAY_DRINK');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532083', 'I\'m not finished yet! No, I have a few more tricks up me sleeve.', null, null, null, null, null, null, null, 'Я так просто не сдамся! Нет, у меня есть ещё пара трюков в запасе.', '9251', '1', '0', '0', 'aran SAY_ELEMENTALS');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532081', 'I am not some simple jester! I am Nielas Aran!', null, null, null, null, null, null, null, 'Я ни какой-то жалкий фокусник! Я Нилас Аран!', '9325', '1', '0', '0', 'aran SAY_EXPLOSION2');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532080', 'Yes, yes, my son is quite powerful... but I have powers of my own!', null, null, null, null, null, null, null, 'Да, да, мой сын могуч... но у меня есть своя сила!', '9242', '1', '0', '0', 'aran SAY_EXPLOSION1');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532079', 'Back to the cold dark with you!', null, null, null, null, null, null, null, 'Убирайтесь в холодный мрак!', '9327', '1', '0', '0', 'aran SAY_BLIZZARD2');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532078', 'I\'ll freeze you all!', null, null, null, null, null, null, null, 'Я заморожу вас всех!', '9246', '1', '0', '0', 'aran SAY_BLIZZARD1');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532077', 'Burn you hellish fiends!', null, null, null, null, null, null, null, 'Горите, выродки!', '9326', '1', '0', '0', 'aran SAY_FLAMEWREATH2');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532075', 'Who are you? What do you want? Stay away from me!', null, null, null, null, null, null, null, 'Кто вы? Что вам нужно? Прочь от меня.', '9324', '1', '0', '0', 'aran SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532076', 'I\'ll show you this beaten dog still has some teeth!', null, null, null, null, null, null, null, 'Знайте же, и побитый пес сможет укусить!', '9245', '1', '0', '0', 'aran SAY_FLAMEWREATH1');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532074', 'I\'ll not be tortured again!', null, null, null, null, null, null, null, 'Никто больше не будет меня пытать!', '9323', '1', '0', '0', 'aran SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('16524', '-1532073', 'Please, no more. My son... he\'s gone mad!', null, null, null, null, null, null, null, 'Умоляю, хватит. Мой сын... он сошел с ума!', '9241', '1', '0', '0', 'aran SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('15688', '-1532072', 'Gather, my pets. There is plenty for all.', null, null, null, null, null, null, null, 'Собирайтесь, слуги мои. Здесь на всех хватит.', '9331', '1', '0', '0', 'terestian SAY_SUMMON2');
INSERT IGNORE INTO `script_texts` VALUES ('15688', '-1532071', 'Come, you dwellers in the dark. Rally to my call!', null, null, null, null, null, null, null, 'Придите, живущие во тьме. Явитесь, по моему зову!', '9265', '1', '0', '0', 'terestian SAY_SUMMON1');
INSERT IGNORE INTO `script_texts` VALUES ('15688', '-1532069', 'Please, accept this humble offering, oh great one.', null, null, null, null, null, null, null, 'Великий, прими это скромное приношение.', '9263', '1', '0', '0', 'terestian SAY_SACRIFICE1');
INSERT IGNORE INTO `script_texts` VALUES ('15688', '-1532070', 'Let the sacrifice serve his testament to my fealty.', null, null, null, null, null, null, null, 'Пусть эта жертва станет знаком моей верности.', '9330', '1', '0', '0', 'terestian SAY_SACRIFICE2');
INSERT IGNORE INTO `script_texts` VALUES ('15688', '-1532068', 'Ah, you\'re just in time. The rituals are about to begin.', null, null, null, null, null, null, null, 'А, вы как раз вовремя. Ритуал вот-вот начнется.', '9260', '1', '0', '0', 'terestian SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('15688', '-1532067', 'My life, is yours. Oh great one.', null, null, null, null, null, null, null, 'Умираю, за тебя. О великий.', '9262', '1', '0', '0', 'terestian SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('15688', '-1532066', 'The great one will be pleased.', null, null, null, null, null, null, null, 'Великий будет доволен.', '9329', '1', '0', '0', 'terestian SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('15688', '-1532065', 'Your blood will anoint my circle.', null, null, null, null, null, null, null, 'Твоя кровь окропит мой круг.', '9264', '1', '0', '0', 'terestian SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('15691', '-1532063', 'You are not a guest.', null, null, null, null, null, null, null, 'У вас нет приглашения', '9308', '1', '0', '0', 'curator SAY_KILL2');
INSERT IGNORE INTO `script_texts` VALUES ('15691', '-1532064', 'This Curator is no longer op... er... ation... al.', null, null, null, null, null, null, null, 'Смотритель завершает ро...бо...тууу.', '9184', '1', '0', '0', 'curator SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('15691', '-1532062', 'Do not touch the displays.', null, null, null, null, null, null, null, 'Экспонаты руками не трогать.', '9187', '1', '0', '0', 'curator SAY_KILL1');
INSERT IGNORE INTO `script_texts` VALUES ('15691', '-1532061', 'Failure to comply will result in offensive action.', null, null, null, null, null, null, null, 'В случае неподчинения будут приняты меры.', '9185', '1', '0', '0', 'curator SAY_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('15691', '-1532060', 'Your request cannot be processed.', null, null, null, null, null, null, null, 'Ваш запрос не может быть обработан.', '9186', '1', '0', '0', 'curator SAY_EVOCATE');
INSERT IGNORE INTO `script_texts` VALUES ('15691', '-1532059', 'This curator is equipped for gallery protection.', null, null, null, null, null, null, null, 'Смотритель снабжен всем необходимым, для защиты галереи.', '9309', '1', '0', '0', 'curator SAY_SUMMON2');
INSERT IGNORE INTO `script_texts` VALUES ('15691', '-1532058', 'Gallery rules will be strictly enforced.', null, null, null, null, null, null, null, 'Соблюдайте правила поведения в галереи.', '9188', '1', '0', '0', 'curator SAY_SUMMON1');
INSERT IGNORE INTO `script_texts` VALUES ('15691', '-1532057', 'The Menagerie is for guests only.', null, null, null, null, null, null, null, 'Вход только по приглашениям.', '9183', '1', '0', '0', 'curator SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17533', '-1532056', 'How well my comfort is revived by this!', null, null, null, null, null, null, null, 'О, как опять душою ожил я!', '9238', '1', '0', '0', 'romulo SAY_ROMULO_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('17533', '-1532055', 'Thou detestable maw, thou womb of death; I enforce thy rotten jaws to open!', null, null, null, null, null, null, null, '\"Проклятая утроба, чрево смерти; Я пасть твою прогнившую раскрою!\"', '9237', '1', '0', '0', 'romulo SAY_ROMULO_RESURRECT');
INSERT IGNORE INTO `script_texts` VALUES ('17533', '-1532054', 'This day\'s black fate on more days doth depend. This but begins the woe. Others must end.', null, null, null, null, null, null, null, 'О, черный день, он лишь начало бед, придут за ним еще другие вслед!', '9236', '1', '0', '0', 'romulo SAY_ROMULO_ENTER');
INSERT IGNORE INTO `script_texts` VALUES ('17533', '-1532053', 'Thou smilest... upon the stroke that... murders me.', null, null, null, null, null, null, null, 'Ты... с улыбкой смотришь на удар... смертельный.', '9235', '1', '0', '0', 'romulo SAY_ROMULO_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17533', '-1532052', 'Wilt thou provoke me? Then have at thee, boy!', null, null, null, null, null, null, null, 'Ты хочешь драться? Берегись, дитя!', '9233', '1', '0', '0', 'romulo SAY_ROMULO_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17534', '-1532051', 'Parting is such sweet sorrow.', null, null, null, null, null, null, null, 'Прощанье в час разлуки, несет с собою столько сладкой муки!', '9201', '1', '0', '0', 'julianne SAY_JULIANNE_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('17534', '-1532050', 'Come, gentle night; and give me back my Romulo!', null, null, null, null, null, null, null, 'Приди же ласковая ночь, верни мне моего Ромуло!', '9200', '1', '0', '0', 'julianne SAY_JULIANNE_RESURRECT');
INSERT IGNORE INTO `script_texts` VALUES ('17534', '-1532049', 'Where is my Lord? Where is my Romulo? Ohh, happy dagger! This is thy sheath! There rust, and let me die!', null, null, null, null, null, null, null, 'Кинжал, Ромуло, вот твои ножны. Останься в них, и дай мне умереть.', '9310', '1', '0', '0', 'julianne SAY_JULIANNE_DEATH02');
INSERT IGNORE INTO `script_texts` VALUES ('17534', '-1532048', 'Romulo, I come! Oh... this do I drink to thee!', null, null, null, null, null, null, null, 'Ромуло, я иду к тебе! Я выпью этот кубок за тебя!', '9198', '1', '0', '0', 'julianne SAY_JULIANNE_DEATH01');
INSERT IGNORE INTO `script_texts` VALUES ('17534', '-1532047', 'Where is my lord? Where is my Romulo?', null, null, null, null, null, null, null, 'Где? Где супруг мой? Где же мой Ромуло?', '9199', '1', '0', '0', 'julianne SAY_JULIANNE_ENTER');
INSERT IGNORE INTO `script_texts` VALUES ('17534', '-1532046', 'What devil art thou, that dost torment me thus?', null, null, null, null, null, null, null, 'Что ты за дьявол, что меня так мучишь?', '9196', '1', '0', '0', 'julianne SAY_JULIANNE_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17521', '-1532045', 'Run away little girl, run away!', null, null, null, null, null, null, null, 'Беги, малышка, беги!', '9278', '1', '0', '0', 'wolf SAY_WOLF_HOOD');
INSERT IGNORE INTO `script_texts` VALUES ('17521', '-1532044', 'Mmmm... delicious.', null, null, null, null, null, null, null, 'Мммм... вкусно.', '9277', '1', '0', '0', 'wolf SAY_WOLF_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('17521', '-1532043', 'All the better to own you with!', null, null, null, null, null, null, null, 'Кем бы мне тут закусить!', '9276', '1', '0', '0', 'wolf SAY_WOLF_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('18168', '-1532042', 'Fixed you, didn\'t I? <cackles>', null, null, null, null, null, null, null, 'Будешь знать, как мне мешать? <cackles>', '9180', '1', '0', '0', 'crone SAY_CRONE_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('18168', '-1532040', 'It will all be over soon! <cackles>', null, null, null, null, null, null, null, 'Скоро всё закончится! <cackles>', '9307', '1', '0', '0', 'crone SAY_CRONE_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('18168', '-1532041', 'How could you? What a cruel, cruel world!', null, null, null, null, null, null, null, 'Как так можно? Ужасный, жестокий мир!', '9178', '1', '0', '0', 'crone SAY_CRONE_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('18168', '-1532039', 'Woe to each and every one of you my pretties! <cackles>', null, null, null, null, null, null, null, 'Горе вам, всем и каждому мои крошки!', '9179', '1', '0', '0', 'crone SAY_CRONE_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17547', '-1532038', 'begins to rust.', null, null, null, null, null, null, null, 'начинает ржаветь.', '0', '2', '0', '0', 'tinhead EMOTE_RUST');
INSERT IGNORE INTO `script_texts` VALUES ('17547', '-1532037', 'Guess I\'m not so rusty, after all.', null, null, null, null, null, null, null, 'Смотри, не так уж я и заржавел.', '9271', '1', '0', '0', 'tinhead SAY_TINHEAD_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('17547', '-1532036', 'Back to being an old rustbucket.', null, null, null, null, null, null, null, 'Опять я стану старой, ржавой рухлядью.', '9270', '1', '0', '0', 'tinhead SAY_TINHEAD_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17547', '-1532035', 'I could really use a heart. Say, can I have yours?', null, null, null, null, null, null, null, 'Мне очень нужно сердце... Может забрать твое?', '9268', '1', '0', '0', 'tinhead SAY_TINHEAD_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17543', '-1532034', 'I guess I\'m not a failure after all.', null, null, null, null, null, null, null, 'Надо же, у меня получилось.', '9257', '1', '0', '0', 'strawman SAY_STRAWMAN_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('17543', '-1532033', 'Don\'t let them make a mattress... out of me.', null, null, null, null, null, null, null, 'Не дайте им... сделать из меня... матрас.', '9256', '1', '0', '0', 'strawman SAY_STRAWMAN_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17543', '-1532032', 'Now what should I do with you? I simply can\'t make up my mind.', null, null, null, null, null, null, null, 'И что же мне с вами делать? Никак не соображу.', '9254', '1', '0', '0', 'strawman SAY_STRAWMAN_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17546', '-1532031', 'I think I\'m going to go take fourty winks.', null, null, null, null, null, null, null, 'Давай, поспи немного.', '9230', '1', '0', '0', 'roar SAY_ROAR_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('17546', '-1532030', 'You didn\'t have to go and do that.', null, null, null, null, null, null, null, 'Ну вот, пришли, всё испортили.', '9229', '1', '0', '0', 'roar SAY_ROAR_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17546', '-1532029', 'Wanna fight? Huh? Do ya? C\'mon, I\'ll fight you with both claws behind my back!', null, null, null, null, null, null, null, 'Я вас не боюсь! Совсем! Хотите сражаться? Хотите, да? Ну же, я буду драться, даже если мне свяжут лапы за спиной!', '9227', '1', '0', '0', 'roar SAY_ROAR_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17535', '-1532028', 'Oh dear, we simply must find a way home! The old wizard could be our only hope! Strawman, Roar, Tinhead, will you... wait! Oh golly, look! We have visitors!', null, null, null, null, null, null, null, 'Тито, мы просто обязаны найти дорогу домой! Старый волшебник, наша единственная надежда! Пугало, Рычал, Нержавей вы... Ой, к нам кто-то пришел!', '9195', '1', '0', '0', 'dorothee SAY_DOROTHEE_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17535', '-1532027', 'Tito, oh Tito, no!', null, null, null, null, null, null, null, 'Тито, ох Тито, нет!', '9192', '1', '0', '0', 'dorothee SAY_DOROTHEE_TITO_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17535', '-1532026', 'Don\'t let them hurt us, Tito! Oh, you won\'t, will you?', null, null, null, null, null, null, null, 'Они ведь нас не обидят, правда Тито? Я боюсь!', '9191', '1', '0', '0', 'dorothee SAY_DOROTHEE_SUMMON');
INSERT IGNORE INTO `script_texts` VALUES ('17535', '-1532025', 'Oh at last, at last. I can go home.', null, null, null, null, null, null, null, 'Наконец-то, я смогу попасть домой.', '9190', '1', '0', '0', 'dorothee SAY_DOROTHEE_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('16457', '-1532024', 'Death comes. Will your conscience be clear?', null, null, null, null, null, null, null, 'Смерть близиться. Чиста ли ваша совесть?', '9206', '1', '0', '0', 'maiden SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('16457', '-1532022', 'Cast out your corrupt thoughts.', null, null, null, null, null, null, null, 'Отбросьте ваши грязные мысли.', '9313', '1', '0', '0', 'maiden SAY_REPENTANCE1');
INSERT IGNORE INTO `script_texts` VALUES ('16457', '-1532023', 'Your impurity must be cleansed.', null, null, null, null, null, null, null, 'Я выжгу вашу нечестивость.', '9208', '1', '0', '0', 'maiden SAY_REPENTANCE2');
INSERT IGNORE INTO `script_texts` VALUES ('16457', '-1532020', 'This is for the best.', null, null, null, null, null, null, null, 'И это к лучшему.', '9312', '1', '0', '0', 'maiden SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('16457', '-1532021', 'Impure thoughts lead to profane actions.', null, null, null, null, null, null, null, 'Нечистые мысли ведут к нечестивым поступкам.', '9311', '1', '0', '0', 'maiden SAY_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('16457', '-1532018', 'Your behavior will not be tolerated!', null, null, null, null, null, null, null, 'Я не потерплю такого поведения!', '9204', '1', '0', '0', 'maiden SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('16457', '-1532019', 'Ah ah ah...', null, null, null, null, null, null, null, 'Нет нет нет...', '9207', '1', '0', '0', 'maiden SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('15687', '-1532016', 'I\'ve gone and made a mess.', null, null, null, null, null, null, null, 'Какой же тут беспорядок.', '9315', '1', '0', '0', 'moroes SAY_KILL_3');
INSERT IGNORE INTO `script_texts` VALUES ('15687', '-1532017', 'How terribly clumsy of me...', null, null, null, null, null, null, null, 'Как-то неловко вышло...', '9213', '1', '0', '0', 'moroes SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('15687', '-1532015', 'Time... Never enough time.', null, null, null, null, null, null, null, 'Время...Мне вечно не хватает времени.', '9314', '1', '0', '0', 'moroes SAY_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('15687', '-1532014', 'One more for dinner this evening.', null, null, null, null, null, null, null, 'Прекрасно, ещё одно блюдо на ужин.', '9214', '1', '0', '0', 'moroes SAY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('15687', '-1532012', 'Now, where was I? Oh yes...', null, null, null, null, null, null, null, 'Так, на чём мы остановились! Ах да...', '9215', '1', '0', '0', 'moroes SAY_SPECIAL_1');
INSERT IGNORE INTO `script_texts` VALUES ('15687', '-1532013', 'You rang?', null, null, null, null, null, null, null, 'Вызывали?', '9316', '1', '0', '0', 'moroes SAY_SPECIAL_2');
INSERT IGNORE INTO `script_texts` VALUES ('15687', '-1532011', 'Hmm, unannounced visitors? Preparations must be made.', null, null, null, null, null, null, null, 'Ммм, нежданные гости? Надо бы приготовиться.', '9211', '1', '0', '0', 'moroes SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('15550', '-1532009', 'Such easy sport.', null, null, null, null, null, null, null, 'Лёгкая добыча.', '9170', '1', '0', '0', 'attumen SAY_RANDOM1');
INSERT IGNORE INTO `script_texts` VALUES ('15550', '-1532010', 'Amateurs! Do not think you can best me! I kill for a living.', null, null, null, null, null, null, null, 'Неумехи, даже не думайте превзойти меня, убийство, это мой хлеб!', '9304', '1', '0', '0', 'attumen SAY_RANDOM2');
INSERT IGNORE INTO `script_texts` VALUES ('15550', '-1532008', 'I always knew... someday I would become... the hunted.', null, null, null, null, null, null, null, 'Ах.. Всегда знал... когда-нибудь... я стану добычей.', '9165', '1', '0', '0', 'attumen SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('15550', '-1532007', 'Weapons are merely a convenience for a warrior of my skill!', null, null, null, null, null, null, null, 'Настоящий воин может обойтись и без оружия!', '9166', '1', '0', '0', 'attumen SAY_DISARMED');
INSERT IGNORE INTO `script_texts` VALUES ('15550', '-1532006', 'Another trophy to add to my collection!', null, null, null, null, null, null, null, 'Ещё один трофей, для моей коллекции!', '9300', '1', '0', '0', 'attumen SAY_KILL2');
INSERT IGNORE INTO `script_texts` VALUES ('15550', '-1532005', 'It was... inevitable.', null, null, null, null, null, null, null, 'Это было... неизбежно.', '9169', '1', '0', '0', 'attumen SAY_KILL1');
INSERT IGNORE INTO `script_texts` VALUES ('16151', '-1532004', 'Come, Midnight, let\'s disperse this petty rabble!', null, null, null, null, null, null, null, 'Ко мне, Полночь, разгоним этот жалкий сброд!', '9168', '1', '0', '0', 'attumen SAY_MOUNT');
INSERT IGNORE INTO `script_texts` VALUES ('16151', '-1532003', 'Perhaps you would rather test yourselves against a more formidable opponent?!', null, null, null, null, null, null, null, 'Возможно вы предпочли бы померяться силами с более грозным противником!?', '9299', '1', '0', '0', 'attumen SAY_APPEAR3');
INSERT IGNORE INTO `script_texts` VALUES ('16151', '-1532002', 'Who dares attack the steed of the Huntsman?', null, null, null, null, null, null, null, 'Кто посмел тронуть моего коня?', '9298', '1', '0', '0', 'attumen SAY_APPEAR2');
INSERT IGNORE INTO `script_texts` VALUES ('16151', '-1532000', 'Well done Midnight!', null, null, null, null, null, null, null, 'Отлично, Полночь!', '9173', '1', '0', '0', 'attumen SAY_MIDNIGHT_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('16151', '-1532001', 'Cowards! Wretches!', null, null, null, null, null, null, null, 'Трусы! Падлецы!', '9167', '1', '0', '0', 'attumen SAY_APPEAR1');
INSERT IGNORE INTO `script_texts` VALUES ('15516', '-1531010', 'I serve to the last!', null, null, null, null, null, null, null, 'Я был верен своему долгу до конца!', '8648', '1', '0', '0', 'sartura SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('15727', '-1531011', 'is weakened!', null, null, null, null, null, null, null, 'ослаблен!', '0', '2', '0', '0', 'cthun EMOTE_WEAKENED');
INSERT IGNORE INTO `script_texts` VALUES ('15516', '-1531009', 'I sentence you to death!', null, null, null, null, null, null, null, 'Я приговариваю вас к смерти!', '8647', '1', '0', '0', 'sartura SAY_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('15263', '-1531007', 'You only delay... the inevetable.', null, null, null, null, null, null, null, 'Вы лишь...откладываете неизбежное.', '8622', '1', '0', '0', 'skeram SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('15516', '-1531008', 'You will be judged for defiling these sacred grounds! The laws of the Ancients will not be challenged! Trespassers will be annihilated!', null, null, null, null, null, null, null, 'Вы будете наказаны за осквернение этого священого места!', '8646', '1', '0', '0', 'sartura SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('15263', '-1531006', 'Prepare for the return of the ancient ones!', null, null, null, null, null, null, null, 'Готовьтесь к возвращению древних!', '8618', '1', '0', '0', 'skeram SAY_SPLIT');
INSERT IGNORE INTO `script_texts` VALUES ('15263', '-1531005', 'The screams of the dying will fill the air. A symphony of terror is about to begin!', null, null, null, null, null, null, null, 'Предсмертные вопли наполнят воздух. Симфония ужаса вот-вот начнётся.', '8620', '1', '0', '0', 'skeram SAY_SLAY3');
INSERT IGNORE INTO `script_texts` VALUES ('15263', '-1531004', 'Spineless wretches! You will drown in rivers of blood!', null, null, null, null, null, null, null, 'Бесхребетные твари! Вы утонете в реках крови!', '8619', '1', '0', '0', 'skeram SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('15263', '-1531003', 'Let your death serve as an example!', null, null, null, null, null, null, null, 'Пусть твоя смерть послужит уроком другим!', '8617', '1', '0', '0', 'skeram SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('15263', '-1531002', 'Tremble! The end is upon you.', null, null, null, null, null, null, null, 'Трепещите, настал час вашей гибели!', '8621', '1', '0', '0', 'skeram SAY_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('15263', '-1531001', 'Cower mortals! The age of darkness is at hand.', null, null, null, null, null, null, null, 'Трепещите, смертные! Пришло время тьмы.', '8616', '1', '0', '0', 'skeram SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('15263', '-1531000', 'Are you so eager to die? I would be happy to accomodate you.', null, null, null, null, null, null, null, 'Хотите умереть? С удовольствием помогу вам в этом.', '8615', '1', '0', '0', 'skeram SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509027', 'I...have...failed.', null, null, null, null, null, null, null, 'Я...проиграл.', '8594', '1', '0', '0', 'ossirian SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509026', 'You are terminated.', null, null, null, null, null, null, null, 'С вами покончено.', '8602', '1', '0', '0', 'ossirian SAY_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509025', 'Sands of the desert rise and block out the sun!', null, null, null, null, null, null, null, 'Взвейтесь, пески пустыни, затмите солнце!', '8598', '1', '0', '0', 'ossirian SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509024', 'Tresspassers will be terminated.', null, null, null, null, null, null, null, 'Нарушители будут уничтожены.', '8601', '1', '0', '0', 'ossirian SAY_RAND_INTRO4');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509023', 'To your posts. Defend the city.', null, null, null, null, null, null, null, 'По места! Защищайте город.', '8600', '1', '0', '0', 'ossirian SAY_RAND_INTRO3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509022', 'The walls have been breached!', null, null, null, null, null, null, null, 'Враг прорвался!', '8599', '1', '0', '0', 'ossirian SAY_RAND_INTRO2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509021', 'Protect the city at all costs!', null, null, null, null, null, null, null, 'Защищайте город любой ценой!', '8597', '1', '0', '0', 'ossirian SAY_RAND_INTRO1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509020', 'My powers return!', null, null, null, null, null, null, null, 'Силы возвращаются ко мне!', '8596', '1', '0', '0', 'ossirian SAY_SURPREME3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509019', 'My powers are renewed!', null, null, null, null, null, null, null, 'Мои силы восстановлены!', '8595', '1', '0', '0', 'ossirian SAY_SURPREME2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509018', 'I am rejuvinated!', null, null, null, null, null, null, null, 'Мои раны исцелились!', '8593', '1', '0', '0', 'ossirian SAY_SURPREME1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509017', 'Soon you will know the price of your meddling, mortals... The master is nearly whole... And when he rises, your world will be cease!', null, null, null, null, null, null, null, 'Скоро вы узнаете цену своего вмешательства, смертные... Хозяин почти невредим... И когда он поднимается, вашему миру настанет конец!', '0', '1', '0', '0', 'rajaxx SAY_COMPLETE_QUEST');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509016', 'Breath your last!', null, null, null, null, null, null, null, 'Это ваш последний вздох!', '8604', '1', '0', '0', 'rajaxx SAY_KILLS_ANDOROV');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509015', 'You are not worth my time $N!', null, null, null, null, null, null, null, 'Я не стану тратить на вас время!', '8614', '1', '0', '0', 'rajaxx SAY_DEAGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509014', 'Warriors! Captains! Continue the fight!', null, null, null, null, null, null, null, 'Воины! Командиры! Продолжайте бой!', '8613', '1', '0', '0', 'rajaxx SAY_UNK4');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509013', 'Do not hesitate! Destroy them!', null, null, null, null, null, null, null, 'Не медлите ни секунды! Уничтожьте их!', '8606', '1', '0', '0', 'rajaxx SAY_UNK3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509012', 'Crush them! Drive them out!', null, null, null, null, null, null, null, 'Разбейте их! Сметите их!', '8605', '1', '0', '0', 'rajaxx SAY_UNK2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509011', 'Attack and make them pay dearly!', null, null, null, null, null, null, null, 'В атаку и пусть их кровь прольётся рекой!', '8603', '1', '0', '0', 'rajaxx SAY_UNK1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509010', 'Impudent fool! I will kill you myself!', null, null, null, null, null, null, null, 'Дерзкий глупец! Я сам тебя убью!', '8609', '1', '0', '0', 'rajaxx SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509009', 'Fandral! Your time has come! Go and hide in the Emerald Dream and pray we never find you!', null, null, null, null, null, null, null, 'Ну вот и всё Френдрал! Беги, прячься в своём Изумрудном Сне! И молись, чтоб мы никогда тебя не нашли!', '8607', '1', '0', '0', 'rajaxx SAY_WAVE7');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509008', 'Staghelm will whimper and beg for his life, just as his whelp of a son did! One thousand years of injustice will end this day!', null, null, null, null, null, null, null, 'Олений Рог будет скулить, и просить пощады, как скулил его сын, этот щенок! С тысячелетней несправедливостью будет покончено!', '8611', '1', '0', '0', 'rajaxx SAY_WAVE6');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509007', 'Fear is for the enemy! Fear and death!', null, null, null, null, null, null, null, 'Страх и смерть! Вот что ждёт наших врагов', '8608', '1', '0', '0', 'rajaxx SAY_WAVE5');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509006', 'No longer will we wait behind barred doors and walls of stone! No longer will our vengeance be denied! The dragons themselves will tremble before our wrath!', null, null, null, null, null, null, null, 'Мы больше не будем прятаться за закрытыми дверями и каменными стенами! Хватит откладывать то, что должно быть сделано! Пускай даже драконы содрогнуться перед нашей яростью.', '8610', '1', '0', '0', 'rajaxx SAY_WAVE4');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509005', 'The time of our retribution is at hand! Let darkness reign in the hearts of our enemies!', null, null, null, null, null, null, null, 'Пришло время возмездия! Да воцариться тьма в сердцах наших врагов!', '8612', '1', '0', '0', 'rajaxx SAY_WAVE3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509004', 'Remember, Rajaxx, when I said I\'d kill you last? I lied...', null, null, null, null, null, null, null, 'Помнишь, Раджакс, когда я сказал что убью тебя последним? Я тогда соврал...', '0', '1', '0', '0', 'andorov SAY_ANDOROV_ATTACK');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509003', 'They come now. Try not to get yourself killed, young blood.', null, null, null, null, null, null, null, 'Они приближаются. Попытайтесь не дать себя убить, молодая кровь.', '0', '1', '0', '0', 'andorov SAY_ANDOROV_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509002', 'sets eyes on $N!', null, null, null, null, null, null, null, 'разуй глаза $N!', '0', '2', '0', '0', 'buru EMOTE_TARGET');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509000', 'senses your fear.', null, null, null, null, null, null, null, 'чувствуйте ваш страх.', '0', '2', '0', '0', 'moam EMOTE_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1509001', 'bristles with energy!', null, null, null, null, null, null, null, 'рассвирепел!', '0', '2', '0', '0', 'moan EMOTE_MANA_FULL');
INSERT IGNORE INTO `script_texts` VALUES ('11981', '-1469031', 'goes into a frenzy!', null, null, null, null, null, null, null, 'идет в безумие!', '0', '2', '0', '0', 'flamegor EMOTE_FRENZY');
INSERT IGNORE INTO `script_texts` VALUES ('13020', '-1469030', 'Forgive me $N, your death only adds to my failure.', null, null, null, null, null, null, null, 'Прости меня $N, твоя смерть усилила горечь моего поражения.', '8284', '1', '0', '0', 'vaelastrasz SAY_KILLTARGET');
INSERT IGNORE INTO `script_texts` VALUES ('13020', '-1469029', 'Nefarius\' hate has made me stronger than ever before. You should have fled, while you could, mortals! The fury of Blackrock courses through my veins!', null, null, null, null, null, null, null, 'Ненависть Нефариуса предала мне сил. Смертные, вам следовало бежать, но теперь уже слишком поздно, ярость Чёрной Горы бежит по моим венам!', '8285', '1', '0', '0', 'vaelastrasz SAY_HALFLIFE');
INSERT IGNORE INTO `script_texts` VALUES ('13020', '-1469028', 'FLAME! DEATH! DESTRUCTION! COWER MORTALS BEFORE THE WRATH OF LORD....NO! I MUST FIGHT THIS!', null, null, null, null, null, null, null, 'ПЛАМЯ! СМЕРТЬ! РАЗРУШЕНИЯ! СМЕРТНЫЕ СКЛОНИТЕСЬ ПЕРЕД ГНЕВОМ ГОСПОДИНА... НЕТ! Я ДОЛЖЕН УСТОЯТЬ! АЛЕКСТРАЦА ПОМОГИ МНЕ, Я НЕДОЛЖЕН СДАВАТЬСЯ!', '8283', '1', '0', '0', 'vaelastrasz SAY_LINE3');
INSERT IGNORE INTO `script_texts` VALUES ('13020', '-1469027', 'I beg you Mortals, flee! Flee before I lose all control. The Black Fire rages within my heart. I must release it!', null, null, null, null, null, null, null, 'Умоляю, смертные! Бегите, бегите пока я еще могу себя удержать. Чёрный огонь бушует в моём сердце. Я должен дать ему волю.', '8282', '1', '0', '0', 'vaelastrasz SAY_LINE2');
INSERT IGNORE INTO `script_texts` VALUES ('13020', '-1469026', 'Too late...friends. Nefarius\' corruption has taken hold. I cannot...control myself.', null, null, null, null, null, null, null, 'Слишком поздно... друзья. Проклятие Нефариуса поработило меня. Я не могу... сдержать себя', '8281', '1', '0', '0', 'vaelastrasz SAY_LINE1');
INSERT IGNORE INTO `script_texts` VALUES ('12435', '-1469025', 'If I fall into the abyss I\'ll take all of you mortals with me...', null, null, null, null, null, null, null, 'Если я паду в бездну, я заберу вас всех смертных с собой...', '8278', '1', '0', '0', 'razorgore SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('12435', '-1469024', 'No! Not another one! I\'ll have your heads for this atrocity.', null, null, null, null, null, null, null, 'Нет только не это, вы дорого заплатите за свою жестокость.', '8277', '1', '0', '0', 'razorgore SAY_EGGS_BROKEN3');
INSERT IGNORE INTO `script_texts` VALUES ('12435', '-1469022', 'You\'ll pay for forcing me to do this.', null, null, null, null, null, null, null, 'Вы заплатите за то, что заставили меня это сделать.', '8275', '1', '0', '0', 'razorgore SAY_EGGS_BROKEN1');
INSERT IGNORE INTO `script_texts` VALUES ('12435', '-1469023', 'Fools! These eggs are more precious than you know.', null, null, null, null, null, null, null, 'Глупцы! Эти яйца куда ценнее чем вы думаете.', '8276', '1', '0', '0', 'razorgore SAY_EGGS_BROKEN2');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469021', 'Rogues? Stop hiding and face me!', null, null, null, null, null, null, null, 'Разбойник? Хватит скрываться и сразись со мной!', '0', '1', '0', '0', 'nefarian SAY_ROGUE');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469020', 'Hunters and your annoying pea-shooters!', null, null, null, null, null, null, null, 'Охотники и ваши раздражающие стрелы-горошины!', '0', '1', '0', '0', 'nefarian SAY_HUNTER');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469019', 'Warlocks, you shouldn\'t be playing with magic you don\'t understand. See what happens?', null, null, null, null, null, null, null, 'Чернокнижники, вы не должны играть с магией вы не понимаете. Посмотрим что произойдет?', '0', '1', '0', '0', 'nefarian SAY_WARLOCK');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469018', 'Shamans, show me what your totems can do!', null, null, null, null, null, null, null, 'Шаманы, покажите что ваши тотемы могут сделать!', '0', '1', '0', '0', 'nefarian SAY_SHAMAN');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469017', 'Paladins, I\'ve heard you have many lives. Show me.', null, null, null, null, null, null, null, 'Паладины, я слышал у вас есть много жизней. Покажите мне.', '0', '1', '0', '0', 'nefarian SAY_PALADIN');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469016', 'Priests! If you\'re going to keep healing like that, we might as well make it a little more interesting!', null, null, null, null, null, null, null, 'Священники! Если вы собираетесь продолжать лечить как этот, мы могли бы заодно сделать это немного более интересным!', '0', '1', '0', '0', 'nefarian SAY_PRIEST');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469015', 'Druids and your silly shapeshifting. Let\'s see it in action!', null, null, null, null, null, null, null, 'Друиды и ваша глупая перемена формы. Давайте увидим это в действии!', '0', '1', '0', '0', 'nefarian SAY_DRUID');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469013', 'Mages too? You should be more careful when you play with magic...', null, null, null, null, null, null, null, 'Тоже маги? Вы должны быть более аккуратны когда играете с магией...', '0', '1', '0', '0', 'nefarian SAY_MAGE');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469014', 'Warriors, I know you can hit harder than that! Let\'s see it!', null, null, null, null, null, null, null, 'Воины, я знаю вы можете бить сильней чем сейчас! Покажите мне это!', '0', '1', '0', '0', 'nefarian SAY_WARRIOR');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469012', 'This cannot be! I am the Master here! You mortals are nothing to my kind! DO YOU HEAR? NOTHING!', null, null, null, null, null, null, null, 'Этого не может быть! Я тут хозяин! Вы, смертные, мне не ровня, СЛЫШИТЕ, Вы ничто...', '8292', '1', '0', '0', 'nefarian SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469011', 'Worthless $N! Your friends will join you soon enough!', null, null, null, null, null, null, null, 'Ничтожество $N! Скоро твои друзья последуют за тобой!', '8293', '1', '0', '0', 'nefarian SAY_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469010', 'Impossible! Rise my minions! Serve your master once more!', null, null, null, null, null, null, null, 'Не может быть! Вставайте, слуги, послужите своему господину ещё раз!', '8291', '1', '0', '0', 'nefarian SAY_RAISE_SKELETONS');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469009', 'Burn, you wretches! Burn!', null, null, null, null, null, null, null, 'Горите, насекомые! Горите!', '8290', '1', '0', '0', 'nefarian SAY_SHADOWFLAME');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469008', 'Enough! Now you vermin shall feel the force of my birthright, the fury of the earth itself.', null, null, null, null, null, null, null, 'Довольно, черви! Сейчас Вы узнаете мою истинную силу, силу самой земли!', '8289', '1', '0', '0', 'nefarian SAY_XHEALTH');
INSERT IGNORE INTO `script_texts` VALUES ('11583', '-1469007', 'Well done, my minions. The mortals\' courage begins to wane! Now, let\'s see how they contend with the true Lord of Blackrock Spire!', null, null, null, null, null, null, null, 'Отлично, слуги мои! Смертные уже не столь дерзки, посмотрим, как они справятся с истинным повелителем Пика Чёрной Горы!', '8288', '1', '0', '0', 'nefarian SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('10162', '-1469006', 'Ah, the heroes. You are persistent, aren\'t you. Your allied attempted to match his power against mine, and had to pay the price. Now he shall serve me, by slaughtering you. Get up little red wyrm and destroy them!', null, null, null, null, null, null, null, 'А, герои. Настойчивые да, ваш союзник уже пытался бороться со мной... и... поплатился, теперь он послужит мне! Ну-ка, маленький красный червяк, убей их!', '8279', '1', '0', '0', 'victor_nefarius SAY_VAEL_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('10162', '-1469005', 'Let the games begin!', null, null, null, null, null, null, null, 'Хм хм хм! Ну что ж поиграем!', '8280', '1', '0', '0', 'victor_nefarius SAY_GAMESBEGIN_2');
INSERT IGNORE INTO `script_texts` VALUES ('10162', '-1469004', 'In this world where time is your enemy, it is my greatest ally. This grand game of life that you think you play in fact plays you. To that I say...', null, null, null, null, null, null, null, 'В мире, где время ваш противник, оно является моим союзником. Это великая игра жизни, в которую играете вы, но на самом деле играют вами. Вот о чем я говорю…', '0', '0', '0', '0', 'victor_nefarius SAY_GAMESBEGIN_1');
INSERT IGNORE INTO `script_texts` VALUES ('14020', '-1469003', 'flinches as its skin shimmers.', null, null, null, null, null, null, null, 'вздрагивает, поскольку его кожа мерцает.', '0', '2', '0', '0', 'chromaggus EMOTE_SHIMMER');
INSERT IGNORE INTO `script_texts` VALUES ('14020', '-1469002', 'goes into a killing frenzy!', null, null, null, null, null, null, null, 'входит в смертельное безумство!', '0', '2', '0', '0', 'chromaggus EMOTE_FRENZY');
INSERT IGNORE INTO `script_texts` VALUES ('12017', '-1469001', 'Clever Mortals but I am not so easily lured away from my sanctum!', null, null, null, null, null, null, null, 'Умно смертные, но вам не удастся, так просто, выманить меня!', '8287', '1', '0', '0', 'broodlord SAY_LEASH');
INSERT IGNORE INTO `script_texts` VALUES ('12017', '-1469000', 'None of your kind should be here! You\'ve doomed only yourselves!', null, null, null, null, null, null, null, 'Вас не должно быть здесь! Вы сами обрекли себя на смерть!', '8286', '1', '0', '0', 'broodlord SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('11502', '-1409017', 'DIE INSECT!', null, null, null, null, null, null, null, 'УМРИ НАСЕКОМОЕ!', '8051', '1', '0', '0', 'ragnaros SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('11502', '-1409018', 'MY PATIENCE IS DWINDLING! COME NATS TO YOUR DEATH!', null, null, null, null, null, null, null, 'МОЁ ТЕРПЕНИЕ НА ИСХОДЕ! УМРИТЕ ЖЕ НИЧТОЖНЫЕ ТВАРИ!', '8048', '1', '0', '0', 'ragnaros SAY_MAGMABURST');
INSERT IGNORE INTO `script_texts` VALUES ('11502', '-1409015', 'BY FIRE BE PURGED!', null, null, null, null, null, null, null, 'ОЧИСТИТЕСЬ ОГНЁМ!', '8046', '1', '0', '0', 'ragnaros SAY_HAND');
INSERT IGNORE INTO `script_texts` VALUES ('11502', '-1409016', 'TASTE THE FLAMES OF SULFURON!', null, null, null, null, null, null, null, 'ОТВЕДАЙТЕ ПЛАМЯ СУЛЬФУРОНА!', '8047', '1', '0', '0', 'ragnaros SAY_WRATH');
INSERT IGNORE INTO `script_texts` VALUES ('11502', '-1409014', 'YOU CANNOT DEFEAT THE LIVING FLAME! COME YOU MINIONS OF FIRE! COME FORTH YOU CREATURES OF HATE! YOUR MASTER CALLS!', null, null, null, null, null, null, null, 'ВАМ НЕ ОДОЛЕТЬ ЖИВОЕ ПЛЯМЯ! ПРИЙДИТЕ, ВОИНЫ ОГНЯ, ПРИЙДИТЕ, ПОРОЖДЕНИЯ НЕНАВИСТИ! ПОВЕЛИТЕЛЬ ПРИЗЫВАЕТ ВАС!', '8050', '1', '0', '0', 'ragnaros SAY_REINFORCEMENTS2');
INSERT IGNORE INTO `script_texts` VALUES ('11502', '-1409013', 'COME FORTH, MY SERVANTS! DEFEND YOUR MASTER!', null, null, null, null, null, null, null, 'ПРИДИТЕ СЛУГИ МОИ! ЗАЩИТИТЕ СВОЕГО ГОСПОДИНА!', '8049', '1', '0', '0', 'ragnaros SAY_REINFORCEMENTS1');
INSERT IGNORE INTO `script_texts` VALUES ('12018', '-1409012', 'NOW FOR YOU, INSECTS. BOLDLY YOU SAUGHT THE POWER OF RAGNAROS NOW YOU SHALL SEE IT FIRST HAND.', null, null, null, null, null, null, null, 'ЧТОЖЕ ДО ВАС, СМЕРТНЫЕ, ВЫ НАШЛИ ТО, ЧТО ИСКАЛИ! УЗРИТЕ СИЛУ РАГНАРОСА!', '8045', '1', '0', '0', 'ragnaros SAY_ARRIVAL5_RAG');
INSERT IGNORE INTO `script_texts` VALUES ('12018', '-1409011', 'FOOL! YOU ALLOWED THESE INSECTS TO RUN RAMPANT THROUGH THE HALLOWED CORE, AND NOW YOU LEAD THEM TO MY VERY LAIR? YOU HAVE FAILED ME, EXECUTUS! JUSTICE SHALL BE MET, INDEED!', null, null, null, null, null, null, null, 'ГЛУПЕЦ! ТЫ ПУСТИЛ ЭТИХ НАСЕКОМЫХ В МОЁ СВЯТИЛИЩЕ, ТЫ ПРИВЁЛ ИХ ПРЯМО К МОЕМУ ЛОГОВУ! ТЫ ПРЕДАЛ МЕНЯ, ЭКЗЕКУТУС! ДА СВЕРШИТСЯ ПРАВОСУДИЕ!', '8044', '1', '0', '0', 'ragnaros SAY_ARRIVAL3_RAG');
INSERT IGNORE INTO `script_texts` VALUES ('12018', '-1409010', 'These mortal infidels, my lord! They have invaded your sanctum, and seek to steal your secrets!', null, null, null, null, null, null, null, 'Это безумные смертные, мой повелитель! Они вторглись в ваше святилище, и стремятся украсть ваши тайны.', '8041', '1', '0', '0', 'ragnaros SAY_ARRIVAL2_MAJ');
INSERT IGNORE INTO `script_texts` VALUES ('12018', '-1409009', 'TOO SOON! YOU HAVE AWAKENED ME TOO SOON, EXECUTUS! WHAT IS THE MEANING OF THIS INTRUSION?', null, null, null, null, null, null, null, 'СЛИШКОМ РАНО! ТЫ РАЗБУДИЛ МЕНЯ СЛИШКОМ РАНО, ЭКЗЕКУТУС! ЧТО ЭТО ЗА НЕЗВАННЫЕ ГОСТИ?', '8043', '1', '0', '0', 'ragnaros SAY_ARRIVAL1_RAG');
INSERT IGNORE INTO `script_texts` VALUES ('12018', '-1409008', 'Behold Ragnaros, the Firelord! He who was ancient when this world was young! Bow before him, mortals! Bow before your ending!', null, null, null, null, null, null, null, 'Узрите Рагнароса, Повелителя Пламени! Того кто видел зорю этого мира, склонитесь перед ним, смертные! Склонитесь перед вашей погибелью!', '8040', '1', '0', '0', 'ragnaros SAY_SUMMON_MAJ');
INSERT IGNORE INTO `script_texts` VALUES ('12018', '-1409007', 'Impossible! Stay your attack mortals! I submitt! I submitt! Brashly you have come to rest the secrets of the living flame. You will soon regret the recklessness of your quest. I go now to summon the lord whos house this is. Should you seek an audiance with him your paltry lives will surly be forfit. Nevertheless seek out his lair if you dare!', null, null, null, null, null, null, null, 'Это не возможно! Остановитесь смертные! Я вынужден сдаться! Вы бесцеремонно вторглись сюда, чтобы похитить тайны Живого пламени, но выпожалеете о своем безрассудстве! Я призову своего господина и выедвали переживете встречу с ним! Однако вы моежете заглянуть в его обитель, если посмеете...', '8038', '1', '0', '0', 'majordomo SAY_DEFEAT');
INSERT IGNORE INTO `script_texts` VALUES ('12018', '-1409006', 'Burn mortals! Burn for this transgression!', null, null, null, null, null, null, null, 'Горите смертные! Горите за ваши прегрешения!', '8036', '1', '0', '0', 'majordomo SAY_SPECIAL');
INSERT IGNORE INTO `script_texts` VALUES ('12018', '-1409005', 'Ashes to Ashes!', null, null, null, null, null, null, null, 'Прах к праху!', '8037', '1', '0', '0', 'majordomo SAY_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('12018', '-1409004', 'The runes of warding have been destroyed! Hunt down the infedels my bretheren.', null, null, null, null, null, null, null, 'Охранные руны разрушены! Уничтожьте этих безумцев, братья.', '8039', '1', '0', '0', 'majordomo SAY_SPAWN');
INSERT IGNORE INTO `script_texts` VALUES ('12018', '-1409003', 'Reckless mortals, none may challenge the sons of the living flame!', null, null, null, null, null, null, null, 'Дерзкие смертные, никто не сможет устоять против Сынов живого пламени!', '8035', '1', '0', '0', 'majordomo SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('11988', '-1409002', '%s refuses to die while its master is in trouble.', null, null, null, null, null, null, null, '%s отказывается умирать, в то время как его владелец в беде.', '0', '2', '0', '0', 'core rager EMOTE_LOWHP');
INSERT IGNORE INTO `script_texts` VALUES ('11982', '-1409001', '%s goes into a killing frenzy!', null, null, null, null, null, null, null, '%s приходит в смертельное безумство!', '0', '2', '0', '0', 'magmadar EMOTE_FRENZY');
INSERT IGNORE INTO `script_texts` VALUES ('12056', '-1409000', '%s performs one last service for Ragnaros.', null, null, null, null, null, null, null, '%s выполняет одно последнее одолжение для Рагнароса.', '0', '2', '0', '0', 'geddon EMOTE_SERVICE');
INSERT IGNORE INTO `script_texts` VALUES ('15378', '-1350030', 'glances at her compatriots.', null, null, null, null, null, null, null, null, '0', '2', '0', '2', 'MERITHRA_EMOTE_1');
INSERT IGNORE INTO `script_texts` VALUES ('15378', '-1350029', 'Succumb to the endless dream, little ones. Let it consume you!', null, null, null, null, null, null, null, null, '0', '1', '0', '53', 'MERITHRA_YELL_1');
INSERT IGNORE INTO `script_texts` VALUES ('15378', '-1350028', 'We will push them back, Anachronos. This I vow. Uphold the end of this task. Let not your hands falter as you seal our fates behind the barrier.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'MERITHRA_SAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('15378', '-1350027', 'There is a way...', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'MERITHRA_SAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('15380', '-1350026', 'nods knowingly.', null, null, null, null, null, null, null, null, '0', '2', '0', '273', 'ARYGOS_EMOTE_1');
INSERT IGNORE INTO `script_texts` VALUES ('15380', '-1350025', 'Let them feel the wrath of the Blue Flight! May Malygos protect me!', null, null, null, null, null, null, null, null, '0', '1', '0', '53', 'ARYGOS_YELL_1');
INSERT IGNORE INTO `script_texts` VALUES ('15380', '-1350024', 'This distraction will give you and the young druid time enough to seal the gate. Do not falter. Now, let us see how they deal with chaotic magic.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'ARYGOS_SAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('15379', '-1350023', 'Alexstrasza grant me the resolve to drive our enemies back!', null, null, null, null, null, null, null, null, '0', '1', '0', '53', 'CAELESTRASZ_YELL_1');
INSERT IGNORE INTO `script_texts` VALUES ('15379', '-1350022', 'Do not forget the sacrifices made on this day, night elf. We have all suffered immensely at the hands of these beasts.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'CAELESTRASZ_SAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('15379', '-1350021', 'Aye, Fandral, remember these words: Let not your grief guide your faith. These thoughts you hold... dark places you go, night elf. Absolution cannot be had through misguided vengeance.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'CAELESTRASZ_SAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('15382', '-1350020', 'hurls the Scepter of the Shifting Sands into the barrier, shattering it.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'FANDRAL_EMOTE_2');
INSERT IGNORE INTO `script_texts` VALUES ('15382', '-1350019', 'falls to one knee - exhausted.', null, null, null, null, null, null, null, null, '0', '2', '0', '16', 'FANDRAL_EMOTE_1');
INSERT IGNORE INTO `script_texts` VALUES ('15382', '-1350018', 'My son\'s soul will find no comfort in this hollow victory, dragon. I will have him back.Thought it takes a millennia, I WILL have my son back!', null, null, null, null, null, null, null, null, '0', '0', '0', '254', 'FANDRAL_SAY_6');
INSERT IGNORE INTO `script_texts` VALUES ('15382', '-1350017', 'I want nothing to do with Silithus, the Qiraji and least of all, any damned dragons!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'FANDRAL_SAY_5');
INSERT IGNORE INTO `script_texts` VALUES ('15382', '-1350016', 'After the savagery that my people have witnessed and felt, you expect me to accept another burden, dragon? Surely you are mad.', null, null, null, null, null, null, null, null, '0', '0', '0', '25', 'FANDRAL_SAY_4');
INSERT IGNORE INTO `script_texts` VALUES ('15382', '-1350015', 'Ancient ones guide my hand... Wake from your slumber! WAKE AND SEAL THIS CURSED PLACE!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'FANDRAL_SAY_3');
INSERT IGNORE INTO `script_texts` VALUES ('15382', '-1350014', 'It is done dragon. Lead the way...', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'FANDRAL_SAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('15382', '-1350013', 'My forces cannot overcome the Qiraji defenses. We will not be able to get close enough to place your precious barrier, dragon.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'FANDRAL_SAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('15381', '-1350012', 'kneels down to pick up the fragments of the shattered scepter.', null, null, null, null, null, null, null, null, '0', '2', '0', '87', 'ANACHRONOS_EMOTE_3');
INSERT IGNORE INTO `script_texts` VALUES ('15381', '-1350011', 'shakes his head in dissapointment.', null, null, null, null, null, null, null, null, '0', '2', '0', '274', 'ANACHRONOS_EMOTE_2');
INSERT IGNORE INTO `script_texts` VALUES ('15381', '-1350010', 'hands the Scepter of the Shifting Sands to Fandral Staghelm.', null, null, null, null, null, null, null, null, '0', '2', '0', '1', 'ANACHRONOS_EMOTE_1');
INSERT IGNORE INTO `script_texts` VALUES ('15381', '-1350009', 'And now you know all that there is to know, mortal...', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'ANACHRONOS_SAY_10');
INSERT IGNORE INTO `script_texts` VALUES ('15381', '-1350008', 'Lord Staghelm, where are you going? You would shatter our bond for the sake of pride?', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'ANACHRONOS_SAY_9');
INSERT IGNORE INTO `script_texts` VALUES ('15381', '-1350007', 'Before I leave this place, I make one final offreing to you, Lord Staghelm. Should a time arise in which you must gain entry to this accursed fortress, use the Scepter of the Shifting Sands on the sacred gong. The magic holding the barrier together will dissipate and the horrors of Ahn\'Qiraj will be unleashed upon the world once more.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'ANACHRONOS_SAY_8');
INSERT IGNORE INTO `script_texts` VALUES ('15381', '-1350006', 'There is but one duty that remains...', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'ANACHRONOS_SAY_7');
INSERT IGNORE INTO `script_texts` VALUES ('15381', '-1350005', 'It... It is over, Lord Staghelm. We are victorious. Albeit the cost for this victory was great.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'ANACHRONOS_SAY_6');
INSERT IGNORE INTO `script_texts` VALUES ('15381', '-1350004', 'FINISH THE SPELL STAGHELM! I CANNOT HOLD THE GLYPHS OF WARDING IN PLACE MUCH LONGER! CALL FORTH THE ROOTS!', null, null, null, null, null, null, null, null, '0', '0', '0', '53', 'ANACHRONOS_SAY_5');
INSERT IGNORE INTO `script_texts` VALUES ('15381', '-1350003', 'The sands of time will halt, but only for a moment! I will now conjure the barrier.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'ANACHRONOS_SAY_4');
INSERT IGNORE INTO `script_texts` VALUES ('15381', '-1350002', 'Stay close...', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'ANACHRONOS_SAY_3');
INSERT IGNORE INTO `script_texts` VALUES ('15381', '-1350001', 'NOW, STAGHELM! WE GO NOW! Prepare your magic!', null, null, null, null, null, null, null, null, '0', '0', '0', '81', 'ANACHRONOS_SAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('11136', '-1329003', 'Don\'t cross those beams!', null, null, null, null, null, null, null, 'Не пересекайте те лучи!', '0', '0', '0', '0', 'freed_soul SAY_ZAPPED3');
INSERT IGNORE INTO `script_texts` VALUES ('15381', '-1350000', 'We must act quickly or all shall be lost!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'ANACHRONOS_SAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('11136', '-1329001', 'Rivendare must die', null, null, null, null, null, null, null, 'Ривендер должен умереть', '0', '0', '0', '0', 'freed_soul SAY_ZAPPED1');
INSERT IGNORE INTO `script_texts` VALUES ('11136', '-1329002', 'Who you gonna call?', null, null, null, null, null, null, null, 'Кого вы собираетесь призвать?', '0', '0', '0', '0', 'freed_soul SAY_ZAPPED2');
INSERT IGNORE INTO `script_texts` VALUES ('11136', '-1329000', 'Thanks to Egan', null, null, null, null, null, null, null, 'Спасибо Эган', '0', '0', '0', '0', 'freed_soul SAY_ZAPPED0');
INSERT IGNORE INTO `script_texts` VALUES ('14834', '-1309023', 'Minions of Hakkar, hear your God. The sanctity of this temple has been compromised. Invaders encroach upon holy ground! The Altar of Blood must be protected. Kill them all!', null, null, null, null, null, null, null, 'Прислужники племени Хаккари, услышьте своего Бога. Неприкосновенность этого храма поставлена под угрозу. Захватчики вторгаются на святую землю! Алтарь Крови должен быть защищен. Убейте их всех!', '0', '1', '0', '0', 'hakkar SAY_PROTECT_ALTAR');
INSERT IGNORE INTO `script_texts` VALUES ('14834', '-1309021', 'Fleeing will do you no good, mortals!', null, null, null, null, null, null, null, 'Побег не принесет вам ничего хорошего, смертные!', '0', '1', '0', '0', 'hakkar SAY_FLEEING');
INSERT IGNORE INTO `script_texts` VALUES ('14834', '-1309022', 'You dare set foot upon Hakkari holy ground? Minions of Hakkar, destroy the infidels!', null, null, null, null, null, null, null, 'Вы смеете ступать на святую землю Хаккари? Прислужники племени Хаккари, уничтожьте неверных!', '0', '1', '0', '0', 'hakkar SAY_MINION_DESTROY');
INSERT IGNORE INTO `script_texts` VALUES ('14834', '-1309020', 'PRIDE HERALDS THE END OF YOUR WORLD. COME, MORTALS! FACE THE WRATH OF THE SOULFLAYER!', null, null, null, null, null, null, null, 'ГОРДЫНЯ, ПЕРВЫЙ ВЕСНИК ГИБЕЛИ ВАШЕГО МИРА. ПОДОЙДИТЕ, СМЕРТНЫЕ, УЗРИТЕ ЯРОСТЬ СВЕЖЕВАТЕЛЯ ДУШ!', '8414', '1', '0', '0', 'hakkar SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('11382', '-1309018', 'I\'m keeping my eye on you, $N!', null, null, null, null, null, null, null, 'Я слежу за тобой, $N!', '0', '1', '0', '0', 'mandokir SAY_WATCH');
INSERT IGNORE INTO `script_texts` VALUES ('11382', '-1309019', 'Don\'t make me angry. You won\'t like it when I\'m angry.', null, null, null, null, null, null, null, 'Не зли меня. Тебе не понравится когда я разозлюсь.', '0', '1', '0', '0', 'mandokir SAY_WATCH_WHISPER');
INSERT IGNORE INTO `script_texts` VALUES ('11382', '-1309017', 'GRATS!', null, null, null, null, null, null, null, 'КРЫСЫ!', '0', '1', '0', '0', 'mandokir SAY_GRATS_JINDO');
INSERT IGNORE INTO `script_texts` VALUES ('11382', '-1309015', 'I\'ll feed your souls to Hakkar himself!', null, null, null, null, null, null, null, 'Я скормлю ваши души Хаккару!', '8413', '1', '0', '0', 'mandokir SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('11382', '-1309016', 'DING!', null, null, null, null, null, null, null, 'ЗВЕНИТЕ!', '0', '1', '0', '0', 'mandokir SAY_DING_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('11380', '-1309014', 'Welcome to da great show friends! Step right up to die!', null, null, null, null, null, null, null, 'Добро пожаловать на представление, друзья мои! Встречайте свою смерть!', '8425', '1', '0', '0', 'jindo SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('14515', '-1309013', 'At last, I am free of the Soulflayer!', null, null, null, null, null, null, null, 'Наконец-то, я свободна от Свежевателя Душ!', '8412', '1', '0', '0', 'arlokk SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('14515', '-1309012', 'Feast on $n, my pretties!', null, null, null, null, null, null, null, 'Наслаждайся $n, моими угощениями!', '0', '1', '0', '0', 'arlokk SAY_FEAST_PANTHER');
INSERT IGNORE INTO `script_texts` VALUES ('14515', '-1309011', 'Bethekk, your priestess calls upon your might!', null, null, null, null, null, null, null, 'Бетекк, твоя жрица взывает к тебе!', '8416', '1', '0', '0', 'arlokk SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('14509', '-1309010', 'Hakkar binds me no more! Peace at last!', null, null, null, null, null, null, null, 'Хаккар больше не властен надо мной! Наконец-то я обрету покой!', '8424', '1', '0', '0', 'thekal SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('14509', '-1309009', 'Shirvallah, fill me with your RAGE!', null, null, null, null, null, null, null, 'Шервала, наполни меня своей яростью!', '8419', '1', '0', '0', 'thekal SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('14510', '-1309007', 'Aid me my brood!', null, null, null, null, null, null, null, 'Помоги мне мое дитя!', '0', '1', '0', '0', 'marli SAY_SPIDER_SPAWN');
INSERT IGNORE INTO `script_texts` VALUES ('14510', '-1309008', 'Bless you mortal for this release. Hakkar controls me no longer...', null, null, null, null, null, null, null, 'Благодарю вас, смертные. Отныне хаккар не властен надо мной...', '8423', '1', '0', '0', 'marli SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('14510', '-1309006', 'Shadra, make of me your avatar!', null, null, null, null, null, null, null, 'Шадра, сделайте меня своим воплощением!', '0', '1', '0', '0', 'marli SAY_TRANSFORM');
INSERT IGNORE INTO `script_texts` VALUES ('14517', '-1309004', 'Finally ...death. Curse you Hakkar! Curse you!', null, null, null, null, null, null, null, 'Смерть... наконец-то. Будь ты проклят Хаккар! Будь ты проклят!', '8422', '1', '0', '0', 'jeklik SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('14510', '-1309005', 'Draw me to your web mistress Shadra. Unleash your venom!', null, null, null, null, null, null, null, 'Вплети меня в узор своей паутины, госпожа Шандра. Выплесни свой яд!', '8418', '1', '0', '0', 'marli SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('14517', '-1309003', 'I command you to rain fire down upon these invaders!', null, null, null, null, null, null, null, 'Я приказываю, чтобы вы нахлынули на этих захватчиков огненным дождем!', '0', '1', '0', '0', 'jeklik SAY_RAIN_FIRE');
INSERT IGNORE INTO `script_texts` VALUES ('14517', '-1309002', 'Lord Hir\'eek, grant me wings of vengance!', null, null, null, null, null, null, null, 'Лорд Хайрик, даруй мне крылья ненависти!', '8417', '1', '0', '0', 'jeklik SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('14507', '-1309001', 'Ssserenity..at lassst!', null, null, null, null, null, null, null, 'Ссспокойствие... накоонец!', '0', '1', '0', '0', 'venoxis SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('14507', '-1309000', 'Let the coils of hate unfurl!', null, null, null, null, null, null, null, 'Да развернуться кольца ненависти!', '8421', '1', '0', '0', 'venoxis SAY_TRANSFORM');
INSERT IGNORE INTO `script_texts` VALUES ('15608', '-1269028', 'Gul\'dan speaks the truth! We should return at once to tell our brothers of the news! Retreat back trought the portal!', null, null, null, null, null, null, null, 'Гул\'Дан говорит правду! Мы должны вернуться сразу, чтобы сообщить нашим братьям вести! Отступаем назад в портал!', '0', '1', '0', '0', 'medivh SAY_ORCS_ANSWER');
INSERT IGNORE INTO `script_texts` VALUES ('15608', '-1269027', 'Orcs of the Horde! This portalis the gateway to your new destiny! Azeroth lies before you, ripe for the taking!', null, null, null, null, null, null, null, 'Орки Орды! Этот портал – путь к вашей новой судьбе! Азерот лежит перед вами, созревший для захвата!', '0', '1', '0', '0', 'medivh SAY_ORCS_ENTER');
INSERT IGNORE INTO `script_texts` VALUES ('15608', '-1269026', 'I am grateful for your aid, champions. Now, Gul\'dan\'s Horde will sweep across this world, like a locust swarm, and all my designs, all my carefully laid plans will at last fall into place.', null, null, null, null, null, null, null, 'Я признателен вам, герои... Теперь орда Гул\'Дана пройдёт по этому миру как стая саранчи, и все мои замыслы, все мои тщательно выстроенные планы наконец-то исполняться.', '10440', '1', '0', '0', 'medivh SAY_WIN');
INSERT IGNORE INTO `script_texts` VALUES ('15608', '-1269025', 'No... damn this feeble mortal coil...', null, null, null, null, null, null, null, 'Нет… будь проклята, эта жалкая смертная оболочка...', '10441', '1', '0', '0', 'medivh SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('15608', '-1269024', 'The shield is nearly gone! All that I have worked for is in danger!', null, null, null, null, null, null, null, 'Щит вот-вот исчезнет, все мои планы под угрозой!', '10439', '1', '0', '0', 'medivh SAY_WEAK25');
INSERT IGNORE INTO `script_texts` VALUES ('15608', '-1269022', 'Champions, my shield grows weak!', null, null, null, null, null, null, null, 'Герои, мой щит слабеет!', '10437', '1', '0', '0', 'medivh SAY_WEAK75');
INSERT IGNORE INTO `script_texts` VALUES ('15608', '-1269023', 'My powers must be concentrated on the portal! I do not have time to hold the shield!', null, null, null, null, null, null, null, 'Все мои силы уходят на портал, я не могу отвлекаться на щит.', '10438', '1', '0', '0', 'medivh SAY_WEAK50');
INSERT IGNORE INTO `script_texts` VALUES ('15608', '-1269021', 'What is this? Champions, coming to my aid? I sense the hand of the dark one in this. Truly this sacred event bears his blessing?', null, null, null, null, null, null, null, 'Что это? Герои пришли мне на помощь? Я чувствую за этим волю тёмного владыки, воистину это событие несёт на себе печать его благословения.', '10436', '1', '0', '0', 'medivh SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('15608', '-1269020', 'The time has come! Gul\'dan, order your warlocks to double their efforts! Moments from now the gateway will open, and your Horde will be released upon this ripe, unsuspecting world!', null, null, null, null, null, null, null, 'Время пришло! Гул\'Дан, Прикажи чернокнижникам удвоить усилия! Ещё немного, и врата распахнуться, и Орда хлынет в этот мир ничего не подозревающий и созревший для жатвы!', '10435', '1', '0', '0', 'medivh SAY_ENTER');
INSERT IGNORE INTO `script_texts` VALUES ('17881', '-1269018', 'goes into a frenzy!', null, null, null, null, null, null, null, 'идет в безумие!', '0', '2', '0', '0', 'aeonus EMOTE_FRENZY');
INSERT IGNORE INTO `script_texts` VALUES ('20201', '-1269019', 'Stop! Do not go further, mortals. You are ill-prepared to face the forces of the Infinite Dragonflight. Come, let me help you.', null, null, null, null, null, null, null, 'Остановитесь! Не идите далее, смертные. Вы еще не готовы столкнуться с силами Бесконечного Полета Дракона. Подойдите, позвольте мне помочь вам.', '0', '0', '0', '0', 'saat SAY_SAAT_WELCOME');
INSERT IGNORE INTO `script_texts` VALUES ('17881', '-1269016', 'No one can stop us! No one!', null, null, null, null, null, null, null, 'Никто не сможет остановить нас! Никто!', '10404', '1', '0', '0', 'aeonus SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('17881', '-1269017', 'It is only a matter...of time.', null, null, null, null, null, null, null, 'Мы победим! Это всего лишь вопрос... времени.', '10405', '1', '0', '0', 'aeonus SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17881', '-1269013', 'Let us see what fate lays in store...', null, null, null, null, null, null, null, 'Посмотрим, что вам приготовила судьба...', '10402', '1', '0', '0', 'aeonus SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17881', '-1269014', 'Your time is up, slave of the past!', null, null, null, null, null, null, null, 'Твой срок истек, раб прошлого!', '10401', '1', '0', '0', 'aeonus SAY_BANISH');
INSERT IGNORE INTO `script_texts` VALUES ('17881', '-1269015', 'One less obstacle in our way!', null, null, null, null, null, null, null, 'Одной помехой меньше!', '10403', '1', '0', '0', 'aeonus SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('17879', '-1269010', 'Leaving so soon?', null, null, null, null, null, null, null, 'Уже уходишь, так быстро?', '10416', '1', '0', '0', 'chrono_lord_deja SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('17879', '-1269011', 'Time ... is on our side.', null, null, null, null, null, null, null, 'Время ... на нашей стороне.', '10417', '1', '0', '0', 'chrono_lord_deja SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17881', '-1269012', 'The time has come to shatter this clockwork universe forever! Let us no longer be slaves of the hourglass! I warn you: those who do not embrace the greater path shall become victims of its passing!', null, null, null, null, null, null, null, 'Пришло время как следует встряхнуть шестеренки вселенной! Довольно быть рабами времени! Те же из вас кто не примет этого великого пути, будут сметены!', '10400', '1', '0', '0', 'aeonus SAY_ENTER');
INSERT IGNORE INTO `script_texts` VALUES ('17879', '-1269008', 'You have outstayed your welcome, Timekeeper. Begone!', null, null, null, null, null, null, null, 'Тебе здесь больше не рады, Хранитель Времени, уходи!', '10413', '1', '0', '0', 'chrono_lord_deja SAY_BANISH');
INSERT IGNORE INTO `script_texts` VALUES ('17879', '-1269009', 'I told you it was a fool\'s quest!', null, null, null, null, null, null, null, 'Я ведь предупреждал тебя от бессмысленности твоей затеи!', '10415', '1', '0', '0', 'chrono_lord_deja SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('17879', '-1269007', 'If you will not cease this foolish quest, then you will die!', null, null, null, null, null, null, null, 'Откажитесь от своей затеи, или умрите!', '10414', '1', '0', '0', 'chrono_lord_deja SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17880', '-1269004', 'Your days are done.', null, null, null, null, null, null, null, 'Вот и все.', '10446', '1', '0', '0', 'temporus SAY_SLAY2');
INSERT IGNORE INTO `script_texts` VALUES ('17880', '-1269005', 'My death means ... little.', null, null, null, null, null, null, null, 'Моя смерть….ничего не значит.', '10447', '1', '0', '0', 'temporus SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('17879', '-1269006', 'Why do you aid the Magus? Just think of how many lives could be saved if the portal is never opened, if the resulting wars could be erased ...', null, null, null, null, null, null, null, 'Почему вы помогаете Магу? Только подумайте, скольких смертей можно было бы избежать, сколько воин бы не произошло, если бы портал никогда не открылся...', '10412', '1', '0', '0', 'chrono_lord_deja SAY_ENTER');
INSERT IGNORE INTO `script_texts` VALUES ('17880', '-1269001', 'So be it ... you have been warned.', null, null, null, null, null, null, null, 'Да будет так ... Вы предупреждены.', '10444', '1', '0', '0', 'temporus SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17880', '-1269002', 'Time... sands of time is run out for you.', null, null, null, null, null, null, null, 'Хранитель Времени... Твой срок истек.', '10443', '1', '0', '0', 'temporus SAY_BANISH');
INSERT IGNORE INTO `script_texts` VALUES ('17880', '-1269003', 'You should have left when you had the chance.', null, null, null, null, null, null, null, 'У тебя была возможность остаться в живых.', '10445', '1', '0', '0', 'temporus SAY_SLAY1');
INSERT IGNORE INTO `script_texts` VALUES ('17880', '-1269000', 'Why do you persist? Surely you can see the futility of it all. It is not too late! You may still leave with your lives ...', null, null, null, null, null, null, null, 'Почему вы сопротивляетесь? Разве вы не видите, что всё напрасно... Вы еще можете уйти отсюда живыми!', '10442', '1', '0', '0', 'temporus SAY_ENTER');
INSERT IGNORE INTO `script_texts` VALUES ('10184', '-1249003', 'It seems you\'ll need another lesson, mortals!', null, null, null, null, null, null, null, 'Кажется, вам будет нужен другой урок, смертные!', '0', '1', '0', '293', 'onyxia SAY_PHASE_3_TRANS');
INSERT IGNORE INTO `script_texts` VALUES ('10184', '-1249004', 'takes in a deep breath...', null, null, null, null, null, null, null, 'глубоко вздыхает...', '0', '1', '0', '0', 'onyxia EMOTE_BREATH');
INSERT IGNORE INTO `script_texts` VALUES ('10184', '-1249001', 'Learn your place mortal!', null, null, null, null, null, null, null, 'Знайте своё место, смертные?', '0', '1', '0', '0', 'onyxia SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('10184', '-1249002', 'This meaningless exertion bores me. I\'ll incinerate you all from above!', null, null, null, null, null, null, null, 'Эта бессмыслица наводит скуку на меня. Я испепелю вас всех сверху до низу!', '0', '1', '0', '254', 'onyxia SAY_PHASE_2_TRANS');
INSERT IGNORE INTO `script_texts` VALUES ('10184', '-1249000', 'How fortuitous. Usually, I must leave my lair to feed.', null, null, null, null, null, null, null, 'Как неожиданно. Обычно, я должен оставлять свое логово, чтобы питаться.', '0', '1', '0', '0', 'onyxia SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('9503', '-1230000', 'Ah, hits the spot!', null, null, null, null, null, null, null, 'Ах, приходится по вкусу!', '0', '0', '0', '0', 'rocknot SAY_GOT_BEER');
INSERT IGNORE INTO `script_texts` VALUES ('9019', '-1230001', 'Come to aid the Throne!', null, null, null, null, null, null, null, 'Приезжайте, чтобы помочь Короне!', '0', '1', '0', '0', 'dagran SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('9019', '-1230002', 'Hail to the king, baby!', null, null, null, null, null, null, null, 'Салют королю, ребенку!', '0', '1', '0', '0', 'dagran SAY_SLAY');
INSERT IGNORE INTO `script_texts` VALUES ('3974', '-1189021', 'Release the hounds!', null, null, null, null, null, null, null, 'Спустится!', '5841', '1', '0', '0', 'loksey SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('4543', '-1189018', 'More... More souls.', null, null, null, null, null, null, null, 'Еще... Еще одна душа.', '5845', '1', '0', '0', 'thalnos SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('6487', '-1189019', 'You will not defile these mysteries!', null, null, null, null, null, null, null, 'Вы не оскверните наши таинства!', '5842', '1', '0', '0', 'doan SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('6487', '-1189020', 'Burn in righteous fire!', null, null, null, null, null, null, null, 'Сгорите в праведном огне!', '5843', '1', '0', '0', 'doan SAY_SPECIALAE');
INSERT IGNORE INTO `script_texts` VALUES ('4543', '-1189017', 'No rest, for the angry dead.', null, null, null, null, null, null, null, 'Нет покоя свирепому мертвецу.', '5846', '1', '0', '0', 'thalnos SAY_HEALTH');
INSERT IGNORE INTO `script_texts` VALUES ('4543', '-1189016', 'We hunger for vengeance.', null, null, null, null, null, null, null, 'Мы жаждем мести.', '5844', '1', '0', '0', 'thalnos SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('3983', '-1189014', 'Purged by pain!', null, null, null, null, null, null, null, 'Искупи свои грехи болью!', '5848', '1', '0', '0', 'vishas SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('3983', '-1189015', 'The monster got what he deserved.', null, null, null, null, null, null, null, 'Монстр получил то, чего он заслужил.', '0', '0', '1', '0', 'vishas SAY_TRIGGER_VORREL');
INSERT IGNORE INTO `script_texts` VALUES ('3983', '-1189013', 'I\'ll rip the secrets from your flesh!', null, null, null, null, null, null, null, 'Я вырву эти тайны из вашей плоти!', '5850', '1', '0', '0', 'vishas SAY_HEALTH2');
INSERT IGNORE INTO `script_texts` VALUES ('3977', '-1189009', 'The Light has spoken!', null, null, null, null, null, null, null, 'Свет изрёк своё слово!', '5839', '1', '0', '0', 'whitemane SAY_WH_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('3977', '-1189010', 'Arise, my champion!', null, null, null, null, null, null, null, 'Восстань, защитник мой!', '5840', '1', '0', '0', 'whitemane SAY_WH_RESSURECT');
INSERT IGNORE INTO `script_texts` VALUES ('3983', '-1189011', 'Tell me... tell me everything!', null, null, null, null, null, null, null, 'Вы расскажите мне... все!', '5847', '1', '0', '0', 'vishas SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('3983', '-1189012', 'Naughty secrets!', null, null, null, null, null, null, null, 'Греховные тайны!', '5849', '1', '0', '0', 'vishas SAY_HEALTH1');
INSERT IGNORE INTO `script_texts` VALUES ('3975', '-1189003', 'Hah, is that all?', null, null, null, null, null, null, null, 'Ха, и это все?', '5831', '1', '0', '0', 'herod SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('3975', '-1189004', '%s becomes enraged!', null, null, null, null, null, null, null, '%s приходит в ярость!', '0', '2', '0', '0', 'herod EMOTE_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('3976', '-1189005', 'Infidels! They must be purified!', null, null, null, null, null, null, null, 'Нечестивцы! Да покарает их свет!', '5835', '1', '0', '0', 'mograine SAY_MO_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('3976', '-1189006', 'Unworthy!', null, null, null, null, null, null, null, 'Недостойные!', '5836', '1', '0', '0', 'mograine SAY_MO_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('3976', '-1189007', 'At your side, milady!', null, null, null, null, null, null, null, 'Слушаюсь, миледи!', '5837', '1', '0', '0', 'mograine SAY_MO_RESSURECTED');
INSERT IGNORE INTO `script_texts` VALUES ('3977', '-1189008', 'What, Mograine has fallen? You shall pay for this treachery!', null, null, null, null, null, null, null, 'Магрейн пал? Вы заплатите за своё вероломство!', '5838', '1', '0', '0', 'whitemane SAY_WH_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('3975', '-1189001', 'Blades of Light!', null, null, null, null, null, null, null, 'Клинки Света!', '5832', '1', '0', '0', 'herod SAY_WHIRLWIND');
INSERT IGNORE INTO `script_texts` VALUES ('3975', '-1189002', 'Light, give me strength!', null, null, null, null, null, null, null, 'Свет, дай мне силу!', '5833', '1', '0', '0', 'herod SAY_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('7358', '-1129002', 'Come, spirits, attend your master!', null, null, null, null, null, null, null, 'Придите, духи, услышьте своего господина!', '5829', '1', '0', '0', 'amnennar SAY_SUMMON30');
INSERT IGNORE INTO `script_texts` VALUES ('7358', '-1129003', 'I am the hand of the Lich King!', null, null, null, null, null, null, null, 'Я длань Короля Лича!', '5827', '1', '0', '0', 'amnennar SAY_HP');
INSERT IGNORE INTO `script_texts` VALUES ('7358', '-1129004', 'Too...easy!', null, null, null, null, null, null, null, 'Слишком... просто!', '5826', '1', '0', '0', 'amnennar SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('3975', '-1189000', 'Ah, I have been waiting for a real challenge!', null, null, null, null, null, null, null, 'Аа, наконец-то достойный противник!', '5830', '1', '0', '0', 'herod SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1070005', 'looks at you unexpectadly.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'cluck EMOTE_H_HELLO');
INSERT IGNORE INTO `script_texts` VALUES ('620', '-1070006', 'starts pecking at the feed.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'cluck EMOTE_CLUCK_TEXT2');
INSERT IGNORE INTO `script_texts` VALUES ('7358', '-1129000', 'You\'ll never leave this place... alive.', null, null, null, null, null, null, null, 'Вы не уйдете от сюда... живыми.', '5825', '1', '0', '0', 'amnennar SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('7358', '-1129001', 'To me, my servants!', null, null, null, null, null, null, null, 'Ко мне, слуги мои!', '5828', '1', '0', '0', 'amnennar SAY_SUMMON60');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1070002', 'Please tell me that you didn\'t just do what I think you just did. Please tell me that I\'m not going to have to hurt you...', null, null, null, null, null, null, null, 'Пожалуйста скажите мне, что вы не просто делали то, что я думаю, что вы только что сделали. Пожалуйста скажите мне, что мне не придется причинить вам боль...', '0', '0', '0', '0', 'SAY_GUARD_SIL_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1070003', 'As if we don\'t have enough problems, you go and create more!', null, null, null, null, null, null, null, 'Как будто у нас не достаточно проблем, вы идете и создаете больше!', '0', '0', '0', '0', 'SAY_GUARD_SIL_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('620', '-1070004', 'looks up at you quizzically. Maybe you should inspect it?', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'cluck EMOTE_A_HELLO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1060002', 'REUSE', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1060001', 'REUSE', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1060000', 'REUSE', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE');
INSERT IGNORE INTO `script_texts` VALUES ('7228', '-1070000', 'None may steal the secrets of the makers!', null, null, null, null, null, null, null, 'Никто не похитит тайны создателей!', '5851', '1', '0', '0', 'ironaya SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1070001', 'Taste blade, mongrel!', null, null, null, null, null, null, null, 'Попробуй лезвие, полукровка!', '0', '0', '0', '0', 'SAY_GUARD_SIL_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('4508', '-1047009', 'I will rather rest a moment and come again to breath, before I return to Ratchet.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Willix');
INSERT IGNORE INTO `script_texts` VALUES ('4508', '-1047010', 'Many thanks for your help.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Willix');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1060005', 'REUSE', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1060004', 'REUSE', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1060003', 'REUSE', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE');
INSERT IGNORE INTO `script_texts` VALUES ('4508', '-1047007', 'I am glad that we are out again from this damned ditch. However, up here it is not much better!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Willix');
INSERT IGNORE INTO `script_texts` VALUES ('4508', '-1047008', 'Finally! I am glad that I come, finally out here.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Willix');
INSERT IGNORE INTO `script_texts` VALUES ('4508', '-1047005', 'I do not understand how these disgusting animals can live at such a place.... puh as this stinks!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Willix');
INSERT IGNORE INTO `script_texts` VALUES ('4508', '-1047006', 'I think, I see a way how we come out of this damned thorn tangle.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Willix');
INSERT IGNORE INTO `script_texts` VALUES ('4508', '-1047002', 'Help! Get this Raging Agam\'ar from me!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Willix');
INSERT IGNORE INTO `script_texts` VALUES ('4508', '-1047003', 'In this ditch there are Blueleaf Tuber! As if the gold waited only to be dug out, I say it you!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Willix');
INSERT IGNORE INTO `script_texts` VALUES ('4508', '-1047004', 'Danger is behind every corner.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Willix');
INSERT IGNORE INTO `script_texts` VALUES ('4508', '-1047001', 'There on top resides Charlga Razorflank. The damned old Crone.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Willix');
INSERT IGNORE INTO `script_texts` VALUES ('4508', '-1047000', 'Phew! Finally, out here. However, it will not become easy. Detain your eyes after annoyance.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Willix');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043017', 'Attacked! Help get this $N off of me!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Disciple SAY_ATTACKED');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043016', 'We must go and gather with the other Disciples. There is much work to be done before I can make another attempt to restore the Barrens. Farewell, brave souls!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Naralex SAY_FAREWELL');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043015', 'Ah, to be pulled from the dreaded nightmare! I thank you, my loyal Disciple, along with your brave companions.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Naralex SAY_THANK_YOU');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043014', 'At last! Naralex awakes from the nightmare.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Disciple SAY_NARALEX_AWAKES');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043013', 'I AM AWAKE, AT LAST!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'Naralex SAY_I_AM_AWAKE');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043012', 'This Mutanus the Devourer is a minion from Naralex\'s nightmare no doubt!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Disciple SAY_MUTANUS_THE_DEVOURER');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043011', '%s dreams up a horrendous vision. Something stirs beneath the murky waters.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Naralex EMOTE_HORRENDOUS_VISION');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043010', '%s writhes in agony. The Disciple seems to be breaking through.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Naralex EMOTE_WRITHE_IN_AGONY');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043009', '%s tosses fitfully in troubled sleep.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Naralex EMOTE_TROUBLED_SLEEP');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043008', '%s begins to perform the awakening ritual on Naralex.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Disciple EMOTE_AWAKENING_RITUAL');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043007', 'Protect me brave souls as I delve into this Emerald Dream to rescue Naralex and put an end to this corruption!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Disciple SAY_EMERALD_DREAM');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043006', 'Beyond this corridor, Naralex lies in fitful sleep. Let us go awaken him before it is too late.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Disciple SAY_BEYOND_THIS_CORRIDOR');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043005', 'The caverns have been purified. To Naralex\'s chamber we go!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Disciple SAY_CAVERNS_PURIFIED');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043004', 'Within this circle of fire I must cast the spell to banish the spirits of the slain Fanglords.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Disciple SAY_BANISH_THE_SPIRITS');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043003', 'Come. We must continue. There is much to be done before we can pull Naralex from his nightmare.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Disciple SAY_MUST_CONTINUE');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043002', 'These caverns were once a temple of promise for regrowth in the Barrens. Now, they are the halls of nightmares.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Disciple SAY_TEMPLE_OF_PROMISE');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043001', 'I must make the necessary preparations before the awakening ritual can begin. You must protect me!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Disciple SAY_MAKE_PREPARATIONS');
INSERT IGNORE INTO `script_texts` VALUES ('3678', '-1043000', 'At last! Naralex can be awakened! Come aid me, brave adventurers!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'Disciple SAY_AT_LAST');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1036001', 'We\'re under attack! A vast, ye swabs! Repel the invaders!', null, null, null, null, null, null, null, 'Нас атакуют! Вперед швабры лохматые! Сметем захватчиков!', '5777', '1', '7', '0', 'smite INST_SAY_ALARM2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1036000', 'You there, check out that noise!', null, null, null, null, null, null, null, 'Эй, вы, гляньте, что там за шум!', '5775', '1', '7', '0', 'smite INST_SAY_ALARM1');
INSERT IGNORE INTO `script_texts` VALUES ('3849', '-1033007', 'About time someone killed the wretch.', null, null, null, null, null, null, null, 'Надеюсь, кто-нибудь, однажды, уничтожит этих призренных.', '0', '0', '1', '1', 'prisoner adamant SAY_BOSS_DIE_AD');
INSERT IGNORE INTO `script_texts` VALUES ('3850', '-1033008', 'For once I agree with you... scum.', null, null, null, null, null, null, null, 'Я согласен с тобой... ничтожество.', '0', '0', '7', '1', 'prisoner ashcrombe SAY_BOSS_DIE_AS');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1033006', 'Good luck with Arugal. I must hurry back to Hadrec now.', null, null, null, null, null, null, null, 'Удачи с Аругалом. Я должен поспешить обратно к Хадрику.', '0', '0', '1', '1', 'prisoner adamant SAY_POST2_DOOR_AD');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1033005', 'There we go!', null, null, null, null, null, null, null, 'Пошлите.', '0', '0', '1', '1', 'prisoner adamant SAY_POST1_DOOR_AD');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1033004', 'You are indeed courageous for wanting to brave the horrors that lie beyond this door.', null, null, null, null, null, null, null, 'Ты действительно бесстрашный, внуши страх этим придуркам, что находятся за дверью.', '0', '0', '1', '1', 'prisoner adamant SAY_OPEN_DOOR_AD');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1033003', 'Free from this wretched cell at last! Let me show you to the courtyard....', null, null, null, null, null, null, null, 'Освободим этих несчастных! Проведи меня во внутрь...', '0', '0', '1', '1', 'prisoner adamant SAY_FREE_AD');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1033002', 'There it is! Wide open. Good luck to you conquering what lies beyond. I must report back to the Kirin Tor at once!', null, null, null, null, null, null, null, 'Вот оно! Дверь открылась. Удачи тебе разузнать, что лежит дальше. Я должен вернуться обратно в Кирин Тор!', '0', '0', '7', '1', 'prisoner ashcrombe SAY_POST_DOOR_AS');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1033001', 'I have just the spell to get this door open. Too bad the cell doors weren\'t locked so haphazardly.', null, null, null, null, null, null, null, 'Я знаю заклинание, чтобы открыть эту дверь. Жаль, что дверь закрыта на все замки.', '0', '0', '7', '1', 'prisoner ashcrombe SAY_OPEN_DOOR_AS');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1033000', 'Follow me and I\'ll open the courtyard door for you.', null, null, null, null, null, null, null, 'Спасибо за освобождение, я открою эту дверь для тебя, и затем сам покину это место.', '0', '0', '7', '1', 'prisoner ashcrombe SAY_FREE_AS');
INSERT IGNORE INTO `script_texts` VALUES ('1755', '-1000463', 'The Defias shall succeed! No meek adventurer will stop us!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('7766', '-1000462', 'That\'s it! That\'s what you were waiting for! KILL THEM!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('1754', '-1000461', 'Hmm, it could be that meddle Shaw. I will see what I can discover. Be off with you. I\'ll contact you again soon.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('1755', '-1000460', 'VanCleef sends word that the plans are underway. But he\'s hear rumors about someone snooping about.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('1754', '-1000459', 'There you are. What news from Westfall?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('1756', '-1000458', 'Yes, sir!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('8856', '-1000456', 'I shall use the time wisely, milord. Thank you.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('1754', '-1000457', 'It\'s time for my meditation, leave me.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('1754', '-1000455', 'Ah, thank you kindly. I will leave you to the library while I tend to this small matter.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('8856', '-1000454', 'Milord, your guest has arrived. He awaits your presence.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('8856', '-1000453', 'Thank you. The Light be with you both.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('1756', '-1000452', 'Of course. He awaits you in the library.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('8856', '-1000451', 'Good day to you both. I would speak to Lord Lescovar.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('7766', '-1000450', 'Wait here. Spybot will make Lescovar come out as soon as possible. Be ready! Attack only after you\'ve overheard their conversation.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('8856', '-1000499', 'By your command!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('17077', '-1000498', 'Welcome, kind spirit. What has brought you to us?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ancestral wolf SAY_WOLF_WELCOME');
INSERT IGNORE INTO `script_texts` VALUES ('17077', '-1000496', '%s lifts its head into the air, as if listening for something.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'ancestral wolf EMOTE_WOLF_LIFT_HEAD');
INSERT IGNORE INTO `script_texts` VALUES ('17077', '-1000497', '%s lets out a howl that rings across the mountains to the north and motions for you to follow.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'ancestral wolf EMOTE_WOLF_HOWL');
INSERT IGNORE INTO `script_texts` VALUES ('18210', '-1000488', 'It is best that we split up now, in case they send more after us. Hopefully one of us will make it back to Garrosh. Farewell stranger.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_COMPLETE');
INSERT IGNORE INTO `script_texts` VALUES ('18210', '-1000487', 'FROST SHOCK!!!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_SHOCK');
INSERT IGNORE INTO `script_texts` VALUES ('18210', '-1000486', 'Ride the lightning, filth!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_LIGHTNING');
INSERT IGNORE INTO `script_texts` VALUES ('18210', '-1000485', 'Where do you think you\'re going? Kill them all!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_MORE_REPLY');
INSERT IGNORE INTO `script_texts` VALUES ('18210', '-1000484', 'More of them coming! Watch out!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_MORE');
INSERT IGNORE INTO `script_texts` VALUES ('18210', '-1000483', 'Don\'t let them escape! Kill the strong one first!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_NO_ESCAPE');
INSERT IGNORE INTO `script_texts` VALUES ('18210', '-1000482', 'Look out!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_START');
INSERT IGNORE INTO `script_texts` VALUES ('11218', '-1000445', '%s wakes up!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'kerlonian EMOTE_KER_AWAKEN');
INSERT IGNORE INTO `script_texts` VALUES ('11218', '-1000444', 'Oh, I can see Liladris from here... Tell her I\'m here, won\'t you?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_END');
INSERT IGNORE INTO `script_texts` VALUES ('11218', '-1000443', 'It\'s quiet... Too quiet...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_ALERT_2');
INSERT IGNORE INTO `script_texts` VALUES ('11218', '-1000440', 'Oh, I am so tired...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_SLEEP_3');
INSERT IGNORE INTO `script_texts` VALUES ('11218', '-1000441', 'You don\'t mind if I stop here for a moment, do you?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_SLEEP_4');
INSERT IGNORE INTO `script_texts` VALUES ('11218', '-1000442', 'Be on the alert! The Blackwood furbolgs are numerous in the area...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_ALERT_1');
INSERT IGNORE INTO `script_texts` VALUES ('11218', '-1000439', 'Yaaaaawwwwwnnnn...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_SLEEP_2');
INSERT IGNORE INTO `script_texts` VALUES ('11218', '-1000436', '%s suddenly falls asleep', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'kerlonian EMOTE_KER_SLEEP_2');
INSERT IGNORE INTO `script_texts` VALUES ('11218', '-1000437', '%s begins to drift off...', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'kerlonian EMOTE_KER_SLEEP_3');
INSERT IGNORE INTO `script_texts` VALUES ('11218', '-1000438', 'This looks like the perfect place for a nap...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_SLEEP_1');
INSERT IGNORE INTO `script_texts` VALUES ('11218', '-1000435', '%s looks very sleepy...', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'kerlonian EMOTE_KER_SLEEP_1');
INSERT IGNORE INTO `script_texts` VALUES ('11218', '-1000434', 'Liladris has been waiting for me at Maestra\'s Post, so we should make haste, $N.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_START');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000433', '%s fades away after a long pause.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'ringo EMOTE_RIN_END_8');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000432', 'Ringo? Wake up! Don\'t worry, I\'ll take care of you.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_SPR_END_7');
INSERT IGNORE INTO `script_texts` VALUES ('6784', '-1000431', 'Ugh.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_END_6');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000430', '%s stands up after a short pause.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'ringo EMOTE_RIN_END_5');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000429', '%s collapses onto the ground.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'ringo EMOTE_RIN_END_4');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000427', 'Ringo! You\'re okay!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_SPR_END_2');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000428', 'Oh... I\'m feeling faint...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_END_3');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000426', 'Spraggle! I didn\'t think I\'d make it back!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_END_1');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000425', 'How am I feeling? Quite soaked, thank you.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_WAKE_4');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000424', 'Yes, I must go on.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_WAKE_3');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000423', 'I am feeling a little better now, thank you.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_WAKE_2');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000422', 'Where... Where am I?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_WAKE_1');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000421', 'I\'m not feeling so well...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_FAINT_4');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000420', 'Uuuuuuggggghhhhh....', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_FAINT_3');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000419', 'Maybe... you could carry me?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_FAINT_2');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000418', 'The heat... I can\'t take it...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_FAINT_1');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000417', 'Remember, if I faint again, the water that Spraggle gave you will revive me.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_START_2');
INSERT IGNORE INTO `script_texts` VALUES ('9999', '-1000416', 'Well, I\'m not sure how far I\'ll make it in this state... I\'m feeling kind of faint...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_START_1');
INSERT IGNORE INTO `script_texts` VALUES ('4966', '-1000415', '%s, too injured, gives up the chase.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'hendel EMOTE_SURRENDER');
INSERT IGNORE INTO `script_texts` VALUES ('4966', '-1000414', 'We can only stay a few moments before returning to the tower. If you wish to speak to us more you may find us there.', null, null, null, null, null, null, null, null, '0', '0', '7', '0', 'hendel SAY_PROGRESS_4_TER');
INSERT IGNORE INTO `script_texts` VALUES ('4966', '-1000413', 'I apologize for taking so long to get here. I wanted Lady Proudmoore to be present also.', null, null, null, null, null, null, null, 'Извиняюсь что задержался. Я хотел, чтобы Леди Праудмур также присутствовала.', '0', '0', '7', '0', 'hendel SAY_PROGRESS_3_TER');
INSERT IGNORE INTO `script_texts` VALUES ('3568', '-1000412', 'Please... please... Miss Proudmore. I didn\'t mean to...', null, null, null, null, null, null, null, 'Пожалуйста ... пожалуйста ... Леди Праудмур. Я не хотел ...', '0', '0', '7', '0', 'hendel SAY_PROGRESS_2_HEN');
INSERT IGNORE INTO `script_texts` VALUES ('3568', '-1000411', 'Why don\'t we deal with you now, Hendel? Lady Proudmoore will speak for you back in the tower.', null, null, null, null, null, null, null, 'Почему бы нам не разобраться с вами сейчас, Хендел? Леди Джайна Праудмур ждет доклада в башне.', '0', '0', '7', '0', 'hendel SAY_PROGRESS_1_TER');
INSERT IGNORE INTO `script_texts` VALUES ('10638', '-1000410', 'Here they come! Defend yourself!', null, null, null, null, null, null, null, 'Они здесь! Защитите себя!', '0', '0', '1', '5', 'kanati SAY_KAN_START');
INSERT IGNORE INTO `script_texts` VALUES ('7780', '-1000409', 'You find it, you keep it! Don\'t tell no one that Rin\'ji talked to you!', null, null, null, null, null, null, null, 'Вы нашли то,что спрятал Ринджи. И вы не кому не скажите об этом!', '0', '0', '1', '0', 'SAY_RIN_PROGRESS_2');
INSERT IGNORE INTO `script_texts` VALUES ('7780', '-1000408', 'Rin\'ji will tell you secret now... $n, should go to the Overlook Cliffs. Rin\'ji hid something on island  there', null, null, null, null, null, null, null, 'Ринджи будет говорить вам тайну сейчас... $n, ты должен идти к Отвесным скалам. Ринджи спрятал секрет на острове  там...', '0', '0', '1', '0', 'SAY_RIN_PROGRESS_1');
INSERT IGNORE INTO `script_texts` VALUES ('7780', '-1000407', 'Rin\'ji can see road now, $n. Rin\'ji knows the way home.', null, null, null, null, null, null, null, 'Ринджи может видеть дорогу сейчас $n. Ринджи знает путь домой.', '0', '0', '1', '0', 'SAY_RIN_COMPLETE');
INSERT IGNORE INTO `script_texts` VALUES ('6182', '-1000406', 'Rin\'ji is being attacked!', null, null, null, null, null, null, null, 'Ринджи есть атакованный!', '0', '0', '1', '0', 'SAY_RIN_HELP_2');
INSERT IGNORE INTO `script_texts` VALUES ('6182', '-1000405', 'Rin\'ji needs help!', null, null, null, null, null, null, null, 'Ринджи не может помогать!', '0', '0', '1', '0', 'SAY_RIN_HELP_1');
INSERT IGNORE INTO `script_texts` VALUES ('6182', '-1000404', 'Attack my sisters! The troll must not escape!', null, null, null, null, null, null, null, 'Нападите на моих охранников! Тролль не должен сидет за решеткой!', '0', '0', '0', '0', 'SAY_RIN_BY_OUTRUNNER');
INSERT IGNORE INTO `script_texts` VALUES ('6182', '-1000403', 'Rin\'ji is free!', null, null, null, null, null, null, null, 'Ринджи свободен!', '0', '0', '0', '0', 'SAY_RIN_FREE');
INSERT IGNORE INTO `script_texts` VALUES ('6182', '-1000402', 'Taste your world\'s corruption!', null, null, null, null, null, null, null, 'Почувствуйте как увядает ваш мир!', '0', '1', '0', '0', 'emeriss SAY_CASTCORRUPTION');
INSERT IGNORE INTO `script_texts` VALUES ('14889', '-1000401', 'Hope is a DISEASE of the soul! This land shall wither and die!', null, null, null, null, null, null, null, 'Надежда - это болезнь души! Этот мир должен засохнуть и умереть!', '0', '1', '0', '0', 'emeriss SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('20021', '-1000400', 'Children of Madness - I release you upon this world!', null, null, null, null, null, null, null, 'Безумцы! Я освобожу от вас этот мир!', '0', '1', '0', '0', 'taerar SAY_SUMMONSHADE');
INSERT IGNORE INTO `script_texts` VALUES ('20021', '-1000399', 'Peace is but a fleeting dream! Let the NIGHTMARE reign!', null, null, null, null, null, null, null, 'Мир - это лишь мимолетный сон! Пусть темнота правит миром!', '0', '1', '0', '0', 'taerar SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('5955', '-1000398', 'And try not to get lost this time...', null, null, null, null, null, null, null, 'И постараюсь больше не теряться в это время...', '0', '0', '0', '0', 'tooga SAY_TORT_POST_6');
INSERT IGNORE INTO `script_texts` VALUES ('5955', '-1000397', 'Yes Torta. Whatever your heart desires...', null, null, null, null, null, null, null, 'Да Торта. Любые твои желания... Я готов их выполнить немедленно!', '0', '0', '0', '0', 'tooga SAY_TOOG_POST_5');
INSERT IGNORE INTO `script_texts` VALUES ('5955', '-1000396', 'Enough with the rambling. I am starving! Now, get your dusty shell into that ocean and bring momma some grub.', null, null, null, null, null, null, null, 'Хватит путешествий. Я изголодался! Теперь найти бы в этом океане песка какую небудь еду и отнести моей супруге.', '0', '0', '0', '0', 'tooga SAY_TORT_POST_4');
INSERT IGNORE INTO `script_texts` VALUES ('5955', '-1000395', 'My dearest Torta. I have been gone for so long. Finally we are reunited. At long last our love can blossom again.', null, null, null, null, null, null, null, 'Моя дорогая Торта. Я так долго отсутствовал, но наконец мы воссоединились. Теперь наша любовь возродится!', '0', '0', '0', '0', 'tooga SAY_TOOG_POST_3');
INSERT IGNORE INTO `script_texts` VALUES ('5955', '-1000394', 'You have any idea how long I\'ve been waiting here? And where\'s dinner? All that time gone and nothing to show for it?', null, null, null, null, null, null, null, 'Вы не знаете что я тут так долго я жду? И где еда? Я потратил столько времени и все в пустую?', '0', '0', '0', '0', 'tooga SAY_TORT_POST_2');
INSERT IGNORE INTO `script_texts` VALUES ('5955', '-1000393', 'Torta, my love! I have returned at long last.', null, null, null, null, null, null, null, 'О Торта, моя любовь! В конце концов я вернулся.', '0', '0', '0', '0', 'tooga SAY_TOOG_POST_1');
INSERT IGNORE INTO `script_texts` VALUES ('5955', '-1000392', 'Torta must be so worried.', null, null, null, null, null, null, null, 'Торта должно быть очень волнуется.', '0', '0', '0', '0', 'tooga SAY_TOOG_WORRIED');
INSERT IGNORE INTO `script_texts` VALUES ('5955', '-1000391', 'I\'m Thirsty.', null, null, null, null, null, null, null, 'Меня зовут Туги и я хочу пить!', '0', '0', '0', '0', 'tooga SAY_TOOG_THIRST');
INSERT IGNORE INTO `script_texts` VALUES ('21027', '-1000390', 'Thank you, $n. Please return to my brethren at the Altar of Damnation, near the Hand of Gul\'dan, and tell them that Wilda is safe. May the Earthmother watch over you...', null, null, null, null, null, null, null, 'Спасибо, $n. Пожалуйста вернитесь к моим братьям в Алтарь Проклятия, возле Руки Гулдана, и скажи им, что Вильда в безопасности. Возможно, Мать-Земля охраняет вас...', '0', '0', '0', '0', 'wilda SAY_WIL_END');
INSERT IGNORE INTO `script_texts` VALUES ('21027', '-1000389', 'It shouldn\'t be much further, $n. The exit is just up ahead.', null, null, null, null, null, null, null, 'Этого не должно более повториться, $n. Лагерь нагов  заканчивается, вперед!', '0', '0', '0', '0', 'wilda SAY_WIL_JUST_AHEAD');
INSERT IGNORE INTO `script_texts` VALUES ('21027', '-1000388', 'The tumultuous nature of the great waterways of Azeroth and Draenor are a direct result of tormented water spirits.', null, null, null, null, null, null, null, 'Беспорядочная природа больших водных артерий Азерота и Дренора - прямой результат отравления водяных духов.', '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS5');
INSERT IGNORE INTO `script_texts` VALUES ('21027', '-1000387', 'Lady Vashj must answer for these atrocities. She must be brought to justice!', null, null, null, null, null, null, null, 'Леди Вайш должена ответить за зверства. Она должна предстать перед правосудием!', '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS4');
INSERT IGNORE INTO `script_texts` VALUES ('21027', '-1000386', 'Now we must find the exit.', null, null, null, null, null, null, null, 'Сейчас мы должны найти выход.', '0', '0', '0', '0', 'wilda SAY_WIL_FIND_EXIT');
INSERT IGNORE INTO `script_texts` VALUES ('21027', '-1000384', 'Grant me protection $n, I must break trough their foul magic!', null, null, null, null, null, null, null, 'Спасибо за защиту $n, я должена остановить их грязные действия!', '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS1');
INSERT IGNORE INTO `script_texts` VALUES ('21027', '-1000385', 'The naga of Coilskar are exceptionally cruel to their prisoners. It is a miracle that I survived inside that watery prison for as long as I did. Earthmother be praised.', null, null, null, null, null, null, null, 'Мирмидоны исключительно жестоки к своим заключенным. Это - чудо, что я выжила внутри этой водяной тюрьмы. Мать-Земля может гордиться мной.', '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS2');
INSERT IGNORE INTO `script_texts` VALUES ('21027', '-1000383', 'Naga attackers! Defend yourself!', null, null, null, null, null, null, null, 'Нага атакует! Защитите меня!', '0', '0', '0', '0', 'wilda SAY_WIL_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('21027', '-1000381', 'I sense the tortured spirits, $n. They are this way, come quickly!', null, null, null, null, null, null, null, 'Я слышу измученных духов, $n. Они ждут моей помощи! Скорее!', '0', '0', '0', '1', 'wilda SAY_WIL_START');
INSERT IGNORE INTO `script_texts` VALUES ('21027', '-1000382', 'Watch out!', null, null, null, null, null, null, null, 'Остерегайтесь!', '0', '0', '0', '0', 'wilda SAY_WIL_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('3465', '-1000380', 'Captain Brightsun, $N here has freed me! $N, I am certain the Captain will reward your bravery.', null, null, null, null, null, null, null, 'Капитан Талотас Сияющий, $N освободил(а) меня! $N, я уверен что Капитан вознаградит вашу храбрость.', '0', '0', '1', '66', 'gilthares SAY_GIL_FREED');
INSERT IGNORE INTO `script_texts` VALUES ('3465', '-1000379', 'Ah, the sweet salt air of Ratchet.', null, null, null, null, null, null, null, 'Ах, сладкий соленый воздух Кабестана.', '0', '0', '1', '0', 'gilthares SAY_GIL_SWEET');
INSERT IGNORE INTO `script_texts` VALUES ('3465', '-1000378', 'Almost back to Ratchet! Let\'s keep up the pace...', null, null, null, null, null, null, null, 'Почти добрались до Кабестана! Бежим, держать темп...', '0', '0', '1', '0', 'gilthares SAY_GIL_ALMOST');
INSERT IGNORE INTO `script_texts` VALUES ('3465', '-1000377', 'Get this $C off of me!', null, null, null, null, null, null, null, 'со мной $C, скучно не будет!', '0', '0', '1', '0', 'gilthares SAY_GIL_AGGRO_4');
INSERT IGNORE INTO `script_texts` VALUES ('3465', '-1000376', '$C coming right at us!', null, null, null, null, null, null, null, '$C, бегут прямо на нас!', '0', '0', '1', '0', 'gilthares SAY_GIL_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('3465', '-1000375', '$C heading this way fast! Time for revenge!', null, null, null, null, null, null, null, 'Вендетта! Время мести.', '0', '0', '1', '0', 'gilthares SAY_GIL_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('3465', '-1000374', 'Help! $C attacking!', null, null, null, null, null, null, null, 'На помощь! $C на меня напали!', '0', '0', '1', '0', 'gilthares SAY_GIL_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('3465', '-1000373', 'Looks like the Southsea Freeboters are heavily entrenched on the coast. This could get rough.', null, null, null, null, null, null, null, 'Похоже головорезы устраиваются на побережье весьма основательно.', '0', '0', '1', '25', 'gilthares SAY_GIL_FREEBOOTERS');
INSERT IGNORE INTO `script_texts` VALUES ('3465', '-1000372', 'Now I feel better. Let\'s get back to Ratchet. Come on, $n.', null, null, null, null, null, null, null, 'Сейчас я чувствую себя лучше. Давайте добираться обратно в Кабестан. Идем $n.', '0', '0', '1', '23', 'gilthares SAY_GIL_PROCEED');
INSERT IGNORE INTO `script_texts` VALUES ('3465', '-1000371', 'At last! Free from Northwatch Hold! I need a moment to catch my breath...', null, null, null, null, null, null, null, 'Наконец! Я свободен от фанатиков из Терамора! Дай несколько минут перевести дыхание...', '0', '0', '1', '5', 'gilthares SAY_GIL_AT_LAST');
INSERT IGNORE INTO `script_texts` VALUES ('3465', '-1000370', 'Stay close, $n. I\'ll need all the help I can get to break out of here. Let\'s go!', null, null, null, null, null, null, null, 'Не покидай меня $n. Мне будет нужна твоя помощь, иначе я умру здесь... Как все остальные члены моей бедной команды.', '0', '0', '1', '1', 'gilthares SAY_GIL_START');
INSERT IGNORE INTO `script_texts` VALUES ('10646', '-1000369', 'Finally, free at last... I must be going now, thanks for helping me escape. I can get back to Freewind Post by myself.', null, null, null, null, null, null, null, 'Наконец то я свободна... Я должена сейчас идти, спасибо за помощь. Я смогу добраться обратно на Заставу Вольного Ветра.', '0', '0', '1', '0', 'lakota SAY_LAKO_END');
INSERT IGNORE INTO `script_texts` VALUES ('10646', '-1000366', 'Look out, the Grimtotem are upon us!', null, null, null, null, null, null, null, 'Берегитесь, Зловещий Тотем будет преследовать нас!', '0', '0', '1', '0', 'lakota SAY_LAKO_LOOK_OUT');
INSERT IGNORE INTO `script_texts` VALUES ('10646', '-1000367', 'Here they come.', null, null, null, null, null, null, null, 'Вот он', '0', '0', '1', '0', 'lakota SAY_LAKO_HERE_COME');
INSERT IGNORE INTO `script_texts` VALUES ('10646', '-1000368', 'More Grimtotems are coming this way!', null, null, null, null, null, null, null, 'Сейчас все таурены Зловещего Тотема придут сюда!', '0', '0', '1', '0', 'lakota SAY_LAKO_MORE');
INSERT IGNORE INTO `script_texts` VALUES ('10646', '-1000365', 'Be on guard... Arnak has some strange power over the Grimtotem... they will not be happy to see me escape.', null, null, null, null, null, null, null, 'Будьте на стороже... Арнак Зловещий Тотем не будет доволен моим бегством.', '0', '0', '1', '0', 'lakota SAY_LAKO_START');
INSERT IGNORE INTO `script_texts` VALUES ('17969', '-1000363', 'Now this looks familiar. If we keep heading east, I think we can... Ahh, Wyvern on the attack!', null, null, null, null, null, null, null, 'Теперь это выглядит знакомым. Если мы держим курс на восток, то я думаю, мы можем... Аах, Виверна! В атаку!', '0', '0', '1', '0', 'paoka SAY_WYVERN');
INSERT IGNORE INTO `script_texts` VALUES ('17969', '-1000364', 'Thanks a bunch... I can find my way back to Whitereach Post from here. Be sure to talk with Motega Firemane; perhaps you can keep him from sending me home.', null, null, null, null, null, null, null, '\"\"\"Спасибо за компанию... Отсюда я смогу найти дорогу до заставы Белого Плеса . Не забудте поговорить с Мотегой Огненной Гривой; возможно вы сможете уговорить его не наказывать меня.\"\"\"', '0', '0', '1', '0', 'paoka SAY_COMPLETE');
INSERT IGNORE INTO `script_texts` VALUES ('17969', '-1000362', 'Let\'s go $N. I am ready to reach Whitereach Post.', null, null, null, null, null, null, null, 'Давайте идти $N. Я готов отправиться в Белый Плес', '0', '0', '1', '0', 'paoka SAY_START');
INSERT IGNORE INTO `script_texts` VALUES ('17969', '-1000360', 'The strands of LIFE have been severed! The Dreamers must be avenged!', null, null, null, null, null, null, null, 'Нити жизни были разорваны! Дремлющий будет отомщен!', '0', '1', '0', '0', ' ysondre SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17969', '-1000361', 'Come forth, ye Dreamers � and claim your vengeance!', null, null, null, null, null, null, null, 'Идите ко мне, месть Дремлющего будет беспощадна!', '0', '1', '0', '0', ' ysondre SAY_SUMMONDRUIDS');
INSERT IGNORE INTO `script_texts` VALUES ('17900', '-1000359', 'Thank you for helping me. I know my way back from here.', null, null, null, null, null, null, null, 'Спасибо за помощь. Дальше я знаю путь.', '0', '0', '0', '0', 'KAYA_END');
INSERT IGNORE INTO `script_texts` VALUES ('10181', '-1000357', 'Let\'s go before they find out I\'m free!', null, null, null, null, null, null, null, 'Давайте уйдем, прежде чем они узнают что я свободен!', '0', '0', '0', '1', 'KAYA_SAY_START');
INSERT IGNORE INTO `script_texts` VALUES ('10181', '-1000358', 'Look out! We\'re under attack!', null, null, null, null, null, null, null, 'Берегитесь! Мы под нападением!', '0', '0', '0', '0', 'KAYA_AMBUSH');
INSERT IGNORE INTO `script_texts` VALUES ('18760', '-1000356', 'Okay, okay... gimme a minute to rest now. You gone and beat me up good.', null, null, null, null, null, null, null, 'Хорошо, хорошо... ты дал мне минуту, чтобы отдохнуть. Ты основательно меня избил, сумашедший.', '0', '0', '1', '14', 'calvin SAY_COMPLETE');
INSERT IGNORE INTO `script_texts` VALUES ('18760', '-1000355', 'All right, you win! I surrender! Just don\'t hurt me!', null, null, null, null, null, null, null, 'Ладно, ты победил! Я сдаюсь! Только не трогай меня, пожалуйста!', '0', '1', '0', '0', 'sprysprocket SAY_END');
INSERT IGNORE INTO `script_texts` VALUES ('18760', '-1000353', 'Just chill!', null, null, null, null, null, null, null, 'Как холодно!', '0', '4', '0', '0', 'sprysprocket SAY_WHISPER_CHILL');
INSERT IGNORE INTO `script_texts` VALUES ('18760', '-1000354', 'Stupid grenade picked a fine time to backfire! So much for high quality goblin engineering!', null, null, null, null, null, null, null, 'Тупая граната сломалась в самый неподходящий момент! Это называется высококачественная разработка гоблинов!', '0', '1', '0', '0', 'sprysprocket SAY_GRENADE_FAIL');
INSERT IGNORE INTO `script_texts` VALUES ('18588', '-1000352', '%s takes the Southfury moonstone and escapes into the river. Follow her!', null, null, null, null, null, null, null, '%s берёт Лунный камень из реки Строптивая и уходит обратно в реку. Следуйте за ней!', '0', '3', '0', '0', 'sprysprocket EMOTE_START');
INSERT IGNORE INTO `script_texts` VALUES ('9453', '-1000350', 'That kodo sure is a beauty. Wait a minute, where are my bifocals? Perhaps you should inspect the beast for me.', null, null, null, null, null, null, null, 'Этот кодо выглядит прекрасно. Минутку, где же мои очки? Надеюсь ты поухаживал за кодо для меня.', '0', '0', '0', '0', 'kodo round SAY_SMEED_HOME_3');
INSERT IGNORE INTO `script_texts` VALUES ('18262', '-1000351', 'You, there! Hand over that moonstone and nobody gets hurt!', null, null, null, null, null, null, null, 'Эй, вы! Отдайте этот Лунный камень и никто не пострадает!', '0', '1', '0', '0', 'sprysprocket SAY_START');
INSERT IGNORE INTO `script_texts` VALUES ('1978', '-1000346', 'Look out, $N! Enemies ahead!', null, null, null, null, null, null, null, 'Смотри, $N! Впереди враги!', '0', '0', '0', '0', 'kayra SAY_AMBUSH2');
INSERT IGNORE INTO `script_texts` VALUES ('11856', '-1000347', 'We\'re almost to the refuge! Let\'s go.', null, null, null, null, null, null, null, 'Мы все ищем убежище! Пойдемте.', '0', '0', '0', '0', 'kayra SAY_END');
INSERT IGNORE INTO `script_texts` VALUES ('11856', '-1000348', 'Ah...the wondrous sound of kodos. I love the way they make the ground shake... inspect the beast for me.', null, null, null, null, null, null, null, 'Ах...превосходные звуки кодо. Я люблю, когда они встрясывают землю... исследуют дорогу для меня.', '0', '0', '0', '0', 'kodo round SAY_SMEED_HOME_1');
INSERT IGNORE INTO `script_texts` VALUES ('11856', '-1000349', 'Hey, look out with that kodo! You had better inspect that beast before I give you credit!', null, null, null, null, null, null, null, 'Эй, будь внимательней с этим кодо! Осматривай его лучше, и я дам тебе награду!', '0', '0', '0', '0', 'kodo round SAY_SMEED_HOME_2');
INSERT IGNORE INTO `script_texts` VALUES ('1978', '-1000345', 'Let\'s keep moving. We\'re not safe here!', null, null, null, null, null, null, null, 'Давайте двигаться. Здесь не безопасно.', '0', '0', '0', '0', 'kayra SAY_PROGRESS');
INSERT IGNORE INTO `script_texts` VALUES ('1978', '-1000344', 'Looks like we won\'t get away so easy. Get ready!', null, null, null, null, null, null, null, 'Нам не выбраться отсюда так просто. Готовьтесь!', '0', '0', '0', '0', 'kayra SAY_AMBUSH1');
INSERT IGNORE INTO `script_texts` VALUES ('1978', '-1000343', 'Is the way clear? Let\'s get out while we can, $N.', null, null, null, null, null, null, null, 'Путь чист? Давай выбираться, как можем, $N.', '0', '0', '0', '0', 'kayra SAY_START');
INSERT IGNORE INTO `script_texts` VALUES ('1978', '-1000340', '%s howls in delight at the sight of his lunch!', null, null, null, null, null, null, null, '%s плачет от радости при виде еды!', '0', '2', '0', '0', 'kyle EMOTE_SEE_LUNCH');
INSERT IGNORE INTO `script_texts` VALUES ('1978', '-1000341', '%s eats his lunch.', null, null, null, null, null, null, null, '%s ест свой ланч!', '0', '2', '0', '0', 'kyle EMOTE_EAT_LUNCH');
INSERT IGNORE INTO `script_texts` VALUES ('1978', '-1000342', '%s thanks you with a special dance.', null, null, null, null, null, null, null, '%s благодарит вас особым танцем!', '0', '2', '0', '0', 'kyle EMOTE_DANCE');
INSERT IGNORE INTO `script_texts` VALUES ('1978', '-1000339', 'Something tells me this $r wants the mysterious fossil too. Help!', null, null, null, null, null, null, null, 'Что-то говорит мне, что $r хочет этот мистический артефакт тоже. Помогите!', '0', '0', '7', '0', 'remtravel SAY_REM_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('1978', '-1000337', 'Ah. I remember now! I gave the mysterious fossil to Hollee! Check with her.', null, null, null, null, null, null, null, 'Ах. Теперь я понял! Я дам странное ископаемое Холли! Идём к ней.', '0', '0', '7', '0', 'remtravel SAY_REM_REMEMBER');
INSERT IGNORE INTO `script_texts` VALUES ('1978', '-1000338', '%s goes back to work, oblivious to everything around him.', null, null, null, null, null, null, null, '%s вернись к работе, обыщи всё, что рядом с ним.', '0', '2', '7', '0', 'remtravel EMOTE_REM_END');
INSERT IGNORE INTO `script_texts` VALUES ('1978', '-1000336', 'Nope. didn\'t leave the fossil back here!', null, null, null, null, null, null, null, 'Ноп. Оставь ископаемое здесь и не трогай!', '0', '0', '7', '0', 'remtravel SAY_REM_PROGRESS');
INSERT IGNORE INTO `script_texts` VALUES ('1978', '-1000335', 'Oh wait! I\'m supposed to be looking for that mysterious fossil!', null, null, null, null, null, null, null, 'Подождите! Я бы хотел посмотреть на эту странное ископаемое!', '0', '0', '7', '0', 'remtravel SAY_REM_MOSS_PROGRESS');
INSERT IGNORE INTO `script_texts` VALUES ('17768', '-1000334', '%s inspects the ancient, mossy stone.', null, null, null, null, null, null, null, '%s исследует древний, болотистый камень.', '0', '2', '7', '0', 'remtravel EMOTE_REM_MOSS');
INSERT IGNORE INTO `script_texts` VALUES ('19720', '-1000333', 'Interesting... I hadn\'t noticed this earlier...', null, null, null, null, null, null, null, 'Интересно...мне об этом ничего не сказали ранее...', '0', '0', '7', '0', 'remtravel SAY_REM_MOSS');
INSERT IGNORE INTO `script_texts` VALUES ('19720', '-1000332', 'Oh wait, that\'s Hollee\'s tent... and it\'s empty.', null, null, null, null, null, null, null, 'Подождите, это палатка Холли...но в ней никого нет.', '0', '0', '7', '0', 'remtravel SAY_REM_TENT1_2');
INSERT IGNORE INTO `script_texts` VALUES ('19720', '-1000331', 'I bet you I left it in the tent!', null, null, null, null, null, null, null, 'Я уверен в тебе, поэтому, оставлю эту вещь в палатке!', '0', '0', '7', '0', 'remtravel SAY_REM_TENT1_1');
INSERT IGNORE INTO `script_texts` VALUES ('19720', '-1000330', 'No mysterious fossil here... Ah, but my copy of Green Hills of Stranglethorn. What a good book!', null, null, null, null, null, null, null, 'Нет здесь странного ископаемого... Ах, а как же моя копия Зеленые холмы Тернистой долины. Потрясающая книга!', '0', '0', '7', '0', 'remtravel SAY_REM_BOOK');
INSERT IGNORE INTO `script_texts` VALUES ('19720', '-1000329', 'Hrm, nothing up here.', null, null, null, null, null, null, null, 'Хмм...нет, не здесь', '0', '0', '7', '0', 'remtravel SAY_REM_RAMP1_2');
INSERT IGNORE INTO `script_texts` VALUES ('19720', '-1000328', 'Now where did I put that mysterious fossil? Ah, maybe up there...', null, null, null, null, null, null, null, 'Где же я поднял это ископаемое? Ах, может быть здесь...', '0', '0', '7', '0', 'remtravel SAY_REM_RAMP1_1');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000327', 'Ok, $n, let\'s go find where I left that mysterious fossil. Follow me!', null, null, null, null, null, null, null, 'Хорошо, $n, давай найдём место, где я потерял это ископаемое. Следуй за мной!', '0', '0', '7', '0', 'remtravel SAY_REM_START');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000326', 'YARRR! Swabie, what have ye done?! He\'s gone mad! Baton him down the hatches! Hoist the mast! ARRRR! Every man for hi\'self!', null, null, null, null, null, null, null, 'АРРР! Сваби, что ты наделал?! Он обезумел! Сделай обратно! Поднять мачту! АРР! Каждый сам за себя!', '0', '0', '7', '0', 'threshwackonator SAY_AT_CLOSE');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000325', '\"Threshwackonator First Mate unit prepared to follow\"', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Бешенатор Первый помощник капитана готов следовать за вами.\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '2', '0', '0', 'threshwackonator EMOTE_START');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000324', '%s growls in acknowledgement before straightening and making her way off into the forest.', null, null, null, null, null, null, null, 'Туманна согласно рычит перед тем, как выпрямиться и убежать в лес.', '0', '2', '0', '0', 'mist EMOTE_AT_HOME');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000322', 'Show $n the meaning of pain, Sunstriker!', null, null, null, null, null, null, null, 'Покажи $n ,что такое боль, Воитель Солнцесвет!', '0', '1', '1', '0', 'kelerun SayId4');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000323', 'Mist! I feared I would never see you again! Yes, I am well, do not worry for me. You must rest and recover your health.', null, null, null, null, null, null, null, 'Туманна! Я уж боялась, что никогда тебя не увижу! У меня все хорошо, не беспокойся. А тебе надо отдохнуть и поправить здоровье.', '0', '0', '7', '0', 'mist SAY_AT_HOME');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000321', 'Show this upstart how a real Blood Knight fights, Swiftblade!', null, null, null, null, null, null, null, 'Покажите этот приём, как настоящий боец Рыцарей Крови, Воительница Быстромеч!', '0', '1', '1', '0', 'kelerun SayId3');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000320', 'Champion Lightrend, make me proud!', null, null, null, null, null, null, null, 'Воитель Светорез, сделай меня смелым!', '0', '1', '1', '0', 'kelerun SayId2');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000319', 'Let the trial begin, Bloodwrath, attack!', null, null, null, null, null, null, null, 'Начнём тренировку, Кровогнев, атакуй!', '0', '1', '1', '0', 'kelerun SayId1');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000318', 'Try to take better care of yourself, Quinn. You were lucky this time.', null, null, null, null, null, null, null, 'Постарайся побыстрее выздороветь, Квинн. Ты всегда был удачлив.', '0', '0', '1', '1', 'erland SAY_BYE');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000317', 'I\'ve been better. Ivar the Foul got the better of me...', null, null, null, null, null, null, null, 'Мне уже лучше. Ивар Нечистый помог мне...', '0', '0', '33', '1', 'erland SAY_QUINN_REPLY');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000316', 'Hello, Quinn. How are you faring?', null, null, null, null, null, null, null, 'Привет, Квинн. Как поживаешь?', '0', '0', '1', '1', 'erland SAY_QUINN');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000315', 'If I am excused, then I\'d like to check on Quinn...', null, null, null, null, null, null, null, 'Если я выберусь отсюда, то пойду к Квинну...', '0', '0', '1', '1', 'erland SAY_CHECK_NEXT');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000314', 'Masses of wolves are to the east, and whoever lived at Malden\'s Orchard is gone.', null, null, null, null, null, null, null, 'Большое количество волков на востоке, об этом сообщили жители Сада Мальдена.', '0', '0', '1', '1', 'erland SAY_RANE_REPLY');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000313', 'It\'s good to see you again, Erland. What is your report?', null, null, null, null, null, null, null, 'Рад увидеть тебя снова, Эрланд. Какие новости?', '0', '0', '33', '1', 'erland SAY_RANE');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000312', 'We made it! Thanks, $N. I couldn\'t have gotten without you.', null, null, null, null, null, null, null, 'Мы сделали это! Спасибо, $N. Я бы ничего не смог сделать без тебя.', '0', '0', '1', '0', 'erland SAY_END');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000311', 'We\'re almost there!', null, null, null, null, null, null, null, 'Мы все здесь!', '0', '0', '1', '0', 'erland SAY_PROGRESS');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000310', 'Oh no! A $C is upon us!', null, null, null, null, null, null, null, 'О нет! $C прикрой нас!', '0', '0', '1', '0', 'erland SAY_AGGRO_3');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000309', 'Beware! I am under attack!', null, null, null, null, null, null, null, 'Берегись! Меня атакуют!', '0', '0', '1', '0', 'erland SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000308', 'A $C attacks!', null, null, null, null, null, null, null, 'Атакуй $C!', '0', '0', '1', '0', 'erland SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000307', 'Be careful, $N. Those wolves like to hide among the trees.', null, null, null, null, null, null, null, 'Будьте осторожны, $N. Эти волки любят скрываться у деревьев.', '0', '0', '1', '0', 'erland SAY_START_2');
INSERT IGNORE INTO `script_texts` VALUES ('19685', '-1000306', 'Let\'s get to the others, and keep an eye open for those wolves cutside...', null, null, null, null, null, null, null, 'Давайте найдём другие, и держите глаза открытыми, здесь полно волков...', '0', '0', '1', '0', 'erland SAY_START_1');
INSERT IGNORE INTO `script_texts` VALUES ('20415', '-1000305', 'Hmm... I don\'t think this blinking red light is a good thing...', null, null, null, null, null, null, null, 'Хмм... Я не думаю, что этот мерцающий красный свет предвещает что-то хорошее...', '0', '0', '0', '0', 'wizzlecrank SAY_END');
INSERT IGNORE INTO `script_texts` VALUES ('20415', '-1000304', 'That was a close one! Well, let\'s get going, it\'s still a ways to Ratchet!', null, null, null, null, null, null, null, 'Они закрыли ещё одну! Хорошо, давайте идти, я проложу дорогу в Кабестан!', '0', '0', '0', '0', 'wizzlecrank SAY_PROGRESS_3');
INSERT IGNORE INTO `script_texts` VALUES ('18879', '-1000303', 'Come on, don\'t break down on me now!', null, null, null, null, null, null, null, 'Давай, не сдавайся!', '0', '0', '0', '393', 'wizzlecrank SAY_PROGRESS_2');
INSERT IGNORE INTO `script_texts` VALUES ('20907', '-1000302', 'Looks like we\'re out of woods, eh? Wonder what this does...', null, null, null, null, null, null, null, 'Кажется, мы вышли из леса, ааа? Чудесно, что мы это сделали...', '0', '0', '0', '0', 'wizzlecrank SAY_PROGRESS_1');
INSERT IGNORE INTO `script_texts` VALUES ('20209', '-1000301', 'There\'s the stolen shredder! Stop it or Lugwizzle will have our hides!', null, null, null, null, null, null, null, 'Это украденный крошшер! Остановись или Зубовиззл сдерёт с тебя шкуру!', '0', '1', '0', '0', 'wizzlecrank SAY_MERCENARY');
INSERT IGNORE INTO `script_texts` VALUES ('20209', '-1000300', 'Okay, I think I\'ve got it, now. Follow me, $n!', null, null, null, null, null, null, null, 'Хорошо, Я думаю у меня это есть. Следуй за мной, $n!', '0', '0', '0', '1', 'wizzlecrank SAY_STARTUP2');
INSERT IGNORE INTO `script_texts` VALUES ('20209', '-1000299', 'Arrrgh! This isn\'t right!', null, null, null, null, null, null, null, 'Арррр! Это не правильно!', '0', '0', '0', '0', 'wizzlecrank SAY_STARTUP1');
INSERT IGNORE INTO `script_texts` VALUES ('20209', '-1000298', 'Alright, alright I think I can figure out how to operate this thing...', null, null, null, null, null, null, null, 'Ладно, ладно я думаю я смогу разобраться, как это работает...', '0', '0', '0', '393', 'wizzlecrank SAY_START');
INSERT IGNORE INTO `script_texts` VALUES ('20209', '-1000297', 'Meet me down by the orchard-- I just need to put my gun away.', null, null, null, null, null, null, null, 'Встречаемся внизу у фруктового сада. Мне надо убрать свою пушку.', '0', '0', '7', '0', 'stilwell SAY_DS_PROLOGUE');
INSERT IGNORE INTO `script_texts` VALUES ('20209', '-1000296', 'We\'ve done it! We won!', null, null, null, null, null, null, null, 'Мы сделали это! Мы выиграли!', '0', '0', '7', '0', 'stilwell SAY_DS_DOWN_3');
INSERT IGNORE INTO `script_texts` VALUES ('18351', '-1000295', 'One more down!', null, null, null, null, null, null, null, 'Ещё один пал!', '0', '0', '7', '0', 'stilwell SAY_DS_DOWN_2');
INSERT IGNORE INTO `script_texts` VALUES ('18351', '-1000294', 'We showed that one!', null, null, null, null, null, null, null, 'Мы покажем его одного.', '0', '0', '7', '0', 'stilwell SAY_DS_DOWN_1');
INSERT IGNORE INTO `script_texts` VALUES ('18351', '-1000293', 'To the house! Stay close to me, no matter what! I have my gun and ammo there!', null, null, null, null, null, null, null, 'В дом! Прикрой меня и не спрашивай зачем! Я достану оружие и патроны!', '0', '0', '7', '0', 'stilwell SAY_DS_START');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000292', 'Cloaking systems online! CLUCK! Engaging cloak for transport to Booty Bay!', null, null, null, null, null, null, null, 'Маскировка системы включена! КЛИК! Маскировка для кораблей в Пиратскую бухту!', '0', '0', '0', '0', 'oox SAY_OOX_END');
INSERT IGNORE INTO `script_texts` VALUES ('7806', '-1000291', 'No one challanges the Wastewander nomads - not even robotic chickens! ATTACK!', null, null, null, null, null, null, null, 'Аварийная ситуация включена! Включения запасного двигателя! КЛИК!', '0', '0', '0', '0', 'oox SAY_OOX17_AMBUSH_REPLY');
INSERT IGNORE INTO `script_texts` VALUES ('7806', '-1000290', 'CLUCK! Sensors detect spatial anomaly - danger imminent! CLUCK!', null, null, null, null, null, null, null, 'КЛИК! Сенсоры обнаружили пространственную аномалию - надвигающаяся опасность! КЛИК!', '0', '0', '0', '0', 'oox SAY_OOX_AMBUSH');
INSERT IGNORE INTO `script_texts` VALUES ('7806', '-1000289', 'Thread analyzed! Activating combat plan beta! CLUCK!', null, null, null, null, null, null, null, 'Угроза анализирована! Активировать альтернативный план! КЛИК!', '0', '0', '0', '0', 'oox SAY_OOX_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('7806', '-1000288', 'Physical threat detected! Evasive action! CLUCK!', null, null, null, null, null, null, null, 'Физическая угроза замечена! Хитрое действие! КЛИК!', '0', '0', '0', '0', 'oox SAY_OOX_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('7806', '-1000287', 'Emergency power activated! Initializing ambulanory motor! CLUCK!', null, null, null, null, null, null, null, 'Аварийная ситуация включена! Включения запасного двигателя! КЛИК!', '0', '0', '0', '0', 'oox SAY_OOX_START');
INSERT IGNORE INTO `script_texts` VALUES ('22916', '-1000286', 'Lurosa, I am entrusting the Relics of Aviana to $N, who will take them to Morthis Whisperwing. I must return completely to the Emerald Dream now. Do not let $N fail!', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Луроса, я отдаю Реликвии Авианы $N, $который:которая; отнесёт их Мортису Шелест Крыла. Я должен добраться до Изумрудного Сна сейчас же. Не дай $N провалиться!\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '0', '0', '1', 'clintar SAY_END');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000285', 'We have all three of the relics, but my energy is rapidly fading. We must make our way back to Dreamwarden Lurosa! He will let you know what to do next.', null, null, null, null, null, null, null, 'У нас все три реликвии, но моя энергия резко упала. Мы должны вернутся к Хранителю снов Луросе! Он даст вам знать, что делать дальше.', '0', '0', '0', '0', 'clintar SAY_RELIC3');
INSERT IGNORE INTO `script_texts` VALUES ('24981', '-1000284', 'I\'ve recovered the second relic. Take a moment to rest, and then we\'ll continue to the last reliquary.', null, null, null, null, null, null, null, 'Я восстановил вторую реликвию. Отдохните, и мы продолжим поиск третей реликвии.', '0', '0', '0', '0', 'clintar SAY_RELIC2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000283', 'That\'s the first relic, but there are still two more. Follow me, $N.', null, null, null, null, null, null, null, 'Это первая реликвия, кроме того есть ещё две. Следуй за мной, $N.', '0', '0', '0', '0', 'clintar SAY_RELIC1');
INSERT IGNORE INTO `script_texts` VALUES ('15420', '-1000282', 'Begone from this place!', null, null, null, null, null, null, null, 'Убирайся из этого места!', '0', '0', '0', '0', 'clintar SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('15420', '-1000281', 'The Emerald Dream will never be yours!', null, null, null, null, null, null, null, 'Изумрудный сон никогда не будет вашим!', '0', '0', '0', '0', 'clintar SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('6172', '-1000280', 'A shadowy, sinister presence has invaded the Emerald Dream. Its power is poised to spill over into our world, $N. We must oppose it! That\'s why I cannot accompany you in person.', null, null, null, null, null, null, null, 'Тёмный, мрачный вид овладел Изумрудным Сном. Его сила равномерно распространяется по всему миру, $N. Мы должны предотвратить это! Но я не могу сопровождать тебя.', '0', '0', '0', '1', 'clintar SAY_START');
INSERT IGNORE INTO `script_texts` VALUES ('3439', '-1000279', 'Alright, we give up! Don\'t hurt us!', null, null, null, null, null, null, null, 'Ладно, мы сдаемся! Не пораньте нас!', '0', '0', '0', '0', 'larry SAY_GIVEUP');
INSERT IGNORE INTO `script_texts` VALUES ('3439', '-1000277', '2...', null, null, null, null, null, null, null, 'Два...', '0', '0', '0', '0', 'larry SAY_COUNT_2');
INSERT IGNORE INTO `script_texts` VALUES ('3439', '-1000278', 'Time to meet your maker!', null, null, null, null, null, null, null, 'Время увидеть твоего создателя.', '0', '0', '0', '0', 'larry SAY_ATTACK_5');
INSERT IGNORE INTO `script_texts` VALUES ('3439', '-1000276', '1...', null, null, null, null, null, null, null, 'Один...', '0', '0', '0', '0', 'larry SAY_COUNT_1');
INSERT IGNORE INTO `script_texts` VALUES ('3439', '-1000275', 'Now I\'m gonna give you to the count of \'3\' to get out of here before I sick the dogs on you.', null, null, null, null, null, null, null, 'Я даю тебе 3 секунды убраться отсюда, иначе я натравлю собак на тебя.', '0', '0', '0', '0', 'larry SAY_COUNT');
INSERT IGNORE INTO `script_texts` VALUES ('3439', '-1000274', 'Time to teach you a lesson in manners, little $Gboy:girl;!', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Время научить тебя манерам, $Gмаленький:маленькая; $Gмальчик:девочка;!\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '0', '0', '0', 'larry SAY_START');
INSERT IGNORE INTO `script_texts` VALUES ('3439', '-1000273', 'Doctor Draxlegauge will give you further instructions, $N. Many thanks for your help!', null, null, null, null, null, null, null, 'Доктор Драксельмер даст тебе дальнейшие инструкции, $N. Большое спасибо за твою помощь!', '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_9');
INSERT IGNORE INTO `script_texts` VALUES ('3439', '-1000272', '%s hands one glowing goggles over to Doctor Draxlegauge.', null, null, null, null, null, null, null, 'держит светящиеся очки около Доктора Драксельмера.', '0', '2', '0', '0', 'phizzlethorpe EMOTE_PROGRESS_8');
INSERT IGNORE INTO `script_texts` VALUES ('6248', '-1000271', 'Phew! Glad to be back from that creepy cave.', null, null, null, null, null, null, null, 'Хух! Я рад уйти из этой страшной пещеры.', '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_7');
INSERT IGNORE INTO `script_texts` VALUES ('6248', '-1000270', 'I\'ve done it! I have harnessed the power of the stone into the goggles! Let\'s get out of here!', null, null, null, null, null, null, null, 'Я сделал это! Я зарядил очки силой камня! Давай выбираться отсюда!', '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_6');
INSERT IGNORE INTO `script_texts` VALUES ('6248', '-1000269', 'Almost done! Just a little longer!', null, null, null, null, null, null, null, 'Почти всё сделано! Ещё не много!', '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_5');
INSERT IGNORE INTO `script_texts` VALUES ('6248', '-1000268', 'Help!!! Get these things off me so I can get my work done!', null, null, null, null, null, null, null, 'Помогите!!! Уберите это от меня и я смогу закончить работу!', '0', '0', '0', '0', 'phizzlethorpe SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('6248', '-1000267', '%s begins tinkering with the goggles before the stone.', null, null, null, null, null, null, null, 'начинает работать с очками перед камнем.', '0', '2', '0', '0', 'phizzlethorpe EMOTE_PROGRESS_4');
INSERT IGNORE INTO `script_texts` VALUES ('17318', '-1000266', 'I\'ll begin drawing energy from the stone. Your job, $N, is to defend me. This place is cursed... trust me.', null, null, null, null, null, null, null, 'Я начинаю вытягивание энергии из камня. Твоя работа, $N, защитить меня. Это место проклято... защити меня.', '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_3');
INSERT IGNORE INTO `script_texts` VALUES ('17318', '-1000265', 'I discovered this cave on our first day here. I believe the energy in the stone can be used to our advantage.', null, null, null, null, null, null, null, 'Я исследовал эту пещеру в первый же день нашего пребывания здесь. Я верю, что энергия этого камня может быть полезной для нас.', '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_2');
INSERT IGNORE INTO `script_texts` VALUES ('17318', '-1000264', 'Ok, $N. Follow me to the cave where I\'ll attempt to harness the power of the rune stone into these goggles.', null, null, null, null, null, null, null, 'Хорошо, $N. Следуй за мной в пещеру, где я попытаюсь использовать силу рунического камня в этих очках.', '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_1');
INSERT IGNORE INTO `script_texts` VALUES ('17318', '-1000263', 'Farewell to you, and may Elune be with you always.', null, null, null, null, null, null, null, 'Удачи, и помни, Елуна всегда с тобой!', '0', '0', '2', '3', 'garments SAY_SHAYA_GOODBYE');
INSERT IGNORE INTO `script_texts` VALUES ('17318', '-1000262', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those corrupt timberlings with Elune\'s power behind me!', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Спасибо тебе! Спасибо, $GЖрец:Жрица;. Сейчас мы уничтожим эту порчу при помощи силы Елуны!\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '0', '2', '4', 'garments SAY_SHAYA_THANKS');
INSERT IGNORE INTO `script_texts` VALUES ('17318', '-1000261', 'Farewell to you, and may the Light be with you always.', null, null, null, null, null, null, null, 'Удачи, и знай, свет всегда рядом с тобой.', '0', '0', '7', '3', 'garments SAY_DOLF_GOODBYE');
INSERT IGNORE INTO `script_texts` VALUES ('17318', '-1000260', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those wendigo with the Light on my side!', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Спасибо! Спасибо тебе, $GЖрец:Жрица;. Сейчас я могу побороть этих Вендиго с помощью силы света!\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '0', '7', '4', 'garments SAY_DOLF_THANKS');
INSERT IGNORE INTO `script_texts` VALUES ('17318', '-1000259', 'Farewell to you, and may our ancestors be with you always!', null, null, null, null, null, null, null, 'Удачи, и помни, твои предки всегда с тобой!', '0', '0', '1', '3', 'garments SAY_KORJA_GOODBYE');
INSERT IGNORE INTO `script_texts` VALUES ('17243', '-1000258', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those humans with your power to back me!', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Спасибо тебе! Спасибо, $GЖрец:Жрица;. Сейчас мы уничтожим тех людишек с помощью силы!\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '0', '1', '4', 'garments SAY_KORJA_THANKS');
INSERT IGNORE INTO `script_texts` VALUES ('17243', '-1000256', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those murlocs with the Light on my side!', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Спасибо тебе! Спасибо, $GЖрец:Жрица;. Сейчас мы уничтожим тех мурлоков силой света!\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '0', '7', '4', 'garments SAY_ROBERTS_THANKS');
INSERT IGNORE INTO `script_texts` VALUES ('17243', '-1000257', 'Farewell to you, and may the Light be with you always.', null, null, null, null, null, null, null, 'Удачи, и знай, свет всегда рядом с тобой.', '0', '0', '7', '3', 'garments SAY_ROBERTS_GOODBYE');
INSERT IGNORE INTO `script_texts` VALUES ('16483', '-1000255', 'Good luck stranger, and welcome to Shattrath City.', null, null, null, null, null, null, null, 'Удачи незнакомец, и добро пожаловать в Шаттрат.', '0', '4', '0', '0', 'SAY_KHAD_SERV_21');
INSERT IGNORE INTO `script_texts` VALUES ('16483', '-1000254', 'Khadgar should be ready to see you again. Just remember that to serve the Sha\'tar you will most likely have to ally with the Aldor or the Scryers. And seeking the favor of one group will cause the others\' dislike.', null, null, null, null, null, null, null, 'Кадгар готов увидеть тебя снова. Запомни, что служить Ша\'тар лучше вместе с Провидцами или Алдорами. И прибегая к одной фракции, вы портите отношения с другой.', '0', '4', '0', '0', 'SAY_KHAD_SERV_20');
INSERT IGNORE INTO `script_texts` VALUES ('16483', '-1000253', 'Such is their animosity that they vie for the honor of being sent to assist the naaru there. Each day, that decision is made here by A\'dal. The armies gather here to receive A\'dal\'s blessing before heading to Shadowmoon.', null, null, null, null, null, null, null, 'Они борются за честь, помогают Наару. Каждый день, решение принимается А\'далом. Армия собирается здесь для того, чтобы получить благословление А\'дала перед походом в Долину Призрачной Луны.', '0', '4', '0', '0', 'SAY_KHAD_SERV_19');
INSERT IGNORE INTO `script_texts` VALUES ('16483', '-1000252', 'There he continues to wage war on Illidan with the assistance of the Aldor and the Scryers. The two factions have not given up on their old feuds, though.', null, null, null, null, null, null, null, 'Он продолжает вести войну заодно с Алдорами и Провидцами. Тем не менее, две фракции не прекратили войну между собой.', '0', '4', '0', '0', 'SAY_KHAD_SERV_18');
INSERT IGNORE INTO `script_texts` VALUES ('16483', '-1000250', 'The Aldor\'s most holy temple and its surrounding dwellings lie on the terrace above. As a holy site, only the initiated are welcome inside.', null, null, null, null, null, null, null, 'У Алдоров святые храмы и ближайший располагается на террасе выше. Так как это священное место, только посвященные могут зайти внутрь.', '0', '4', '0', '0', 'SAY_KHAD_SERV_16');
INSERT IGNORE INTO `script_texts` VALUES ('16483', '-1000251', 'The attacks against Shattrath continued, but the city did not fall, as you can see. On the contrary, the naaru known as Xi\'ri led a successful incursion into Shadowmoon Valley - Illidan\'s doorstep.', null, null, null, null, null, null, null, 'Нападения на Шаттрат продолжались, но город не пал, как видите. Один из Наару, известный как Кси\'ри, вёл успешное вторжение в Долине Призрачной Луны - на Храм Иллидана.', '0', '4', '0', '0', 'SAY_KHAD_SERV_17');
INSERT IGNORE INTO `script_texts` VALUES ('16483', '-1000249', 'Many of the priesthood had been slain by the same magisters who now vowed to serve the naaru. They were not happy to share the city with their former enemies.', null, null, null, null, null, null, null, 'Большинство священников были убиты магистрами, которые сейчас поклялись служить Наару. Они не хотят делить город со своими бывшими врагами.', '0', '4', '0', '0', 'SAY_KHAD_SERV_15');
INSERT IGNORE INTO `script_texts` VALUES ('16483', '-1000248', 'The Aldor are followers of the Light and forgiveness and redemption are values they understand. However, they found hard to forget the deeds of the blood elves while under Kael\'s command.', null, null, null, null, null, null, null, 'Алдоры - последователи света и прощения и искупление - цена, которую они понимают. Однако, они с трудом забыли дела Кровавых Эльфов, находящихся под командованием Келя.', '0', '4', '0', '0', 'SAY_KHAD_SERV_14');
INSERT IGNORE INTO `script_texts` VALUES ('23002', '-1000247', 'The naaru accepted the defectors, who would become known as the Scryers; their dwelling lies in the platform above. Only those initiated with the Scryers are allowed there.', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Наару приняли изменников, которые стали известны как провидцы; их жилище располагалось на платформе выше. Только посвященные с Провидцами находятся здесь.\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '4', '0', '0', 'SAY_KHAD_SERV_13');
INSERT IGNORE INTO `script_texts` VALUES ('23002', '-1000246', 'The defection of Voren\'thal and his followers was the largest loss ever incurred by Kael\'s forces. And these weren\'t just any blood elves. Many of the best and brightest amongst Kael\'s scholars and magisters had been swayed by Voren\'thal\'s influence.', null, null, null, null, null, null, null, 'Измена Ворен\'таля и его последователей была огромнейшей потерей для сил Келя. И это были не все Кровавые Эльфы. Большинство из лучших и блестящих ученых и магистров из числа Келя перешли на сторону Ворен\'таля.', '0', '4', '0', '0', 'SAY_KHAD_SERV_12');
INSERT IGNORE INTO `script_texts` VALUES ('23002', '-1000245', 'As the naaru approached him, Voren\'thal kneeled before him and uttered the following words: \"I\'ve seen you in a vision, naaru. My race\'s only hope for survival lies with you. My followers and I are here to serve you.\"', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Как только наару подошли к нему, Ворен\'таль приклонился перед А\'далом и произнёс следующие слова: \"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Я видел вас в своём видении, Наару. Моя раса надеется на выживание только под вашей защитой. Мои последователи и я здесь, чтобы служить вам.\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '4', '0', '0', 'SAY_KHAD_SERV_11');
INSERT IGNORE INTO `script_texts` VALUES ('2768', '-1000243', 'As the regiment of blood elves crossed this very bridge, the Aldor\'s exarchs and vindicators lined up to defend the Terrace of Light. But then the unexpected happened.', null, null, null, null, null, null, null, 'Когда Кровавые Эльфы пересекли этот мост, Алдорские экзархи и воздатели присоединились к обороне Террасы Света. Но вдруг случилось непредвиденное.', '0', '4', '0', '0', 'SAY_KHAD_SERV_9');
INSERT IGNORE INTO `script_texts` VALUES ('2768', '-1000244', 'The blood elves laid down their weapons in front of the city\'s defenders; their leader, a blood elf elder known as Voren\'thal, stormed into the Terrace of Light and demanded to speak to A\'dal.', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Кровавые Эльфы сложили их оружие перед защитниками города; ихний лидер, старый кровавый эльф, известный как Ворен\'таль, ворвался в Террасу Света и потребовал разговора с А\'далом.\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '4', '0', '0', 'SAY_KHAD_SERV_10');
INSERT IGNORE INTO `script_texts` VALUES ('2768', '-1000242', 'It wouldn\'t be long, however, before the city came under attack once again. This time, the attack came from Illidan\'s armies. A large regiment of blood elves had been sent by Illidan\'s ally, Kael\'thas Sunstrider, to lay waste to the city.', null, null, null, null, null, null, null, 'Это было не за долго до того, как город снова подвергся атаке. На этот раз атака происходила от армии Иллидана. Большое количество Кровавых Эльфов были посланы союзником Иллидана, Кель\'тасом Солнечным Скитальцем, для разорения города.', '0', '4', '0', '0', 'SAY_KHAD_SERV_8');
INSERT IGNORE INTO `script_texts` VALUES ('2768', '-1000241', 'The priesthood, known as the Aldor, quickly regained its strength as word spread that the naaru had returned and reconstruction soon began. The ruined temple is now used as an infirmary for injured refugees.', null, null, null, null, null, null, null, 'Священники, известные как Алдоры, быстро возвратили былую мощь, распространяя слухи о том, что Наару всоре вернутся и реконструкция начнётся. Разрушенный замок сейчас используется, как лазарет для раненых беженцев.', '0', '4', '0', '0', 'SAY_KHAD_SERV_7');
INSERT IGNORE INTO `script_texts` VALUES ('2768', '-1000239', 'The Sha\'tar, or \"born from light\" are the naaru that came to Outland to fight the demons of the Burning Legion.', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Ша\'тар, или \"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"рождённые в свете\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\" - Наару, которые пришли в Запределье для борьбы против демонов Пылающего Легиона.\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '4', '0', '0', 'SAY_KHAD_SERV_5');
INSERT IGNORE INTO `script_texts` VALUES ('2768', '-1000240', 'They were drawn to the ruins of Shattrath City where a small remnant of the draenei priesthood conducted its rites inside a ruined temple on this very spot.', null, null, null, null, null, null, null, 'Они были вытащены из-под руин Шаттрата, где небольшое количество Дренейского духовенства руководило обрядами внутри разрушенного храма в этом месте.', '0', '4', '0', '0', 'SAY_KHAD_SERV_6');
INSERT IGNORE INTO `script_texts` VALUES ('2768', '-1000237', 'The city was left in ruins and darkness... until the Sha\'tar arrived.', null, null, null, null, null, null, null, 'Город был в руинах и тьме...до прихода Ша\'тар.', '0', '4', '0', '0', 'SAY_KHAD_SERV_3');
INSERT IGNORE INTO `script_texts` VALUES ('2768', '-1000238', 'Let us go into the Lower City. I will warn you that as one of the only safe havens in Outland, Shattrath has attracted droves of refugees from all wars, current and past.', null, null, null, null, null, null, null, 'Давайте зайдём в Нижний город. Я предупреждаю вас, что это один из сохранившихся убежищ в Запределье, в Шаттрате много беженцев со многих войн, настоящих и прошедших.', '0', '4', '0', '0', 'SAY_KHAD_SERV_4');
INSERT IGNORE INTO `script_texts` VALUES ('2768', '-1000235', 'Shattrath was once the draenei capital of this world. Its name means \"dwelling of light.\"', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Шаттрат - одна из столиц дренеев в этом мире. Его имя означает \"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Дом Света.\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '4', '0', '0', 'SAY_KHAD_SERV_1');
INSERT IGNORE INTO `script_texts` VALUES ('2768', '-1000236', 'When the Burning Legion turned the orcs against the draenei, the fiercest battle was fought here. The draenei fought tooth and nail, but in the end the city fell.', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Когда Пылающий Легион столкнул орков с дренеями, жестокий бой проходил здесь. Дренеи бились как могли, но в конце концов город сгорел.\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '4', '0', '0', 'SAY_KHAD_SERV_2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000233', 'Farewell to you, and may shadow always protect you!', null, null, null, null, null, null, null, 'Удачи, и помни, что тьма всегда защитит тебя!', '0', '0', '1', '3', 'garments SAY_DG_KEL_GOODBYE');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000234', 'Follow me, stranger. This won\'t take long.', null, null, null, null, null, null, null, 'Следуй за мной незнакомец. Это займёт не много времени.', '0', '0', '0', '0', 'SAY_KHAD_SERV_0');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000230', 'Our time is at an end $N. I would wish you luck, if such a thing existed.', null, null, null, null, null, null, null, 'Наше время близится к концу $N. Я пожелал бы вам удачи, если бы такая вещь существовала.', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_14');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000231', 'Ah, $GPriest:Priestess; you came along just in time. I appreciate it.', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Ах, $GЖрец:Жрица; ты как раз во время. Я благодарен тебе.\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '0', '0', '20', 'garments SAY_COMMON_HEALED');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000232', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those gnolls with your power to back me!', null, null, null, null, null, null, null, '\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"Спасибо тебе! Спасибо, $GЖрец:Жрица;. Теперь я могу поймать этих гноллов вместе с тобой!\"\"\"\"\"\"\"\"\"\"\"\"\"\"\"', '0', '0', '1', '4', 'garments SAY_DG_KEL_THANKS');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000229', 'All we know of this timeway is that it leads to Mount Hyjal. The Infinite Dragonflight have gone to great lengths to prevent our involvement. We know next to nothing, mortal. Soridormi is currently attempting to break through the timeway\'s defenses but has thus far been unsuccessful. You might be our only hope of breaking through and resolving the conflict.', null, null, null, null, null, null, null, 'Все мы знаем об этом временном пути он приводит к Горе Хиджал. Бог Бронзовых драконов пошел на все, чтобы предотвратить нашу причастность. Мы почти ничего не знаем, смертный. Соридорми в настоящее время пытается прорваться через защиту временного пути, но до этого все попытки были неудачны. Вы могли бы быть нашей единственной надеждой, прорвав и решив конфликт.', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_13');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000228', 'The timeways are currently ranked in order from least catastrophic to most catastrophic. Note that they are all classified as catastrophic, meaning that any single one of these timeways collapsing would mean that your world would end. We only classify them in such a way so that the heroes and adventurers that are sent here know which timeway best suits their abilities.', null, null, null, null, null, null, null, 'Временной путь в настоящее время оценивается в порядке от наименее катастрофического до наиболее катастрофического. Отметьте, что они все классифицированы как катастрофические, это означает, что любой, единственный из этих способов времени разрушившись означал бы, что вашему миру наступит конец. Мы классифицируем их только в таком направлении, так чтобы герои и авантюристы, которых посылают сюда, знали в каком временном пути проявятся их лучшие способности.', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_12');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000227', 'This timeway is currently collapsing. What that may hold for the past, present and future is currently unknown...', null, null, null, null, null, null, null, 'На сей раз путь в настоящее время разрушается. Что это может означать для прошлого, настоящего и будущего в данный момент неизвестно...', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_11');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000226', 'We have very little information on this timeway. Sa\'at has been dispatched and is currently inside. The data we have gathered from his correspondence is that the Infinite Dragonflight are once again attempting to alter time. Could it be that the opening of the Dark Portal is being targeted for sabotage? Let us hope not...', null, null, null, null, null, null, null, 'У нас есть очень немного информации относительно этого временного пути. Са\'ат был послан и в настоящее время находится внутри. Данные, которые мы собрали из его письма, - то, что Бог Бронзовых драконов еще раз пытается изменить время. Могло случиться так, что открытие Темной Двери является диверсией? Давайте надеяться, что нет...', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_10');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000225', 'This timeway is in great disarray! We have agents inside right now attempting to restore order. What information I have indicates that Thrall\'s freedom is in jeopardy. A malevolent organization known as the Infinite Dragonflight is trying to prevent his escape. I fear without outside assistance, all will be lost.', null, null, null, null, null, null, null, 'На сей раз путь очень запутан! У нас есть агенты внутри, прямо сейчас пытающиеся восстановить порядок. Та информация, которой я располагаю, указывает, что свобода Тралла находится под угрозой срыва. Злорадная организация, известная как Бог Бронзовых драконов, пытается предотвратить его спасение. Я боюсь без внешней помощи, все будет потеряно.', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_9');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000224', 'These tunnels that you see are called timeways. They are infinite in number. The ones that currently exist in your reality are what the master has deemed as \'trouble spots.\' These trouble spots may differ completely in theme but they always share a cause. That is, their existence is a result of the same temporal disturbance. Remember that should you venture inside one...', null, null, null, null, null, null, null, 'Эти туннели, которые вы видите, называются временными путями. Они бесконечны в числе. Те, которые в настоящее время существуют в вашей действительности, - то, что хозяин считал как \'горячие точки.\' Эти горячие точки могут отличаться полностью по основной мысли, но они всегда схожи по сути. Таким образом, их существование - результат того же самого временного беспокойства. Помните, что вы должны рисковать душой или внутренним миром...', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_8');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000223', 'For normal maintenance of time, the Keepers of Time are sufficient caretakers. We are able to deal with most ordinary disturbances. I speak of little things, such as rogue mages changing something in the past to elevate their status or wealth in the present.', null, null, null, null, null, null, null, 'Для нормального поддержания времени, Хранители Времени - достаточные сторожи. Мы в состоянии иметь дело с большинством обычных беспорядков. Я говорю о небольших вещах, таких как волшебники жулики, изменяющие кое-что в прошлом, чтобы поднять их статус или богатство в настоящем.', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_7');
INSERT IGNORE INTO `script_texts` VALUES ('21648', '-1000222', 'As custodians of time, we watch over and care for Nozdormu\'s realm. The master is away at the moment, which means that attempts are being made to dramatically alter time. The master never meddles in the affairs of mortals but instead corrects the alterations made to time by others. He is reactionary in this regard.', null, null, null, null, null, null, null, 'Как хранители времени, мы следим и заботимся о царстве Ноздорму. Хозяин далеко в настоящее время, что означает увеличенное количество попыток изменить время. Хозяин никогда не вмешивается в дела смертных, но вместо этого исправляет изменения, сделанные ко времени другими. Он является противодействующим в этой ситуации.', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_6');
INSERT IGNORE INTO `script_texts` VALUES ('23141', '-1000221', 'There are only two truths to be found here: First, that time is chaotic, always in flux, and completely malleable and second, perception does not dictate reality.', null, null, null, null, null, null, null, 'Есть только две истины, которые будут найдены здесь: Во-первых, это время является хаотичным, всегда в движении, и полностью подвластным и во-вторых, восприятие не ограничивает действительность.', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_5');
INSERT IGNORE INTO `script_texts` VALUES ('22083', '-1000220', 'Follow me, please.', null, null, null, null, null, null, null, 'Следуй за мной, пожалуйста.', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_4');
INSERT IGNORE INTO `script_texts` VALUES ('22083', '-1000219', 'It is strange, I know... Most mortals cannot actually comprehend what they see here, as often, what they see is not anchored within their own perception of reality.', null, null, null, null, null, null, null, 'Это странно, я знаю... Большинство смертных не могут фактически понять то, что они видят здесь, как часто, то что они видят, не укладывается в пределах их собственного восприятия действительности.', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_3');
INSERT IGNORE INTO `script_texts` VALUES ('22083', '-1000218', 'We do not know if the Caverns of Time have always been accessible to mortals. Truly, it is impossible to tell as the Timeless One is in perpetual motion, changing our timeways as he sees fit. What you see now may very well not exist tomorrow. You may wake up and have no memory of this place.', null, null, null, null, null, null, null, 'Мы не знаем, были ли Пещеры Времени всегда доступны для смертных. Действительно, невозможно сказать, поскольку Вневременной находится в бесконечном движении, изменяя наш временной путь, как ему заблагорассудится. Что вы видите сейчас скорей всего не будет существовать завтра. Вы можете проснуться и не помнить об этом месте.', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_2');
INSERT IGNORE INTO `script_texts` VALUES ('22083', '-1000217', 'Greetings, $N. I will guide you through the cavern. Please try and keep up.', null, null, null, null, null, null, null, 'Приветствую, $N. Я проведу вас через пещеру. Пожалуйста постарайтесь продержаться на высоком уровне.', '0', '3', '0', '0', 'WHISPER_CUSTODIAN_1');
INSERT IGNORE INTO `script_texts` VALUES ('22083', '-1000216', 'Emergency shutdown aborted.', null, null, null, null, null, null, null, 'Чрезвычайное выключение отменено.', '0', '2', '0', '0', 'manaforge_control EMOTE_ABORT');
INSERT IGNORE INTO `script_texts` VALUES ('22083', '-1000214', 'Emergency shutdown will complete in ten seconds.', null, null, null, null, null, null, null, 'Через десять секунд чрезвычайное выключение закончится.', '0', '2', '0', '0', 'manaforge_control EMOTE_10');
INSERT IGNORE INTO `script_texts` VALUES ('22083', '-1000215', 'Emergency shutdown complete.', null, null, null, null, null, null, null, 'Чрезвычайное выключение завершено.', '0', '2', '0', '0', 'manaforge_control EMOTE_COMPLETE');
INSERT IGNORE INTO `script_texts` VALUES ('23139', '-1000213', 'Emergency shutdown will complete in thirty seconds.', null, null, null, null, null, null, null, 'Через тридцать секунд чрезвычайное выключение закончится.', '0', '2', '0', '0', 'manaforge_control EMOTE_30');
INSERT IGNORE INTO `script_texts` VALUES ('23139', '-1000212', 'Emergency shutdown will complete in one minute.', null, null, null, null, null, null, null, 'Через одну минуту чрезвычайное выключение закончится.', '0', '2', '0', '0', 'manaforge_control EMOTE_60');
INSERT IGNORE INTO `script_texts` VALUES ('23139', '-1000211', 'Warning! %s emergency shutdown process initiated by $N. Shutdown will complete in two minutes.', null, null, null, null, null, null, null, 'Внимание! Чрезвычайный процесс выключения начат $N. Через две минуты выключение закончится.', '0', '2', '0', '0', 'manaforge_control EMOTE_START');
INSERT IGNORE INTO `script_texts` VALUES ('15420', '-1000209', 'Very well. Let\'s see what you have to show me, $N.', null, null, null, null, null, null, null, 'Очень хорошо. Давай посмотрим что ты можешь показать мне, $N.', '0', '0', '1', '0', 'anvilward SAY_ANVIL1');
INSERT IGNORE INTO `script_texts` VALUES ('15420', '-1000210', 'What manner of trick is this, $R? If you seek to ambush me, I warn you I will not go down quietly!', null, null, null, null, null, null, null, 'Что это за уловка, $R? Если вы хотите заманить меня в засаду, я предупреждаю вас, я не буду проигрывать спокойно!', '0', '0', '1', '0', 'anvilward SAY_ANVIL2');
INSERT IGNORE INTO `script_texts` VALUES ('28315', '-1000208', 'Frenzyheart kill you if you come back. You no welcome here no more!', null, null, null, null, null, null, null, 'Приветствую горожанин', '0', '0', '0', '0', 'vekjik SAY_TEXTID_VEKJIK1');
INSERT IGNORE INTO `script_texts` VALUES ('23139', '-1000207', 'You have my blessing', null, null, null, null, null, null, null, 'Ты получил мое благословление', '0', '0', '0', '0', 'ashyen_and_keleth SAY_REWARD_BLESS');
INSERT IGNORE INTO `script_texts` VALUES ('23139', '-1000206', '%s starts pecking at the feed.', null, null, null, null, null, null, null, 'начинает клевать как на пастбище.', '0', '2', '0', '0', 'cluck EMOTE_CLUCK_TEXT2');
INSERT IGNORE INTO `script_texts` VALUES ('9623', '-1000205', '%s looks at you unexpectadly.', null, null, null, null, null, null, null, 'выглядит необычно.', '0', '2', '0', '0', 'cluck EMOTE_H_HELLO');
INSERT IGNORE INTO `script_texts` VALUES ('9623', '-1000204', '%s looks up at you quizzically. Maybe you should inspect it?', null, null, null, null, null, null, null, 'выглядит насмешливо. Возможно вы должны осмотреть это?', '0', '2', '0', '0', 'cluck EMOTE_A_HELLO');
INSERT IGNORE INTO `script_texts` VALUES ('9623', '-1000203', 'Sweet, sweet embrace... take me...', null, null, null, null, null, null, null, 'Конфета, сладкое объятие... берет меня...', '0', '0', '0', '0', 'injured_patient SAY_DOC3');
INSERT IGNORE INTO `script_texts` VALUES ('9623', '-1000202', 'HOORAY! I AM SAVED!', null, null, null, null, null, null, null, 'УРА! Я СПАСЕН!', '0', '0', '0', '0', 'injured_patient SAY_DOC2');
INSERT IGNORE INTO `script_texts` VALUES ('9623', '-1000201', 'I\'m saved! Thank you, doctor!', null, null, null, null, null, null, null, 'Я спасен! Спасибо тебе, доктор!', '0', '0', '0', '0', 'injured_patient SAY_DOC1');
INSERT IGNORE INTO `script_texts` VALUES ('9623', '-1000200', 'As if we don\'t have enough problems, you go and create more!', null, null, null, null, null, null, null, 'Как будто у нас не достаточно проблем, вы идете и создаете больше!', '0', '0', '0', '0', 'SAY_GUARD_SIL_AGGRO3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000199', 'Please tell me that you didn\'t just do what I think you just did. Please tell me that I\'m not going to have to hurt you...', null, null, null, null, null, null, null, 'Пожалуйста скажите мне, что вы не просто делали то, что я думаю, что вы только что сделали. Пожалуйста скажите мне, что мне не придется причинить вам боль...', '0', '0', '0', '0', 'SAY_GUARD_SIL_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000197', '%s kneels down and pick up the amulet.', null, null, null, null, null, null, null, 'приклонись и подбери амулет.', '0', '2', '1', '0', 'lady_sylvanas EMOTE_LAMENT_END');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000198', 'Taste blade, mongrel!', null, null, null, null, null, null, null, 'Попробуй лезвие, полукровка!', '0', '0', '0', '0', 'SAY_GUARD_SIL_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000196', 'Belore...', null, null, null, null, null, null, null, 'Прежде...', '0', '0', '1', '0', 'lady_sylvanas SAY_LAMENT_END');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000194', 'I give up! Please don\'t kill me!', null, null, null, null, null, null, null, 'Я сдаюсь! Пожалуйста не убивай меня!', '0', '0', '0', '0', 'unkor SAY_SUBMIT');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000195', 'I choose the third option: KILLING YOU!', null, null, null, null, null, null, null, 'Я выберу третий вариант: УБИТЬ ТЕБЯ!', '0', '0', '0', '0', 'floon SAY_FLOON_ATTACK');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000192', 'OK, OK! Lump give up!', null, null, null, null, null, null, null, 'Хорошо, хорошо! Глыба сдается!', '0', '0', '0', '0', ' SAY_LUMP_DEFEAT');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000193', 'Thank you, dear $C, you just saved my life.', null, null, null, null, null, null, null, 'Спасибо тебе, дорогой $C, ты только что спас мою жизнь.', '0', '0', '1', '0', 'stillblade SAY_HEAL');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000190', 'In Nagrand, food hunt ogre!', null, null, null, null, null, null, null, 'В Награнде, огры охотники за пищей!', '0', '0', '0', '0', ' SAY_LUMP_0');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000191', 'You taste good with maybe a little salt and pepper.', null, null, null, null, null, null, null, 'Ваши вкусности возможно с небольшим количеством соли и перца.', '0', '0', '0', '0', ' SAY_LUMP_1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000189', 'Objective acquired. Initiating security routines.', null, null, null, null, null, null, null, 'Цель определена. Введение программу безопасности.', '0', '0', '0', '0', 'converted_sentry SAY_CONVERTED_2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000187', 'Thank you, dear $C, you just saved my life.', null, null, null, null, null, null, null, 'Спасибо тебе, дорогой $C, ты только что спас мою жизнь.', '0', '0', '7', '0', 'faulk SAY_HEAL');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000188', 'Deployment sucessful. Trespassers will be neutralized.', null, null, null, null, null, null, null, 'Установлено успешно. Нарушители будут нейтрализованы.', '0', '0', '0', '0', 'converted_sentry SAY_CONVERTED_1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000186', 'Now I cut you!', null, null, null, null, null, null, null, 'Теперь я разрежу тебя!', '0', '1', '7', '0', 'engineer_spark SAY_ATTACK');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000185', '%s puts the shell to his ear.', null, null, null, null, null, null, null, 'прикладывает раковину к его уху.', '0', '2', '7', '0', 'engineer_spark EMOTE_SHELL');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000184', 'Yes Master, all goes along as planned.', null, null, null, null, null, null, null, 'Да мастер, все идет по плану.', '0', '0', '7', '0', 'engineer_spark SAY_TEXT');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000182', 'Ughhh... I hurt. Can you help me?', null, null, null, null, null, null, null, 'Угххх... Я ранен. Ты можешь мне помочь?', '0', '0', '7', '0', 'draenei_survivor SAY_HELP3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000183', 'I don\'t know if I can make it, please help me...', null, null, null, null, null, null, null, 'Я неуверен что смогу это сделать, пожалуйста помоги мне...', '0', '0', '7', '0', 'draenei_survivor SAY_HELP4');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000181', 'Everything hurts, Please make it stop...', null, null, null, null, null, null, null, 'Все болит, Пожалуйста заставьте это превратиться...', '0', '0', '7', '0', 'draenei_survivor SAY_HELP2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000179', 'Ugh... what is this place? Is that all that\'s left of the ship over there?', null, null, null, null, null, null, null, 'Тьфу... что это за место? Неужели сквозь все это судно прошло?', '0', '0', '7', '0', 'draenei_survivor SAY_HEAL4');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000180', 'Oh, the pain...', null, null, null, null, null, null, null, 'Ох, боль...', '0', '0', '7', '0', 'draenei_survivor SAY_HELP1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000177', '$C, Where am I? Who are you? Oh no! What happened to the ship?', null, null, null, null, null, null, null, '$C, Где я? Кто ты? Ох нет! Что случилось с кораблем?', '0', '0', '7', '0', 'draenei_survivor SAY_HEAL2');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000178', '$C You saved me! I owe you a debt that I can never repay. I\'ll go see if I can help the others.', null, null, null, null, null, null, null, '$C вы спасли меня! Я у вас в долгу, который я никогда не смогу вернуть. Я пойду посмотрю могу ли я помочь остальным.', '0', '0', '7', '0', 'draenei_survivor SAY_HEAL3');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000175', 'Thank you, mortal.', null, null, null, null, null, null, null, 'Спасибо тебе, смертный.', '0', '0', '11', '0', ' SAY_JUST_EATEN');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000176', 'The last thing I remember is the ship falling and us getting into the pods. I\'ll go see how I can help. Thank you!', null, null, null, null, null, null, null, 'Последней вещью, которую я помню, является крушение судна и нас входящих в стадо. Я пойду посмотрю, чем я могу помочь. Спасибо!', '0', '0', '7', '0', 'draenei_survivor SAY_HEAL1');
INSERT IGNORE INTO `script_texts` VALUES ('21469', '-1000174', 'Good $N, you are under the spell\'s influence. I must analyze it quickly, then we can talk.', null, null, null, null, null, null, null, 'Дорогой $N, Вы находитесь под влиянием заклинания. Я должен это быстро проанализировать, тогда мы сможем поговорить.', '0', '0', '7', '0', 'daranelle SAY_SPELL_INFLUENCE');
INSERT IGNORE INTO `script_texts` VALUES ('12429', '-1000173', 'NOOOOooooooo!', null, null, null, null, null, null, null, 'НЕЕЕЕЕЕееееет!', '0', '1', '0', '0', 'nether_drake SAY_NIHIL_INTERRUPT');
INSERT IGNORE INTO `script_texts` VALUES ('12429', '-1000172', 'No doubt the fools that banished me are long dead. I shall take wing survey my demense. Pray to whatever gods you hold dear that we do not meet again.', null, null, null, null, null, null, null, 'Без сомнения дураки, которые изгнали меня, уже давно мертвы. Я пошлю на разведку свою демонессу. Молитесь любым богам, дорожите этим, мы не встретимся снова.', '0', '1', '0', '0', 'nether_drake SAY_NIHIL_4');
INSERT IGNORE INTO `script_texts` VALUES ('12429', '-1000171', 'Where shall I begin? I cannot bother myself with a worm such as yourself. There is a world to be conquered!', null, null, null, null, null, null, null, 'С чего бы начать? Я не могу позаботиться о таких червях сам. Этот мир будет завоеван!', '0', '1', '0', '0', 'nether_drake SAY_NIHIL_3');
INSERT IGNORE INTO `script_texts` VALUES ('12429', '-1000170', 'All of Draenor shall quick beneath my feet! I will destroy this world and reshape it in my image!', null, null, null, null, null, null, null, 'Все Дреноры должны склониться у моих ног! Я разрушу этот мир и изменю его по своему желанию!', '0', '1', '0', '0', 'nether_drake SAY_NIHIL_2');
INSERT IGNORE INTO `script_texts` VALUES ('12429', '-1000169', 'Muahahahaha! You fool! You\'ve released me from my banishment in the interstices between space and time!', null, null, null, null, null, null, null, 'Муахаххахаха! Вы дураки! Вы освободили меня из моего изгнания в промежутках между пространством и временем!', '0', '1', '0', '0', 'nether_drake SAY_NIHIL_1');
INSERT IGNORE INTO `script_texts` VALUES ('12429', '-1000167', 'System failure in five, f-o-u-r...', null, null, null, null, null, null, null, 'Отказ системы через пять, че-ты-рее...', '11352', '1', '0', '0', 'doomwalker SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('12429', '-1000168', 'Who dares awaken Aquementas?', null, null, null, null, null, null, null, 'Кто смеет пробуждать Аквамонтоса?', '0', '1', '0', '0', 'aquementas AGGRO_YELL_AQUE');
INSERT IGNORE INTO `script_texts` VALUES ('12429', '-1000166', 'Target exterminated.', null, null, null, null, null, null, null, 'Цель уничтожена.', '11351', '1', '0', '0', 'doomwalker SAY_SLAY_3');
INSERT IGNORE INTO `script_texts` VALUES ('12429', '-1000164', 'Threat level zero.', null, null, null, null, null, null, null, 'Угроза устранена.', '11349', '1', '0', '0', 'doomwalker SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('12429', '-1000165', 'Directive accomplished.', null, null, null, null, null, null, null, 'Задача выполнена.', '11350', '1', '0', '0', 'doomwalker SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000161', 'Magnitude set. Release.', null, null, null, null, null, null, null, 'Магнитуда установлена. Запуск.', '11346', '1', '0', '0', 'doomwalker SAY_EARTHQUAKE_2');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000162', 'Trajectory locked.', null, null, null, null, null, null, null, 'Траектория задана.', '11347', '1', '0', '0', 'doomwalker SAY_OVERRUN_1');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000163', 'Engage maximum speed.', null, null, null, null, null, null, null, 'Включаю максимальную скорость.', '11348', '1', '0', '0', 'doomwalker SAY_OVERRUN_2');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000160', 'Tectonic disruption commencing.', null, null, null, null, null, null, null, 'Начинаю тектоническую дестабилизацию.', '11345', '1', '0', '0', 'doomwalker SAY_EARTHQUAKE_1');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000159', 'Do not proceed. You will be eliminated!', null, null, null, null, null, null, null, 'Не приближайтесь. Вы будете уничтожены!', '11344', '1', '0', '0', 'doomwalker SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000158', 'Impudent whelps, you only delay the inevitable. Where one has fallen, ten shall rise. Such is the will of Kazzak...', null, null, null, null, null, null, null, 'Нахальные щенки, вы только задерживаете неминуемое. Как один спрыгнет, десятерых освободят. Таково желание Каззака...', '0', '1', '0', '0', 'kazzak SAY_RAND2');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000157', 'Invaders, you dangle upon the precipice of oblivion! The Burning Legion comes and with it comes your end.', null, null, null, null, null, null, null, 'Захватчики, вас повесят над пропастью забвения! Пылающий Легион на подходе, и с этим ваш конец все ближе.', '0', '1', '0', '0', 'kazzak SAY_RAND1');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000156', '%s goes into a frenzy!', null, null, null, null, null, null, null, 'приходит в безумство!', '0', '2', '0', '0', 'kazzak EMOTE_FRENZY');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000155', 'The Legion... will never... fall.', null, null, null, null, null, null, null, 'Легион... никогда... не падет.', '11340', '1', '0', '0', 'kazzak SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000154', 'The universe will be remade.', null, null, null, null, null, null, null, 'Грядет передел вселенной.', '11339', '1', '0', '0', 'kazzak SAY_KILL3');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000153', 'Contemptible wretch!', null, null, null, null, null, null, null, 'Презренная тварь!', '11338', '1', '0', '0', 'kazzak SAY_KILL2');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000152', 'Kirel Narak!', null, null, null, null, null, null, null, 'Кирел Нарак!', '11337', '1', '0', '0', 'kazzak SAY_KILL1');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000151', 'I\'ll rip the flesh from your bones!', null, null, null, null, null, null, null, 'Я сорву плоть с ваших костей!', '11336', '1', '0', '0', 'kazzak SAY_SURPREME2');
INSERT IGNORE INTO `script_texts` VALUES ('20129', '-1000150', 'All life must be eradicated!', null, null, null, null, null, null, null, 'Все живое должно быть уничтожено!', '11335', '1', '0', '0', 'kazzak SAY_SURPREME1');
INSERT IGNORE INTO `script_texts` VALUES ('10427', '-1000149', 'All mortals will perish!', null, null, null, null, null, null, null, 'Смертным не место в этом мире!', '11334', '1', '0', '0', 'kazzak SAY_AGGRO2');
INSERT IGNORE INTO `script_texts` VALUES ('10427', '-1000148', 'The Legion will conquer all!', null, null, null, null, null, null, null, 'Легион покорит вселенную!', '11333', '1', '0', '0', 'kazzak SAY_AGGRO1');
INSERT IGNORE INTO `script_texts` VALUES ('16295', '-1000146', 'Liatha, get someone to look at those injuries. Thank you for bringing her back safely.', null, null, null, null, null, null, null, 'Лиантэ, заставьте кого-нибудь присматривать за теми ранами. Спасибо, что вернули ее в целости и сохранности.', '0', '0', '1', '0', 'lilatha CAPTAIN_ANSWER');
INSERT IGNORE INTO `script_texts` VALUES ('10427', '-1000147', 'I remember well the sting of defeat at the conclusion of the Third War. I have waited far too long for my revenge. Now the shadow of the Legion falls over this world. It is only a matter of time until all of your failed creation... is undone.', null, null, null, null, null, null, null, 'Я хорошо помню горечь поражения на исходе третей войны, я слишком долго жаждал мести. Отныне все сущее накроет тень легиона. Еще немного и этот жалкий мир исчезнет без следа.', '11332', '1', '0', '0', 'kazzak SAY_INTRO');
INSERT IGNORE INTO `script_texts` VALUES ('16295', '-1000145', 'Captain Helios, I\'ve been rescued from the Amani Catacombs. Reporting for duty, sir!', null, null, null, null, null, null, null, 'Капитан Гелиос, я был спасен из Катакомб Амани. Явился на службу, сэр!', '0', '0', '1', '0', 'lilatha SAY_END2');
INSERT IGNORE INTO `script_texts` VALUES ('16295', '-1000143', 'There\'s Farstrider Enclave now, $C. Not far to go... Look out! Troll ambush!!', null, null, null, null, null, null, null, 'А сейчас в Анклав Странников, $C. Не далеко идти... Смотрите! Засада троллей!!', '0', '0', '1', '0', 'lilatha SAY_PROGRESS3');
INSERT IGNORE INTO `script_texts` VALUES ('16295', '-1000144', 'Thank you for saving my life and bringing me back to safety, $N', null, null, null, null, null, null, null, 'Спасибо что спасли мою жизнь и вернули меня обратно в целости и сохранности, $N', '0', '0', '1', '0', 'lilatha SAY_END1');
INSERT IGNORE INTO `script_texts` VALUES ('16295', '-1000142', 'I can see the light at the end of the tunnel!', null, null, null, null, null, null, null, 'Я вижу свет в конце тоннеля!', '0', '0', '1', '0', 'lilatha SAY_PROGRESS2');
INSERT IGNORE INTO `script_texts` VALUES ('16295', '-1000141', '$N, let\'s use the antechamber to the right.', null, null, null, null, null, null, null, '$N, пройдите по вестибюлю направо.', '0', '0', '1', '0', 'lilatha SAY_PROGRESS1');
INSERT IGNORE INTO `script_texts` VALUES ('16295', '-1000140', 'Let\'s go.', null, null, null, null, null, null, null, 'Пошли.', '0', '0', '1', '0', 'lilatha SAY_START');
INSERT IGNORE INTO `script_texts` VALUES ('17085', '-1000139', 'Avruu\'s magic is broken! I\'m free once again!', null, null, null, null, null, null, null, 'Чары Авруу сломаны! Я снова свободен!', '0', '0', '0', '0', 'aeranas SAY_FREE');
INSERT IGNORE INTO `script_texts` VALUES ('17085', '-1000138', 'Avruu\'s magic... it still controls me. You must fight me, mortal. It\'s the only way to break the spell!', null, null, null, null, null, null, null, 'Волшебство Авруу... все еще управляет мной. Вы должны бороться со мной, смертные. Это - единственный способ снять заклинание!', '0', '0', '0', '0', 'aeranas SAY_SUMMON');
INSERT IGNORE INTO `script_texts` VALUES ('19831', '-1000137', 'See to it, Ardonis!', null, null, null, null, null, null, null, 'Посмотри на это, Ардонис!', '0', '0', '0', '0', 'dawnforge COMMANDER_DAWNFORGE_5');
INSERT IGNORE INTO `script_texts` VALUES ('19831', '-1000135', 'I am returning to Tempest Keep. See to it that I do not have reason to return!', null, null, null, null, null, null, null, 'Я возвращаюсь в Аркатрац. Проследите, чтобы у меня не было причины вернуться!', '0', '0', '0', '0', 'dawnforge PATHALEON_CULATOR_IMAGE_2_2');
INSERT IGNORE INTO `script_texts` VALUES ('19831', '-1000136', 'Yes, my lord.', null, null, null, null, null, null, null, 'Да, мой повелитель.', '0', '0', '0', '0', 'dawnforge COMMANDER_DAWNFORGE_4 SAY_ARCANIST_ARDONIS_2');
INSERT IGNORE INTO `script_texts` VALUES ('19831', '-1000133', 'Duro will be reinforced! Ultris was a complete disaster. I will NOT have that mistake repeated!', null, null, null, null, null, null, null, 'Даро будет укреплен! Ультрис был полным бедствием. Я НЕ допущу той же ошибки!', '0', '0', '0', '0', 'dawnforge PATHALEON_CULATOR_IMAGE_2');
INSERT IGNORE INTO `script_texts` VALUES ('19831', '-1000134', 'We\'ve had too many setbacks along the way: Hellfire Citadel, Fallen Sky Ridge, Firewing Point... Prince Kael\'thas will tolerate no further delays. I will tolerate nothing other than complete success!', null, null, null, null, null, null, null, 'У нас было слишком много неудач по пути: Цитадель Адского огня, Упавший Горный хребет Неба, Пункт Огнекрылов... Принц Кель\'тас не потерпит дальнейших задержек. Я не потерплю ничего кроме полного успеха!', '0', '0', '0', '0', 'dawnforge PATHALEON_CULATOR_IMAGE_2_1');
INSERT IGNORE INTO `script_texts` VALUES ('19831', '-1000132', 'My lord!', null, null, null, null, null, null, null, 'Повелитель!', '0', '0', '0', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_3');
INSERT IGNORE INTO `script_texts` VALUES ('19831', '-1000131', 'Indeed, it is not a request.', null, null, null, null, null, null, null, 'Действительно, это не просьба.', '0', '0', '0', '0', 'dawnforge SAY_PATHALEON_CULATOR_IMAGE_1');
INSERT IGNORE INTO `script_texts` VALUES ('19831', '-1000130', 'You will do as ordered. Manaforge Duro has come under heavy attack by mana creatures and the situation is out of control. Failure to comply will not be tolerated!', null, null, null, null, null, null, null, 'Вы сделаете как приказано. Манагорн Даро подвергся тяжелому нападению мана существ, и ситуация является неконтролируемой. Отказ подчиниться не приемлим!', '0', '0', '0', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_2');
INSERT IGNORE INTO `script_texts` VALUES ('19831', '-1000129', 'You cannot be serious! We are severely understaffed and can barely keep this manaforge functional!', null, null, null, null, null, null, null, 'Вы не можете быть серьезным! Мы весьма нуждаемся в рабочей силе и можем только держать Манагорн функциональным!', '0', '0', '0', '0', 'dawnforge SAY_ARCANIST_ARDONIS_1');
INSERT IGNORE INTO `script_texts` VALUES ('19831', '-1000128', 'We need you to send reinforcements to Manaforge Duro, Ardonis. This is not a request, it\'s an order.', null, null, null, null, null, null, null, 'Мы нуждаемся в вас, чтобы послать подкрепление в Манагорн Даро, Ардонис. Это не просьба, это - приказ.', '0', '0', '0', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_1');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000127', 'The Affray is over.', null, null, null, null, null, null, null, 'Драка заканчивается.', '0', '0', '0', '0', 'twiggy SAY_TWIGGY_OVER');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000126', 'Challenger is down!', null, null, null, null, null, null, null, 'Претендент падает!', '0', '0', '0', '0', 'twiggy SAY_TWIGGY_DOWN');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000125', 'You! Enter the fray!', null, null, null, null, null, null, null, 'Вы! В драку!', '0', '0', '0', '0', 'twiggy SAY_TWIGGY_FRAY');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000124', 'The Affray has begun. $n, get ready to fight!', null, null, null, null, null, null, null, 'Драка началась! $n, готовьтесь драться!', '0', '0', '0', '0', 'twiggy SAY_TWIGGY_BEGIN');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000123', 'Ready when you are, $c.', null, null, null, null, null, null, null, 'Готовый, когда вы есть, $c.', '0', '0', '0', '15', 'big will SAY_BIG_WILL_READY');
INSERT IGNORE INTO `script_texts` VALUES ('16993', '-1000122', 'You won\'t keep me from getting to Falcon Watch!', null, null, null, null, null, null, null, 'Вы не помешаете мне добраться до Часов Сокола!', '0', '0', '1', '0', 'wounded elf SAY_ELF_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('16993', '-1000120', 'Did you hear something?', null, null, null, null, null, null, null, 'Вы слышали что-нибудь?', '0', '0', '1', '0', 'wounded elf SAY_ELF_SUMMON2');
INSERT IGNORE INTO `script_texts` VALUES ('16993', '-1000121', 'Falcon Watch, at last! Now, where\'s my... Oh no! My pack, it\'s missing! Where has -', null, null, null, null, null, null, null, 'Часы Сокола, наконец! Теперь, где мой... О нет! Мой пакет, он отсутствует! Где -', '0', '0', '1', '0', 'wounded elf SAY_ELF_COMPLETE');
INSERT IGNORE INTO `script_texts` VALUES ('16993', '-1000119', 'Allow me a moment to rest. The journey taxes what little strength I have.', null, null, null, null, null, null, null, 'Позвольте мне немного отдохнуть. Тяжесть поездки отнимает у меня много сил.', '0', '0', '1', '16', 'wounded elf SAY_ELF_RESTING');
INSERT IGNORE INTO `script_texts` VALUES ('16993', '-1000118', 'Over there! They\'re following us!', null, null, null, null, null, null, null, 'Вон там! Они преследуют нас!', '0', '0', '1', '0', 'wounded elf SAY_ELF_SUMMON1');
INSERT IGNORE INTO `script_texts` VALUES ('17312', '-1000116', 'hugs her father.', null, null, null, null, null, null, null, 'обнимает ее отца.', '0', '2', '7', '0', 'magwin EMOTE_HUG');
INSERT IGNORE INTO `script_texts` VALUES ('16993', '-1000117', 'Thank you for agreeing to help. Now, let\'s get out of here $N.', null, null, null, null, null, null, null, 'Спасибо, что приняли помощь. Теперь давайте выбираться отсюда $N.', '0', '0', '1', '0', 'wounded elf SAY_ELF_START');
INSERT IGNORE INTO `script_texts` VALUES ('17312', '-1000115', 'You can thank $N for getting me back here safely, father.', null, null, null, null, null, null, null, 'Вы можете благодарить $N за мое возвращение, отец.', '0', '0', '7', '0', 'magwin SAY_END2');
INSERT IGNORE INTO `script_texts` VALUES ('17312', '-1000114', 'Father! Father! You\'re alive!', null, null, null, null, null, null, null, 'Отец! Отец! Ты жив!', '0', '0', '7', '0', 'magwin SAY_END1');
INSERT IGNORE INTO `script_texts` VALUES ('17312', '-1000112', 'Help me!', null, null, null, null, null, null, null, 'Помогите мне!', '0', '0', '7', '0', 'magwin SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('17312', '-1000113', 'My poor family. Everything has been destroyed.', null, null, null, null, null, null, null, 'Мое бедное семейство. Все было уничтожено.', '0', '0', '7', '0', 'magwin SAY_PROGRESS');
INSERT IGNORE INTO `script_texts` VALUES ('12858', '-1000110', 'Go report that the outpost is taken. We will remain here.', null, null, null, null, null, null, null, 'Идите сообщите, что застава взята. Мы останемся здесь.', '0', '0', '1', '0', 'torek SAY_END');
INSERT IGNORE INTO `script_texts` VALUES ('17312', '-1000111', 'Our house is this way, through the thicket.', null, null, null, null, null, null, null, 'Наш дом по этой дороге, там за зарослями.', '0', '0', '7', '0', 'magwin SAY_START');
INSERT IGNORE INTO `script_texts` VALUES ('12858', '-1000109', 'Silverwing is ours!', null, null, null, null, null, null, null, 'Сереброкрылый наш!', '0', '0', '1', '0', 'torek SAY_WIN');
INSERT IGNORE INTO `script_texts` VALUES ('12858', '-1000108', 'Prepare yourselves. Silverwing is just around the bend.', null, null, null, null, null, null, null, 'Готовьтесь сами. Сереброкрылый сейчас согнется в дугу.', '0', '0', '1', '0', 'torek SAY_PREPARE');
INSERT IGNORE INTO `script_texts` VALUES ('12858', '-1000107', 'Ok, Lets move out!', null, null, null, null, null, null, null, 'Хорошо, уходим!', '0', '0', '1', '0', 'torek SAY_MOVE');
INSERT IGNORE INTO `script_texts` VALUES ('12858', '-1000106', 'Everyone ready?', null, null, null, null, null, null, null, 'Все готовы?', '0', '0', '1', '0', 'torek SAY_READY');
INSERT IGNORE INTO `script_texts` VALUES ('467', '-1000105', 'Help!', null, null, null, null, null, null, null, 'Помогите!', '0', '0', '7', '0', 'defias traitor SAY_AGGRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('467', '-1000104', '%s coming in fast! Prepare to fight!', null, null, null, null, null, null, null, '%s быстрее ко мне! Готовься к битве!', '0', '0', '7', '0', 'defias traitor SAY_AGGRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('467', '-1000103', 'You can go tell Stoutmantle this is where the Defias Gang is holed up, $N.', null, null, null, null, null, null, null, 'Вы можете сходить рассказать об этом Камнегривам, они скрываются в Братстве Справедливости, $N.', '0', '0', '7', '0', 'defias traitor SAY_END');
INSERT IGNORE INTO `script_texts` VALUES ('467', '-1000102', 'The entrance is hidden here in Moonbrook. Keep your eyes peeled for thieves. They want me dead.', null, null, null, null, null, null, null, 'Проход спрятан в Лунноречье. Не спускайте своих глаз с воров. Они хотят моей смерти.', '0', '0', '7', '0', 'defias traitor SAY_PROGRESS');
INSERT IGNORE INTO `script_texts` VALUES ('467', '-1000101', 'Follow me, $N. I\'ll take you to the Defias hideout. But you better protect me or I am as good as dead.', null, null, null, null, null, null, null, 'Иди за мной, $N. Я отведу тебя к укрытию Братства. Но ты лучше защищай меня, или я более хорош когда мертвый', '0', '0', '7', '0', 'defias traitor SAY_START');
INSERT IGNORE INTO `script_texts` VALUES ('6109', '-1000100', 'Come, little ones. Face me!', null, null, null, null, null, null, null, 'Подойди малявка, смотри на меня!', '0', '1', '0', '0', 'azuregos SAY_TELEPORT');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000004', 'goes into a berserker rage!', null, null, null, null, null, null, null, 'приходит в бешенство!', '0', '2', '0', '0', 'EMOTE_GENERIC_BERSERK');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000005', 'UNUSED', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE ME');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000001', '%s goes into a killing frenzy!', null, null, null, null, null, null, null, 'приходит в смертельное безумство!', '0', '2', '0', '0', 'EMOTE_GENERIC_FRENZY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1000000', '<TrinityScript Text Entry Missing!>', null, null, null, null, null, null, null, '<ScriptDev2 Text Entry Missing!>', '0', '0', '0', '0', 'DEFAULT_TEXT');
INSERT IGNORE INTO `script_texts` VALUES ('24664', '-1585024', 'Vengeance burns!', null, null, null, null, null, null, null, 'Месть жжет как пламя!', '12415', '1', '0', '0', 'kaelthas MT SAY_PHOENIX');
INSERT IGNORE INTO `script_texts` VALUES ('24664', '-1585025', 'Felomin ashal!', null, null, null, null, null, null, null, 'Феломин Ашал!', '12417', '1', '0', '0', 'kaelthas MT SAY_FLAMESTRIKE');
INSERT IGNORE INTO `script_texts` VALUES ('24664', '-1585026', 'I\'ll turn your world... upside... down...', null, null, null, null, null, null, null, 'Я переверну ваш мир... вверх... дном...', '12418', '1', '0', '0', 'kaelthas MT SAY_GRAVITY_LAPSE');
INSERT IGNORE INTO `script_texts` VALUES ('24664', '-1585027', 'Master... grant me strength.', null, null, null, null, null, null, null, 'Владыка... дай мне сил.', '12419', '1', '0', '0', 'kaelthas MT SAY_TIRED');
INSERT IGNORE INTO `script_texts` VALUES ('24664', '-1585028', 'Do not... get too comfortable.', null, null, null, null, null, null, null, 'Не слишком-то расслабляйтесь.', '12420', '1', '0', '0', 'kaelthas MT SAY_RECAST_GRAVITY');
INSERT IGNORE INTO `script_texts` VALUES ('24664', '-1585029', 'My demise accomplishes nothing! The Master will have you! You will drown in your own blood! This world shall burn! Aaaghh!', null, null, null, null, null, null, null, 'Моя смерть ничего не изменит! Владыка доберется до вас! Вы захлебнетесь собственной кровью! Весь мир поглотит пламя! Аааа!', '12421', '1', '0', '0', 'kaelthas MT SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('33993', '-1590000', 'Emalon the Storm Watcher overcharges a Tempest Minion!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'emalon EMOTE_OVERCHARGE_TEMPEST_MINION');
INSERT IGNORE INTO `script_texts` VALUES ('33993', '-1590001', 'A Tempest Minion appears to defend Emalon!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'emalon EMOTE_MINION_RESPAWN');
INSERT IGNORE INTO `script_texts` VALUES ('33993', '-1590002', 'Archavon the Stone Watcher goes into a berserker rage!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'archavon EMOTE_BERSERK');
INSERT IGNORE INTO `script_texts` VALUES ('28684', '-1601000', 'This kingdom belongs to the Scourge! Only the dead may enter.', null, null, null, null, null, null, null, 'Это королевство принадлежит Плети! Только мёртвые могут пройти.', '0', '1', '0', '0', 'SAY_AGGRO boss_krik_thir');
INSERT IGNORE INTO `script_texts` VALUES ('28684', '-1601001', 'You were foolish to come.', null, null, null, null, null, null, null, 'Придти сюда было глупо с твоей стороны.', '0', '1', '0', '0', 'SAY_SLAY_1 boss_krik_thir');
INSERT IGNORE INTO `script_texts` VALUES ('28684', '-1601002', 'As Anub\'Arak commands!', null, null, null, null, null, null, null, 'По приказу Ануб\'Арака!', '0', '1', '0', '0', 'SAY_SLAY_2 boss_krik_thir');
INSERT IGNORE INTO `script_texts` VALUES ('28684', '-1601003', 'I should be grateful. But I long ago lost the capacity.', null, null, null, null, null, null, null, 'Я поблагодарил бы вас. Но я давно забыл как это делается.', '0', '1', '0', '0', 'SAY_DEATH boss_krik_thir');
INSERT IGNORE INTO `script_texts` VALUES ('28586', '-1602000', 'I am the greatest of my father\'s sons! Your end has come!', null, null, null, null, null, null, null, 'Я величайший из сыновей своего отца! Пришел ваш конец!', '14149', '1', '0', '0', 'bjarngrim SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('28586', '-1602001', 'So ends your curse!', null, null, null, null, null, null, null, 'Освобождаю тебя от проклятия жизней!', '14153', '1', '0', '0', 'bjarngrim SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('28586', '-1602002', 'Flesh... is... weak!', null, null, null, null, null, null, null, 'Плоть... слаба!', '14154', '1', '0', '0', 'bjarngrim SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('28586', '-1602003', '...', null, null, null, null, null, null, null, '...', '14155', '1', '0', '0', 'bjarngrim SAY_SLAY_3');
INSERT IGNORE INTO `script_texts` VALUES ('28586', '-1602004', 'How can it be...? Flesh is not... stronger!', null, null, null, null, null, null, null, 'Это невозможно... Плоть не может быть сильнее!', '14156', '1', '0', '0', 'bjarngrim SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('28586', '-1602005', 'Defend yourself, for all the good it will do!', null, null, null, null, null, null, null, 'Зачищайтесь, хотя это вряд ли поможет!', '14151', '1', '0', '0', 'bjarngrim SAY_BATTLE_STANCE');
INSERT IGNORE INTO `script_texts` VALUES ('28586', '-1602006', '%s switches to Battle Stance!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'bjarngrim EMOTE_BATTLE_STANCE');
INSERT IGNORE INTO `script_texts` VALUES ('28586', '-1602007', 'GRAAAAAH! Behold the fury of iron and steel!', null, null, null, null, null, null, null, 'ВААААУ! Узрите же ярость железа и стали!', '14152', '1', '0', '0', 'bjarngrim SAY_BERSEKER_STANCE');
INSERT IGNORE INTO `script_texts` VALUES ('28586', '-1602008', '%s switches to Berserker Stance!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'bjarngrim EMOTE_BERSEKER_STANCE');
INSERT IGNORE INTO `script_texts` VALUES ('28586', '-1602009', 'Give me your worst!', null, null, null, null, null, null, null, 'Покажите мне все на что вы способны!', '14150', '1', '0', '0', 'bjarngrim SAY_DEFENSIVE_STANCE');
INSERT IGNORE INTO `script_texts` VALUES ('28586', '-1602010', '%s switches to Defensive Stance!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'bjarngrim EMOTE_DEFENSIVE_STANCE');
INSERT IGNORE INTO `script_texts` VALUES ('28546', '-1602011', 'You wish to confront the master? You must weather the storm!', null, null, null, null, null, null, null, 'Вы хотите встретиться с господином? Для этого необходимо постараться!', '14453', '1', '0', '0', 'ionar SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('28546', '-1602012', 'Shocking ... I know!', null, null, null, null, null, null, null, 'Шокирует... Неправда ли!', '14456', '1', '0', '0', 'ionar SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('28546', '-1602013', 'You atempt the unpossible.', null, null, null, null, null, null, null, 'Твои попытки тщетны.', '14457', '1', '0', '0', 'ionar SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('28546', '-1602014', 'Your spark of light is ... extinguish.', null, null, null, null, null, null, null, 'Огонь твоей жизни... потушен.', '14458', '1', '0', '0', 'ionar SAY_SLAY_3');
INSERT IGNORE INTO `script_texts` VALUES ('28546', '-1602015', 'Master... you have guests.', null, null, null, null, null, null, null, 'Господин... к вам гости.', '14459', '1', '0', '0', 'ionar SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('28546', '-1602016', 'The slightest spark shall be your undoing.', null, null, null, null, null, null, null, 'Малейшая искра станет причиной вашей смерти.', '14454', '1', '0', '0', 'ionar SAY_SPLIT_1');
INSERT IGNORE INTO `script_texts` VALUES ('28546', '-1602017', 'No one is safe!', null, null, null, null, null, null, null, 'Никто не в безопасности.', '14455', '1', '0', '0', 'ionar SAY_SPLIT_2');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602018', 'What hope is there for you? None!', null, null, null, null, null, null, null, 'Есть ли у вас надежда? Нет!', '14162', '1', '0', '0', 'loken SAY_AGGRO0');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602019', 'I have witnessed the rise and fall of empires. The birth and extinction of entire species. Over countless millennia the foolishness of mortals has remained beyond a constant. Your presence here confirms this.', null, null, null, null, null, null, null, 'Я был свидетелем рассвета и падения империй. Рождения и исчезновение целых рас. И только безрассудство смертных всегда оставалось неизменным. Ваше присутствие подтверждает это.', '14160', '1', '0', '0', 'loken SAY_INTRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602020', 'My master has shown me the future, and you have no place in it. Azeroth will be reborn in darkness. Yogg-Saron shall be released! The Pantheon shall fall!', null, null, null, null, null, null, null, 'Есть ли у вас надежда? Нет!', '14162', '1', '0', '0', 'loken SAY_INTRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602021', 'Only mortal...', null, null, null, null, null, null, null, 'Всего лишь смертные создания...', '14166', '1', '0', '0', 'loken SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602022', 'I... am... FOREVER!', null, null, null, null, null, null, null, 'Я... ВЕЧЕН!', '14167', '1', '0', '0', 'loken SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602023', 'What little time you had, you wasted!', null, null, null, null, null, null, null, 'Все твое время, потеряно напрасно!', '14168', '1', '0', '0', 'loken SAY_SLAY_3');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602024', 'My death... heralds the end of this world.', null, null, null, null, null, null, null, 'Моя смерть... предвещает конец этого мира.', '14172', '1', '0', '0', 'loken SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602025', 'You cannot hide from fate!', null, null, null, null, null, null, null, 'От судьбы не спрячешься!', '14163', '1', '0', '0', 'loken SAY_NOVA_1');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602026', 'Come closer. I will make it quick.', null, null, null, null, null, null, null, 'Подойдите поближе. Я сделаю все быстро.', '14164', '1', '0', '0', 'loken SAY_NOVA_2');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602027', 'Your flesh cannot hold out for long.', null, null, null, null, null, null, null, 'Ваша плоть долго не продержится.', '14165', '1', '0', '0', 'loken SAY_NOVA_3');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602028', 'You stare blindly into the abyss!', null, null, null, null, null, null, null, 'Вы слепо смотрите в бездну!', '14169', '1', '0', '0', 'loken SAY_75HEALTH');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602029', 'Your ignorance is profound. Can you not see where this path leads?', null, null, null, null, null, null, null, 'Ваша невежественность поражает. Неужели вы не видите конец этого пути?', '14170', '1', '0', '0', 'loken SAY_50HEALTH');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602030', 'You cross the precipice of oblivion!', null, null, null, null, null, null, null, 'Вы перешли границу забвения!', '14171', '1', '0', '0', 'loken SAY_25HEALTH');
INSERT IGNORE INTO `script_texts` VALUES ('28923', '-1602031', '%s begins to cast Lightning Nova!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'loken EMOTE_NOVA');
INSERT IGNORE INTO `script_texts` VALUES ('28587', '-1602032', 'It is you who have destroyed my children? You... shall... pay!', null, null, null, null, null, null, null, 'Так это вы убили моих детей? Вы... заплатите!', '13960', '1', '0', '0', 'volkhan SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('28587', '-1602033', 'The armies of iron will conquer all!', null, null, null, null, null, null, null, 'Армии из железа покорят всех!', '13965', '1', '0', '0', 'volkhan SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('28587', '-1602034', 'Ha, pathetic!', null, null, null, null, null, null, null, 'Ха, ничтожества!', '13966', '1', '0', '0', 'volkhan SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('28587', '-1602035', 'You have cost me too much work!', null, null, null, null, null, null, null, 'Из-за тебя я и так уже слишком многого не сделал!', '13967', '1', '0', '0', 'volkhan SAY_SLAY_3');
INSERT IGNORE INTO `script_texts` VALUES ('28587', '-1602036', 'The master was right... to be concerned.', null, null, null, null, null, null, null, 'Хозяин не зря... беспокоился.', '13968', '1', '0', '0', 'volkhan SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('28587', '-1602037', 'I will crush you beneath my boots!', null, null, null, null, null, null, null, 'Я растопчу вас своими сапогами!', '13963', '1', '0', '0', 'volkhan SAY_STOMP_1');
INSERT IGNORE INTO `script_texts` VALUES ('28587', '-1602038', 'All my work... undone!', null, null, null, null, null, null, null, 'Вся моя робота на... смарку!', '13964', '1', '0', '0', 'volkhan SAY_STOMP_2');
INSERT IGNORE INTO `script_texts` VALUES ('28587', '-1602039', 'Life from the lifelessness... death for you.', null, null, null, null, null, null, null, 'Жизнь из безжизненности... смерть для вас.', '13961', '1', '0', '0', 'volkhan SAY_FORGE_1');
INSERT IGNORE INTO `script_texts` VALUES ('28587', '-1602040', 'Nothing is wasted in the process. You will see....', null, null, null, null, null, null, null, 'У нас безотходные процедуры. Вот увидите...', '13962', '1', '0', '0', 'volkhan SAY_FORGE_2');
INSERT IGNORE INTO `script_texts` VALUES ('28587', '-1602041', '%s runs to his anvil!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'volkhan EMOTE_TO_ANVIL');
INSERT IGNORE INTO `script_texts` VALUES ('28587', '-1602042', '%s prepares to shatter his Brittle Golems!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'volkhan EMOTE_SHATTER');
INSERT IGNORE INTO `script_texts` VALUES ('27975', '-1603000', 'You shouldn\'t have come...now you will die!', null, null, null, null, null, null, null, 'Вам не надо было приходить...теперь вы умрёте!', '13487', '1', '0', '0', 'maiden of grief SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('27975', '-1603001', 'Why must it be this way?', null, null, null, null, null, null, null, 'Разве нельзя было поступить подругому?', '13488', '1', '0', '0', 'maiden of grief SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('27975', '-1603002', 'You had it coming!', null, null, null, null, null, null, null, 'Кто то сам напросился!', '13489', '1', '0', '0', 'maiden of grief SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('27975', '-1603003', 'My burden grows heavier.', null, null, null, null, null, null, null, 'Моя ноша становится всё тяжелее.', '13490', '1', '0', '0', 'maiden of grief SAY_SLAY_3');
INSERT IGNORE INTO `script_texts` VALUES ('27975', '-1603004', 'This is your own fault!', null, null, null, null, null, null, null, 'Я не виновата!', '13491', '1', '0', '0', 'maiden of grief SAY_SLAY_4');
INSERT IGNORE INTO `script_texts` VALUES ('27975', '-1603005', 'I hope you all rot! I never...wanted...this.', null, null, null, null, null, null, null, 'Надеюсь вы все подохните! Я этого...не...хотела.', '13493', '1', '0', '0', 'maiden of grief SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('27975', '-1603006', 'So much lost time... that you\'ll never get back!', null, null, null, null, null, null, null, 'Потерянно так много времени...и вам его не когда не вернуть!', '13492', '1', '0', '0', 'maiden of grief SAY_STUN');
INSERT IGNORE INTO `script_texts` VALUES ('27977', '-1603007', 'Crush....', null, null, null, null, null, null, null, 'Крушить...', '14176', '1', '0', '0', 'krystallus SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('27977', '-1603008', 'Ha...ha...ha...ha...', null, null, null, null, null, null, null, 'Ха.. Ха... ха... ха...', '14177', '1', '0', '0', 'krystallus SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('27977', '-1603009', 'Uuuuhhhhhhhhhh......', null, null, null, null, null, null, null, 'Уааааааххххааааа......', '14179', '1', '0', '0', 'krystallus SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('27977', '-1603010', 'Break.... you....', null, null, null, null, null, null, null, 'Сломать... всех...', '14178', '1', '0', '0', 'krystallus SAY_SHATTER');
INSERT IGNORE INTO `script_texts` VALUES ('27978', '-1603011', 'Soft, vulnerable shells. Brief, fragile lives. You can not escape the curse of flesh!', null, null, null, null, null, null, null, 'Мягкие не надёжные оболочки. Мимолётные хрупкие жизни. Вы не можете избавиться от проклятия плоти!', '0', '1', '0', '0', 'Sjonnir SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('27978', '-1603012', 'Flesh is no match for iron!', null, null, null, null, null, null, null, 'Плоть не сравница с железом!', '0', '1', '0', '0', 'Sjonnir SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('27978', '-1603013', 'Armies of iron will smother the world!', null, null, null, null, null, null, null, 'Армии железа заполонят этот мир!', '0', '1', '0', '0', 'Sjonnir SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('27978', '-1603015', 'Loken will not rest, until the forge is retaken. You changed nothing!', null, null, null, null, null, null, null, 'Локен не отступится, пока кузница снова не будет захваченна. Вы нечего не измените!', '0', '1', '0', '0', 'Sjonnir SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603016', 'Now that\'s owning your supper!', null, null, null, null, null, null, null, 'Хэ, а вы не зря едите свой хлеб!', '14244', '1', '0', '0', 'brann SAY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603017', 'Press on, that\'s the way!', null, null, null, null, null, null, null, 'Поднажмите, да вот так!', '14245', '1', '0', '0', 'brann SAY_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603018', 'Keep it up now. Plenty of death-dealing for everyone!', null, null, null, null, null, null, null, 'Продолжайте в том же духе. Смерти на всех хватит!', '14246', '1', '0', '0', 'brann SAY_KILL_3');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603019', 'I\'m all kinds of busted up. Might not... make it...', null, null, null, null, null, null, null, 'Меня сильно потрепало. Могу... не пережить это...', '14257', '1', '0', '0', 'brann SAY_LOW_HEALTH');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603020', 'Not yet, not... yet-', null, null, null, null, null, null, null, 'Ещё не время... ещё не ы.....', '14258', '1', '0', '0', 'brann SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603021', 'I\'m doing everything I can!', null, null, null, null, null, null, null, 'Я делаю всё что могу!', '14260', '1', '0', '0', 'brann SAY_PLAYER_DEATH_1');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603022', 'Light preserve you!', null, null, null, null, null, null, null, 'Храни тебя свет!', '14261', '1', '0', '0', 'brann SAY_PLAYER_DEATH_2');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603023', 'I hope this is all worth it!', null, null, null, null, null, null, null, 'Надеюсь это того стоило!', '14262', '1', '0', '0', 'brann SAY_PLAYER_DEATH_3');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603024', 'Time to get some answers! Let\'s get this show on the road!', null, null, null, null, null, null, null, 'Пора узнать кое какие ответы! Начнём веселье!', '14259', '1', '0', '0', 'brann SAY_ESCORT_START');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603025', 'Don\'t worry. Old Brann has got your back. Keep that metal monstrosity busy and I\'ll see if I can sweet talk this machine into helping you.', null, null, null, null, null, null, null, 'Не волнуйтесь. Старик Бранн прикроет вас. Вы пока займите чем нибудь это железное чудище, а я попробую уболтать эту машину помочь нам.', '14274', '1', '0', '0', 'brann SAY_SPAWN_DWARF');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603026', 'This is a wee bit trickier that before... Oh, bloody--incomin\'!', null, null, null, null, null, null, null, 'Это будет посложнее предыдущего... Ох бес побери, наступают!', '14275', '1', '0', '0', 'brann SAY_SPAWN_TROGG');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603027', 'What in the name o\' Madoran did THAT do? Oh! Wait: I just about got it...', null, null, null, null, null, null, null, 'Что во имя Мадорана это было? Оо! Погодите, я почти понял...', '14276', '1', '0', '0', 'brann SAY_SPAWN_OOZE');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603028', 'Ha, that did it. Help\'s a-coming. Take this you glow-eying brute!', null, null, null, null, null, null, null, 'Ха, вот так. Помощь уже в пути. Получай ты светящийся железный чурбан!', '14277', '1', '0', '0', 'brann SAY_SPAWN_EARTHEN');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603029', 'Take a moment and relish this with me! Soon all will be revealed! Okay then, let\'s do this!', null, null, null, null, null, null, null, 'Насладитесь этим моментом вместе со мной! Скоро мне всё станет извесно! Ну поехали!', '14247', '1', '0', '0', 'brann SAY_EVENT_INTRO_1');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603030', 'Now keep an eye out! I\'ll have this licked in two shakes of a--', null, null, null, null, null, null, null, 'Теперь будте внимательны не успеете и глазом моргнуть как а.....', '14248', '1', '0', '0', 'brann SAY_EVENT_INTRO_2');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603031', 'Warning! Life form pattern not recognized. Archival processing terminated. Continued interference will result in targeted response.', null, null, null, null, null, null, null, 'Предупреждение! Форма жизни не распознанна.. Обработка архива прекращенна. Последущее вмешательство приведёт к нацеленному протеводействию.', '13765', '1', '0', '0', 'brann SAY_EVENT_INTRO_3_ABED');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603032', 'Oh, that doesn\'t sound good. We might have a complication or two...', null, null, null, null, null, null, null, 'Оо! это не хорошо. У нас могут возникнуть небольшие затруднения...', '14249', '1', '0', '0', 'brann SAY_EVENT_A_1');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603033', 'Security breach in progress. Analysis of historical archives transferred to lower priority queue. Countermeasures engaged.', null, null, null, null, null, null, null, 'Обнаруженно вторжение в систему. Преоритетность работ по анализу исторических архивов пониженно. Ответные меры инициированны.', '13756', '1', '0', '0', 'brann SAY_EVENT_A_2_KADD');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603034', 'Ah, you want to play hardball, eh? That\'s just my game!', null, null, null, null, null, null, null, 'Аа, значит играем по крупному? Обожаю такие игры!', '14250', '1', '0', '0', 'brann SAY_EVENT_A_3');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603035', 'Couple more minutes and I\'ll--', null, null, null, null, null, null, null, 'Ещё пара минут и Я ув.....', '14251', '1', '0', '0', 'brann SAY_EVENT_B_1');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603036', 'Threat index threshold exceeded. Celestial archive aborted. Security level heightened.', null, null, null, null, null, null, null, 'Порог допустимой угрозы превышен. Астрономический архив отключён. Уровень безопасности повышен.', '13761', '1', '0', '0', 'brann SAY_EVENT_B_2_MARN');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603037', 'Heightened? What\'s the good news?', null, null, null, null, null, null, null, 'Повышение? А хорошие новости есть?', '14252', '1', '0', '0', 'brann SAY_EVENT_B_3');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603038', 'So that was the problem? Now I\'m makin\' progress...', null, null, null, null, null, null, null, 'Так вот в чём было дело? Теперь я двигаюсь в нужном направлении...', '14253', '1', '0', '0', 'brann SAY_EVENT_C_1');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603039', 'Critical threat index. Void analysis diverted. Initiating sanitization protocol.', null, null, null, null, null, null, null, 'Критическое значение уровря угрозы. Перенаправление анализа бездны. Инициирование протокола отчищения.', '13767', '1', '0', '0', 'brann SAY_EVENT_C_2_ABED');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603040', 'Hang on! Nobody\'s gonna\' be sanitized as long as I have a say in it!', null, null, null, null, null, null, null, 'Стойте! Не кокого отчищения не будет пока я тут командую!', '14254', '1', '0', '0', 'brann SAY_EVENT_C_3');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603041', 'Ha! The old magic fingers finally won through! Now let\'s get down to-', null, null, null, null, null, null, null, 'Ха.! Мои старые добрые пальцы наконец то преодалели эту преграду! Теперь перейдём к гх.....', '14255', '1', '0', '0', 'brann SAY_EVENT_D_1');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603042', 'Alert! Security fail safes deactivated. Beginning memory purge...', null, null, null, null, null, null, null, 'Внимание! Меры предостарожности деактивированы. Начинаю стирание памяти и.....', '13768', '1', '0', '0', 'brann SAY_EVENT_D_2_ABED');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603043', 'Purge? No no no no no! Where did I-- Aha, this should do the trick...', null, null, null, null, null, null, null, 'Стирание памяти? Нет нет нет нет! Куда я Аха, вот это должно помочь...', '14256', '1', '0', '0', 'brann SAY_EVENT_D_3');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603044', 'System online. Life form pattern recognized. Welcome Branbronzan. Query?', null, null, null, null, null, null, null, 'Система в рабочем режиме. Форма жизни распознанна. Добро пожаловать Бранбранзор. Запрос?', '13769', '1', '0', '0', 'brann SAY_EVENT_D_4_ABED');
INSERT IGNORE INTO `script_texts` VALUES ('31134', '-1608007', 'Shiver and die!', null, null, null, null, null, null, null, null, '13948', '1', '0', '0', 'cyanigosa SAY_BREATH_ATTACK');
INSERT IGNORE INTO `script_texts` VALUES ('31134', '-1608008', 'The world has forgotten what true magic is! Let this be a reminder!', null, null, null, null, null, null, null, null, '13949', '1', '0', '0', 'cyanigosa SAY_SPECIAL_ATTACK_1');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603046', 'Tell me how that dwarfs came to be! And start at the beginning!', null, null, null, null, null, null, null, 'Расскажи мне как появились дворфы! И начни с самого начала!', '14264', '1', '0', '0', 'brann SAY_EVENT_END_02');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603047', 'Accessing prehistoric data. Retrieved. In the beginning Earthen were created to-', null, null, null, null, null, null, null, 'Считывание доисторических данных. Завершенно. Изначально земельники были созданны для-', '13770', '1', '0', '0', 'brann SAY_EVENT_END_03_ABED');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603048', 'Right, right! I know that the Earthen were made of stone to shape the deep reaches of the world but what about the anomalies? Matrix non-stabilizing and whatnot.', null, null, null, null, null, null, null, 'Аха! Я знаю что земельники были созданны из камня для работы над дальними уголками мира. А что за отклоления матрица не стабилизировалась или что?', '14265', '1', '0', '0', 'brann SAY_EVENT_END_04');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603049', 'Accessing. In the early stages of its development cycle Azeroth suffered infection by parasitic, necrophotic symbiotes.', null, null, null, null, null, null, null, 'Считывание. На ранних стадиях цикла развития Азерот был заражён паразитическими никрофатическими симбионтами.', '13771', '1', '0', '0', 'brann SAY_EVENT_END_05_ABED');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603050', 'Necro-what? Speak bloody common will ya?', null, null, null, null, null, null, null, 'Некро- что то там? Говорил же на нормальном языке?', '14266', '1', '0', '0', 'brann SAY_EVENT_END_06');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603051', 'Designation: Old Gods. Old Gods rendered all systems, including Earthen defenseless in order to facilitate assimilation. This matrix destabilization has been termed the Curse of Flesh. Effects of destabilization increased over time.', null, null, null, null, null, null, null, 'Определение: Древние Боги. Древние Боги деактивировали защиту всех систем, включая системы зимельников для облегчения осемиляции. Данные дестабилизация матрицы была класифицированна как Проклятие Плоти. Действие эфектов дестабилизации усилилось со временем.', '13772', '1', '0', '0', 'brann SAY_EVENT_END_07_ABED');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603052', 'Old Gods eh? So they zapped the Earthen with this Curse of Flesh. And then what?', null, null, null, null, null, null, null, 'Древние Боги? Значит они поразили земельников Проклятием Плоти. И что потом?', '14267', '1', '0', '0', 'brann SAY_EVENT_END_08');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603053', 'Accessing. Creators arrived to extirpate symbiotic infection. Assessment revealed that Old God infestation had grown malignant. Excising parasites would result in loss of host.', null, null, null, null, null, null, null, 'Считывание. Создатели прибыли для ликвидации симбиотической инфекции. Оценка ситуации показала что заражение от Древних Богов перешло в стадию максимальной злокачествености. Удаление паразитов могло привести к потере организма носителя.', '13757', '1', '0', '0', 'brann SAY_EVENT_END_09_KADD');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603054', 'If they killed the Old Gods Azeroth would have been destroyed.', null, null, null, null, null, null, null, 'Если бы они убили Древних Богов то Азерот был бы уничтожен.', '14268', '1', '0', '0', 'brann SAY_EVENT_END_10');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603055', 'Correct. Creators neutralized parasitic threat and contained it within the host. Forge of Wills and other systems were instituted to create new Earthen. Safeguards were implemented and protectors were appointed.', null, null, null, null, null, null, null, 'Да верно. Создатели нитролизовали паразитическую угрозу и изолировали её внутри организма носителя. Для производства Земельников были установленны Кузня Воли и другие системы. Также были внедренны меры предостарожности и назначенны хранители.', '13758', '1', '0', '0', 'brann SAY_EVENT_END_11_KADD');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603056', 'What protectors?', null, null, null, null, null, null, null, 'Какие хронители?', '14269', '1', '0', '0', 'brann SAY_EVENT_END_12');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603057', 'Designations: Aesir and Vanir or in common nomenclator Storm and Earth Giants. Sentinel Loken designated supreme. Dragon Aspects appointed to monitor evolution of Azeroth.', null, null, null, null, null, null, null, 'Определение: Езир и Ванир или согласнно обще принетой классификации Великан Бури и Великан Земли. Страж Локен избран главным куратором. Драконны назначенны для контроля за развитием жизни на Азероте.', '13759', '1', '0', '0', 'brann SAY_EVENT_END_13_KADD');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603058', 'Aesir and Vanir. Okay. So the Forge of Wills started to make new Earthen. But what happened to the old ones?', null, null, null, null, null, null, null, 'Езир и Ванир. Так. Значит Кузня Воли начала производство новых Земельников. Но что случилось со старыми?', '14270', '1', '0', '0', 'brann SAY_EVENT_END_14');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603059', 'Additional background is relevant to your query. Following global combat between-', null, null, null, null, null, null, null, 'Найденна дополнительная информация соответствующая вашему запросу. Следствие глобальной войны между Езиром и Ваниром-', '13762', '1', '0', '0', 'brann SAY_EVENT_END_15_MARN');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603060', 'Hold everything! The Aesir and Vanir went to war? Why?', null, null, null, null, null, null, null, 'Стоп! Езир и Ванир стали воевать? Почему?', '14271', '1', '0', '0', 'brann SAY_EVENT_END_16');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603061', 'Unknown. Data suggests that impetus for global combat originated with prime designate Loken who neutralized all remaining Aesir and Vanir affecting termination of conflict. Prime designate Loken then initiated stasis of several seed races including Earthen, Giant and Vrykul at designated holding facilities.', null, null, null, null, null, null, null, 'Ответ не известен. Имеющиеся данные предпологают что инициатором глобальной войны был главный куратор Локен который нитролизовал Езира и Ванира, что повлияло на завершение конфликта. После этого главный куратор Локен инициировал преостановку жизнидеятельности нескольких изначальных расс включая Земельников, Великанов и Гигантов на специальных консервационных объектах.', '13763', '1', '0', '0', 'brann SAY_EVENT_END_17_MARN');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603064', 'Well now. That\'s a lot to digest. I\'m gonna need some time to take all of this in. Thank you!', null, null, null, null, null, null, null, null, '14273', '1', '0', '0', 'brann SAY_EVENT_END_20');
INSERT IGNORE INTO `script_texts` VALUES ('31134', '-1608001', 'I will end the Kirin Tor!', null, null, null, null, null, null, null, null, '13952', '1', '0', '0', 'cyanigosa SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('31134', '-1608002', 'Dalaran will fall!', null, null, null, null, null, null, null, null, '13953', '1', '0', '0', 'cyanigosa SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('31134', '-1608003', 'So ends your defiance of the Spell-Weaver!', null, null, null, null, null, null, null, null, '13954', '1', '0', '0', 'cyanigosa SAY_SLAY_3');
INSERT IGNORE INTO `script_texts` VALUES ('31134', '-1608004', 'Perhaps... we have... underestimated... you.', null, null, null, null, null, null, null, null, '13955', '1', '0', '0', 'cyanigosa SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('31134', '-1608005', 'A valiant defense, but this city must be razed. I will fulfill Malygos\'s wishes myself!', null, null, null, null, null, null, null, null, '13946', '1', '0', '0', 'cyanigosa SAY_SPAWN');
INSERT IGNORE INTO `script_texts` VALUES ('31134', '-1608006', 'Am I interrupting?', null, null, null, null, null, null, null, null, '13951', '1', '0', '0', 'cyanigosa SAY_DISRUPTION');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603063', 'Essentially that is correct.', null, null, null, null, null, null, null, 'В основном это верно.', '13764', '1', '0', '0', 'brann SAY_EVENT_END_19_MARN');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603045', 'Query? What do you think I\'m here for? Tea and biscuits? Spill the beans already!', null, null, null, null, null, null, null, null, '14263', '1', '0', '0', 'brann SAY_EVENT_END_01');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603062', 'This Loken sounds like a nasty character. Glad we don\'t have to worry about the likes of him anymore. So if I\'m understanding you lads the original Earthen eventually woke up from this statis. And by that time this destabily-whatever had turned them into our brother dwarfs. Or at least dwarf ancestors. Hm?', null, null, null, null, null, null, null, null, '14272', '1', '0', '0', 'brann SAY_EVENT_END_18');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603065', 'Acknowledged Branbronzan. Session terminated.', null, null, null, null, null, null, null, 'Принято Бранбранзор. Сеанс завершён.', '13773', '1', '0', '0', 'brann SAY_EVENT_END_21_ABED');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603066', 'Loken?! That\'s downright bothersome... We might\'ve neutralized the iron dwarves, but I\'d lay odds there\'s another machine somewhere else churnin\' out a whole mess o\' these iron vrykul!', null, null, null, null, null, null, null, 'Локен? Час отчасу не легче. Мы справились с железными дворфами, но готов поспорить, что рядом есть другая машина, выплёвывающая железных врайколов пачками!', '14278', '1', '0', '0', 'brann SAY_VICTORY_SJONNIR_1');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603067', 'I\'ll use the forge to make badtches o\' earthen to stand guard... But our greatest challenge still remains: find and stop Loken!', null, null, null, null, null, null, null, 'Я воспользуюсь кузней и создам несколько отрядов земельников для охраны... Но наша главная задача ещё не решена,  надо найти и остановить Локена!', '14279', '1', '0', '0', 'brann SAY_VICTORY_SJONNIR_2');
INSERT IGNORE INTO `script_texts` VALUES ('28070', '-1603068', 'I think it\'s time to see what\'s behind the door near the entrance. I\'m going to sneak over there, nice and quiet. Meet me at the door and I\'ll get us in.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'brann SAY_ENTRANCE_MEET');
INSERT IGNORE INTO `script_texts` VALUES ('29305', '-1604010', 'We fought back da Scourge. What chance joo be thinkin\' JOO got?', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'SAY_AGGRO boss_moorabi');
INSERT IGNORE INTO `script_texts` VALUES ('29305', '-1604012', 'Who gonna stop me; you? ', null, null, null, null, null, null, null, '\"Кто меня остановит; ты?\"', '0', '1', '0', '0', 'SAY_SLAY_2 boss_moorabi');
INSERT IGNORE INTO `script_texts` VALUES ('29305', '-1604013', 'Not so tough now.', null, null, null, null, null, null, null, 'Теперь ты не помеха.', '0', '1', '0', '0', 'SAY_SLAY_3 boss_moorabi');
INSERT IGNORE INTO `script_texts` VALUES ('29305', '-1604014', 'If our gods can die... den so can we... ', null, null, null, null, null, null, null, 'Если наши боги могут умереть... то можем и мы...', '0', '1', '0', '0', 'SAY_DEATH boss_moorabi');
INSERT IGNORE INTO `script_texts` VALUES ('29305', '-1604015', 'Get ready for somethin\'... much... BIGGAH! ', null, null, null, null, null, null, null, 'Приготовтесь... к чемуто... БОЛЬШОМУ!', '0', '1', '0', '0', 'SAY_TRANSFORM boss_moorabi');
INSERT IGNORE INTO `script_texts` VALUES ('29305', '-1604016', 'Da ground gonna swallow you up', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'SAY_QUAKE boss_moorabi');
INSERT IGNORE INTO `script_texts` VALUES ('29305', '-1604017', '%s begins to transform!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'EMOTE_TRANSFORM boss_moorabi');
INSERT IGNORE INTO `script_texts` VALUES ('31134', '-1608000', 'We finish this now, champions of Kirin Tor!', null, null, null, null, null, null, null, null, '13947', '1', '0', '0', 'cyanigosa SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('30451', '-1615000', 'I fear nothing! Least of all you!', null, null, null, null, null, null, null, 'Я не боюсь ничего! Тем более вас!', '14111', '1', '0', '0', 'shadron SAY_SHADRON_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('30451', '-1615001', 'You are insignificant!', null, null, null, null, null, null, null, 'Ты ничтожество!', '14112', '1', '0', '0', 'shadron SAY_SHADRON_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('30451', '-1615002', 'Such mediocre resistance!', null, null, null, null, null, null, null, 'Какое слабое сопротивление!', '14113', '1', '0', '0', 'shadron SAY_SHADRON_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('30451', '-1615003', 'We...are superior! How could this...be...', null, null, null, null, null, null, null, 'Мы... выше всех! Как это... возможно...', '14118', '1', '0', '0', 'shadron SAY_SHADRON_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('30451', '-1615004', 'You are easily bested! ', null, null, null, null, null, null, null, 'Вас легко провести!', '14114', '1', '0', '0', 'shadron SAY_SHADRON_BREATH');
INSERT IGNORE INTO `script_texts` VALUES ('30451', '-1615005', 'I will take pity on you Sartharion, just this once.', null, null, null, null, null, null, null, 'Я пожалею тебя Сартарион, но только в этот раз.', '14117', '1', '0', '0', 'shadron SAY_SHADRON_RESPOND');
INSERT IGNORE INTO `script_texts` VALUES ('30451', '-1615006', 'Father tought me well!', null, null, null, null, null, null, null, 'Отец не зря учил меня!', '14115', '1', '0', '0', 'shadron SAY_SHADRON_SPECIAL_1');
INSERT IGNORE INTO `script_texts` VALUES ('30451', '-1615007', 'On your knees!', null, null, null, null, null, null, null, 'На колени!', '14116', '1', '0', '0', 'shadron SAY_SHADRON_SPECIAL_2');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615008', 'A Shadron Disciple appears in the Twilight!', null, null, null, null, null, null, null, 'Ученик Шадрон появляется в Сумерках!', '0', '5', '0', '0', 'shadron WHISPER_SHADRON_DICIPLE');
INSERT IGNORE INTO `script_texts` VALUES ('30452', '-1615009', 'You have no place here. Your place is among the departed.', null, null, null, null, null, null, null, 'Вам здесь не место. Ваше место среди усопших.', '14122', '1', '0', '0', 'tenebron SAY_TENEBRON_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('30452', '-1615010', 'No contest.', null, null, null, null, null, null, null, 'Не интересно.', '14123', '1', '0', '0', 'tenebron SAY_TENEBRON_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('30452', '-1615011', 'Typical... Just as I was having fun.', null, null, null, null, null, null, null, 'Как обычно... Только я вошла во вкус.', '14124', '1', '0', '0', 'tenebron SAY_TENEBRON_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('30452', '-1615012', 'I should not... have held back...', null, null, null, null, null, null, null, 'Ненадо было... вмешиваться...', '14129', '1', '0', '0', 'tenebron SAY_TENEBRON_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('30452', '-1615013', 'To darkness I condemn you...', null, null, null, null, null, null, null, 'Приговариваю вас к тьме...', '14125', '1', '0', '0', 'tenebron SAY_TENEBRON_BREATH');
INSERT IGNORE INTO `script_texts` VALUES ('30452', '-1615014', 'It is amusing to watch you struggle. Very well, witness how it is done.', null, null, null, null, null, null, null, 'Занятно наблюдать, как ты сражаешься. Хорошо, смотри, как надо это делать.', '14128', '1', '0', '0', 'tenebron SAY_TENEBRON_RESPOND');
INSERT IGNORE INTO `script_texts` VALUES ('30452', '-1615015', 'Arrogant little creatures! To challenge powers you do not yet understand...', null, null, null, null, null, null, null, 'Самонадеянные мелкие твари! Вы бросаете вызов силам, от понимания которых, вы еще далеки!', '14126', '1', '0', '0', 'tenebron SAY_TENEBRON_SPECIAL_1');
INSERT IGNORE INTO `script_texts` VALUES ('30452', '-1615016', 'I am no mere dragon! You will find I am much, much, more...', null, null, null, null, null, null, null, 'Я не простой дракон! Увидите совсем, совсем, не простой...', '14127', '1', '0', '0', 'tenebron SAY_TENEBRON_SPECIAL_2');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615017', '%s begins to hatch eggs in the twilight!', null, null, null, null, null, null, null, '%s начинает высиживать яйца в сумерках!', '0', '5', '0', '0', 'tenebron WHISPER_HATCH_EGGS');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615018', 'It is my charge to watch over these eggs. I will see you burn before any harm comes to them!', null, null, null, null, null, null, null, 'Моя обязанность оберегать эти яйца. И вы сгорите прежде, чем хоть пальцем дотронетесь их.', '14093', '1', '0', '0', 'sartharion SAY_SARTHARION_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615019', 'This pathetic siege ends NOW!', null, null, null, null, null, null, null, 'Это смехотворное сражение закончиться СЕЙЧАСЖЕ!', '14103', '1', '0', '0', 'sartharion SAY_SARTHARION_BERSERK');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615020', 'Burn, you miserable wretches!', null, null, null, null, null, null, null, 'Гори, злощасный сброд!', '14098', '1', '0', '0', 'sartharion SAY_SARTHARION_BREATH');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615021', 'Shadron! Come to me, all is at risk!', null, null, null, null, null, null, null, 'Шадрон! На помощь, все на кону!', '14105', '1', '0', '0', 'sartharion SARTHARION_CALL_SHADRON');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615022', 'Tenebron! The eggs are yours to protect as well!', null, null, null, null, null, null, null, 'Тенеброн! Ты тоже должна защищать яйца!', '14106', '1', '0', '0', 'sartharion SAY_SARTHARION_CALL_TENEBRON');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615023', 'Vesperon! The clutch is in danger! Assist me!', null, null, null, null, null, null, null, 'Весперон! Кладка в опасности! Помоги мне!', '14104', '1', '0', '0', 'sartharion SAY_SARTHARION_CALL_VESPERON');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615024', 'Such is the price... of failure...', null, null, null, null, null, null, null, 'Такова цена... неудачи...', '14107', '1', '0', '0', 'sartharion SAY_SARTHARION_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615025', 'Such flammable little insects....', null, null, null, null, null, null, null, 'Мелкая мошка хорошо горит...', '14099', '1', '0', '0', 'sartharion SAY_SARTHARION_SPECIAL_1');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615026', 'Your charred bones will litter the floor!', null, null, null, null, null, null, null, 'Ваши обугленные кости завалят весь пол!', '14100', '1', '0', '0', 'sartharion SAY_SARTHARION_SPECIAL_2');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615027', 'How much heat can you take?', null, null, null, null, null, null, null, 'Посмотрим, как вы переносите жару?', '14101', '1', '0', '0', 'sartharion SAY_SARTHARION_SPECIAL_3');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615028', 'All will be reduced to ash!', null, null, null, null, null, null, null, 'От вас останется всего лишь горстка пепла!', '14102', '1', '0', '0', 'sartharion SAY_SARTHARION_SPECIAL_4');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615029', 'You will make a fine meal for the hatchlings.', null, null, null, null, null, null, null, 'Из тебя выйдет хорошая закуска для детенышей.', '14094', '1', '0', '0', 'sartharion SAY_SARTHARION_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615030', 'You are the grave disadvantage.', null, null, null, null, null, null, null, 'Тебе смертельно не повезло.', '14096', '1', '0', '0', 'sartharion SAY_SARTHARION_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615031', 'This is why we call you lesser beeings.', null, null, null, null, null, null, null, 'Вот поэтому мы и называем вас низшими созданиями.', '14097', '1', '0', '0', 'sartharion SAY_SARTHARION_SLAY_3');
INSERT IGNORE INTO `script_texts` VALUES ('28860', '-1615032', 'The lava surrounding %s churns!', null, null, null, null, null, null, null, 'A Vesperon Disciple appears in the Twilight!', '0', '5', '0', '0', 'sartharion WHISPER_LAVA_CHURN');
INSERT IGNORE INTO `script_texts` VALUES ('30449', '-1615033', 'You pose no threat, lesser beings...give me your worst!', null, null, null, null, null, null, null, 'Вы безобидны, ничтожные создания... покажите мне, на что вы способны!', '14133', '1', '0', '0', 'vesperon SAY_VESPERON_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('30449', '-1615034', 'The least you could do is put up a fight...', null, null, null, null, null, null, null, 'Все, что тебе удалось, это затеять драку...', '14134', '1', '0', '0', 'vesperon SAY_VESPERON_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('30449', '-1615035', 'Was that the best you can do?', null, null, null, null, null, null, null, 'И это все, что ты можешь сделать?', '14135', '1', '0', '0', 'vesperon SAY_VESPERON_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('30449', '-1615036', 'I still have some...fight..in...me...', null, null, null, null, null, null, null, 'Я все еще... могу... драться...', '14140', '1', '0', '0', 'vesperon SAY_VESPERON_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('30449', '-1615037', 'I will pick my teeth with your bones!', null, null, null, null, null, null, null, 'Я буду ковыряться в зубах вашими костями!', '14136', '1', '0', '0', 'vesperon SAY_VESPERON_BREATH');
