INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609202', 'The sky weeps at the devastation of sister earth! Soon, tears of blood will rain down upon us!', null, null, null, null, null, null, null, 'Небо оплакивает опусташённые земли! Скоро, и жалкие слёзы Азерота прольются на нас дождём!', '14678', '1', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609203', 'Death knights of Acherus, the death march begins!', null, null, null, null, null, null, null, 'Рыцари Смерти Акеруса, смертный поход начался!', '14681', '1', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609204', 'Soldiers of the Scourge, death knights of Acherus, minions of the darkness: hear the call of the Highlord!', null, null, null, null, null, null, null, 'Воины Плети, рыцари смерти Акеруса, слуги тьмы услыште зов Верховного Лорда!', '14679', '1', '0', '22', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609205', 'RISE!', null, null, null, null, null, null, null, 'ВОСТАНТЕ!', '14680', '1', '0', '15', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609206', 'The skies turn red with the blood of the fallen! The Lich King watches over us, minions! Onward! Leave only ashes and misery in your destructive wake!', null, null, null, null, null, null, null, 'Небеса станут красными от крови падших. Король Лич взерает на вас своих слуг! Оставляйте за собой лиш пепел и страдания!', '14682', '1', '0', '25', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609207', 'Scourge armies approach!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'Korfax, Champion of the Light');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609208', 'Stand fast, brothers and sisters! The Light will prevail!', null, null, null, null, null, null, null, 'Готовтесь браться и сёстры! За СВЕТ!', '14487', '1', '0', '0', 'Lord Maxwell Tyrosus');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609209', 'Kneel before the Highlord!', null, null, null, null, null, null, null, 'Склонитесь перед Верховным Лордом!', '14683', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609210', 'You stand no chance!', null, null, null, null, null, null, null, 'У вас нет шанса!', '14684', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609211', 'The Scourge will destroy this place!', null, null, null, null, null, null, null, 'Плеть разгромит эту часовню!', '14685', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609212', 'Your life is forfeit.', null, null, null, null, null, null, null, 'Ваша жизнь уже не пренадлежит вам.', '14686', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609213', 'Life is meaningless without suffering.', null, null, null, null, null, null, null, 'Жизнь бессмысленна без страданий.', '14687', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609214', 'How much longer will your forces hold out?', null, null, null, null, null, null, null, 'Сколько ещё продержутся ваши войска?', '14688', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609215', 'The Argent Dawn is finished!\"', null, null, null, null, null, null, null, 'Серебренному Рассвету пришёл конец!', '14689', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609216', 'Spare no one!', null, null, null, null, null, null, null, 'Не щядите не кого!', '14690', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609217', 'What is this?! My... I cannot strike...', null, null, null, null, null, null, null, 'Что это?! Я... не могу нанести удар...', '14691', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609218', 'Obey me, blade!', null, null, null, null, null, null, null, 'Повинуйся мне, меч!', '14692', '1', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609219', 'You will do as I command! I am in control here!', null, null, null, null, null, null, null, 'Ты сделаеш так как я скажу! Я тут коммандую!', '14693', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609220', 'I can not... the blade fights me.', null, null, null, null, null, null, null, 'Я не могу... клинок не слушается меня.', '14694', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609221', 'What is happening to me?', null, null, null, null, null, null, null, 'Что со мной?', '14695', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609222', 'Power...wanes...', null, null, null, null, null, null, null, 'Сила... покидает меня....', '14696', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609223', 'Ashbringer defies me...', null, null, null, null, null, null, null, 'Изпепелитель отвергает меня...', '14697', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609224', 'Minions, come to my aid!', null, null, null, null, null, null, null, 'Слуги, напомощь!', '14698', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609225', 'You cannot win, Darion!', null, null, null, null, null, null, null, 'Дарион, тебе не победить!', '14584', '1', '0', '0', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609226', 'Bring them before the chapel!', null, null, null, null, null, null, null, 'Привести их к часовне!', '14585', '1', '0', '0', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609227', 'Stand down, death knights. We have lost... The Light... This place... No hope...', null, null, null, null, null, null, null, 'Назад рыцари смерти. Мы проиграли... Свет тут... Нет надежды...', '14699', '0', '0', '68', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609228', 'Have you learned nothing, boy? You have become all that your father fought against! Like that coward, Arthas, you allowed yourself to be consumed by the darkness...the hate... Feeding upon the misery of those you tortured and killed!', null, null, null, null, null, null, null, 'Ты так нечему и не научился мальчишка? В тебе ссоеденилось всё с чем бился твой отец! Как трусливый, Артас ты позволили тме.. и ненависти... затмить твой разум. Боль тех кого ты мучил и убивал лиш укрепляла тебя!', '14586', '0', '0', '378', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609229', 'Your master knows what lies beneath the chapel. It is why he dares not show his face! He\'s sent you and your death knights to meet their doom, Darion.', null, null, null, null, null, null, null, 'Твой господин знает что скрывается под часовней. Потому то он не рискнул приехать сам, а прислал сюда на верную гибель тебя и твоих рыцарей смерти, Дарин.', '14587', '0', '0', '25', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609230', 'What you are feeling right now is the anguish of a thousand lost souls! Souls that you and your master brought here! The Light will tear you apart, Darion!', null, null, null, null, null, null, null, 'То что тывствуеш сейчас это муки тысячи загубленных душ! Душ которые ты и твой господин завлекли сюда! Свет воздаст тебе по заслугам, Дарион!', '14588', '0', '0', '1', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609231', 'Save your breath, old man. It might be the last you ever draw.', null, null, null, null, null, null, null, 'Цени каждый вздох старик. Он может стать последним.', '14700', '0', '0', '25', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609232', 'My son! My dear, beautiful boy!', null, null, null, null, null, null, null, 'Мой сын! Мой дорогой милый мальчик!', '14493', '0', '0', '0', 'Highlord Alexandros Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609233', 'Father!', null, null, null, null, null, null, null, 'Отец!', '14701', '0', '0', '5', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609234', 'Argh...what...is...', null, null, null, null, null, null, null, 'Ааах... что... это..', '14702', '0', '0', '68', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609235', 'Father, you have returned!', null, null, null, null, null, null, null, 'Отец, ты вернулся!', '14703', '0', '0', '0', 'Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609236', 'You have been gone a long time, father. I thought...', null, null, null, null, null, null, null, 'Тебя так долго небыло, отец. Я думал...', '14704', '0', '0', '0', 'Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609237', 'Nothing could have kept me away from here, Darion. Not from my home and family.', null, null, null, null, null, null, null, 'Ничто не могло помешать мне быть здесь, Дарион. Тут мой дом и семья.', '14494', '0', '0', '1', 'Highlord Alexandros Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609238', 'Father, I wish to join you in the war against the undead. I want to fight! I can sit idle no longer!', null, null, null, null, null, null, null, 'Отец, Я хочу сражаться вместе с тобой против нежети я жажду битв! Я больше не могу оставаться без участным!', '14705', '0', '0', '6', 'Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609239', 'Darion Mograine, you are barely of age to hold a sword, let alone battle the undead hordes of Lordaeron! I couldn\'t bear losing you. Even the thought...', null, null, null, null, null, null, null, 'Дарион Могрейн, ты едва достиг того возраста когда можеш взять в руки меч не говоря уже о том чтобы сражаться с ордами нежети Лордерона! Я не переживу если ты погибнеш. Сама мысль...', '14495', '0', '0', '1', 'Highlord Alexandros Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609240', 'If I die, father, I would rather it be on my feet, standing in defiance against the undead legions! If I die, father, I die with you!', null, null, null, null, null, null, null, 'Если мне суждено умереть отец, пусть это случится в битве с легионами нежети, плечом к плечу с тобой!', '14706', '0', '0', '6', 'Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609241', 'My son, there will come a day when you will command the Ashbringer and, with it, mete justice across this land. I have no doubt that when that day finally comes, you will bring pride to our people and that Lordaeron will be a better place because of you. But, my son, that day is not today.', null, null, null, null, null, null, null, 'Мой сын, однажды ты завлодееш Изпепелителем и с его помощью востановиш справедливость в этих землях. Не сомневаюсь, что когда наконец придёт этот день ты сделаеш Лордерон светлее и чище, наш народ будет гордиться тобой. Но этот день ещё не настал.', '14496', '0', '0', '1', 'Highlord Alexandros Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609242', 'Do not forget...', null, null, null, null, null, null, null, 'Не забывай...', '14497', '0', '0', '6', 'Highlord Alexandros Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609243', 'Touching...', null, null, null, null, null, null, null, 'Трогательно...', '14803', '1', '0', '0', 'The Lich King');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609244', 'You have\'ve betrayed me! You betrayed us all you monster! Face the might of Mograine!', null, null, null, null, null, null, null, 'Ты предал меня! Ты предал нас всех чудовище! Узари в Моргрейне в гневе!', '14707', '1', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609245', 'He\'s mine now...', null, null, null, null, null, null, null, 'Теперь он мой...', '14805', '0', '0', '0', 'The Lich King');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609246', 'Pathetic...', null, null, null, null, null, null, null, 'И плачевно...', '14804', '0', '0', '0', 'The Lich King');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609247', 'You\'re a damned monster, Arthas!', null, null, null, null, null, null, null, 'Ты гнусное чудовище, Артас!', '14589', '0', '0', '25', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609248', 'You were right, Fordring. I did send them in to die. Their lives are meaningless, but yours...', null, null, null, null, null, null, null, 'Ты прав Ордринг. Я действительно послал их на верную смерть. Их жизни ничего не стоят, ноо вот твоя...', '14806', '0', '0', '1', 'The Lich King');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609249', 'How simple it was to draw the great Tirion Fordring out of hiding. You\'ve left yourself exposed, paladin. Nothing will save you...', null, null, null, null, null, null, null, 'Как просто оказалось вытащить великого Териона Фордринга из его норы. Ты сейчас беззащитен паладин. Нечто тебя не спасёт...', '14807', '0', '0', '1', 'The Lich King');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609250', 'ATTACK!!!', null, null, null, null, null, null, null, 'В АТАКУ!!!', '14488', '1', '0', '0', 'Lord Maxwell Tyrosus');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609251', 'APOCALYPSE!', null, null, null, null, null, null, null, 'КОНЕЦ СВЕТА!', '14808', '1', '0', '0', 'The Lich King');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609252', 'That day is not today...', null, null, null, null, null, null, null, 'Этот день ещё не настал...', '14708', '0', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609253', 'Tirion!', null, null, null, null, null, null, null, 'Тирион!', '14709', '1', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609254', 'ARTHAS!!!!', null, null, null, null, null, null, null, 'АРТАС!!!!', '14591', '1', '0', '0', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609255', 'What is this?', null, null, null, null, null, null, null, 'Что это такое?', '14809', '1', '0', '0', 'The Lich King');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609256', 'Your end.', null, null, null, null, null, null, null, 'Конец тебе.', '14592', '1', '0', '0', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609257', 'Impossible...', null, null, null, null, null, null, null, 'Невозможно...', '14810', '1', '0', '0', 'The Lich King');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609258', 'This... isn\'t... over...', null, null, null, null, null, null, null, 'Это... ещё... не конец...', '14811', '1', '0', '25', 'The Lich King');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609259', 'When next we meet it won\'t be on holy ground, paladin.', null, null, null, null, null, null, null, 'В следующий раз мы уже встретимся не на свещенной земле, паладин.', '14812', '1', '0', '1', 'The Lich King');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609260', 'Rise, Darion, and listen...', null, null, null, null, null, null, null, 'Поднимись, Дарион, и слушай...', '14593', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609261', 'We have all been witness to a terrible tragedy. The blood of good men has been shed upon this soil! Honorable knights, slain defending their lives - our lives!', null, null, null, null, null, null, null, 'Мы все стали свидетелемя ужасной трагедии. Кровь благородных людей пролилась на эту землю! Кровь славных рыцарей, что сложили свои головы защищая свои и наши жизни!', '14594', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609262', 'And while such things can never be forgotten, we must remain vigilant in our cause!', null, null, null, null, null, null, null, 'А таких подвигах никогда нельзя забывать, мы должны ревниво оберегать наши идеалы!', '14595', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609263', 'The Lich King must answer for what he has done and must not be allowed to cause further destruction to our world.', null, null, null, null, null, null, null, 'Король Лич ответит за то что он натворил, мы не позволим ему и дальше сеять разрушение в нашем мире.', '14596', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609264', 'I make a promise to you now, brothers and sisters: The Lich King will be defeated! On this day, I call for a union.', null, null, null, null, null, null, null, 'Братья и сёстры, сегодня я даю вам обед: Король Лич будет повержен!! В этот день, я объявляю о заключении союза.', '14597', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609265', 'The Argent Dawn and the Order of the Silver Hand will come together as one! We will succeed where so many before us have failed!', null, null, null, null, null, null, null, 'Серебрянный Рассвет и Серебрянная Длань объеденятся! Мы преуспеем там где прежде все терпели неудачю!', '14598', '0', '0', '0', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609266', 'We will take the fight to Arthas and tear down the walls of Icecrown!', null, null, null, null, null, null, null, 'Мы сразимся с Артосом и стены цитаделя Ледяной Короны падут!', '14599', '0', '0', '15', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609267', 'The Argent Crusade comes for you, Arthas!', null, null, null, null, null, null, null, 'Серебрянный Авангард идёт за тобой, Артас!', '14600', '1', '0', '15', 'Highlord Tirion Fordring');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609268', 'So too do the Knights of the Ebon Blade... While our kind has no place in your world, we will fight to bring an end to the Lich King. This I vow!', null, null, null, null, null, null, null, 'За это же ратуют и Рыцари из Чёрного Клинка... И хотя для нас нет места в вашем мире, мы будем биться за то чтобы не сложить Короля Лича. Клянусь!', '14710', '0', '0', '1', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609269', 'Thousands of Scourge rise up at the Highlord\'s command.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609270', 'The army marches towards Light\'s Hope Chapel.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609271', 'After over a hundred Defenders of the Light fall, Highlord Tirion Fordring arrives.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609272', 'flee', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Orbaz');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609273', 'kneels in defeat before Tirion Fordring.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609274', 'arrives.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Highlord Alexandros Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609275', 'becomes a shade of his past, and walks up to his father.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609276', 'hugs his father.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609277', 'disappears, and the Lich King appears.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Alexandros');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609278', 'becomes himself again...and is now angry.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Highlord Darion Mograine');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609279', 'casts a spell on Tirion.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'The Lich King');
INSERT IGNORE INTO `script_texts` VALUES ('29173', '-1609280', 'gasps for air.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'Highlord Tirion Fordring');
