INSERT IGNORE INTO `script_texts` VALUES ('32273', '-1595047', 'My time... has run out...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'infinite SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595070', 'Glad you could make it, Uther.', null, null, null, null, null, null, null, null, '12828', '0', '0', '1', 'culling SAY_PHASE101');
INSERT IGNORE INTO `script_texts` VALUES ('26528', '-1595071', 'Watch your tone with me, boy. You may be the prince, but I\'m still your superior as a paladin!', null, null, null, null, null, null, null, null, '12839', '0', '0', '25', 'culling SAY_PHASE102');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595072', 'As if I could forget. Listen, Uther, there\'s something about the plague you should know... ', null, null, null, null, null, null, null, null, '12829', '0', '0', '0', 'culling SAY_PHASE103');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595073', 'Oh, no. We\'re too late. These people have all been infected! They may look fine now, but it\'s just a matter of time before they turn into the undead!', null, null, null, null, null, null, null, null, '12830', '0', '0', '1', 'culling SAY_PHASE104');
INSERT IGNORE INTO `script_texts` VALUES ('26528', '-1595074', 'What?', null, null, null, null, null, null, null, null, '12840', '0', '0', '5', 'culling SAY_PHASE105');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595075', 'This entire city must be purged.', null, null, null, null, null, null, null, null, '12831', '0', '0', '1', 'culling SAY_PHASE106');
INSERT IGNORE INTO `script_texts` VALUES ('26528', '-1595076', 'How can you even consider that? There\'s got to be some other way.', null, null, null, null, null, null, null, null, '12841', '0', '0', '1', 'culling SAY_PHASE107');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595077', 'Damn it, Uther! As your future king, I order you to purge this city!', null, null, null, null, null, null, null, null, '12832', '1', '0', '5', 'culling SAY_PHASE108');
INSERT IGNORE INTO `script_texts` VALUES ('26528', '-1595078', 'You are not my king yet, boy! Nor would I obey that command even if you were!', null, null, null, null, null, null, null, null, '12842', '1', '0', '22', 'culling SAY_PHASE109');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595079', 'Then I must consider this an act of treason.', null, null, null, null, null, null, null, null, '12833', '0', '0', '0', 'culling SAY_PHASE110');
INSERT IGNORE INTO `script_texts` VALUES ('26528', '-1595080', 'Treason? Have you lost your mind, Arthas?', null, null, null, null, null, null, null, null, '12843', '0', '0', '5', 'culling SAY_PHASE111');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595081', 'Have I? Lord Uther, by my right of succession and the sovereignty of my crown, I hereby relieve you of your command and suspend your paladins from service.', null, null, null, null, null, null, null, null, '12834', '0', '0', '1', 'culling SAY_PHASE112');
INSERT IGNORE INTO `script_texts` VALUES ('26497', '-1595082', 'Arthas! You can\'t just--', null, null, null, null, null, null, null, null, '12837', '0', '0', '1', 'culling SAY_PHASE113');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595083', 'It\'s done! Those of you who have the will to save this land, follow me! The rest of you... get out of my sight!', null, null, null, null, null, null, null, null, '12835', '0', '0', '0', 'culling SAY_PHASE114');
INSERT IGNORE INTO `script_texts` VALUES ('26528', '-1595084', 'You\'ve just crossed a terrible threshold, Arthas.', null, null, null, null, null, null, null, null, '12844', '0', '0', '25', 'culling SAY_PHASE115');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595085', 'Jaina?', null, null, null, null, null, null, null, null, '12836', '0', '0', '1', 'culling SAY_PHASE116');
INSERT IGNORE INTO `script_texts` VALUES ('26497', '-1595086', 'I\'m sorry, Arthas. I can\'t watch you do this.', null, null, null, null, null, null, null, null, '12838', '0', '0', '1', 'culling SAY_PHASE117');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595087', 'Take position here, and I will lead a small force inside Stratholme to begin the culling. We must contain and purge the infected for the sake of all Lordaeron!', null, null, null, null, null, null, null, null, '14327', '0', '0', '1', 'culling SAY_PHASE118');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595088', 'Everyone looks ready. Remember, these people are all infected with the plague and will die soon. We must purge Stratholme to protect the remainder of Lordaeron from the Scourge. Let\'s go.', null, null, null, null, null, null, null, null, '14293', '0', '0', '1', 'culling SAY_PHASE201');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1595089', 'Prince Arthas, may the light be praised! Many people in the town have begun to fall seriously ill, can you help us?', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'culling SAY_PHASE202');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595090', 'I can only help you with a clean death.', null, null, null, null, null, null, null, null, '14294', '0', '0', '0', 'culling SAY_PHASE203');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1595091', 'What? This can\'t be!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'culling SAY_PHASE204');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595092', 'That was just the beginning.', null, null, null, null, null, null, null, null, '14295', '0', '0', '1', 'culling SAY_PHASE205');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595093', 'I won\'t allow it, Mal\'Ganis! Better that these people die by my hand than serve as your slaves in death!', null, null, null, null, null, null, null, null, '14296', '0', '0', '5', 'culling SAY_PHASE208');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595094', 'Mal\'ganis will send out some of his Scourge minions to interfere with us. Those of you with the strongest steel and magic shall go forth and destroy them. I will lead the rest of my forces in purging Stratholme of the infected.', null, null, null, null, null, null, null, null, '14885', '0', '0', '1', 'culling SAY_PHASE209');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595095', 'Champions, meet me at town hall at once! We must take the fight to Mal\'Ganis.', null, null, null, null, null, null, null, null, '14297', '0', '0', '1', 'culling SAY_PHASE210');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595096', 'Follow me. I know the way trought.', null, null, null, null, null, null, null, null, '14298', '0', '0', '1', 'culling SAY_PHASE301');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1595097', 'Ah, you\'ve finally arrived Prince Arthas. You\'re just in the nick of time.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'culling SAY_PHASE302');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595098', 'Yes, I\'m glad i could get you before the plague.', null, null, null, null, null, null, null, null, '14299', '0', '0', '1', 'culling SAY_PHASE303');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595099', 'What is this sorcery?', null, null, null, null, null, null, null, null, '14300', '0', '0', '0', 'culling SAY_PHASE304');
INSERT IGNORE INTO `script_texts` VALUES ('0', '-1595100', 'There\'s no need for you to understand, Arthas. All you need to do is die!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'culling SAY_PHASE305');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595101', 'Mal\'Ganis appears to have more than scourge in his arsenal. We should make haste.', null, null, null, null, null, null, null, null, '14301', '0', '0', '0', 'culling SAY_PHASE306');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595102', 'More vile sorcery! Be ready for anything.', null, null, null, null, null, null, null, null, '14302', '0', '0', '0', 'culling SAY_PHASE307');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595103', 'Let\'s move on.', null, null, null, null, null, null, null, null, '14303', '0', '0', '0', 'culling SAY_PHASE308');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595104', 'Watch your backs... they have us surrounded in this hall.', null, null, null, null, null, null, null, null, '14304', '0', '0', '0', 'culling SAY_PHASE309');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595105', 'One less obstacle to deal with.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'culling SAY_PHASE310');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595106', 'Mal\'Ganis is not making this easy...', null, null, null, null, null, null, null, null, '14305', '0', '0', '0', 'culling SAY_PHASE311');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595107', 'They\'re very persistent.', null, null, null, null, null, null, null, null, '14306', '0', '0', '0', 'culling SAY_PHASE312');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595108', 'What else can be put in my way?', null, null, null, null, null, null, null, null, '14307', '0', '0', '0', 'culling SAY_PHASE313');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595109', 'I do what i must for Lordaeron. And neither your words nor your actions will stop me.', null, null, null, null, null, null, null, null, '14309', '0', '0', '5', 'culling SAY_PHASE315');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595110', 'The quickest path to Mal\'Ganis lays behind that bookshelf ahead.', null, null, null, null, null, null, null, null, '14308', '0', '0', '0', 'culling SAY_PHASE401');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595111', 'This will only take a moment.', null, null, null, null, null, null, null, null, '14310', '0', '0', '0', 'culling SAY_PHASE402');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595112', 'I believe that secret passage still works!', null, null, null, null, null, null, null, null, '14311', '0', '0', '0', 'culling SAY_PHASE403');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595113', 'Let\'s move trought here as quickly as possible. If the undead don\'t kill us the fires might.', null, null, null, null, null, null, null, null, '14312', '0', '0', '0', 'culling SAY_PHASE404');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595114', 'Rest a moment and clear your lungs. But we must move again soon.', null, null, null, null, null, null, null, null, '14313', '0', '0', '0', 'culling SAY_PHASE405');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595115', 'That\'s enough, we must move again. Mal\'Ganis awaits.', null, null, null, null, null, null, null, null, '14314', '0', '0', '0', 'culling SAY_PHASE406');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595116', 'At last some good luck, Market Row has not caught fire yet. Mal\'Ganis is supposed to be on Crusader Square which is just ahead. Tell me when you\'re ready to move forth.', null, null, null, null, null, null, null, null, '14315', '0', '0', '0', 'culling SAY_PHASE407');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595117', 'Justice will be done!', null, null, null, null, null, null, null, null, '14316', '0', '0', '0', 'culling SAY_PHASE501');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595118', 'We\'re going to finish this right now Mal\'Ganis... just you and me!', null, null, null, null, null, null, null, null, '14317', '0', '0', '0', 'culling SAY_PHASE502');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595119', 'I\'ll hunt you to the ends of the earth if I have to! Do you hear me? To the ends of the earth!', null, null, null, null, null, null, null, null, '14318', '1', '0', '5', 'culling SAY_PHASE503');
INSERT IGNORE INTO `script_texts` VALUES ('26499', '-1595120', 'You performed well this day. Anything that Mal\'Ganis has left behind is yours. Take it as your reward. I must now begin plans for an expedition to Northrend.', null, null, null, null, null, null, null, null, '14319', '0', '0', '5', 'culling SAY_PHASE504');
INSERT IGNORE INTO `script_texts` VALUES ('31111', '-1700039', 'I PITY DA FOOL!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT IGNORE INTO `script_texts` VALUES ('31111', '-1700040', 'You\'ve already been pitied FOOL!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT IGNORE INTO `script_texts` VALUES ('31111', '-1700041', 'FOOL! I\'ve come to MGA to Pity you!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT IGNORE INTO `script_texts` VALUES ('26631', '-1600000', 'The chill that you feel is the herald of your doom!', null, null, null, null, null, null, null, null, '13173', '1', '0', '0', 'novos SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('26631', '-1600001', 'Such is the fate of all who oppose the Lich King.', null, null, null, null, null, null, null, null, '13175', '1', '0', '0', 'novos SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('26631', '-1600002', 'Your efforts... are in vain.', null, null, null, null, null, null, null, null, '13174', '1', '0', '0', 'novos SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('26631', '-1600003', 'Bolster my defenses! Hurry, curse you!', null, null, null, null, null, null, null, null, '13176', '1', '0', '0', 'novos SAY_NECRO_ADD');
INSERT IGNORE INTO `script_texts` VALUES ('26631', '-1600004', 'Surely you can see the futility of it all!', null, null, null, null, null, null, null, null, '13177', '1', '0', '0', 'novos SAY_REUBBLE_1');
INSERT IGNORE INTO `script_texts` VALUES ('26631', '-1600005', 'Just give up and die already!', null, null, null, null, null, null, null, null, '13178', '1', '0', '0', 'novos SAY_REUBBLE_2');
INSERT IGNORE INTO `script_texts` VALUES ('26630', '-1600006', 'More grunts, more glands, more FOOD!', null, null, null, null, null, null, null, null, '13181', '1', '0', '0', 'trollgore SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('26630', '-1600007', 'You have gone, me gonna eat you!', null, null, null, null, null, null, null, null, '13185', '1', '0', '0', 'trollgore SAY_KILL');
INSERT IGNORE INTO `script_texts` VALUES ('26630', '-1600008', 'So hungry! Must feed!', null, null, null, null, null, null, null, null, '13182', '1', '0', '0', 'trollgore SAY_CONSUME');
INSERT IGNORE INTO `script_texts` VALUES ('26630', '-1600009', 'Corpse go boom!', null, null, null, null, null, null, null, null, '13184', '1', '0', '0', 'trollgore SAY_EXPLODE');
INSERT IGNORE INTO `script_texts` VALUES ('26630', '-1600010', 'Aaaargh...', null, null, null, null, null, null, null, null, '13183', '1', '0', '0', 'trollgore SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('26632', '-1600011', 'Tharon\'ja sees all! The work of mortals shall not end the eternal dynasty!', null, null, null, null, null, null, null, null, '13862', '1', '0', '0', 'tharon\'ja SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('26632', '-1600012', 'As Tharon\'ja predicted.', null, null, null, null, null, null, null, null, '13863', '1', '0', '0', 'tharon\'ja SAY_KILL_1');
INSERT IGNORE INTO `script_texts` VALUES ('26632', '-1600013', 'As it was written.', null, null, null, null, null, null, null, null, '13864', '1', '0', '0', 'tharon\'ja SAY_KILL_2');
INSERT IGNORE INTO `script_texts` VALUES ('26632', '-1600014', 'Your flesh serves Tharon\'ja now!', null, null, null, null, null, null, null, null, '13865', '1', '0', '0', 'tharon\'ja SAY_FLESH_1');
INSERT IGNORE INTO `script_texts` VALUES ('26632', '-1600015', 'Tharon\'ja has a use for your mortal shell!', null, null, null, null, null, null, null, null, '13866', '1', '0', '0', 'tharon\'ja SAY_FLESH_2');
INSERT IGNORE INTO `script_texts` VALUES ('26632', '-1600016', 'No! A taste... all too brief!', null, null, null, null, null, null, null, null, '13867', '1', '0', '0', 'tharon\'ja SAY_SKELETON_1');
INSERT IGNORE INTO `script_texts` VALUES ('26632', '-1600017', 'Tharon\'ja will have more!', null, null, null, null, null, null, null, null, '13868', '1', '0', '0', 'tharon\'ja SAY_SKELETON_2');
INSERT IGNORE INTO `script_texts` VALUES ('26632', '-1600018', 'I\'m... impossible! Tharon\'ja is eternal! Tharon\'ja... is...', null, null, null, null, null, null, null, null, '13869', '1', '0', '0', 'tharon\'ja SAY_DEATH');
INSERT IGNORE INTO `script_texts` VALUES ('31134', '-1608009', 'Who among you can withstand my power?', null, null, null, null, null, null, null, null, '13950', '1', '0', '0', 'cyanigosa SAY_SPECIAL_ATTACK_2');
INSERT IGNORE INTO `script_texts` VALUES ('29315', '-1608010', 'Not--caww--get in way of--rrak-rrak--flee!', null, null, null, null, null, null, null, null, '14219', '1', '0', '0', 'erekem SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('29315', '-1608011', 'Ya ya ya yaaaa', null, null, null, null, null, null, null, null, '14222', '1', '0', '0', 'erekem SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('29315', '-1608012', 'Preeciouuss life---Ra-aak---Wasted!', null, null, null, null, null, null, null, null, '14223', '1', '0', '0', 'erekem SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('29315', '-1608013', 'Only the strong---Ra-aak---Survive!', null, null, null, null, null, null, null, null, '14224', '1', '0', '0', 'erekem SAY_SLAY_3');
