INSERT IGNORE INTO `script_texts` VALUES ('29315', '-1608017', 'Nasty little...A-ak, kaw! Kill! Yes, kill you!', null, null, null, null, null, null, null, null, '14221', '1', '0', '0', 'erekem SAY_BOTH_ADDS_KILLED');
INSERT IGNORE INTO `script_texts` VALUES ('29313', '-1608018', 'Stand aside, mortals!', null, null, null, null, null, null, null, null, '14230', '1', '0', '0', 'ichoron SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('29313', '-1608019', 'I am a force of nature!', null, null, null, null, null, null, null, null, '14234', '1', '0', '0', 'ichoron SAY_SLAY_1');
INSERT IGNORE INTO `script_texts` VALUES ('29313', '-1608020', 'I shall pass!', null, null, null, null, null, null, null, null, '14235', '1', '0', '0', 'ichoron SAY_SLAY_2');
INSERT IGNORE INTO `script_texts` VALUES ('29313', '-1608021', 'You can not stop the tide!', null, null, null, null, null, null, null, null, '14236', '1', '0', '0', 'ichoron SAY_SLAY_3');
