INSERT IGNORE INTO `script_texts` VALUES ('29313', '-1608023', 'I... am fury... unrestrained!', null, null, null, null, null, null, null, null, '14229', '1', '0', '0', 'ichoron SAY_SPAWN');
INSERT IGNORE INTO `script_texts` VALUES ('29313', '-1608024', 'I shall consume, decimate, devastate, and destroy! Yield now to the wrath of the pounding sea!', null, null, null, null, null, null, null, null, '14231', '1', '0', '0', 'ichoron SAY_ENRAGE');
INSERT IGNORE INTO `script_texts` VALUES ('29313', '-1608025', 'I will not be contained! Ngyah!!', null, null, null, null, null, null, null, null, '14233', '1', '0', '0', 'ichoron SAY_SHATTER');
INSERT IGNORE INTO `script_texts` VALUES ('29313', '-1608026', 'Water can hold any form, take any shape... overcome any obstacle.', null, null, null, null, null, null, null, null, '14232', '1', '0', '0', 'ichoron SAY_BUBBLE');
INSERT IGNORE INTO `script_texts` VALUES ('29266', '-1608027', 'It seems my freedom must be bought with blood...', null, null, null, null, null, null, null, null, '14498', '1', '0', '0', 'Xevozz SAY_AGGRO');
INSERT IGNORE INTO `script_texts` VALUES ('29266', '-1608028', 'Nothing personal.', null, null, null, null, null, null, null, null, '14504', '1', '0', '0', 'Xevozz SAY_SLAY_1');
