INSERT IGNORE INTO `script_texts` VALUES ('29266', '-1608032', 'Back in business! Now to execute an exit strategy.', null, null, null, null, null, null, null, null, '14498', '1', '0', '0', 'Xevozz SAY_SPAWN');
INSERT IGNORE INTO `script_texts` VALUES ('29266', '-1608033', 'It would seem that a renegotiation is in order.', null, null, null, null, null, null, null, null, '14503', '1', '0', '0', 'Xevozz SAY_CHARGED');
